CREATE OR REPLACE FUNCTION public.crm_action_set_status_working_of_leader(p_user_id integer, p_user_role integer, p_status boolean, p_date date)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_is_manager BOOL;
	v_is_super_admin BOOL;
	v_action_by INT;
	v_action_by_name VARCHAR;
BEGIN
	SELECT 
		U."UserId",
		U."FullName",
		UT."IsSuperAdmin",
		UT."IsManager"
		INTO v_action_by, v_action_by_name, v_is_super_admin, v_is_manager
	FROM "UserRole" UR 
		JOIN "User" U ON UR."UserId" = U."UserId"
		JOIN "UserTitle" UT ON UT."UserTitleId" = UR."UserTitleId"
	WHERE UR."UserRoleId" = p_user_role;
	
	IF(v_is_super_admin IS TRUE OR v_is_manager IS TRUE) THEN
		IF(EXISTS(SELECT * FROM "LeaderAttendent" LA WHERE LA."UserId" = p_user_id)) THEN		
			-- Cập nhật trạng thái
			UPDATE "LeaderAttendent" 
			SET "IsWorking" = p_status
			WHERE "UserId" = p_user_id;
			
			-- Cập nhật log

			IF(p_status IS FALSE) THEN
				INSERT INTO "LeaderOffLog"("WorkingDate", "UserId", "ActionBy", "ActionByName", "ActionDate")
				VALUES(p_date, p_user_id, v_action_by, v_action_by_name, NOW());
			ELSE 
				DELETE FROM "LeaderOffLog"
				WHERE "WorkingDate" = p_date
				AND "UserId" = p_user_id;
			END IF;
			SELECT 1, 'Cập nhật thành công' INTO v_id, v_mess;
		ELSE
			SELECT 0, 'Mã quản lý không hợp lệ' INTO v_id, v_mess;
		END IF;
	ELSE
		SELECT 0, 'Bạn không có quyền này' INTO v_id, v_mess;
	END IF;
	RETURN QUERY
		SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_add_salepoint(p_action_by integer, p_action_by_name character varying, p_salepoint_name character varying)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
-- 	v_is_manager BOOL;
BEGIN
-- 	SELECT 
-- 		UT."IsManager" INTO v_is_manager
-- 	FROM "User" U
-- 		JOIN "UserRole" UR ON U."UserId" = UR."UserId"
-- 		JOIN "UserTitle" UT ON UR."UserTitleId" = UT."UserTitleId"
-- 	WHERE U."UserId" = p_action_by;
-- 		
	IF(NOT EXISTS (SELECT 1 FROM "SalePoint" WHERE "SalePointName" = p_salePoint_name)) THEN
		INSERT INTO "SalePoint"(
			"SalePointName",
			"IsActive",
			"IsDeleted",	
			"ActionBy",
			"ActionByName",
			"ActionDate"
		)
		VALUES(
			p_salePoint_name,
			TRUE,
			FALSE,
			p_action_by,
			p_action_by_name,
			v_time
		);
		v_id := 1;
		v_mess := 'Tạo điểm bán thành công';
	ELSE
		v_id := -1;
		v_mess := 'Tên điểm bán đã tồn tại';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_confirm_transition(p_user_role_id integer, p_note character varying, p_list_item text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_is_leader BOOL;
	v_is_manager BOOL;
	v_name VARCHAR;
	v_user_id INT;
	ele JSON;
BEGIN	
	SELECT UT."IsManager", UT."IsLeader", U."UserId" INTO v_is_manager, v_is_leader, v_user_id
	FROM "UserRole" U JOIN "UserTitle" UT ON U."UserTitleId" = UT."UserTitleId" 
	WHERE U."UserRoleId" = p_user_role_id;
	
	SELECT U."FullName" INTO v_name
	FROM "User" U 
	WHERE U."UserId" = v_user_id;
	
	IF (v_is_leader IS TRUE) THEN
		FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
			UPDATE "Transition"
			SET 
				"ConfirmTrans" = (ele ->> 'ConfirmTrans') :: INT,
				"ConfirmTransDup" = (ele ->> 'ConfirmTransDup') :: INT,
				"ConfirmBy" = v_user_id,
				"ConfirmByName" = v_name,
				"ConfirmDate" = NOW(),
				"ConfirmStatusId" = 2,
				"Note" = p_note
			WHERE 
				"TransitionId" = (ele ->> 'TransitionId') :: INT
				AND "ConfirmStatusId" = 1	;
		END LOOP;
		SELECT v_user_id, 'Xác Nhận Thành Công' INTO v_id, v_mess;

	ELSEIF v_is_manager IS TRUE THEN
		FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
			UPDATE "Transition"
			SET 
				"ConfirmBy" = v_user_id,
				"ConfirmByName" = v_name,
				"ConfirmDate" = NOW(),
				"ConfirmStatusId" = 2,
				"Note" = p_note
			WHERE 
				"TransitionId" = (ele ->> 'TransitionId') :: INT
				AND "ConfirmStatusId" = 1	;
		END LOOP;
		SELECT v_user_id, 'Xác Nhận Thành Công' INTO v_id, v_mess;
	ELSE
		SELECT -1, 'Bạn Không Có Quyền Này' INTO v_id, v_mess;
	END IF;

	RETURN QUERY
		SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_confirm_transition_v2(p_user_role_id integer, p_note character varying, p_list_item text, p_is_confirm boolean, p_trans_type_id integer, p_sale_point_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_is_staff BOOL;
	v_name VARCHAR;
	v_user_id INT;
	ele JSON;
	v_inventory_id INT;
	v_total INT;
	v_total_dup INT;
	v_total_check INT := 0;
	v_total_dup_check INT := 0;
	v_shift_dis_id INT;
BEGIN	

	SELECT UT."IsStaff", U."UserId" INTO v_is_staff, v_user_id
	FROM "UserRole" U JOIN "UserTitle" UT ON U."UserTitleId" = UT."UserTitleId" 
	WHERE U."UserRoleId" = p_user_role_id;
	
	SELECT U."FullName" INTO v_name
	FROM "User" U 
	WHERE U."UserId" = v_user_id;
	
		IF p_is_confirm IS TRUE THEN
			IF p_trans_type_id = 1 THEN
				FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN
						SELECT 
							I."TotalRemaining",
							I."TotalDupRemaining"
						INTO 
							v_total_check,
							v_total_dup_check
						FROM "Inventory" I 
						WHERE I."SalePointId" = p_sale_point_id
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE;
						
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) OR v_total_dup_check < (ele ->> 'TotalTransDup')::INT THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;			
					ELSE 
						SELECT 
							I."TotalRemaining"
						INTO 
							v_total_check
						FROM "Scratchcard" I
						WHERE I."SalePointId" = p_sale_point_id
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;	
						
					END IF;
				END LOOP;
			END IF;
			
			IF p_trans_type_id = 2 THEN
				FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN
						SELECT 
							I."TotalRemaining",
							I."TotalDupRemaining"
						INTO 
							v_total_check,
							v_total_dup_check
						FROM "Inventory" I 
						WHERE I."SalePointId" = 0
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE;
						
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) OR v_total_dup_check < (ele ->> 'TotalTransDup')::INT THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;
					ELSE 
						SELECT 
							I."TotalRemaining"
						INTO 
							v_total_check
						FROM "Scratchcard" I
						WHERE I."SalePointId" = 0
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;	
						
					END IF;
				END LOOP;
			END IF;
		
			FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
				
				UPDATE "Transition"
				SET 
					"ConfirmTrans" = (ele ->> 'TotalTrans')::INT,
					"ConfirmTransDup" = (ele ->> 'TotalTransDup')::INT,
					"ConfirmBy" = v_user_id,
					"ConfirmByName" = v_name,
					"ConfirmDate" = NOW(),
					"ConfirmStatusId" = 2,
					"Note" = p_note
				WHERE "TransitionId" = (ele ->> 'TransitionId')::INT
					AND "ConfirmStatusId" = 1;
					
				IF p_trans_type_id = 1 THEN
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN		
						IF COALESCE((ele ->> 'TotalTrans')::INT, 0) > 0 THEN
					
							UPDATE "Inventory"
							SET
								"TotalRemaining" = "TotalRemaining" - (ele ->> 'TotalTrans')::INT
							WHERE "SalePointId" = p_sale_point_id
								AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
								AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						END IF;
					
						IF COALESCE((ele ->> 'TotalTransDup')::INT, 0) > 0 THEN
						
							UPDATE "Inventory"
							SET
								"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'TotalTransDup')::INT
							WHERE "SalePointId" = p_sale_point_id
								AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
								AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
								
						END IF;
						
						IF NOT EXISTS (SELECT 1 FROM "Inventory" WHERE "SalePointId" = 0 
							AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN
							
							INSERT INTO "Inventory"(
								"LotteryDate",
								"LotteryChannelId",
								"TotalReceived",
								"TotalRemaining",
								"TotalDupReceived",
								"TotalDupRemaining",
								"SalePointId"
							) VALUES (
								(ele ->> 'LotteryDate')::DATE,
								(ele ->> 'LotteryChannelId')::INT,
								(ele ->> 'TotalTrans')::INT,
								(ele ->> 'TotalTrans')::INT,
								(ele ->> 'TotalTransDup')::INT,
								(ele ->> 'TotalTransDup')::INT,
								0
							);
							
						ELSE 
						
							UPDATE "Inventory" I
							SET
								"TotalReceived" = I."TotalReceived" + (ele ->> 'TotalTrans')::INT,
								"TotalRemaining" = I."TotalRemaining" + (ele ->> 'TotalTrans')::INT,
								"TotalDupReceived" = I."TotalDupReceived" + (ele ->> 'TotalTransDup')::INT,
								"TotalDupRemaining" = I."TotalDupRemaining" + (ele ->> 'TotalTransDup')::INT
							WHERE I."LotteryDate" = (ele ->> 'LotteryDate')::DATE
								AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
								AND I."SalePointId" = 0;
						
						END IF;
					ELSE
						UPDATE "Scratchcard"
						SET "TotalRemaining" = "TotalRemaining" - COALESCE((ele ->> 'TotalTrans')::INT, 0)
						WHERE "SalePointId" = p_sale_point_id 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						IF NOT EXISTS (SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = 0 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN
							
							INSERT INTO "Scratchcard"(
								"LotteryChannelId",
								"TotalReceived",
								"TotalRemaining",
								"SalePointId"
							) VALUES (
								(ele ->> 'LotteryChannelId')::INT,
								(ele ->> 'TotalTrans')::INT,
								(ele ->> 'TotalTrans')::INT,
								0
							);
							
						ELSE 
						
							UPDATE "Scratchcard" I
							SET
								"TotalReceived" = I."TotalReceived" + (ele ->> 'TotalTrans')::INT,
								"TotalRemaining" = I."TotalRemaining" + (ele ->> 'TotalTrans')::INT							
							WHERE I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
								AND I."SalePointId" = 0;
								
						END IF;
					END IF;
					

				
				ELSEIF p_trans_type_id = 2 THEN 
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN	
						IF COALESCE((ele ->> 'TotalTrans')::INT, 0) > 0 THEN
							
							IF NOT EXISTS (SELECT I."InventoryId" 
												FROM "Inventory" I 
												WHERE I."LotteryChannelId" =  (ele ->> 'LotteryChannelId')::INT
												AND I."SalePointId" = p_sale_point_id
												AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE)
							THEN
									
										INSERT INTO "Inventory"(
										"LotteryDate",
										"LotteryChannelId",
										"TotalReceived",
										"TotalRemaining",
										"TotalDupReceived",
										"TotalDupRemaining",
										"SalePointId"
									) VALUES (
										(ele ->> 'LotteryDate')::DATE,
										(ele ->> 'LotteryChannelId')::INT,
										0,
										(ele ->> 'TotalTrans')::INT,
										0,
										(ele ->> 'TotalTransDup')::INT,
										p_sale_point_id
									);
										
							ELSE
									
									UPDATE "Inventory"
									SET
										"TotalRemaining" = "TotalRemaining" + (ele ->> 'TotalTrans')::INT
									WHERE "SalePointId" = p_sale_point_id
										AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
										AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							END IF;
								
						
						END IF;
					
						IF COALESCE((ele ->> 'TotalTransDup')::INT, 0) > 0 THEN
							IF EXISTS (SELECT I."InventoryId" 
												FROM "Inventory" I 
												WHERE I."LotteryChannelId" =  (ele ->> 'LotteryChannelId')::INT
												AND I."SalePointId" = p_sale_point_id
												AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE)
							THEN
								UPDATE "Inventory"
								SET
									"TotalDupRemaining" = "TotalDupRemaining" + (ele ->> 'TotalTransDup')::INT
								WHERE "SalePointId" = p_sale_point_id
									AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
									AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							ELSE
								INSERT INTO "Inventory"(
										"LotteryDate",
										"LotteryChannelId",
										"TotalReceived",
										"TotalRemaining",
										"TotalDupReceived",
										"TotalDupRemaining",
										"SalePointId"
									) VALUES (
										(ele ->> 'LotteryDate')::DATE,
										(ele ->> 'LotteryChannelId')::INT,
										0,
										(ele ->> 'TotalTrans')::INT,
										0,
										(ele ->> 'TotalTransDup')::INT,
										p_sale_point_id
									);
									END IF;
								
						END IF;
					
						SELECT "InventoryId", "TotalRemaining", "TotalDupRemaining"
						INTO  v_inventory_id, v_total, v_total_dup
						FROM "Inventory" I
						WHERE I."LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."SalePointId" = 0;
						
						IF v_total >= (ele ->> 'TotalTrans')::INT THEN 
						
							UPDATE "Inventory" I
							SET
								"TotalRemaining" = I."TotalRemaining" -  (ele ->> 'TotalTrans')::INT
							WHERE I."InventoryId" = v_inventory_id;
						
						END IF;
						
						IF v_total_dup >= (ele ->> 'TotalTransDup')::INT THEN 
						
							UPDATE "Inventory" I
							SET
								"TotalDupRemaining" = I."TotalDupRemaining" - (ele ->> 'TotalTransDup')::INT
							WHERE I."InventoryId" = v_inventory_id;
						
						END IF;
					ELSE
					
						UPDATE "Scratchcard"
						SET "TotalRemaining" = "TotalRemaining" + COALESCE((ele ->> 'TotalTrans')::INT, 0)
						WHERE "SalePointId" = p_sale_point_id 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						IF EXISTS (SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = 0 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN

							UPDATE "Scratchcard" I
							SET
								"TotalReceived" = I."TotalReceived" - (ele ->> 'TotalTrans')::INT,
								"TotalRemaining" = I."TotalRemaining" - (ele ->> 'TotalTrans')::INT							
							WHERE I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
								AND I."SalePointId" = 0;
								
						END IF;
							
					END IF;
				
				ELSEIF p_trans_type_id = 3 THEN
				
					IF COALESCE((ele ->> 'TotalTrans')::INT, 0) > 0 THEN
				
						UPDATE "Inventory"
						SET
							"TotalRemaining" = "TotalRemaining" - (ele ->> 'TotalTrans')::INT
						WHERE "SalePointId" = p_sale_point_id
							AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
					
					END IF;
				
					IF COALESCE((ele ->> 'TotalTransDup')::INT, 0) > 0 THEN
					
						UPDATE "Inventory"
						SET
							"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'TotalTransDup')::INT
						WHERE "SalePointId" = p_sale_point_id
							AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
					
					END IF;
					
					IF NOT EXISTS (SELECT 1 FROM "Inventory" WHERE "SalePointId" = 0 
						AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
						AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN
						
						INSERT INTO "Inventory"(
							"LotteryDate",
							"LotteryChannelId",
							"TotalReceived",
							"TotalRemaining",
							"TotalDupReceived",
							"TotalDupRemaining",
							"SalePointId"
						) VALUES (
							(ele ->> 'LotteryDate')::DATE,
							(ele ->> 'LotteryChannelId')::INT,
							(ele ->> 'TotalTrans')::INT,
							(ele ->> 'TotalTrans')::INT,
							(ele ->> 'TotalTransDup')::INT,
							(ele ->> 'TotalTransDup')::INT,
							p_sale_point_id
						);
						
					ELSE 
					
						UPDATE "Inventory" I
						SET
							"TotalReceived" = I."TotalReceived" + (ele ->> 'TotalTrans')::INT,
							"TotalRemaining" = I."TotalRemaining" + (ele ->> 'TotalTrans')::INT,
							"TotalDupReceived" = I."TotalDupReceived" + (ele ->> 'TotalTransDup')::INT,
							"TotalDupRemaining" = I."TotalDupRemaining" + (ele ->> 'TotalTransDup')::INT
						WHERE I."LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."SalePointId" = 0;
					
					END IF;
				
				ELSE
					
					v_id := -1;
					v_mess := 'Có lỗi xảy ra';
					
				END IF;
			
			END LOOP;
		
		ELSE 
		
			FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
			
				UPDATE "Transition"
				SET
					"ConfirmBy" = v_user_id,
					"ConfirmByName" = v_name,
					"ConfirmDate" = NOW(),
					"ConfirmStatusId" = 3,
					"Note" = p_note
				WHERE "TransitionId" = (ele ->> 'TransitionId')::INT
					AND "ConfirmStatusId" = 1;
					
			END LOOP;	
		
		END IF;
	
		v_id := 1;
		v_mess := 'Xác nhận thành công';

	RETURN QUERY
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;

		RETURN QUERY
		SELECT v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_confirm_transition_v3(p_user_role_id integer, p_note character varying, p_list_item text, p_is_confirm boolean, p_trans_type_id integer, p_sale_point_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_is_staff BOOL;
	v_name VARCHAR;
	v_user_id INT;
	ele JSON;
	v_inventory_id INT;
	v_total INT;
	v_total_dup INT;
	v_total_check INT := 0;
	v_total_dup_check INT := 0;
	v_shift_dis_id INT;
BEGIN	

	SELECT UT."IsStaff", U."UserId" INTO v_is_staff, v_user_id
	FROM "UserRole" U JOIN "UserTitle" UT ON U."UserTitleId" = UT."UserTitleId" 
	WHERE U."UserRoleId" = p_user_role_id;
	
	SELECT U."FullName" INTO v_name
	FROM "User" U 
	WHERE U."UserId" = v_user_id;
	
		IF p_is_confirm IS TRUE THEN
			IF p_trans_type_id = 1 THEN
				FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN
						SELECT 
							I."TotalRemaining",
							I."TotalDupRemaining"
						INTO 
							v_total_check,
							v_total_dup_check
						FROM "Inventory" I 
						WHERE I."SalePointId" = p_sale_point_id
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE;
						
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) OR v_total_dup_check < (ele ->> 'TotalTransDup')::INT THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;			
					ELSE 
						SELECT 
							I."TotalRemaining"
						INTO 
							v_total_check
						FROM "Scratchcard" I
						WHERE I."SalePointId" = p_sale_point_id
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;	
						
					END IF;
				END LOOP;
			END IF;
			
			IF p_trans_type_id = 2 THEN
				FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN
						SELECT 
							I."TotalRemaining",
							I."TotalDupRemaining"
						INTO 
							v_total_check,
							v_total_dup_check
						FROM "Inventory" I 
						WHERE I."SalePointId" = 0
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE;
						
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) OR v_total_dup_check < (ele ->> 'TotalTransDup')::INT THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;
					ELSE 
						SELECT 
							I."TotalRemaining"
						INTO 
							v_total_check
						FROM "Scratchcard" I
						WHERE I."SalePointId" = 0
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
						IF(NOT EXISTS (SELECT 1 FROM "Transition" T WHERE T."TransitionId" = (ele ->> 'TransitionId')::INT AND T."ConfirmStatusId" = 1)) THEN
							RAISE 'Yêu cầu đã được xác nhận trước';
						END IF;
						
						IF v_total_check < COALESCE((ele ->> 'TotalTrans')::INT) THEN
							RAISE 'Không đủ vé trong kho hàng';
						END IF;	
						
					END IF;
				END LOOP;
			END IF;
		
			FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
				
				UPDATE "Transition"
				SET 
					"ConfirmTrans" = (ele ->> 'TotalTrans')::INT,
					"ConfirmTransDup" = (ele ->> 'TotalTransDup')::INT,
					"ConfirmBy" = v_user_id,
					"ConfirmByName" = v_name,
					"ConfirmDate" = NOW(),
					"ConfirmStatusId" = 2,
					"Note" = p_note
				WHERE "TransitionId" = (ele ->> 'TransitionId')::INT
					AND "ConfirmStatusId" = 1;
					
				IF p_trans_type_id = 1 THEN
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN		
						IF COALESCE((ele ->> 'TotalTrans')::INT, 0) > 0 THEN
					
							UPDATE "Inventory"
							SET
								"TotalRemaining" = "TotalRemaining" - (ele ->> 'TotalTrans')::INT
							WHERE "SalePointId" = p_sale_point_id
								AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
								AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						END IF;
					
						IF COALESCE((ele ->> 'TotalTransDup')::INT, 0) > 0 THEN
						
							UPDATE "Inventory"
							SET
								"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'TotalTransDup')::INT
							WHERE "SalePointId" = p_sale_point_id
								AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
								AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
								
						END IF;
						
						IF NOT EXISTS (SELECT 1 FROM "Inventory" WHERE "SalePointId" = 0 
							AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN
							
							INSERT INTO "Inventory"(
								"LotteryDate",
								"LotteryChannelId",
								"TotalReceived",
								"TotalRemaining",
								"TotalDupReceived",
								"TotalDupRemaining",
								"SalePointId"
							) VALUES (
								(ele ->> 'LotteryDate')::DATE,
								(ele ->> 'LotteryChannelId')::INT,
								(ele ->> 'TotalTrans')::INT,
								(ele ->> 'TotalTrans')::INT,
								(ele ->> 'TotalTransDup')::INT,
								(ele ->> 'TotalTransDup')::INT,
								0
							);
							
						ELSE 
						
							UPDATE "Inventory" I
							SET
								"TotalReceived" = I."TotalReceived" + (ele ->> 'TotalTrans')::INT,
								"TotalRemaining" = I."TotalRemaining" + (ele ->> 'TotalTrans')::INT,
								"TotalDupReceived" = I."TotalDupReceived" + (ele ->> 'TotalTransDup')::INT,
								"TotalDupRemaining" = I."TotalDupRemaining" + (ele ->> 'TotalTransDup')::INT
							WHERE I."LotteryDate" = (ele ->> 'LotteryDate')::DATE
								AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
								AND I."SalePointId" = 0;
						
						END IF;
					ELSE
						UPDATE "Scratchcard"
						SET "TotalRemaining" = "TotalRemaining" - COALESCE((ele ->> 'TotalTrans')::INT, 0)
						WHERE "SalePointId" = p_sale_point_id 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						IF NOT EXISTS (SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = 0 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN
							
							INSERT INTO "Scratchcard"(
								"LotteryChannelId",
								"TotalReceived",
								"TotalRemaining",
								"SalePointId"
							) VALUES (
								(ele ->> 'LotteryChannelId')::INT,
								(ele ->> 'TotalTrans')::INT,
								(ele ->> 'TotalTrans')::INT,
								0
							);
							
						ELSE 
						
							UPDATE "Scratchcard" I
							SET
								"TotalReceived" = I."TotalReceived" + (ele ->> 'TotalTrans')::INT,
								"TotalRemaining" = I."TotalRemaining" + (ele ->> 'TotalTrans')::INT							
							WHERE I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
								AND I."SalePointId" = 0;
								
						END IF;
					END IF;
					

				
				ELSEIF p_trans_type_id = 2 THEN 
					IF ((ele ->> 'IsScratchcard')::BOOL) IS FALSE THEN	
						IF COALESCE((ele ->> 'TotalTrans')::INT, 0) > 0 THEN
							
							IF NOT EXISTS (SELECT I."InventoryId" 
												FROM "Inventory" I 
												WHERE I."LotteryChannelId" =  (ele ->> 'LotteryChannelId')::INT
												AND I."SalePointId" = p_sale_point_id
												AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE)
							THEN
									
										INSERT INTO "Inventory"(
										"LotteryDate",
										"LotteryChannelId",
										"TotalReceived",
										"TotalRemaining",
										"TotalDupReceived",
										"TotalDupRemaining",
										"SalePointId"
									) VALUES (
										(ele ->> 'LotteryDate')::DATE,
										(ele ->> 'LotteryChannelId')::INT,
										0,
										(ele ->> 'TotalTrans')::INT,
										0,
										(ele ->> 'TotalTransDup')::INT,
										p_sale_point_id
									);
										
							ELSE
									
									UPDATE "Inventory"
									SET
										"TotalRemaining" = "TotalRemaining" + (ele ->> 'TotalTrans')::INT
									WHERE "SalePointId" = p_sale_point_id
										AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
										AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							END IF;
								
						
						END IF;
					
						IF COALESCE((ele ->> 'TotalTransDup')::INT, 0) > 0 THEN
							IF EXISTS (SELECT I."InventoryId" 
												FROM "Inventory" I 
												WHERE I."LotteryChannelId" =  (ele ->> 'LotteryChannelId')::INT
												AND I."SalePointId" = p_sale_point_id
												AND I."LotteryDate" = (ele ->> 'LotteryDate')::DATE)
							THEN
								UPDATE "Inventory"
								SET
									"TotalDupRemaining" = "TotalDupRemaining" + (ele ->> 'TotalTransDup')::INT
								WHERE "SalePointId" = p_sale_point_id
									AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
									AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							ELSE
								INSERT INTO "Inventory"(
										"LotteryDate",
										"LotteryChannelId",
										"TotalReceived",
										"TotalRemaining",
										"TotalDupReceived",
										"TotalDupRemaining",
										"SalePointId"
									) VALUES (
										(ele ->> 'LotteryDate')::DATE,
										(ele ->> 'LotteryChannelId')::INT,
										0,
										(ele ->> 'TotalTrans')::INT,
										0,
										(ele ->> 'TotalTransDup')::INT,
										p_sale_point_id
									);
									END IF;
								
						END IF;
					
						SELECT "InventoryId", "TotalRemaining", "TotalDupRemaining"
						INTO  v_inventory_id, v_total, v_total_dup
						FROM "Inventory" I
						WHERE I."LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."SalePointId" = 0;
						
						IF v_total >= (ele ->> 'TotalTrans')::INT THEN 
						
							UPDATE "Inventory" I
							SET
								"TotalRemaining" = I."TotalRemaining" -  (ele ->> 'TotalTrans')::INT
							WHERE I."InventoryId" = v_inventory_id;
						
						END IF;
						
						IF v_total_dup >= (ele ->> 'TotalTransDup')::INT THEN 
						
							UPDATE "Inventory" I
							SET
								"TotalDupRemaining" = I."TotalDupRemaining" - (ele ->> 'TotalTransDup')::INT
							WHERE I."InventoryId" = v_inventory_id;
						
						END IF;
					ELSE
					
						UPDATE "Scratchcard"
						SET "TotalRemaining" = "TotalRemaining" + COALESCE((ele ->> 'TotalTrans')::INT, 0)
						WHERE "SalePointId" = p_sale_point_id 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
							
						IF EXISTS (SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = 0 
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN

							UPDATE "Scratchcard" I
							SET
								"TotalReceived" = I."TotalReceived" - (ele ->> 'TotalTrans')::INT,
								"TotalRemaining" = I."TotalRemaining" - (ele ->> 'TotalTrans')::INT							
							WHERE I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
								AND I."SalePointId" = 0;
								
						END IF;
							
					END IF;
				
				ELSEIF p_trans_type_id = 3 THEN
				
					IF COALESCE((ele ->> 'TotalTrans')::INT, 0) > 0 THEN
				
						UPDATE "Inventory"
						SET
							"TotalRemaining" = "TotalRemaining" - (ele ->> 'TotalTrans')::INT
						WHERE "SalePointId" = p_sale_point_id
							AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
					
					END IF;
				
					IF COALESCE((ele ->> 'TotalTransDup')::INT, 0) > 0 THEN
					
						UPDATE "Inventory"
						SET
							"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'TotalTransDup')::INT
						WHERE "SalePointId" = p_sale_point_id
							AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
					
					END IF;
					
					IF NOT EXISTS (SELECT 1 FROM "Inventory" WHERE "SalePointId" = 0 
						AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
						AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN
						
						INSERT INTO "Inventory"(
							"LotteryDate",
							"LotteryChannelId",
							"TotalReceived",
							"TotalRemaining",
							"TotalDupReceived",
							"TotalDupRemaining",
							"SalePointId"
						) VALUES (
							(ele ->> 'LotteryDate')::DATE,
							(ele ->> 'LotteryChannelId')::INT,
							(ele ->> 'TotalTrans')::INT,
							(ele ->> 'TotalTrans')::INT,
							(ele ->> 'TotalTransDup')::INT,
							(ele ->> 'TotalTransDup')::INT,
							p_sale_point_id
						);
						
					ELSE 
					
						UPDATE "Inventory" I
						SET
							"TotalReceived" = I."TotalReceived" + (ele ->> 'TotalTrans')::INT,
							"TotalRemaining" = I."TotalRemaining" + (ele ->> 'TotalTrans')::INT,
							"TotalDupReceived" = I."TotalDupReceived" + (ele ->> 'TotalTransDup')::INT,
							"TotalDupRemaining" = I."TotalDupRemaining" + (ele ->> 'TotalTransDup')::INT
						WHERE I."LotteryDate" = (ele ->> 'LotteryDate')::DATE
							AND I."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
							AND I."SalePointId" = 0;
					
					END IF;
				
				ELSE
					
					v_id := -1;
					v_mess := 'Có lỗi xảy ra';
					
				END IF;
			
			END LOOP;
		
		ELSE 
		
			FOR ele IN SELECT * FROM json_array_elements(p_list_item::JSON) LOOP 		
			
				UPDATE "Transition"
				SET
					"ConfirmBy" = v_user_id,
					"ConfirmByName" = v_name,
					"ConfirmDate" = NOW(),
					"ConfirmStatusId" = 3,
					"Note" = p_note
				WHERE "TransitionId" = (ele ->> 'TransitionId')::INT
					AND "ConfirmStatusId" = 1;
					
			END LOOP;	
		
		END IF;
	
		v_id := 1;
		v_mess := 'Xác nhận thành công';

	RETURN QUERY
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;

		RETURN QUERY
		SELECT v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_delete_guest_action(p_guest_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT;
    v_mess TEXT;
    ele JSON;
    v_time TIMESTAMP := NOW();
BEGIN

       UPDATE "GuestAction"
			 SET "IsDeleted" = TRUE 
			 WHERE "GuestId" = p_guest_id;

        v_id := 1;
        v_mess := 'Xóa thành công';

    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_delete_winning_log(p_action_by integer, p_action_by_name character varying, p_winning_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_data TEXT;
BEGIN
	
	IF EXISTS (SELECT 1 FROM "Winning" W WHERE W."WinningId" = p_winning_id) THEN
		SELECT
			TO_JSONB(W.*)::TEXT INTO v_data
		FROM "Winning" W
		WHERE W."WinningId" = p_winning_id;
		
		DELETE FROM "Winning"
		WHERE "WinningId" = p_winning_id;
		
		INSERT INTO "LogDelete"(
			"LogTypeId",
			"LogData",
			"ActionBy",
			"ActionByName",
			"ActionDate"
		)
		VALUES(
			1,
			v_data,
			p_action_by,
			p_action_by_name,
			v_time
		) RETURNING "LogDeleteId" INTO v_id;
		
		v_mess := 'Xoá thành công';
		
	ELSE
		RAISE 'Dòng này đã bị xoá hoặc không tồn tại';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_distribute_for_sales_point(p_action_by integer, p_action_by_name character varying, p_lottery_date timestamp without time zone, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	ele JSON;
	v_last_received INT;
	v_last_received_dup INT;
	re RECORD;
	v_flag INT := 0;
BEGIN
	
	CREATE TEMP TABLE dataTotal ON COMMIT DROP AS (
		SELECT 
			(dt ->> 'AgencyId') :: INT AS "AgencyId",
			(dt ->> 'LotteryChannelId') :: INT AS "LotteryChannelId",
			(dt ->> 'SalePointId') :: INT AS "SalePointId",
			(dt ->> 'TotalReceived') :: INT AS "TotalReceived" ,
			(dt ->> 'TotalDupReceived') :: INT AS "TotalDupReceived"
		FROM json_array_elements(v_data) dt 
	);
-- 	WITH tmp AS(
-- 		SELECT
-- 			dt."AgencyId", 
-- 			dt."LotteryChannelId",
-- 			SUM(dt."TotalReceived") AS "TotalReceived",
-- 			SUM(dt."TotalDupReceived") AS "TotalDupReceived"
-- 		FROM dataTotal dt
-- 		GROUP BY 
-- 			dt."AgencyId", 
-- 			dt."LotteryChannelId"
-- 	),
-- 	tmp2 AS(
-- 		SELECT 
-- 			dt."AgencyId",
-- 			dt."LotteryChannelId",
-- 			SUM(dt."TotalReceived") AS "TotalReceived",
-- 			SUM(dt."TotalDupReceived") AS "TotalDupReceived"
-- 		FROM "InventoryLog" dt
-- 		WHERE dt."LotteryDate" = p_lottery_date::DATE 
-- 		GROUP BY 
-- 			dt."AgencyId",
-- 			dt."LotteryChannelId"
-- 	),
-- 	tmp3 AS(
-- 		SELECT 
-- 			IF."AgencyId",
-- 			IF."LotteryChannelId",
-- 			IF."TotalRemaining"
-- 		FROM "InventoryFull" IF
-- 		WHERE IF."LotteryDate" = p_lottery_date::DATE 
-- 	),
-- 	tmp4 AS(
-- 		SELECT
-- 			tmp3."TotalRemaining" - (tmp."TotalReceived" + tmp."TotalDupReceived" - COALESCE(tmp2."TotalReceived", 0) - COALESCE(tmp2."TotalDupReceived", 0)) AS "Remain"
-- 		FROM tmp
-- 			JOIN tmp3 ON tmp."AgencyId" = tmp3."AgencyId" AND tmp."LotteryChannelId" = tmp3."LotteryChannelId"
-- 			LEFT JOIN tmp2 ON tmp."AgencyId" = tmp2."AgencyId" AND tmp."LotteryChannelId" = tmp2."LotteryChannelId"
-- 	)
-- 	SELECT COUNT(*) INTO v_flag
-- 	FROM tmp4
-- 	WHERE "Remain" < 0;
	
	v_flag := 0;
	
	IF(v_flag <= 0) THEN
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
			IF NOT EXISTS (SELECT 1 FROM "InventoryLog" WHERE "LotteryDate" = p_lottery_date::DATE 
				AND "AgencyId" = (ele ->> 'AgencyId')::INT 
				AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				AND "SalePointId" = (ele ->> 'SalePointId')::INT) THEN	
				
				INSERT INTO "InventoryLog"(
					"LotteryDate",
					"LotteryChannelId",
					"AgencyId",
					"SalePointId",
					"TotalReceived",
					"TotalDupReceived",
					"ActionBy",
					"ActionByName"
				) VALUES (
					p_lottery_date::DATE,
					(ele ->> 'LotteryChannelId')::INT,
					(ele ->> 'AgencyId')::INT,
					(ele ->> 'SalePointId')::INT,
					COALESCE((ele ->> 'TotalReceived')::INT, 0),
					COALESCE((ele ->> 'TotalDupReceived')::INT, 0),
					p_action_by,
					p_action_by_name
				);
				--Cập nhật report vé thường
				IF COALESCE((ele ->> 'TotalReceived')::INT, 0) > 0 THEN 
					WITH shiftTran AS (
						SELECT 
							SF."ShiftDistributeId",
							SF."ShiftId",
							SF."UserId"
						FROM "ShiftTransfer" SF
						WHERE SF."LotteryDate" = p_lottery_date::DATE 
							AND SF."SalePointid" = (ele ->> 'SalePointId')::INT
						GROUP BY SF."ShiftDistributeId", SF."ShiftId", SF."UserId"
					)
					INSERT INTO "ShiftTransfer"(
						"ShiftId",
						"UserId",
						"ShiftDistributeId",
						"LotteryChannelId",
						"LotteryTypeId",
						"ActionBy",
						"ActionByName",
						"ActionDate",
						"LotteryDate",
						"TotalStocks",
						"TotalReceived",
						"TotalTrans",
						"TotalReturns",
						"TotalRemaining",
						"TotalSold",
						"TotalSoldMoney",
						"TotalRetail",
						"TotalRetailMoney",
						"TotalWholesale",
						"TotalWholesaleMoney",
						"SalePointid"
					)
					SELECT
						S."ShiftId",
						S."UserId",
						S."ShiftDistributeId",
						(ele ->> 'LotteryChannelId')::INT,
						1,
						p_action_by,
						p_action_by_name,
						NOW(),
						p_lottery_date::DATE,
						(ele ->> 'TotalReceived')::INT,
						0,
						0,
						0,
						(ele ->> 'TotalReceived')::INT,
						0,
						0,
						0,
						0,
						0,
						0,
						(ele ->> 'SalePointId')::INT
					FROM shiftTran S;
				END IF;
				-- Cập nhật report vé trùng
				IF COALESCE((ele ->> 'TotalDupReceived')::INT, 0) > 0 THEN 
					WITH shiftTran AS (
						SELECT 
							SF."ShiftDistributeId",
							SF."ShiftId",
							SF."UserId"
						FROM "ShiftTransfer" SF
						WHERE SF."LotteryDate" = p_lottery_date::DATE 
							AND SF."SalePointid" = (ele ->> 'SalePointId')::INT
						GROUP BY SF."ShiftDistributeId", SF."ShiftId", SF."UserId"
					)
					INSERT INTO "ShiftTransfer"(
						"ShiftId",
						"UserId",
						"ShiftDistributeId",
						"LotteryChannelId",
						"LotteryTypeId",
						"ActionBy",
						"ActionByName",
						"ActionDate",
						"LotteryDate",
						"TotalStocks",
						"TotalReceived",
						"TotalTrans",
						"TotalReturns",
						"TotalRemaining",
						"TotalSold",
						"TotalSoldMoney",
						"TotalRetail",
						"TotalRetailMoney",
						"TotalWholesale",
						"TotalWholesaleMoney",
						"SalePointid"
					)
					SELECT
						S."ShiftId",
						S."UserId",
						S."ShiftDistributeId",
						(ele ->> 'LotteryChannelId')::INT,
						2,
						p_action_by,
						p_action_by_name,
						NOW(),
						p_lottery_date::DATE,
						(ele ->> 'TotalDupReceived')::INT,
						0,
						0,
						0,
						(ele ->> 'TotalDupReceived')::INT,
						0,
						0,
						0,
						0,
						0,
						0,
						(ele ->> 'SalePointId')::INT
					FROM shiftTran S;
				END IF;
				
				UPDATE "InventoryFull"
				SET
					"TotalRemaining" = "TotalRemaining" - (COALESCE((ele ->> 'TotalReceived')::INT, 0) + COALESCE((ele ->> 'TotalDupReceived')::INT, 0))
				WHERE "LotteryDate" = p_lottery_date::DATE 
					AND "AgencyId" = (ele ->> 'AgencyId')::INT 
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;

			ELSE 
				
				SELECT 
					COALESCE(F."TotalReceived", 0), COALESCE(F."TotalDupReceived", 0)
				INTO 
					v_last_received, v_last_received_dup
				FROM "InventoryLog" F 
				WHERE F."LotteryDate" = p_lottery_date::DATE 
					AND F."AgencyId" = (ele ->> 'AgencyId')::INT
					AND F."SalePointId" = (ele ->> 'SalePointId')::INT
					AND F."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
								
				IF COALESCE((ele ->> 'TotalReceived')::INT, 0) >= 0 THEN
					UPDATE "ShiftTransfer" 
					SET "TotalStocks" = "TotalStocks" - v_last_received + COALESCE((ele ->> 'TotalReceived')::INT, 0),
							"TotalRemaining" = "TotalRemaining" - v_last_received + COALESCE((ele ->> 'TotalReceived')::INT, 0)
					WHERE "LotteryDate" = p_lottery_date::DATE 
						AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
						AND "SalePointid" = (ele ->> 'SalePointId')::INT
						AND "LotteryTypeId" = 1;
				END IF;
				
				IF COALESCE((ele ->> 'TotalDupReceived')::INT, 0) >= 0 THEN
					UPDATE "ShiftTransfer" 
					SET "TotalStocks" = "TotalStocks" - v_last_received_dup + COALESCE((ele ->> 'TotalDupReceived')::INT, 0),
							"TotalRemaining" = "TotalRemaining" - v_last_received_dup + COALESCE((ele ->> 'TotalDupReceived')::INT, 0)
					WHERE "LotteryDate" = p_lottery_date::DATE 
						AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
						AND "SalePointid" = (ele ->> 'SalePointId')::INT
						AND "LotteryTypeId" = 1;
				END IF;

				UPDATE "InventoryFull"
				SET
					"TotalRemaining" = "TotalRemaining" + v_last_received + v_last_received_dup - (COALESCE((ele ->> 'TotalReceived')::INT, 0) + COALESCE((ele ->> 'TotalDupReceived')::INT, 0))
				WHERE "LotteryDate" = p_lottery_date::DATE 
					AND "AgencyId" = (ele ->> 'AgencyId')::INT 
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
			
				UPDATE "InventoryLog" F
				SET
					"TotalReceived" = COALESCE((ele ->> 'TotalReceived')::INT, 0),
					"TotalDupReceived" = COALESCE((ele ->> 'TotalDupReceived')::INT, 0),
					"ActionBy" = p_action_by,
					"ActionByName" = p_action_by_name,
					"ActionDate" = NOW()
				WHERE F."LotteryDate" = p_lottery_date::DATE 
					AND F."AgencyId" = (ele ->> 'AgencyId')::INT
					AND F."SalePointId" = (ele ->> 'SalePointId')::INT
					AND F."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
					
			END IF;
		
		END LOOP;
		
		FOR re IN
		WITH tmp AS (
			SELECT
				"LotteryChannelId",
				SUM("TotalReceived") AS "TotalReceived",
				SUM("TotalDupReceived") AS "TotalDupReceived",
				"SalePointId"
			FROM "InventoryLog"
			WHERE "LotteryDate" = p_lottery_date::DATE
			GROUP BY 
				"LotteryChannelId",
				"SalePointId"
		)
		SELECT t."LotteryChannelId", t."TotalReceived", t."TotalDupReceived", t."SalePointId"  FROM tmp t LOOP
		
			IF NOT EXISTS (SELECT 1 FROM "Inventory" WHERE "LotteryDate" = p_lottery_date::DATE 
				AND "LotteryChannelId" = re."LotteryChannelId"
				AND "SalePointId" = re."SalePointId") THEN
		
				INSERT INTO "Inventory"(
					"LotteryDate",
					"LotteryChannelId",
					"TotalReceived",
					"TotalRemaining",
					"TotalDupReceived",
					"TotalDupRemaining",
					"SalePointId"
				) VALUES (
					 p_lottery_date::DATE,
					 re."LotteryChannelId",
					 re."TotalReceived",
					 re."TotalReceived",
					 re."TotalDupReceived",
					 re."TotalDupReceived",
					 re."SalePointId"
				);
			
			ELSE 
			
				UPDATE "Inventory" I
				SET
					"TotalReceived" = re."TotalReceived",
					"TotalRemaining" = I."TotalRemaining" - (I."TotalReceived" - re."TotalReceived"),
					"TotalDupReceived" = re."TotalDupReceived",
					"TotalDupRemaining" = I."TotalDupRemaining" - (I."TotalDupReceived" - re."TotalDupReceived")
				WHERE I."LotteryDate" = p_lottery_date::DATE 
					AND I."LotteryChannelId" = re."LotteryChannelId"
					AND I."SalePointId" = re."SalePointId";
			
			END IF;
		
		END LOOP;
		Insert INTO  
			"HistoryOfTicketsShared"("DetailOfSharing","UserId","FullName","CreatedDate")
		VALUES (p_data, p_action_by,p_action_by_name,p_lottery_date);
		v_mess := 'Thao tác thành công';

	ELSE
		v_id := -1;
		v_mess := 'Dữ liệu cần được cập nhật';
	END IF;
	
	
	RETURN QUERY 
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;
	
END;
$function$


