END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_distribute_for_sup_agency(p_date timestamp without time zone, p_action_by integer, p_action_by_name character varying, p_data text, p_lock boolean)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT := 1;
		v_mess TEXT;
		v_data JSON := p_data::JSON;
		ele JSON;
		v_total_received INT8;
		v_total_dup_received INT8;
BEGIN
	 IF p_lock IS FALSE THEN 
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
				IF NOT EXISTS (SELECT 1 FROM "InventoryForSupAgency" I WHERE I."Date"::DATE = p_date::DATE 
						AND I."AgencyId" = (ele ->> 'AgencyId')::INT 
						AND I."LotteryChannelId" = (ele->>'LotteryChannelId')::INT
						AND I."SupAgencyId" = (ele ->> 'SupAgencyId')::INT)
				THEN 
					INSERT INTO "InventoryForSupAgency"(
						"Date",
						"AgencyId",
						"SupAgencyId",
						"TotalReceived",
						"TotalDupReceived",
						"LotteryChannelId",
						"ActionBy",
						"ActionByName"
					)
					VALUES(
						p_date,
						(ele->>'AgencyId')::INT4,
						(ele->>'SupAgencyId')::INT4,
						(ele->>'TotalReceived')::INT8,
						(ele->>'TotalDupReceived')::INT8,
						(ele->>'LotteryChannelId')::INT4,
						p_action_by,
						p_action_by_name
					);
					raise notice 'ele TotalReceived % , ele TotalDupReceived %, v_total_received %, v_total_dup_received %', (ele->>'TotalReceived')::INT8, (ele->>'TotalDupReceived')::INT8, v_total_received, v_total_dup_received;
					
					UPDATE "InventoryFull" 
						SET 
							"TotalRemaining" = "TotalRemaining" - (ele->>'TotalReceived')::INT8 - (ele->>'TotalDupReceived')::INT8
					WHERE "AgencyId" = (ele->>'AgencyId')::INT4 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT4
						AND "LotteryDate" = (ele->>'LotteryDate')::DATE;
					
					v_mess := 'Thêm thành công';
				ELSE 
					raise notice '11111111111111111';
					SELECT 
						I."TotalReceived",
						I."TotalDupReceived"
						INTO v_total_received, v_total_dup_received
					FROM "InventoryForSupAgency" I 
					WHERE I."Date"::DATE = p_date::DATE 
						AND I."AgencyId" = (ele ->> 'AgencyId')::INT 
						AND I."LotteryChannelId" = (ele->>'LotteryChannelId')::INT
						AND I."SupAgencyId" = (ele ->> 'SupAgencyId')::INT;
					
					
					Update "InventoryForSupAgency" 
					SET 
						"Date" = p_date,
						"AgencyId" = 	(ele->>'AgencyId')::INT4,
						"SupAgencyId" = (ele->>'SupAgencyId')::INT4,
						"TotalReceived" = (ele->>'TotalReceived')::INT8,
						"TotalDupReceived" = (ele->>'TotalDupReceived')::INT8,
						"ActionBy" = p_action_by,
						"TotalPrice" = ( SELECT T."Price" * (ele->>'TotalReceived')::INT8 FROM "SubAgency" T WHERE T."ModifiedDate" = p_date )
					WHERE "Date"::DATE = p_date::DATE 
						AND "AgencyId" = (ele ->> 'AgencyId')::INT 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT
						AND "SupAgencyId" = (ele ->> 'SupAgencyId')::INT;
					
					raise notice 'ele TotalReceived % , ele TotalDupReceived %, v_total_received %, v_total_dup_received %', (ele->>'TotalReceived')::INT8, (ele->>'TotalDupReceived')::INT8, v_total_received, v_total_dup_received;
						
					UPDATE "InventoryFull" 
						SET 
							"TotalRemaining" = "TotalRemaining" + ( v_total_received - (ele->>'TotalReceived')::INT8 ) - ((ele->>'TotalDupReceived')::INT8 - v_total_dup_received)
					WHERE "AgencyId" = (ele->>'AgencyId')::INT4 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT4
						AND "LotteryDate" = (ele->>'LotteryDate')::DATE;
						
						v_mess := 'Cập nhật thành công';
				END IF;
			END LOOP;
		ELSE
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
				IF NOT EXISTS (SELECT 1 FROM "InventoryForSupAgency" I WHERE I."Date"::DATE = p_date::DATE 
						AND I."AgencyId" = (ele ->> 'AgencyId')::INT 
						AND I."LotteryChannelId" = (ele->>'LotteryChannelId')::INT
						AND I."SupAgencyId" = (ele ->> 'SupAgencyId')::INT)
					THEN 
						INSERT INTO "InventoryForSupAgency"(
							"Date",
							"AgencyId",
							"SupAgencyId",
							"TotalReceived",
							"TotalDupReceived",
							"LotteryChannelId",
							"ActionBy",
							"ActionByName"
						)
						VALUES(
							p_date,
							(ele->>'AgencyId')::INT4,
							(ele->>'SupAgencyId')::INT4,
							(ele->>'TotalReceived')::INT8,
							(ele->>'TotalDupReceived')::INT8,
							(ele->>'LotteryChannelId')::INT4,
							p_action_by,
							p_action_by_name
						);
					UPDATE "InventoryFull" 
						SET 
							"TotalRemaining" = "TotalRemaining" - (ele->>'TotalReceived')::INT8 - (ele->>'TotalDupReceived')::INT8
					WHERE "AgencyId" = (ele->>'AgencyId')::INT4 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT4
						AND "LotteryDate" = (ele->>'LotteryDate')::DATE;
						
						v_mess := 'Thêm thành công';
				ELSE
									
					SELECT 
						I."TotalReceived",
						I."TotalDupReceived"
						INTO v_total_received, v_total_dup_received
					FROM "InventoryForSupAgency" I 
					WHERE I."Date"::DATE = p_date::DATE 
						AND I."AgencyId" = (ele ->> 'AgencyId')::INT 
						AND I."LotteryChannelId" = (ele->>'LotteryChannelId')::INT
						AND I."SupAgencyId" = (ele ->> 'SupAgencyId')::INT;
				
					UPDATE "InventoryForSupAgency" 
					SET "TotalDupReceived" = (ele->>'TotalDupReceived')::INT4
					WHERE "Date"::DATE = p_date::DATE 
						AND "AgencyId" = (ele ->> 'AgencyId')::INT 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT
						AND "SupAgencyId" = (ele ->> 'SupAgencyId')::INT;
						
					UPDATE "InventoryFull" 
						SET 
							"TotalRemaining" = "TotalRemaining" + (v_total_received - (ele->>'TotalReceived')::INT8 ) - ((ele->>'TotalDupReceived')::INT8 - v_total_dup_received)
					WHERE "AgencyId" = (ele->>'AgencyId')::INT4 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT4
						AND "LotteryDate" = (ele->>'LotteryDate')::DATE;
						v_mess := 'Cập nhật thành công';
				END IF;
			END LOOP;
		END IF;
    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_distribute_scratchcard_for_sales_point(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	ele JSON;
	v_total INT8 := 0;
	v_store INT;
	v_check INT := 0;
BEGIN
	
	CREATE TEMP TABLE tmpTable (
		"LotteryChannelId" INT,
		"TotalReceived" INT
	)
	ON COMMIT DROP;
	
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
		INSERT INTO tmpTable("LotteryChannelId", "TotalReceived")
		VALUES ((ele ->> 'LotteryChannelId')::INT, COALESCE((ele ->> 'TotalReceived')::INT, 0));
	END LOOP;
	
	WITH tmp AS (
		SELECT 
			"LotteryChannelId",
			"TotalRemaining"
		FROM "ScratchcardFull"
		WHERE "AgencyId" = 0
	),
	tmp2 AS (
		SELECT
			T."LotteryChannelId",
			SUM(T."TotalReceived") AS "TotalReceived"
		FROM tmpTable T
		GROUP BY T."LotteryChannelId"
	)
	
	SELECT 
		COUNT(1) INTO v_check
	FROM tmp2 T
		JOIN tmp X ON T."LotteryChannelId" = X."LotteryChannelId"
	WHERE T."TotalReceived" > X."TotalRemaining";
	

	IF v_check = 0 THEN
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
			IF NOT EXISTS (SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = (ele ->> 'SalePointId')::INT AND "LotteryChannelId" =(ele ->> 'LotteryChannelId')::INT) THEN	
				
				INSERT INTO "Scratchcard"(
					"TotalReceived",
					"TotalRemaining",
					"SalePointId",
					"LastActionBy",
					"LastActionByName",
					"LotteryChannelId"
				) VALUES (
					COALESCE((ele ->> 'TotalReceived')::INT, 0),
					COALESCE((ele ->> 'TotalReceived')::INT, 0),
					(ele ->> 'SalePointId')::INT,
					p_action_by,
					p_action_by_name,
					(ele ->> 'LotteryChannelId')::INT
				);
				
			ELSE 

				UPDATE "Scratchcard" S
				SET
					"TotalReceived" = S."TotalReceived" + COALESCE((ele ->> 'TotalReceived')::INT, 0),
					"TotalRemaining" = S."TotalRemaining" + COALESCE((ele ->> 'TotalReceived')::INT, 0),
					"LastActionBy" = p_action_by,
					"LastActionByName" = p_action_by_name,
					"LastActionDate" = NOW()
				WHERE S."SalePointId" = (ele ->> 'SalePointId')::INT AND S."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
					
			END IF;
			
			INSERT INTO "ScratchcardLog"(
				"SalePointId",
				"TotalReceived",
				"ActionBy",
				"ActionByName",
				"LotteryChannelId"
			) VALUES(
				(ele ->> 'SalePointId')::INT,
				(ele ->> 'TotalReceived')::INT,
				p_action_by,
				p_action_by_name,
				(ele ->> 'LotteryChannelId')::INT
			);
			
			UPDATE "ScratchcardFull" 
			SET 
				"TotalRemaining" = "TotalRemaining" - COALESCE((ele ->> 'TotalReceived')::INT, 0),
				"LastActionBy" = p_action_by,
				"LastActionByName" = p_action_by_name,
				"LastActionDate" = NOW()
			WHERE "AgencyId" = 0 AND "LotteryChannelId" =(ele ->> 'LotteryChannelId')::INT;
			
		END LOOP;
			
		v_mess := 'Thao tác thành công';
	ELSE 
		v_mess := 'Thao tác không thành công';
		v_id := -1;
	END IF;
	
	
	RETURN QUERY 
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_distribute_scratchcard_for_sub_agency(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	ele JSON;
	v_total INT8 := 0;
	v_store INT;
	v_check INT := 0;
BEGIN
	
	CREATE TEMP TABLE tmpTable (
		"LotteryChannelId" INT,
		"TotalReceived" INT
	)
	ON COMMIT DROP;
	
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
		INSERT INTO tmpTable("LotteryChannelId", "TotalReceived")
		VALUES ((ele ->> 'LotteryChannelId')::INT, COALESCE((ele ->> 'TotalReceived')::INT, 0));
	END LOOP;
	
	WITH tmp AS (
		SELECT 
			"LotteryChannelId",
			"TotalRemaining"
		FROM "ScratchcardFullForSubAgency"
		WHERE "AgencyId" = 0
	),
	tmp2 AS (
		SELECT
			T."LotteryChannelId",
			SUM(T."TotalReceived") AS "TotalReceived"
		FROM tmpTable T
		GROUP BY T."LotteryChannelId"
	)
	
	SELECT 
		COUNT(1) INTO v_check
	FROM tmp2 T
		JOIN tmp X ON T."LotteryChannelId" = X."LotteryChannelId"
	WHERE T."TotalReceived" > X."TotalRemaining";
	

	IF v_check = 0 THEN
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
			IF NOT EXISTS (SELECT 1 FROM "ScratchcardForSubAgency" WHERE "AgencyId" = (ele ->> 'SalePointId')::INT AND "LotteryChannelId" =(ele ->> 'LotteryChannelId')::INT) THEN	
				
				INSERT INTO "ScratchcardForSubAgency"(
					"TotalReceived",
					"TotalRemaining",
					"AgencyId",
					"LastActionBy",
					"LastActionByName",
					"LotteryChannelId"
				) VALUES (
					COALESCE((ele ->> 'TotalReceived')::INT, 0),
					COALESCE((ele ->> 'TotalReceived')::INT, 0),
					(ele ->> 'SalePointId')::INT,
					p_action_by,
					p_action_by_name,
					(ele ->> 'LotteryChannelId')::INT
				);
				UPDATE "ScratchcardFull"
				SET
					"TotalRemaining" = "TotalRemaining" - COALESCE((ele ->> 'TotalReceived')::INT, 0)
				WHERE "LotteryChannelId" =  (ele ->> 'LotteryChannelId')::INT;
			ELSE 

				UPDATE "ScratchcardForSubAgency" S
				SET
					"TotalReceived" = S."TotalReceived" + COALESCE((ele ->> 'TotalReceived')::INT, 0),
					"TotalRemaining" = S."TotalRemaining" + COALESCE((ele ->> 'TotalReceived')::INT, 0),
					"LastActionBy" = p_action_by,
					"LastActionByName" = p_action_by_name,
					"LastActionDate" = NOW()
				WHERE S."AgencyId" = (ele ->> 'SalePointId')::INT AND S."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
				
				UPDATE "ScratchcardFull"
				SET
					"TotalRemaining" = "TotalRemaining" - COALESCE((ele ->> 'TotalReceived')::INT, 0)
				WHERE "LotteryChannelId" =  (ele ->> 'LotteryChannelId')::INT;
			END IF;
			
			INSERT INTO "ScratchcardLogForSubAgency"(
				"SalePointId",
				"TotalReceived",
				"ActionBy",
				"ActionByName",
				"LotteryChannelId"
			) VALUES(
				(ele ->> 'SalePointId')::INT,
				(ele ->> 'TotalReceived')::INT,
				p_action_by,
				p_action_by_name,
				(ele ->> 'LotteryChannelId')::INT
			);
			
			UPDATE "ScratchcardFullForSubAgency" 
			SET 
				"TotalRemaining" = "TotalRemaining" - COALESCE((ele ->> 'TotalReceived')::INT, 0),
				"LastActionBy" = p_action_by,
				"LastActionByName" = p_action_by_name,
				"LastActionDate" = NOW()
			WHERE "AgencyId" = 0 AND "LotteryChannelId" =(ele ->> 'LotteryChannelId')::INT;
			
		END LOOP;
			
		v_mess := 'Thao tác thành công';
	ELSE 
		v_mess := 'Thao tác không thành công';
		v_id := -1;
	END IF;
	
	
	RETURN QUERY 
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_all_report_money_in_a_day(p_date timestamp without time zone)
 RETURNS TABLE("ReturnMoneyId" integer, "ShiftDistributeId" integer, "SalePointId" integer, "ActionDate" date, "ShiftId" integer, "TotalMoneyInADay" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT
		RM."ReturnMoneyId",
		RM."ShiftDistributeId",
		RM."SalePointId",
		RM."ActionDate",
		RM."ShiftId",
		RM."TotalMoneyInDay"
	FROM "ReportMoney" RM
	WHERE RM."ActionDate" = p_date::DATE;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_data_distribute_for_sales_point(p_lottery_date timestamp without time zone)
 RETURNS TABLE("AgencyId" integer, "SalePointId" integer, "LotteryChannelId" integer, "TotalReceived" integer, "TotalDupReceived" integer)
 LANGUAGE plpgsql
AS $function$
BEGIN	
	
	RETURN QUERY 
	SELECT 
		IL."AgencyId",
		IL."SalePointId",
		IL."LotteryChannelId",
		COALESCE(IL."TotalReceived", 0) AS "TotalReceived",
		COALESCE(IL."TotalDupReceived", 0) AS "TotalDupReceived"
	FROM "InventoryLog" IL
	WHERE IL."LotteryDate" = p_lottery_date::DATE;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_data_distribute_for_sup_agency(p_date timestamp without time zone)
 RETURNS TABLE("AgencyId" integer, "SupAgencyId" integer, "SubAgencyName" character varying, "LotteryChannelId" integer, "TotalReceived" bigint, "TotalDupReceived" bigint, "TotalRemaining" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN	
	
	RETURN QUERY 
	SELECT 
		IL."AgencyId",
		IL."SupAgencyId",
		SA."AgencyName",
		IL."LotteryChannelId",
		COALESCE(IL."TotalReceived", 0) AS "TotalReceived",
		COALESCE(IL."TotalDupReceived", 0) AS "TotalDupReceived",
		COALESCE(IL."TotalRemaining",0) AS "TotalRemaining"
	FROM "InventoryForSupAgency" IL
	LEFT JOIN "SubAgency" SA ON SA."AgencyId" = IL."SupAgencyId"
	WHERE IL."Date"::DATE = p_date::DATE;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_data_distribute_scratchcard_for_sale_point()
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "TotalReceived" integer, "TotalRemaining" integer, "LotteryChannelId" integer, "LotteryChannelName" character varying, "ShortName" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	SELECT 
		S."SalePointId",
		SP."SalePointName",
		S."TotalReceived",
		S."TotalRemaining",
		S."LotteryChannelId",
		LC."LotteryChannelName",
		LC."ShortName"
	FROM "Scratchcard" S
		JOIN "SalePoint" SP ON SP."SalePointId" = S."SalePointId"
		JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = S."LotteryChannelId"
	ORDER BY S."SalePointId", LC."LotteryChannelTypeId", LC."LotteryChannelId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_data_distribute_scratchcard_for_sub_agency()
 RETURNS TABLE("AgencyId" integer, "SalePointName" character varying, "TotalReceived" integer, "TotalRemaining" integer, "LotteryChannelId" integer, "LotteryChannelName" character varying, "ShortName" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	SELECT 
		S."AgencyId",
		SP."AgencyName",
		S."TotalReceived",
		S."TotalRemaining",
		S."LotteryChannelId",
		LC."LotteryChannelName",
		LC."ShortName"
	FROM "ScratchcardForSubAgency" S
		JOIN "SubAgency" SP ON SP."AgencyId" = S."AgencyId"
		JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = S."LotteryChannelId"
	ORDER BY S."AgencyId", LC."LotteryChannelTypeId", LC."LotteryChannelId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_data_receive_from_agency(p_lottery_date timestamp without time zone)
 RETURNS TABLE("AgencyId" integer, "LotteryChannelId" integer, "TotalReceived" integer, "TotalRemaining" integer, "IsBlocked" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN	
	
	RETURN QUERY 
	SELECT 
		I."AgencyId",
		I."LotteryChannelId",
		I."TotalReceived",
		I."TotalRemaining",
		I."LotteryDate" < NOW()::DATE AS "IsBlocked"
	FROM "InventoryFull" I
	WHERE I."LotteryDate" = p_lottery_date::DATE;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_data_receive_scratchcard_from_agency(p_date timestamp without time zone)
 RETURNS TABLE("AgencyId" integer, "TotalReceived" bigint, "LotteryChannelId" integer, "LotteryChannelName" character varying, "ShortName" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN	
	
	RETURN QUERY 
		WITH tmp AS (
		SELECT 
			SF."AgencyId",
			SUM(SF."TotalReceived") AS "TotalReceived",
			SF."LotteryChannelId",
			LC."LotteryChannelName",
			LC."ShortName"
		FROM "ScratchcardFullLog" SF
			JOIN "LotteryChannel" LC ON SF."LotteryChannelId" = LC."LotteryChannelId"
		WHERE SF."ActionDate"::DATE = p_date::DATE
		GROUP BY 
			SF."AgencyId",
			SF."LotteryChannelId",
			LC."LotteryChannelName",
			LC."ShortName"
		ORDER BY SF."AgencyId"
	)
	SELECT 
		T."AgencyId",
		T."TotalReceived",
		T."LotteryChannelId",
		T."LotteryChannelName",
		T."ShortName"
	FROM tmp T
		JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
	ORDER BY T."AgencyId", LC."LotteryChannelTypeId", T."LotteryChannelId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_debt_of_staff(p_user_title_id integer)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "TotalDebt" numeric, "PayedDebt" numeric, "UserTitleId" integer, "SalePointId" integer, "SalePointName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	with tmp AS(
		SELECT D."UserId", SUM(D."TotalDebt") AS "TotalDebt"  FROM "Debt" D 
		WHERE D."IsAdded" IS TRUE
		GROUP BY D."UserId"
	),tmp2 AS(
		SELECT D."UserId", D."PayedDebt" AS "PayedDebt"  FROM "Debt" D 
		GROUP BY D."UserId", D."PayedDebt", D."DebtId"
		ORDER BY D."DebtId" DESC LIMIT 1	
	)
	SELECT
		U."UserId",
		U."FullName",
		T."TotalDebt" :: DECIMAL,
		T2."PayedDebt" :: DECIMAL,
		UR."UserTitleId",
		U."SalePointId",
		S."SalePointName"
	FROM "User" U 
		LEFT JOIN tmp T ON T."UserId" = U."UserId"
		LEFT JOIN tmp2 T2 ON T2."UserId" = U."UserId"
		LEFT JOIN "UserRole" UR ON UR."UserId" = U."UserId"
		LEFT JOIN "SalePoint" S ON S."SalePointId" = U."SalePointId"
	WHERE UR."UserTitleId" = p_user_title_id;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_inventory_log(p_date timestamp without time zone)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "LotteryChannelId" integer, "LotteryChannelName" character varying, "TotalReceived" integer, "TotalRemaining" integer, "TotalDupReceived" integer, "TotalDupRemaining" integer, "TransitionLog" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_sale_point_id INT;
BEGIN

	RETURN QUERY 
	SELECT
		SP."SalePointId",
		SP."SalePointName",
		I."LotteryDate",
		I."LotteryChannelId",
		LC."LotteryChannelName",
		I."TotalReceived",
		I."TotalRemaining",
		I."TotalDupReceived",
		I."TotalDupRemaining",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					T."LotteryDate",
					T."LotteryChannelId",
					LCC."LotteryChannelName",
					T."FromSalePointId",
					(CASE WHEN T."FromSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "FromSalePointName",
					T."ToSalePointId",
					(CASE WHEN T."ToSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "ToSalePointName",
					T."TotalTrans",
					T."TotalTransDup",
					T."ActionBy",
					T."ActionByName",
					T."TransitionTypeId",
					T."TransitionDate",
					T."ManagerId",
					U."FullName" AS "ManagerName"
				FROM "Transition" T
					JOIN "LotteryChannel" LCC ON LCC."LotteryChannelId" = T."LotteryChannelId"
					LEFT JOIN "User" U ON U."UserId" = T."ManagerId"
				WHERE T."LotteryDate" = p_date::DATE AND (T."FromSalePointId" = SP."SalePointId" OR T."ToSalePointId" = SP."SalePointId")
					AND T."TransitionTypeId" <> 3 AND T."ConfirmStatusId" = 2
				ORDER BY T."TransitionDate" DESC
			) r
		)::TEXT AS "TransitionLog"
	FROM "SalePoint" SP
		LEFT JOIN "Inventory" I ON I."SalePointId" = SP."SalePointId" AND I."LotteryDate" = p_date::DATE 
		LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = I."LotteryChannelId"
	WHERE (I."SalePointId" IS NULL OR I."SalePointId" <> 0)  AND SP."IsActive" IS TRUE
	ORDER BY SP."SalePointId", LC."LotteryChannelTypeId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_list_lottery_for_return(p_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("DataReceived" text, "DataForReturn" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	--Lấy số vé chia vào ban đầu
	WITH tmp AS (
		SELECT 
			I."AgencyId",
			I."LotteryChannelId",
			I."TotalReceived",
			I."LotteryDate",
			FALSE AS "IsScratchcard"
		FROM "InventoryFull" I
		WHERE I."LotteryDate" = p_date::DATE
		UNION
		SELECT 
			SF."AgencyId",
			SF."LotteryChannelId",
			SUM(SF."TotalReceived") AS "TotalReceived",
			NULL AS "LotteryDate",
			TRUE AS "IsScratchcard"
		FROM "ScratchcardFullLog" SF
		GROUP BY 
			SF."AgencyId",
			SF."LotteryChannelId"
	),
	-- Lấy danh sách chuyển nhận
	tmp2 AS(
		SELECT 
			T."LotteryChannelId",
			T."LotteryDate",
			T."TransitionTypeId",
			T."TotalTrans",
			T."TotalTransDup",
			T."ToAgencyId",
			T."IsScratchcard"
		FROM "Transition" T
		WHERE T."ConfirmStatusId" = 2
			AND (T."IsScratchcard" IS TRUE OR T."LotteryDate" = p_date::DATE)
			AND T."IsDeleted" IS FALSE
	),
	-- Tính số vé còn lại có thể trả ế trong chuyển nhận
	tmp3 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "DataForReturn"
		FROM (
			SELECT 
				T."LotteryChannelId",
				T."IsScratchcard",
				SUM(CASE WHEN T."TransitionTypeId" = 1 THEN "TotalTrans" ELSE - "TotalTrans" END) AS "TotalTrans",
				SUM(CASE WHEN T."TransitionTypeId" = 1 THEN "TotalTransDup" ELSE - "TotalTransDup" END) AS "TotalTransDup"
			FROM tmp2 T
			GROUP BY
				T."LotteryChannelId",
				T."IsScratchcard"
			ORDER BY T."LotteryChannelId"
		)R
	),
	--Tính số vé đã trả 
	tmp4 AS (
		SELECT
			T."ToAgencyId" AS "AgencyId",
			T."LotteryChannelId",
			SUM(T."TotalTrans") AS "TotalTrans",
			SUM(T."TotalTransDup") AS "TotalTransDup"
		FROM tmp2 T
		WHERE T."ToAgencyId" IS NOT NULL AND T."TransitionTypeId" = 3
		GROUP BY 
			T."ToAgencyId",
			T."LotteryChannelId"
	), 
	-- Tính số vé còn lại có thể trả cho đại lý
	tmp5 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "DataReceived"
		FROM (
			SELECT 
				C."AgencyId",
				C."LotteryChannelId",
				C."TotalReceived",
				(COALESCE(R."TotalTrans", 0) + COALESCE(R."TotalTransDup", 0)) AS "TotalHaveReturned",
				(C."TotalReceived" - COALESCE(R."TotalTrans", 0) - COALESCE(R."TotalTransDup", 0)) AS "TotalCanReturn",
				C."LotteryDate",
				C."IsScratchcard"	
			FROM tmp C
				LEFT JOIN tmp4 R ON C."AgencyId" = R."AgencyId" AND C."LotteryChannelId" = R."LotteryChannelId"
			ORDER BY C."AgencyId", C."LotteryChannelId"
		)R
	)
	SELECT 
		tmp5."DataReceived",
		tmp3."DataForReturn"
	FROM tmp5 JOIN tmp3 ON TRUE;
		
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_list_lottery_price_agency(p_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("AgencyId" integer, "AgencyName" character varying, "LotteryChannelId" integer, "LotteryChannelName" character varying, "IsScratchcard" boolean, "Price" numeric, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			A."AgencyId",
			A."AgencyName"
		FROM "Agency" A
		WHERE A."IsDeleted" IS FALSE
	),
	tmp2 AS (
		SELECT 
			LC."LotteryChannelId",
			LC."LotteryChannelName",
			LC."IsScratchcard"
		FROM "LotteryChannel" LC
		WHERE LC."IsDeleted" = FALSE
		ORDER BY LC."LotteryChannelTypeId",
			LC."LotteryChannelId"
	),
	tmp3 AS (
		SELECT
			A."AgencyId",
			A."AgencyName",
			LC."LotteryChannelId",
			LC."LotteryChannelName",
			LC."IsScratchcard"
		FROM tmp A 
			JOIN tmp2 LC ON TRUE
	),
	tmp4 AS (
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY LP."AgencyId", LP."LotteryChannelId" ORDER BY LP."CreatedDate" DESC) AS "Id",
			LP."AgencyId",
			LP."LotteryChannelId",
			LP."CreatedDate",
			LP."Price"
		FROM "LotteryPriceAgency" LP
		WHERE LP."IsDeleted" IS FALSE
			AND (p_date IS NULL OR LP."CreatedDate" <= p_date)
	)
	SELECT 
			A."AgencyId",
			A."AgencyName",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."IsScratchcard",
			P."Price",
			P."CreatedDate"
	FROM tmp3 A
		JOIN tmp4 P ON A."AgencyId" = P."AgencyId" AND A."LotteryChannelId" = P."LotteryChannelId"
	WHERE P."Id" = 1;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_list_lottery_price_sub_agency(p_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("AgencyId" bigint, "AgencyName" character varying, "LotteryChannelId" integer, "LotteryChannelName" character varying, "IsScratchcard" boolean, "Price" numeric, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			A."AgencyId",
			A."AgencyName"
		FROM "SubAgency" A
		WHERE A."IsDelete" IS FALSE
	),
	tmp2 AS (
		SELECT 
			LC."LotteryChannelId",
			LC."LotteryChannelName",
			LC."IsScratchcard"
		FROM "LotteryChannel" LC
		WHERE LC."IsDeleted" = FALSE
		ORDER BY LC."LotteryChannelTypeId",
			LC."LotteryChannelId"
	),
	tmp3 AS (
		SELECT
			A."AgencyId",
			A."AgencyName",
			LC."LotteryChannelId",
			LC."LotteryChannelName",
			LC."IsScratchcard"
		FROM tmp A 
			JOIN tmp2 LC ON TRUE
	),
	tmp4 AS (
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY LP."AgencyId", LP."LotteryChannelId" ORDER BY LP."CreatedDate" DESC) AS "Id",
			LP."AgencyId",
			LP."LotteryChannelId",
			LP."CreatedDate",
			LP."Price",
			LP."LotteryPriceAgencyId"
		FROM "LotteryPriceSubAgency" LP
		WHERE LP."IsDeleted" IS FALSE
			AND (p_date IS NULL OR LP."CreatedDate" <= p_date)
	)
	SELECT 
			A."AgencyId",
			A."AgencyName",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."IsScratchcard",
			P."Price",
			P."CreatedDate"
	FROM tmp3 A
		JOIN tmp4 P ON A."AgencyId" = P."AgencyId" AND A."LotteryChannelId" = P."LotteryChannelId"
	WHERE P."Id" = 1;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_list_other_fees(p_date timestamp without time zone, p_sale_point_id bigint)
 RETURNS TABLE("TransactionId" integer, "TransactionTypeId" integer, "Note" character varying, "Quantity" integer, "Price" numeric, "TotalPrice" numeric, "UserId" integer, "ShiftDistributeId" integer, "ShiftId" integer, "SalePointName" character varying, "ActionDate" timestamp without time zone, "TypeNameId" integer, "Name" character varying, "ActionBy" integer, "ActionName" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
RETURN QUERY
		SELECT 
			T."TransactionId",
			T."TransactionTypeId",
			T."Note",
			T."Quantity",
			T."Price",
			T."TotalPrice",
			T."UserId",
			T."ShiftDistributeId",
			SD."ShiftId",
			S."SalePointName",
			T."ActionDate",
			TN."TypeNameId",
			TN."Name",
			T."ActionBy",
			T."ActionByName"
		FROM "Transaction" T
			LEFT JOIN "SalePoint" S ON T."SalePointId" = S."SalePointId"
			left JOIN "ShiftDistribute" SD ON T."ShiftDistributeId" = SD."ShiftDistributeId"
			left JOIN "TypeName" TN ON T."TypeNameId" = TN."TypeNameId"
		WHERE 
		
			 T."Date"::DATE = p_date
			AND T."IsDeleted" IS FALSE
			AND T."SalePointId" = p_sale_point_id or p_sale_point_id = 0
		AND	T."TransactionTypeId" = 1
		ORDER BY 
			T."ActionDate" DESC;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_list_sub_agency()
 RETURNS TABLE("AgencyId" bigint, "AgencyName" character varying, "IsActive" boolean, "IsDelete" boolean, "Price" numeric)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	SELECT 
		S."AgencyId",
		S."AgencyName",
		S."IsActive",
		S."IsDelete",
		S."Price"
	FROM "SubAgency" S
	ORDER BY S."AgencyId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_sale_point_return(p_date timestamp without time zone)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "LotteryChannelId" integer, "LotteryChannelName" character varying, "TotalReceived" integer, "TotalRemaining" integer, "TotalDupReceived" integer, "TotalDupRemaining" integer, "TransitionLog" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_sale_point_id INT;
BEGIN

	RETURN QUERY 
	SELECT
		SP."SalePointId",
		SP."SalePointName",
		I."LotteryDate",
		I."LotteryChannelId",
		LC."LotteryChannelName",
		I."TotalReceived",
		I."TotalRemaining",
		I."TotalDupReceived",
		I."TotalDupRemaining",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					T."LotteryDate",
					T."LotteryChannelId",
					LCC."LotteryChannelName",
					T."FromSalePointId",
					(CASE WHEN T."FromSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "FromSalePointName",
					T."ToSalePointId",
					(CASE WHEN T."ToSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "ToSalePointName",
					T."TotalTrans",
					T."TotalTransDup",
					T."ActionBy",
					T."ActionByName",
					T."TransitionTypeId",
					T."TransitionDate",
					T."ManagerId",
					U."FullName" AS "ManagerName"
				FROM "Transition" T
					JOIN "LotteryChannel" LCC ON LCC."LotteryChannelId" = T."LotteryChannelId"
					LEFT JOIN "User" U ON U."UserId" = T."ManagerId"
				WHERE T."LotteryDate" = p_date::DATE AND (T."FromSalePointId" = SP."SalePointId" OR T."ToSalePointId" = SP."SalePointId")
					AND T."TransitionTypeId" = 3
				ORDER BY T."TransitionDate"
			) r
		)::TEXT AS "TransitionLog"
	FROM "SalePoint" SP
		LEFT JOIN "Inventory" I ON I."SalePointId" = SP."SalePointId" AND I."LotteryDate" = p_date::DATE
		LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = I."LotteryChannelId"
	WHERE (I."SalePointId" IS NULL OR I."SalePointId" <> 0)  AND SP."IsActive" IS TRUE
	ORDER BY SP."SalePointId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_sell_point_log(p_shift_distribute_id integer, p_sale_point_id integer, p_date timestamp without time zone)
 RETURNS TABLE("SalePointId" integer, "LotteryDate" date, "LotteryChannelId" integer, "LotteryChannelName" character varying, "TotalTrans" integer, "TotalTransDup" integer, "TotalTransScratch" integer, "ActionBy" integer, "ActionByName" character varying, "ActionDate" timestamp without time zone, "LotteryTypeId" integer, "LotteryTypeName" character varying, "TotalValue" numeric, "TransitionId" bigint, "SalePointLogId" bigint, "TransitionTypeId" integer, "FromSalePointId" integer, "ToSalePointId" integer, "ManagerId" integer, "ManagerName" character varying, "PromotionCode" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_sale_point_id INT;
BEGIN

	IF COALESCE(p_sale_point_id, 0) = 0 THEN
	
		SELECT SD."SalePointId" INTO v_sale_point_id FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" = p_shift_distribute_id;
	
	ELSE
	
		v_sale_point_id := p_sale_point_id;
		
	END IF;

	RETURN QUERY
	WITH tmp AS(
		SELECT 
			SL."SalePointId",
			SL."LotteryDate",
			SL."LotteryChannelId",
			NULL AS "LotteryChannelName",
			0 AS "TotalTrans",
			0 AS "TotalTransDup",
			SL."Quantity" AS "TotalTransScratch",
			SL."ActionBy",
			SL."ActionByName",
			SL."ActionDate",
			SL."LotteryTypeId",
			LT."LotteryTypeName",
			SL."TotalValue",
			0 AS "TransitionId",
			SL."SalePointLogId",
			0 AS "TransitionTypeId",
			0 AS "FromSalePointId",
			0 AS "ToSalePointId",
			NULL::INT AS "ManagerId",
			NULL::VARCHAR AS "ManagerName",
			SL."PromotionCode"::TEXT
		FROM "SalePointLog" SL
			JOIN "LotteryType" LT ON LT."LotteryTypeId" = SL."LotteryTypeId"
		WHERE SL."SalePointId" = v_sale_point_id AND SL."ActionDate"::DATE = p_date::DATE AND SL."LotteryTypeId" = 3 AND SL."IsDeleted" IS FALSE
		
		UNION 
		
		SELECT 
			SL."SalePointId",
			SL."LotteryDate",
			SL."LotteryChannelId",
			LC."LotteryChannelName",
			(CASE WHEN SL."LotteryTypeId" = 1 THEN SL."Quantity" ELSE 0 END) AS "TotalTrans",
			(CASE WHEN SL."LotteryTypeId" = 2 THEN SL."Quantity" ELSE 0 END) AS "TotalTransDup",
			0 AS "TotalTransScratch",
			SL."ActionBy",
			SL."ActionByName",
			SL."ActionDate",
			SL."LotteryTypeId",
			LT."LotteryTypeName",
			SL."TotalValue",
			0 AS "TransitionId",
			SL."SalePointLogId",
			0 AS "TransitionTypeId",
			0 AS "FromSalePointId",
			0 AS "ToSalePointId",
			NULL::INT AS "ManagerId",
			NULL::VARCHAR AS "ManagerName",
			SL."PromotionCode"::TEXT
		FROM "SalePointLog" SL
			JOIN "LotteryType" LT ON LT."LotteryTypeId" = SL."LotteryTypeId"
			JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SL."LotteryChannelId"
		WHERE SL."SalePointId" = v_sale_point_id AND SL."ActionDate"::DATE = p_date::DATE AND SL."LotteryTypeId" IN(1,2)  AND SL."IsDeleted" IS FALSE
			
		UNION
		
		SELECT
			v_sale_point_id AS "SalePointId",
			T."LotteryDate",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			T."TotalTrans",
			T."TotalTransDup",
			0 AS "TotalTransScratch",
			T."ActionBy",
			T."ActionByName",
			T."TransitionDate" AS "ActionDate",
			0 AS "LotteryTypeId",
			''::VARCHAR AS "LotteryTypeName",
			0 AS "TotalValue",
			T."TransitionId",
			0 AS "SalePointLogId",
			T."TransitionTypeId",
			T."FromSalePointId",
			T."ToSalePointId",
			T."ManagerId",
			U."FullName" AS "ManagerName",
			NULL::TEXT AS "PromotionCode"
	
		FROM "Transition" T
			JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = T."LotteryChannelId"
			LEFT JOIN "User" U ON U."UserId" = T."ManagerId"
		WHERE (T."FromSalePointId" = v_sale_point_id OR T."ToSalePointId" = v_sale_point_id) AND T."TransitionDate"::DATE = p_date::DATE
	)
	SELECT t.* FROM tmp t ORDER BY t."ActionDate" DESC;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_static_fee(p_month timestamp without time zone)
 RETURNS TABLE("Month" character varying, "SalePointId" integer, "StaticFeeTypeId" bigint, "Value" bigint, "CreatedDate" timestamp without time zone, "CreatedBy" bigint, "CreatedByName" character varying, "StaticFeeId" bigint)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	with tmp AS(
	SELECT
		ROW_NUMBER() OVER ( PARTITION BY ST."StaticFeeTypeId",ST."SalePointId" ORDER BY ST."StaticFeeId" DESC) AS "Id",
		ST."Month" ,
		ST."SalePointId" ,
		ST."StaticFeeTypeId" ,
		ST."Value" ,
		ST."CreatedDate" ,
		ST."CreatedBy" ,
		ST."CreatedByName",
		ST."StaticFeeId" 
	FROM "StaticFee" ST
	WHERE ST."Month" = TO_CHAR(p_month ::DATE,'YYYY-MM'))
	SELECT 
		ST."Month" ,
		ST."SalePointId" ,
		ST."StaticFeeTypeId" ,
		ST."Value" ,
		ST."CreatedDate" ,
		ST."CreatedBy" ,
		ST."CreatedByName",
		ST."StaticFeeId" 
	FROM tmp ST 
	WHERE ST."Id" = 1;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_tmp_winning_ticket(p_number character varying, p_lottery_channel_id integer, p_day timestamp without time zone, p_count_number integer)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "Count" integer)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	SELECT 
		S."SalePointId",
		SP."SalePointName",
		Count(S."SalePointId")::INT AS "Count"
	FROM "SalePointLog" S
	LEFT JOIN "SalePoint" SP ON S."SalePointId" = SP."SalePointId"
	WHERE (S."LotteryDate" BETWEEN (p_day::DATE - INTERVAL'5 day') AND p_day::DATE )
		AND right(S."FourLastNumber", p_count_number) = right(p_number, p_count_number)
		AND right(S."FourLastNumber", p_count_number+1) != right(p_number, p_count_number+1)
		AND S."IsDeleted" = FALSE
		AND S."LotteryChannelId" = p_lottery_channel_id
	GROUP BY 
		S."SalePointId",
		SP."SalePointName"
	ORDER BY "SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_get_winning_ticket(p_number character varying, p_day timestamp without time zone)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "FourLastNumber" character varying, "Count" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_lenght_number INT := LENGTH(p_number);
BEGIN

	RETURN QUERY
	SELECT 
		S."SalePointId",
		SP."SalePointName",
		S."LotteryDate",
		S."FourLastNumber",
		Count(S."SalePointId")::INT AS "Count"
	FROM "SalePointLog" S
	LEFT JOIN "SalePoint" SP ON S."SalePointId" = SP."SalePointId"
	WHERE (S."LotteryDate" BETWEEN (p_day::DATE - INTERVAL'5 day') AND p_day::DATE )
		AND right(S."FourLastNumber", v_lenght_number) = p_number
		AND S."IsDeleted" = FALSE
	GROUP BY 
		S."SalePointId",
		S."LotteryDate",
		S."FourLastNumber",
		SP."SalePointName"
	ORDER BY "SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_insert_or_update_agency(p_agency_name character varying, p_agency_id integer DEFAULT 0, p_is_activity boolean DEFAULT true, p_is_deleted boolean DEFAULT false)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
BEGIN   

	IF p_agency_id = 0
	THEN
		INSERT INTO "Agency" ("AgencyName")
		VALUES (p_agency_name);
	ELSE
		UPDATE "Agency"
			SET 
				"AgencyName" = p_agency_name,
				"IsActive" = p_is_activity,
				"IsDeleted" = p_is_deleted
		WHERE "AgencyId" = p_agency_id;
	END IF;
		
    
    v_id := 1;
    v_mess := 'Update successful';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_insert_or_update_sub_agency(p_agency_name character varying, p_is_activity boolean DEFAULT true, p_is_deleted boolean DEFAULT false, p_price double precision DEFAULT 0, p_agency_id integer DEFAULT 0)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
BEGIN   

	IF p_agency_id = 0
	THEN
		INSERT INTO "SubAgency" ("AgencyName", "Price")
		VALUES (p_agency_name, p_price);
	ELSE
		UPDATE "SubAgency"
			SET 
				"AgencyName" = p_agency_name,
				"Price" = p_price,
				"IsActive" = p_is_activity,
				"IsDelete" = p_is_deleted
		WHERE "AgencyId" = p_agency_id;
	END IF;
		
    
    v_id := 1;
    v_mess := 'Update successful';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_insert_winning(p_action_by integer, p_action_by_name character varying, p_user_role_id integer, p_winning_type_id integer, p_lottery_number character varying, p_lottery_channel_id integer, p_quantity integer, p_winning_price numeric, p_from_sale_point_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_user_id INT;
	v_sale_point_id INT;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
BEGIN

-- 	SELECT 
-- 		UR."UserId" INTO v_user_id
-- 	FROM "UserRole" UR WHERE UR."UserRoleId" = p_user_role_id;
-- 	
-- 	SELECT
-- 		SD."SalePointId", SD."ShiftDistributeId" INTO v_sale_point_id, v_shift_dis_id
-- 	FROM "ShiftDistribute" SD WHERE SD."UserId" = v_user_id AND SD."DistributeDate" = NOW()::DATE;

	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;

	IF COALESCE(v_sale_point_id, 0) > 0 THEN

		INSERT INTO "Winning"(
			"WinningTypeId",
			"LotteryNumber",
			"LotteryChannelId",
			"Quantity",
			"WinningPrice",
			"FromSalePointId",
			"SalePointId",
			"ShiftDistributeId",
			"ActionBy",
			"ActionByName"
		) VALUES(
			p_winning_type_id,
			p_lottery_number,
			p_lottery_channel_id,
			p_quantity,
			p_winning_price,
			p_from_sale_point_id,
			v_sale_point_id,
			v_shift_dis_id,
			p_action_by,
			p_action_by_name
		) RETURNING "WinningId" INTO v_id;
	
		v_mess := 'Ghi nhận thành công';
		
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_log_repayment(p_action_by integer, p_action_by_name character varying, p_sale_point_id integer, p_customer_name character varying, p_note text, p_amount numeric)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
BEGIN
	
	INSERT INTO "Repayment"(
		"CustomerName",
		"Amount",
		"Note",
		"ActionBy",
		"ActionByName",
		"SalePointId"
	) VALUES (
		p_customer_name,
		p_amount,
		p_note,
		p_action_by,
		p_action_by_name,
		p_sale_point_id
	) RETURNING "RepaymentId" INTO v_id;
	
	v_mess := 'Ghi nhận thành công';
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_log_repayment_v2(p_action_by integer, p_action_by_name character varying, p_sale_point_id integer, p_customer_name character varying, p_note text, p_amount numeric, p_user_role_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_sale_point_id INT;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
BEGIN
	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
		
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
		
		INSERT INTO "Repayment"(
			"CustomerName",
			"Amount",
			"Note",
			"ActionBy",
			"ActionByName",
			"SalePointId",
			"ShiftDistributeId"
		) VALUES (
			p_customer_name,
			p_amount,
			p_note,
			p_action_by,
			p_action_by_name,
			v_sale_point_id,
			v_shift_dis_id
		) RETURNING "RepaymentId" INTO v_id;
		v_mess := 'Ghi nhận thành công';
		
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_receive_from_agency(p_action_by integer, p_action_by_name character varying, p_lottery_date timestamp without time zone, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	ele JSON;
BEGIN
	
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
		IF NOT EXISTS (SELECT 1 FROM "InventoryFull" WHERE "LotteryDate" = p_lottery_date::DATE AND "AgencyId" = (ele ->> 'AgencyId')::INT AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT) THEN	
			
			INSERT INTO "InventoryFull"(
				"LotteryDate",
				"LotteryChannelId",
				"AgencyId",
				"TotalReceived",
				"TotalRemaining",
				"ActionBy",
				"ActionByName"
			) VALUES (
				p_lottery_date::DATE,
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'AgencyId')::INT,
				COALESCE((ele ->> 'TotalReceived')::INT, 0),
				COALESCE((ele ->> 'TotalReceived')::INT, 0),
				p_action_by,
				p_action_by_name
			);
			
		ELSE 

			UPDATE "InventoryFull" F
			SET
				"TotalReceived" =  COALESCE((ele ->> 'TotalReceived')::INT, 0),
				"TotalRemaining" = "TotalRemaining" - ("TotalReceived" - COALESCE((ele ->> 'TotalReceived')::INT, 0)),
				"ActionBy" = p_action_by,
				"ActionByName" = p_action_by_name,
				"ActionDate" = NOW()
			WHERE F."LotteryDate" = p_lottery_date::DATE 
				AND F."AgencyId" = (ele ->> 'AgencyId')::INT
				AND F."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
			
		END IF;
	
	END LOOP;
	
	v_mess := 'Thao tác thành công';
	
	RETURN QUERY 
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_receive_scratchcard_from_agency(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	ele JSON;
BEGIN
	
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
		
		IF NOT EXISTS(SELECT 1 FROM "ScratchcardFull" SF WHERE SF."AgencyId" = 0 AND (ele ->> 'LotteryChannelId')::INT = SF."LotteryChannelId") THEN
		
			INSERT INTO "ScratchcardFull"(
				"AgencyId",
				"TotalRemaining",
				"LastActionBy",
				"LastActionByName",
				"LotteryChannelId"
			) VALUES(
				0,
				COALESCE((ele ->> 'TotalReceived')::INT, 0),
				p_action_by,
				p_action_by_name,
				(ele ->> 'LotteryChannelId')::INT
			);
		
		ELSE 
		
			UPDATE "ScratchcardFull" SF
			SET
				"TotalRemaining" = "TotalRemaining" + COALESCE((ele ->> 'TotalReceived')::INT, 0),
				"LastActionBy" = p_action_by,
				"LastActionByName" = p_action_by_name,
				"LastActionDate" = NOW()
			WHERE SF."AgencyId" = 0
				AND SF."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
		
		END IF;
	
		INSERT INTO "ScratchcardFullLog"(
			"AgencyId",
			"TotalReceived",
			"ActionBy",
			"ActionByName",
			"LotteryChannelId"
		) VALUES (
			(ele ->> 'AgencyId')::INT,
			COALESCE((ele ->> 'TotalReceived')::INT, 0),
			p_action_by,
			p_action_by_name,
			(ele ->> 'LotteryChannelId')::INT
		);
	
	END LOOP;
	
	v_mess := 'Thao tác thành công';
	
	RETURN QUERY 
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_repayment_get_list(p_shift_distribute_id integer, p_sale_point_id integer, p_date timestamp without time zone)
 RETURNS TABLE("RepaymentId" integer, "CustomerName" character varying, "Amount" numeric, "Note" text, "ActionBy" integer, "ActionByName" character varying, "ActionDate" timestamp without time zone, "SalePointId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_sale_point_id INT;
BEGIN	
	
	IF COALESCE(p_sale_point_id, 0) = 0 THEN
	
		SELECT SD."SalePointId" INTO v_sale_point_id FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" = p_shift_distribute_id;
	
	ELSE
	
		v_sale_point_id := p_sale_point_id;
		
	END IF;
	
	RETURN QUERY 
	SELECT 
		R."RepaymentId",
		R."CustomerName",
		R."Amount",
		R."Note",
		R."ActionBy",
		R."ActionByName",
		R."ActionDate",
		R."SalePointId"
	FROM "Repayment" R
	WHERE ((COALESCE(p_shift_distribute_id, 0) = 0 AND COALESCE(p_sale_point_id, 0) = 0) OR R."SalePointId" = v_sale_point_id)
		AND R."ActionDate"::DATE = p_date::DATE
	ORDER BY R."ActionDate" DESC;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_repayment_get_list_v2(p_user_role_id integer, p_sale_point_id integer, p_date timestamp without time zone)
 RETURNS TABLE("RepaymentId" integer, "CustomerName" character varying, "Amount" numeric, "Note" text, "ActionBy" integer, "ActionByName" character varying, "ActionDate" timestamp without time zone, "SalePointId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_sale_point_id INT;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
BEGIN	
	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
	
	IF v_is_super_admin IS TRUE OR v_is_manager IS TRUE THEN
	
		RETURN QUERY 
		SELECT 
			R."RepaymentId",
			R."CustomerName",
			R."Amount",
			R."Note",
			R."ActionBy",
			R."ActionByName",
			R."ActionDate",
			R."SalePointId"
		FROM "Repayment" R
		WHERE (COALESCE(p_sale_point_id, 0) = 0 OR R."SalePointId" = p_sale_point_id)
			AND R."ActionDate"::DATE = p_date::DATE
		ORDER BY R."ActionDate" DESC;
		
	ELSE
	
		RETURN QUERY 
		SELECT 
			R."RepaymentId",
			R."CustomerName",
			R."Amount",
			R."Note",
			R."ActionBy",
			R."ActionByName",
			R."ActionDate",
			R."SalePointId"
		FROM "Repayment" R
		WHERE R."ShiftDistributeId" = v_shift_dis_id
			AND R."ActionDate"::DATE = p_date::DATE
		ORDER BY R."ActionDate" DESC;
	
	END IF;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_repayment_get_list_v3(p_user_role_id integer, p_sale_point_id integer, p_date timestamp without time zone)
 RETURNS TABLE("GuestActionId" integer, "GuestId" integer, "FullName" character varying, "TotalPrice" numeric, "Note" character varying, "CreatedBy" integer, "CreatedByName" character varying, "CreatedDate" timestamp without time zone, "SalePointId" integer, "PaymentName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_sale_point_id INT;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
BEGIN	
	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
	
	IF v_is_super_admin IS TRUE OR v_is_manager IS TRUE THEN
	
		RETURN QUERY 
		SELECT 
			GA."GuestActionId",
			GA."GuestId",
			G."FullName",
			GA."TotalPrice",
			GA."Note",
			GA."CreatedBy",
			GA."CreatedByName",
			GA."CreatedDate",
			GA."SalePointId",
			FP."PaymentName"
		FROM "GuestAction" GA
		WHERE (COALESCE(p_sale_point_id, 0) = 0 OR GA."SalePointId" = p_sale_point_id)
			AND GA."CreatedDate"::DATE = p_date::DATE
			AND GA."IsDeleted" IS FALSE
			AND GA."GuestActionTypeId" = 2
		ORDER BY R."ActionDate" DESC;
		
	ELSE
	
		RETURN QUERY 
		SELECT 
			GA."GuestActionId",
			GA."GuestId",
			G."FullName",
			GA."TotalPrice",
			GA."Note",
			GA."CreatedBy",
			GA."CreatedByName",
			GA."CreatedDate",
			GA."SalePointId",
			FP."PaymentName"
		FROM "GuestAction" GA
			JOIN "Guest" G ON G."GuestId" = GA."GuestId"
			JOIN "FormPayment" FP ON GA."FormPaymentId" = FP."FormPaymentId"
		WHERE GA."ShiftDistributeId" = v_shift_dis_id
			AND GA."IsDeleted" IS FALSE
			AND GA."GuestActionTypeId" = 2
		ORDER BY GA."CreatedDate" DESC;
	
	END IF;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_report_money_in_a_shift(p_shift_distribute integer, p_date timestamp without time zone, p_total_money bigint)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT;
    v_mess TEXT;
    ele JSON;
    v_time TIMESTAMP := NOW();
BEGIN
			IF EXISTS (SELECT * FROM "ReportMoney" R WHERE R."ActionDate" = p_date::DATE AND R."ShiftDistributeId" = p_shift_distribute)
				THEN
					Update "ReportMoney" 
					SET "TotalMoneyInDay" = p_total_money
					WHERE "ShiftDistributeId" = p_shift_distribute AND "ActionDate" = p_date::DATE;
				v_id := 1;
        v_mess := 'Sửa thành công';
			ELSE
			INSERT INTO "ReportMoney"("SalePointId","TotalMoneyInDay","ShiftDistributeId","ActionDate","ShiftId" )
			VALUES( 
			 (SELECT SD."SalePointid" FROM "ShiftTransfer" SD
			WHERE SD."ActionDate" :: DATE = p_date::DATE AND SD."ShiftDistributeId" = p_shift_distribute GROUP BY SD."SalePointid"),
			 p_total_money,
			 p_shift_distribute,
			 p_date::DATE,
			 (SELECT SD."ShiftId" FROM "ShiftTransfer" SD
			WHERE SD."ActionDate" :: DATE = p_date::DATE AND SD."ShiftDistributeId" = p_shift_distribute GROUP BY SD."ShiftId") ) ;
        v_id := 1;
        v_mess := 'Thêm thành công';
			END IF;

    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_report_money_in_a_shift_v1(p_shift_distribute integer, p_date timestamp without time zone, p_total_money bigint)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT;
    v_mess TEXT;
BEGIN
			INSERT INTO "ReportMoney" ("SalePointId","TotalMoneyInDay","ShiftDistributeId","ActionDate","ShiftId" )
			SELECT ST."SalePointid",p_total_money,p_shift_distribute,p_date,ST."ShiftId"
			FROM "ShiftTransfer" ST 
			WHERE ST."ActionDate"::DATE = p_date::DATE AND ST."ShiftDistributeId" = p_shift_distribute  LIMIT 1;
			
			
      v_id := 1;
      v_mess := 'Thêm thành công';


    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_return_lottery(p_action_by integer, p_action_by_name character varying, p_data text, p_action_type integer DEFAULT 1, p_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_check_dup INT := 0;
	v_check INT := 0;
	v_check_type BOOL := FALSE;
	v_check_date DATE;
	v_check_channel INT;
	v_time TIMESTAMP := NOW();
BEGIN

	--THÊM 
	IF p_action_type = 1 THEN 
	
		CREATE TEMP TABLE DataCheck ON COMMIT DROP AS (
			WITH tmp AS (
				SELECT 
					I."AgencyId",
					I."LotteryChannelId",
					I."TotalReceived",
					I."LotteryDate",
					FALSE AS "IsScratchcard"
				FROM "InventoryFull" I
				WHERE I."LotteryDate" = p_date::DATE
				UNION
				SELECT 
					SF."AgencyId",
					SF."LotteryChannelId",
					SUM(SF."TotalReceived") AS "TotalReceived",
					NULL AS "LotteryDate",
					TRUE AS "IsScratchcard"
				FROM "ScratchcardFullLog" SF
				GROUP BY 
					SF."AgencyId",
					SF."LotteryChannelId"
			),
			tmp2 AS(
				SELECT 
					T."LotteryChannelId",
					T."LotteryDate",
					T."TransitionTypeId",
					T."TotalTrans",
					T."TotalTransDup",
					T."ToAgencyId",
					T."IsScratchcard"
				FROM "Transition" T
				WHERE T."ConfirmStatusId" = 2
					AND (T."IsScratchcard" IS TRUE OR T."LotteryDate" = p_date::DATE)
					AND T."IsDeleted" IS FALSE
			),
			tmp3 AS (
				SELECT
					T."ToAgencyId" AS "AgencyId",
					T."LotteryChannelId",
					SUM(T."TotalTrans") AS "TotalTrans",
					SUM(T."TotalTransDup") AS "TotalTransDup"
				FROM tmp2 T
				WHERE T."ToAgencyId" IS NOT NULL AND T."TransitionTypeId" = 3
				GROUP BY 
					T."ToAgencyId",
					T."LotteryChannelId"
			)
			SELECT 
				C."AgencyId",
				C."LotteryChannelId",
				(C."TotalReceived" - COALESCE(R."TotalTrans", 0) - COALESCE(R."TotalTransDup", 0)) AS "TotalCanReturn",
				C."LotteryDate",
				C."IsScratchcard"	
			FROM tmp C
				LEFT JOIN tmp3 R ON C."AgencyId" = R."AgencyId" AND C."LotteryChannelId" = R."LotteryChannelId"
			ORDER BY C."AgencyId", C."LotteryChannelId"
					
		);
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		
			-- Cập nhật kho vé cào
			IF (ele ->> 'IsScratchcard')::BOOL IS TRUE THEN
			
				--Kiểm tra đã tồn tại kho vé chưa
				IF NOT EXISTS(SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = 0 AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT) THEN
					RAISE 'Không đủ số lượng vé trong kho';
				END IF;
				
				UPDATE "Scratchcard" 
				SET "TotalRemaining" = "TotalRemaining" - COALESCE((ele->>'TotalTrans')::INT, 0)
				WHERE "SalePointId" = 0 AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
			
			-- Cập nhật kho vé thường
			ELSE 
				
				--Kiểm tra xem kho vé đã tồn tại chưa
				IF NOT EXISTS(
					SELECT 1 FROM "Inventory"
					WHERE "SalePointId" = 0 
						AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT 
						AND "LotteryDate" = (ele->>'LotteryDate')::DATE
				) THEN
					RAISE 'Không đủ số lượng vé trong kho';
				END IF;
			
				UPDATE "Inventory" 
				SET 
					"TotalRemaining" = "TotalRemaining" - COALESCE((ele->>'TotalTrans')::INT, 0),
					"TotalDupRemaining" = "TotalDupRemaining" - COALESCE((ele->>'TotalTransDup')::INT, 0)
				WHERE "SalePointId" = 0
					AND "LotteryChannelId" = (ele->>'LotteryChannelId')::INT
					AND "LotteryDate" = (ele->>'LotteryDate')::DATE
				RETURNING "TotalRemaining", "TotalDupRemaining" INTO v_check, v_check_dup;
				
			END IF;
			
			-- Kiểm tra ra số âm
			IF v_check < 0 OR v_check_dup < 0 THEN
				RAISE 'Không đủ số lượng vé trong kho';
			END IF;
			
			-- Kiểm tra trả về đại lý
-- 			IF NOT EXISTS( 
-- 				SELECT 1 FROM DataCheck D
-- 				WHERE D."AgencyId" = (ele->>'ToAgencyId')::INT 
-- 					AND (ele->>'LotteryChannelId')::INT = D."LotteryChannelId"
-- 					AND (D."LotteryDate" = (ele->>'LotteryDate')::DATE OR (D."IsScratchcard" IS TRUE AND (ele->>'IsScratchcard')::BOOL IS TRUE))
-- 					AND D."TotalCanReturn" >= (COALESCE((ele->>'TotalTrans')::INT, 0) + COALESCE((ele->>'TotalTransDup')::INT, 0))
-- 			) THEN
-- 				RAISE 'Vé đã trả nhiều hơn số vé đã lấy từ đại lý';
-- 			END IF;
					
			-- Thêm dòng log
			INSERT INTO "Transition"(
				"LotteryDate",
				"LotteryChannelId",
				"FromSalePointId",
				"ToAgencyId",
				"TotalTrans",
				"TotalTransDup",
				"ConfirmTrans",
				"ConfirmTransDup",
				"TransitionDate",
				"TransitionTypeId",
				"ConfirmStatusId",
				"IsScratchcard",
				"ActionBy",
				"ActionByName",
				"ConfirmBy",
				"ConfirmByName",
				"ConfirmDate"
			)
			VALUES(
				(ele->>'LotteryDate')::DATE,
				(ele->>'LotteryChannelId')::INT,
				(ele->>'FromSalePointId')::INT,
				(ele->>'ToAgencyId')::INT,
				COALESCE((ele->>'TotalTrans')::INT, 0),
				COALESCE((ele->>'TotalTransDup')::INT, 0),
				COALESCE((ele->>'TotalTrans')::INT, 0),
				COALESCE((ele->>'TotalTransDup')::INT, 0),
				v_time,
				3,
				2,
				(ele->>'IsScratchcard')::BOOL,
				p_action_by,
				p_action_by_name,
				p_action_by,
				p_action_by_name,
				v_time
			);
			
			-- Cập nhật lại bảng CHECK
			UPDATE DataCheck 
			SET "TotalCanReturn" = "TotalCanReturn" - (COALESCE((ele->>'TotalTrans')::INT, 0) + COALESCE((ele->>'TotalTransDup')::INT, 0))
			WHERE "AgencyId" = (ele->>'ToAgencyId')::INT 
				AND (ele->>'LotteryChannelId')::INT = "LotteryChannelId"
				AND ("LotteryDate" = (ele->>'LotteryDate')::DATE OR ("IsScratchcard" IS TRUE AND (ele->>'IsScratchcard')::BOOL IS TRUE));
			
		END LOOP;
		
		v_id := 1;
		v_mess := 'Trả ế thành công';
		
	--SỬA
	ELSEIF p_action_type = 2 THEN 
		-- DO SOMETHING
		v_id := 1;
		v_mess := 'Cập nhật thành công';
	
	--XOÁ
	ELSEIF p_action_type = 3 THEN 
		
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		
			--Cập nhật dòng log
			UPDATE "Transition" 
			SET "IsDeleted" = TRUE
			WHERE "TransitionId" = (ele->>'TransitionId')::INT
			RETURNING "TotalTrans", "TotalTransDup", "IsScratchcard", "LotteryDate", "LotteryChannelId"
			INTO v_check, v_check_dup, v_check_type,v_check_date, v_check_channel;
				
			
			-- Cập nhật kho vé cào
			IF v_check_type IS TRUE THEN
				
				--Kiểm tra đã tồn tại kho vé cào chưa
				IF NOT EXISTS(SELECT 1 FROM "Scratchcard" WHERE "SalePointId" = 0 AND "LotteryChannelId" = v_check_channel) THEN
					RAISE 'Không đủ số lượng vé trong kho';
				END IF;
				
				UPDATE "Scratchcard" 
				SET "TotalRemaining" = "TotalRemaining" + v_check
				WHERE "SalePointId" = 0 AND "LotteryChannelId" = v_check_channel;
			
			-- Cập nhật kho vé thường
			ELSE 
			
				--Kiểm tra xem kho vé đã tồn tại chưa
				IF NOT EXISTS(
					SELECT 1 FROM "Inventory"
					WHERE "SalePointId" = 0 
						AND "LotteryChannelId" = v_check_channel 
						AND "LotteryDate" = v_check_date
				) THEN
					RAISE 'Không đủ số lượng vé trong kho';
				END IF;
			
				UPDATE "Inventory" 
				SET 
					"TotalRemaining" = "TotalRemaining" + v_check::INT,
					"TotalDupRemaining" = "TotalDupRemaining" + v_check_dup::INT
				WHERE "SalePointId" = 0
					AND "LotteryChannelId" = v_check_channel
					AND "LotteryDate" = v_check_date;
				
			END IF;
		END LOOP;
	
		v_id := 1;
		v_mess := 'Xoá thành công';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_return_money_in_a_shift(p_shift_distribute integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT;
    v_mess TEXT;
    ele JSON;
    v_time TIMESTAMP := NOW();
BEGIN
			INSERT INTO "ReportMoney"("SalePointId","TotalMoneyInDay","ShiftDistributeId","ActionDate","ShiftId" )
			VALUES( 
			 (SELECT SD."SalePointid" FROM "ShiftTransfer" SD
			WHERE SD."ActionDate" :: DATE = p_date AND SD."ShiftDistributeId" = p_shift_distribute GROUP BY SD."SalePointid"),
			(SELECT SD."TotalSoldMoney" + SD."TotalRetailMoney" + SD."TotalWholesale" + SD."TotalWholesaleMoney" FROM "ShiftTransfer" SD
			WHERE SD."ActionDate" :: DATE = p_date AND SD."ShiftDistributeId" = p_shift_distribute GROUP BY SD."TotalSoldMoney", SD."TotalRetailMoney", SD."TotalWholesale", SD."TotalWholesaleMoney" ),
			 p_shift_distribute,
			 p_date,
			 (SELECT SD."ShiftId" FROM "ShiftTransfer" SD
			WHERE SD."ActionDate" :: DATE = p_date AND SD."ShiftDistributeId" = p_shift_distribute GROUP BY SD."ShiftId") ) ;
			UPDATE "ReportMoney" SET "TotalMoneyInDay" = "TotalMoneyInDay" + (SELECT SUM(T."TotalPrice") FROM "Transaction" T WHERE T."Date" = p_date AND T."ShiftDistributeId" = p_shift_distribute);
        v_id := 1;
        v_mess := 'Thêm thành công';


    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


