				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
