				(
					SELECT
						ROW_NUMBER() OVER() AS "RowNumber",
						S."TotalRemaining",
						S."LotteryChannelId",
						LC."ShortName",
						LC."LotteryChannelName",
						LC."RetailPrice"
					FROM "Scratchcard" S
						JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = S."LotteryChannelId"
					WHERE S."SalePointId" = v_sale_point_id
					ORDER BY LC."LotteryChannelTypeId"
				) r
			)::TEXT AS "ScratchcardData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesaleQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesalePrice",
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailPrice"
					FROM "SalePointLog" SL
					WHERE SL."ShiftDistributeId" = v_shift_dis_id AND SL."IsDeleted" IS FALSE
				) r
			)::TEXT AS "SoldData",
			v_sale_point_address;
		ELSE
		RETURN QUERY 
			SELECT 
		(
			with tmp AS(
				SELECT SD."UserId", SD."SalePointId",SD."ShiftId" 
					FROM "ShiftDistribute" SD 
				WHERE SD."DistributeDate"::DATE =p_date::DATE AND SD."UserId" = v_user_id AND SD."ShiftDistributeId" = p_shift_distribute_id
					GROUP BY SD."SalePointId", SD."UserId",SD."ShiftId"
			),tmp1 AS (SELECT * FROM "GroupSalePoint" GSP  ORDER BY GSP."GroupSalePointId" DESC LIMIT v_total_leader)
			,tmp2 AS(
			SELECT GSP."UserId" FROM "tmp1" GSP
				LEFT JOIN tmp T ON T."SalePointId" = ANY (GSP."SalePointIds")
				LEFT JOIN "UserRole" UR ON UR."UserId" = GSP."UserId"
				LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = T."SalePointId"
			WHERE UR."UserTitleId" = 4  AND SD."DistributeDate"::DATE =  p_date::DATE
					GROUP BY GSP."UserId",
					GSP."GroupSalePointId",
					GSP."Option" 
				ORDER BY GSP."Option" ASC ,GSP."GroupSalePointId" DESC
					LIMIT 1
				)
				SELECT T."UserId" FROM tmp2 T GROUP BY T."UserId"
		)
		 As "ManagerId",
			(
			with tmp AS(
						SELECT SD."UserId", SD."SalePointId",SD."ShiftId" 
					FROM "ShiftDistribute" SD 
				WHERE SD."DistributeDate"::DATE = p_date::DATE AND SD."UserId" = v_user_id AND SD."ShiftDistributeId" = p_shift_distribute_id
					GROUP BY SD."SalePointId", SD."UserId",SD."ShiftId"
				), tmp1 AS (SELECT * FROM "GroupSalePoint" GSP  ORDER BY GSP."GroupSalePointId" DESC LIMIT v_total_leader)
				,tmp2 AS (SELECT U."FullName",GSP."GroupSalePointId" FROM "tmp1" GSP
						LEFT JOIN tmp T ON T."SalePointId" = ANY (GSP."SalePointIds")
						LEFT JOIN "UserRole" UR ON UR."UserId" = GSP."UserId"
						LEFT JOIN "User" U ON U."UserId" = UR."UserId"
						LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = T."SalePointId" WHERE UR."UserTitleId" = 4  AND SD."DistributeDate"::DATE =  p_date::DATE 
						GROUP BY 
							GSP."UserId",
							U."FullName",
							GSP."GroupSalePointId",
							GSP."Option" 
					ORDER BY GSP."Option" ASC , GSP."GroupSalePointId" DESC
					LIMIT 1
					)
					SELECT T."FullName" FROM tmp2 T GROUP BY T."FullName"
			) As "ManagerName",
			v_user_id,
			v_sale_point_id,
			v_sale_point_name,
			p_shift_distribute_id,
			v_flag,
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
						IC."ShortName",
						LCT."ShortName" AS "ChannelTypeShortName"
					FROM "Inventory" I
						JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = I."LotteryChannelId"
						LEFT JOIN "LotteryChannelType" LCT ON LCT."LotteryChannelTypeId" = IC."LotteryChannelTypeId"
					WHERE I."LotteryDate" = p_date::DATE AND I."SalePointId" = v_sale_point_id
					ORDER BY I."LotteryDate", IC."LotteryChannelTypeId"
				) r
			)::TEXT AS "TodayData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
						IC."ShortName",
						LCT."ShortName" AS "ChannelTypeShortName"
					FROM "Inventory" I
						JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = I."LotteryChannelId"
						LEFT JOIN "LotteryChannelType" LCT ON LCT."LotteryChannelTypeId" = IC."LotteryChannelTypeId"
					WHERE I."LotteryDate" = (p_date + '1 day'::INTERVAL)::DATE AND I."SalePointId" = v_sale_point_id
					ORDER BY I."LotteryDate", IC."LotteryChannelTypeId"
				) r
			)::TEXT AS "TomorrowData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER() AS "RowNumber",
						S."TotalRemaining",
						S."LotteryChannelId",
						LC."ShortName",
						LC."LotteryChannelName",
						LC."RetailPrice"
					FROM "Scratchcard" S
						JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = S."LotteryChannelId"
					WHERE S."SalePointId" = v_sale_point_id
					ORDER BY LC."LotteryChannelTypeId"
				) r
			)::TEXT AS "ScratchcardData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesaleQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesalePrice",
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailPrice"
					FROM "SalePointLog" SL
					WHERE SL."ShiftDistributeId" = v_shift_dis_id AND SL."IsDeleted" IS FALSE
				) r
			)::TEXT AS "SoldData",
			v_sale_point_address;
			END IF;
		END IF;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_get_data_v6(p_shift_distribute_id integer, p_user_role_id integer, p_date timestamp without time zone)
 RETURNS TABLE("ManagerId" integer, "ManagerName" character varying, "UserId" integer, "SalePointId" integer, "SalePointName" character varying, "ShiftDistributeId" integer, "Flag" boolean, "TodayData" text, "TomorrowData" text, "ScratchcardData" text, "SoldData" text, "SalePointAddress" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_sale_point_id INT;
	v_sale_point_name VARCHAR;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
	v_user_id INT;
	v_user_check INT;
	v_shift_id INT;
	v_shift_bef_dis_id INT;
	v_flag BOOL;
	v_sale_point_address VARCHAR;
	v_temp INT;
	v_total_leader INT;
BEGIN
	
	with tmp AS(
	SELECT COUNT(U."UserId") AS "Leaders" FROM "User" U LEFT JOIN "UserRole" UR ON UR."UserId" = U."UserId" WHERE U."IsActive" = TRUE AND UR."UserTitleId" = 4 
	) SELECT T."Leaders" * (T."Leaders" + 1) INTO v_total_leader FROM tmp T;
	SELECT UR."UserId" INTO v_user_check
	FROM "UserRole" UR 
	WHERE UR."UserRoleId" = p_user_role_id;

	SELECT SD."ShiftDistributeId", SD."ShiftId" , SD."SalePointId" INTO v_shift_dis_id, v_shift_id ,v_sale_point_id
	FROM "ShiftDistribute" SD
	WHERE SD."ShiftDistributeId" = p_shift_distribute_id
		AND SD."DistributeDate" = p_date;
	
	IF v_shift_id IS NULL THEN
		SELECT 
			SD."SalePointId", 
			SD."UserId", 
			FALSE
			INTO v_sale_point_id, v_user_id, v_flag
		FROM "ShiftDistribute" SD
		WHERE SD."ShiftDistributeId" = p_shift_distribute_id;
	
	ELSEIF v_shift_id = 1 THEN 
	
		SELECT 
			SD."SalePointId", 
			SD."UserId", 
			(CASE WHEN EXISTS (SELECT 1 FROM "ShiftTransfer" SF WHERE SF."ShiftDistributeId" = v_shift_dis_id) THEN FALSE ELSE TRUE END)
			INTO v_sale_point_id, v_user_id, v_flag
		FROM "ShiftDistribute" SD
		WHERE SD."ShiftDistributeId" = v_shift_dis_id;
		
	ELSE
	
		SELECT SD."ShiftDistributeId" INTO v_shift_bef_dis_id 
		FROM "ShiftDistribute" SD 
		WHERE SD."DistributeDate" = p_date
			AND SD."ShiftId" =  1
			AND SD."SalePointId" =  v_sale_point_id;
			
		IF(EXISTS (SELECT 1 FROM "ShiftTransfer" SF WHERE SF."ShiftDistributeId" = v_shift_bef_dis_id) OR v_shift_bef_dis_id IS NULL) THEN
		
			SELECT 
				SD."SalePointId", 
				SD."UserId" , 
				(CASE WHEN EXISTS (SELECT 1 FROM "ShiftTransfer" SF WHERE SF."ShiftDistributeId" = v_shift_dis_id) THEN FALSE ELSE TRUE END)
				INTO v_sale_point_id, v_user_id, v_flag
			FROM "ShiftDistribute" SD
			WHERE SD."ShiftDistributeId" = p_shift_distribute_id;
			
		END IF;

	END IF;
	
	SELECT SP."SalePointName", SP."FullAddress" INTO v_sale_point_name, v_sale_point_address FROM "SalePoint" SP WHERE SP."SalePointId" = v_sale_point_id;
	IF(v_user_id IS NOT NULL AND v_user_id = v_user_check) THEN
		
			IF (EXISTS (SELECT 1  FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" = p_date::DATE) ) THEN
		
			RETURN QUERY 
	SELECT 
		(
			with tmp AS(
				SELECT SD."UserId", SD."SalePointId",SD."ShiftId" 
					FROM "ShiftDistribute" SD 
				WHERE SD."DistributeDate"::DATE =p_date::DATE AND SD."UserId" = v_user_id AND SD."ShiftDistributeId" = p_shift_distribute_id
					GROUP BY SD."SalePointId", SD."UserId",SD."ShiftId"
			),tmp1 AS (SELECT * FROM "GroupSalePoint" GSP  ORDER BY GSP."GroupSalePointId" DESC LIMIT v_total_leader)
			,tmp2 AS(
			SELECT GSP."UserId" FROM "tmp1" GSP
				LEFT JOIN tmp T ON T."SalePointId" = ANY (GSP."SalePointIds")
				LEFT JOIN "UserRole" UR ON UR."UserId" = GSP."UserId"
				LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = T."SalePointId"
			WHERE UR."UserTitleId" = 4  AND SD."DistributeDate"::DATE =  p_date::DATE AND GSP."UserId" <> (SELECT LOL."UserId"  FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" =   p_date::DATE GROUP BY LOL."UserId" LIMIT 1)
					GROUP BY GSP."UserId",
					GSP."GroupSalePointId",
					GSP."Option" 
				ORDER BY GSP."Option" ASC ,GSP."GroupSalePointId" DESC
					LIMIT 1
				)
				SELECT COALESCE(T."UserId",0) FROM tmp2 T GROUP BY T."UserId"
		)
		 As "ManagerId",
			(
			with tmp AS(
						SELECT SD."UserId", SD."SalePointId",SD."ShiftId" 
					FROM "ShiftDistribute" SD 
				WHERE SD."DistributeDate"::DATE = p_date::DATE AND SD."UserId" = v_user_id AND SD."ShiftDistributeId" = p_shift_distribute_id
					GROUP BY SD."SalePointId", SD."UserId",SD."ShiftId"
				), tmp1 AS (SELECT * FROM "GroupSalePoint" GSP  ORDER BY GSP."GroupSalePointId" DESC LIMIT v_total_leader)
				,tmp2 AS (SELECT U."FullName",GSP."GroupSalePointId" FROM "tmp1" GSP
						LEFT JOIN tmp T ON T."SalePointId" = ANY (GSP."SalePointIds")
						LEFT JOIN "UserRole" UR ON UR."UserId" = GSP."UserId"
						LEFT JOIN "User" U ON U."UserId" = UR."UserId"
						LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = T."SalePointId" WHERE UR."UserTitleId" = 4  AND SD."DistributeDate"::DATE =  p_date::DATE AND GSP."UserId" <> (SELECT LOL."UserId"  FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" =  p_date::DATE GROUP BY LOL."UserId" LIMIT 1)
						GROUP BY 
							GSP."UserId",
							U."FullName",
							GSP."GroupSalePointId",
							GSP."Option" 
					ORDER BY GSP."Option" ASC ,GSP."GroupSalePointId" DESC
					LIMIT 1
					)
					SELECT COALESCE(T."FullName",'Chưa có trưởng nh') FROM tmp2 T GROUP BY T."FullName"
			) As "ManagerName",
			v_user_id,
			v_sale_point_id,
			v_sale_point_name,
			p_shift_distribute_id,
			v_flag,
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
						IC."ShortName",
						LCT."ShortName" AS "ChannelTypeShortName"
					FROM "Inventory" I
						JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = I."LotteryChannelId"
						LEFT JOIN "LotteryChannelType" LCT ON LCT."LotteryChannelTypeId" = IC."LotteryChannelTypeId"
					WHERE I."LotteryDate" = p_date::DATE AND I."SalePointId" = v_sale_point_id
					ORDER BY I."LotteryDate", IC."LotteryChannelTypeId"
				) r
			)::TEXT AS "TodayData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
						IC."ShortName",
						LCT."ShortName" AS "ChannelTypeShortName"
					FROM "Inventory" I
						JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = I."LotteryChannelId"
						LEFT JOIN "LotteryChannelType" LCT ON LCT."LotteryChannelTypeId" = IC."LotteryChannelTypeId"
					WHERE I."LotteryDate" = (p_date + '1 day'::INTERVAL)::DATE AND I."SalePointId" = v_sale_point_id
					ORDER BY I."LotteryDate", IC."LotteryChannelTypeId"
				) r
			)::TEXT AS "TomorrowData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER() AS "RowNumber",
						S."TotalRemaining",
						S."LotteryChannelId",
						LC."ShortName",
						LC."LotteryChannelName",
						LC."RetailPrice"
					FROM "Scratchcard" S
						JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = S."LotteryChannelId"
					WHERE S."SalePointId" = v_sale_point_id
					ORDER BY LC."LotteryChannelTypeId"
				) r
			)::TEXT AS "ScratchcardData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesaleQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesalePrice",
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailPrice"
					FROM "SalePointLog" SL
					WHERE SL."ShiftDistributeId" = v_shift_dis_id AND SL."IsDeleted" IS FALSE
				) r
			)::TEXT AS "SoldData",
			v_sale_point_address;
		ELSE
		RETURN QUERY 
			SELECT 
		(
			0
		)
		 As "ManagerId",
			(
			with tmp AS(
						SELECT SD."UserId", SD."SalePointId",SD."ShiftId" 
					FROM "ShiftDistribute" SD 
				WHERE SD."DistributeDate"::DATE = p_date::DATE AND SD."UserId" = v_user_id AND SD."ShiftDistributeId" = p_shift_distribute_id
					GROUP BY SD."SalePointId", SD."UserId",SD."ShiftId"
				), tmp1 AS (SELECT * FROM "GroupSalePoint" GSP  ORDER BY GSP."GroupSalePointId" DESC LIMIT v_total_leader)
				,tmp2 AS (SELECT U."FullName",GSP."GroupSalePointId" FROM "tmp1" GSP
						LEFT JOIN tmp T ON T."SalePointId" = ANY (GSP."SalePointIds")
						LEFT JOIN "UserRole" UR ON UR."UserId" = GSP."UserId"
						LEFT JOIN "User" U ON U."UserId" = UR."UserId"
						LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = T."SalePointId" WHERE UR."UserTitleId" = 4  AND SD."DistributeDate"::DATE =  p_date::DATE 
						GROUP BY 
							GSP."UserId",
							U."FullName",
							GSP."GroupSalePointId",
							GSP."Option" 
					ORDER BY GSP."Option" ASC , GSP."GroupSalePointId" DESC
					LIMIT 1
					)
					SELECT T."FullName" FROM tmp2 T GROUP BY T."FullName"
			) As "ManagerName",
			v_user_id,
			v_sale_point_id,
			v_sale_point_name,
			p_shift_distribute_id,
			v_flag,
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
						IC."ShortName",
						LCT."ShortName" AS "ChannelTypeShortName"
					FROM "Inventory" I
						JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = I."LotteryChannelId"
						LEFT JOIN "LotteryChannelType" LCT ON LCT."LotteryChannelTypeId" = IC."LotteryChannelTypeId"
					WHERE I."LotteryDate" = p_date::DATE AND I."SalePointId" = v_sale_point_id
					ORDER BY I."LotteryDate", IC."LotteryChannelTypeId"
				) r
			)::TEXT AS "TodayData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER(ORDER BY LCT."LotteryChannelTypeId") AS "RowNumber",
						I."LotteryDate",
						I."LotteryChannelId",
						IC."LotteryChannelName",
						IC."RetailPrice",
						I."TotalRemaining",
						I."TotalDupRemaining",
						IC."ShortName",
						LCT."ShortName" AS "ChannelTypeShortName"
					FROM "Inventory" I
						JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = I."LotteryChannelId"
						LEFT JOIN "LotteryChannelType" LCT ON LCT."LotteryChannelTypeId" = IC."LotteryChannelTypeId"
					WHERE I."LotteryDate" = (p_date + '1 day'::INTERVAL)::DATE AND I."SalePointId" = v_sale_point_id
					ORDER BY I."LotteryDate", IC."LotteryChannelTypeId"
				) r
			)::TEXT AS "TomorrowData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						ROW_NUMBER() OVER() AS "RowNumber",
						S."TotalRemaining",
						S."LotteryChannelId",
						LC."ShortName",
						LC."LotteryChannelName",
						LC."RetailPrice"
					FROM "Scratchcard" S
						JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = S."LotteryChannelId"
					WHERE S."SalePointId" = v_sale_point_id
					ORDER BY LC."LotteryChannelTypeId"
				) r
			)::TEXT AS "ScratchcardData",
			(
				SELECT array_to_json(
					ARRAY_AGG (r))
				FROM
				(
					SELECT
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesaleQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" NOT IN(1,6)) AS "TotalWholesalePrice",
						SUM(SL."Quantity") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailQuantity",
						SUM(SL."TotalValue") FILTER(WHERE SL."LotteryPriceId" IN(1,6)) AS "TotalRetailPrice"
					FROM "SalePointLog" SL
					WHERE SL."ShiftDistributeId" = v_shift_dis_id AND SL."IsDeleted" IS FALSE
				) r
			)::TEXT AS "SoldData",
			v_sale_point_address;
			END IF;
		END IF;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_get_inventory_data(p_date timestamp without time zone)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "ShiftDistributeId" integer, "TodayData" text, "TomorrowData" text, "ScratchcardData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_sale_point_id INT;
	v_sale_point_name VARCHAR;
	v_shift_dis_id INT;
	v_user_id INT;
BEGIN

	RETURN QUERY 
	SELECT 
		0,
		'Inventory'::VARCHAR,
		0,
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					ROW_NUMBER() OVER(ORDER BY IC."LotteryChannelTypeId") AS "RowNumber",
					T."LotteryDate",
					T."LotteryChannelId",
					IC."LotteryChannelName",
					IC."ShortName",
				  IC."LotteryChannelTypeId",
					(COALESCE(SUM(T."TotalTrans") FILTER(WHERE T."TransitionTypeId" = 1),0) - COALESCE(SUM(T."TotalTrans") FILTER(WHERE T."TransitionTypeId" = 2),0)) AS "TotalRemaining",
					(COALESCE(SUM(T."TotalTransDup") FILTER(WHERE T."TransitionTypeId" = 1),0) - COALESCE(SUM(T."TotalTransDup") FILTER(WHERE T."TransitionTypeId" = 2),0)) AS "TotalDupRemaining"
				FROM "Transition" T
					JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = T."LotteryChannelId"
				WHERE T."LotteryDate" = p_date::DATE AND T."ConfirmStatusId" = 2  AND T."IsScratchcard" IS FALSE
				GROUP BY
					T."LotteryDate",
					T."LotteryChannelId",
					IC."LotteryChannelName",
					IC."ShortName",
				  IC."LotteryChannelTypeId"
			) r
		)::TEXT AS "TodayData",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					ROW_NUMBER() OVER(ORDER BY IC."LotteryChannelTypeId") AS "RowNumber",
					T."LotteryDate",
					T."LotteryChannelId",
					IC."LotteryChannelName",
					IC."ShortName",
					IC."LotteryChannelTypeId",
					(COALESCE(SUM(T."TotalTrans") FILTER(WHERE T."TransitionTypeId" = 1), 0) - COALESCE(SUM(T."TotalTrans") FILTER(WHERE T."TransitionTypeId" = 2),0))  AS "TotalRemaining",
					(COALESCE(SUM(T."TotalTransDup") FILTER(WHERE T."TransitionTypeId" = 1),0) - COALESCE(SUM(T."TotalTransDup") FILTER(WHERE T."TransitionTypeId" = 2),0))  AS "TotalDupRemaining"
				FROM "Transition" T
					JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = T."LotteryChannelId"
				WHERE T."LotteryDate" = (p_date + '1 day'::INTERVAL)::DATE AND T."ConfirmStatusId" = 2 AND T."IsScratchcard" IS FALSE
				GROUP BY
					T."LotteryDate",
					T."LotteryChannelId",
					IC."LotteryChannelName",
					IC."ShortName",
					IC."LotteryChannelTypeId"
			) r
		)::TEXT AS "TomorrowData",
			(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					ROW_NUMBER() OVER(ORDER BY IC."LotteryChannelTypeId") AS "RowNumber",
					T."LotteryChannelId",
					IC."LotteryChannelName",
					IC."ShortName",
					IC."LotteryChannelTypeId",
					(COALESCE(SUM(T."TotalTrans") FILTER(WHERE T."TransitionTypeId" = 1), 0) - COALESCE(SUM(T."TotalTrans") FILTER(WHERE T."TransitionTypeId" = 2),0))  AS "TotalRemaining",
					(COALESCE(SUM(T."TotalTransDup") FILTER(WHERE T."TransitionTypeId" = 1),0) - COALESCE(SUM(T."TotalTransDup") FILTER(WHERE T."TransitionTypeId" = 2),0))  AS "TotalDupRemaining"
				FROM "Transition" T
					JOIN "LotteryChannel" IC ON IC."LotteryChannelId" = T."LotteryChannelId"
				WHERE T."IsScratchcard" IS TRUE AND T."ConfirmStatusId" = 2
				GROUP BY
					T."LotteryChannelId",
					IC."LotteryChannelName",
					IC."ShortName",
					IC."LotteryChannelTypeId"
			) r
		)::TEXT AS "ScratchcardData";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_lottery(p_user_role_id integer, p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	v_sale_point_id INT;
	v_user_id INT;
	ele JSON;
	v_shift_dis_id INT;
BEGIN
	
	SELECT 
		UR."UserId" INTO v_user_id
	FROM "UserRole" UR WHERE UR."UserRoleId" = p_user_role_id;
	
	SELECT
		SD."SalePointId", SD."ShiftDistributeId" INTO v_sale_point_id, v_shift_dis_id
	FROM "ShiftDistribute" SD WHERE SD."UserId" = v_user_id AND SD."DistributeDate" = NOW()::DATE;
	
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
			INSERT INTO "SalePointLog"(
				"SalePointId",
				"LotteryDate",
				"LotteryChannelId",
				"Quantity",
				"LotteryTypeId",
				"LotteryPriceId",
				"TotalValue",
				"ShiftDistributeId",
				"ActionBy",
				"ActionByName"
			) VALUES(
				v_sale_point_id,
				(ele ->> 'LotteryDate')::DATE,
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'Quantity')::INT,
				(ele ->> 'LotteryTypeId')::INT,
				(ele ->> 'LotteryPriceId')::INT,
				(SELECT CEIL((ele ->> 'Quantity')::INT * LP."Price") FROM "LotteryPrice" LP WHERE LP."LotteryPriceId" = (ele ->> 'LotteryPriceId')::INT),
				v_shift_dis_id,
				p_action_by,
				p_action_by_name
			);
			
			IF (ele ->> 'LotteryTypeId')::INT = 1 THEN 
			
				UPDATE "Inventory"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
			
			ELSEIF (ele ->> 'LotteryTypeId')::INT = 2 THEN
			
				UPDATE "Inventory"
				SET
					"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT;
			
			ELSE
			
				UPDATE "Scratchcard"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id;
			
			END IF;
			
	
		END LOOP;
	
		v_id := v_sale_point_id;
		v_mess := 'Lưu thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;

	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_lottery_v2(p_shift_dis_id integer, p_user_role_id integer, p_action_by integer, p_action_by_name character varying, p_data text, p_guest_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	v_sale_point_id INT;
	v_user_id INT;
	ele JSON;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
	v_check INT := 0;
BEGIN
	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
	
	
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
			INSERT INTO "SalePointLog"(
				"SalePointId",
				"LotteryDate",
				"LotteryChannelId",
				"Quantity",
				"LotteryTypeId",
				"LotteryPriceId",
				"TotalValue",
				"ActionBy",
				"ActionByName",
				"ShiftDistributeId",
				"GuestId",
				"FourLastNumber"
			) VALUES(
				v_sale_point_id,
				(CASE WHEN (ele ->> 'LotteryTypeId')::INT = 3 THEN NULL::DATE ELSE (ele ->> 'LotteryDate')::DATE END),
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'Quantity')::INT,
				(ele ->> 'LotteryTypeId')::INT,
				(ele ->> 'LotteryPriceId')::INT,
				(SELECT (CASE WHEN (ele ->> 'Quantity')::INT >= 110 AND (ele ->> 'LotteryPriceId')::INT = 6 THEN  CEIL((ele ->> 'Quantity')::INT * LP."Price") + ((ele ->> 'Quantity')::INT /100) ELSE CEIL((ele ->> 'Quantity')::INT * LP."Price") END) FROM "LotteryPrice" LP WHERE LP."LotteryPriceId" = (ele ->> 'LotteryPriceId')::INT),
				p_action_by,
				p_action_by_name,
				v_shift_dis_id,
				COALESCE(p_guest_id,NULL),
				(ele->>'FourLastNumber')::VARCHAR
			);
			
			IF (ele ->> 'LotteryTypeId')::INT = 1 THEN 
			
				UPDATE "Inventory"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSEIF (ele ->> 'LotteryTypeId')::INT = 2 THEN
			
				UPDATE "Inventory"
				SET
					"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalDupRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSE
			
				UPDATE "Scratchcard"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
					
			
			END IF;
			
	
		END LOOP;
	
		v_id := v_sale_point_id;
		v_mess := 'Lưu thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;

	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_lottery_v3(p_shift_dis_id integer, p_user_role_id integer, p_action_by integer, p_action_by_name character varying, p_data text, p_guest_id integer DEFAULT NULL::integer, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	v_sale_point_id INT;
	v_user_id INT;
	ele JSON;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
	v_check INT := 0;
	v_time TIMESTAMP DEFAULT NOW();
	v_array INT[] := '{}'::INT[];
	v_tmp INT;
	v_step INT;
BEGIN	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
	
		--Tạo hoá đơn khi chưa có
	IF p_order_id IS NULL THEN
		
		INSERT INTO "HistoryOfOrder" (
			"SalePointId",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"IsDeleted",
			"ShiftDistributeId"
		)
		VALUES (
			v_sale_point_id,
			p_action_by,
			p_action_by_name,
			v_time,
			FALSE,
			v_shift_dis_id
		) RETURNING "HistoryOfOrderId" INTO p_order_id;
		
	END IF;
	
	--Lấy danh sách id bán hàng
	SELECT "SalePointLogIds" INTO v_array FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = p_order_id;
	IF v_array IS NULL THEN 
		v_array := '{}'::INT[];
	END IF;
	
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
			INSERT INTO "SalePointLog"(
				"SalePointId",
				"LotteryDate",
				"LotteryChannelId",
				"Quantity",
				"LotteryTypeId",
				"LotteryPriceId",
				"TotalValue",
				"ActionBy",
				"ActionByName",
				"ShiftDistributeId",
				"GuestId",
				"HistoryOfOrderId",
				"FourLastNumber"
			) VALUES(
				v_sale_point_id,
				(CASE WHEN (ele ->> 'LotteryTypeId')::INT = 3 THEN NULL::DATE ELSE (ele ->> 'LotteryDate')::DATE END),
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'Quantity')::INT,
				(ele ->> 'LotteryTypeId')::INT,
				(ele ->> 'LotteryPriceId')::INT,
				(SELECT (CASE WHEN (ele ->> 'Quantity')::INT >= 110 AND (ele ->> 'LotteryPriceId')::INT = 6 THEN  CEIL((ele ->> 'Quantity')::INT * LP."Price") + ((ele ->> 'Quantity')::INT /100) ELSE CEIL((ele ->> 'Quantity')::INT * LP."Price") END) FROM "LotteryPrice" LP WHERE LP."LotteryPriceId" = (ele ->> 'LotteryPriceId')::INT),
				p_action_by,
				p_action_by_name,
				v_shift_dis_id,
				COALESCE(p_guest_id,NULL),
				p_order_id,
				(ele->>'FourLastNumber')::VARCHAR
			) RETURNING "SalePointLogId" INTO v_tmp;
			SELECT 	((ele ->> 'Quantity')::INT / P."Step") INTO v_step FROM "Promotion" P WHERE P."RegionId" =(
			SELECT L."RegionId" FROM "LotteryChannel" L WHERE L."LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT);
			SELECT * INTO v_mess FROM crm_update_reward_lottery(NOW()::DATE,v_step,v_tmp);

			v_array := array_append(v_array, v_tmp);
			
			IF (ele ->> 'LotteryTypeId')::INT = 1 THEN 
			
				UPDATE "Inventory"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSEIF (ele ->> 'LotteryTypeId')::INT = 2 THEN
			
				UPDATE "Inventory"
				SET
					"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalDupRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSE
			
				UPDATE "Scratchcard"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
					
			
			END IF;
			
	
		END LOOP;
		
		UPDATE "HistoryOfOrder" 
		SET 
			"SalePointLogIds" = v_array
		WHERE "HistoryOfOrderId" = p_order_id;
		
		v_id := v_sale_point_id;
		v_mess := 'Lưu thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess, p_order_id;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_lottery_v4(p_shift_dis_id integer, p_user_role_id integer, p_action_by integer, p_action_by_name character varying, p_data text, p_guest_id integer DEFAULT NULL::integer, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer, "PromotionCode" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	v_sale_point_id INT;
	v_user_id INT;
	ele JSON;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
	v_check INT := 0;
	v_time TIMESTAMP DEFAULT NOW();
	v_array INT[] := '{}'::INT[];
	v_tmp INT;
	v_step INT;
	v_quantity INT;
	v_promotioncode TEXT;
	v_data_promotion TEXT;
	v_data_promotion_id TEXT;
BEGIN	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
	
		--Tạo hoá đơn khi chưa có
	IF p_order_id IS NULL THEN
		
		INSERT INTO "HistoryOfOrder" (
			"SalePointId",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"IsDeleted",
			"ShiftDistributeId"
		)
		VALUES (
			v_sale_point_id,
			p_action_by,
			p_action_by_name,
			v_time,
			FALSE,
			v_shift_dis_id
		) RETURNING "HistoryOfOrderId" INTO p_order_id;
		
	END IF;
	
	--Lấy danh sách id bán hàng
	SELECT "SalePointLogIds" INTO v_array FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = p_order_id;
	IF v_array IS NULL THEN 
		v_array := '{}'::INT[];
	END IF;
	
	
	
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
			IF COALESCE(p_guest_id,NULL) IS NOT NULL
			THEN
				v_quantity:=0;
			ELSEIF (ele ->> 'LotteryChannelId')::INT < 1000
			THEN
				v_quantity= COALESCE(v_quantity,0) + (ele ->> 'Quantity')::INT;
			END IF;
		END LOOP;
		
		v_step := (SELECT (v_quantity::INT / P."Step") FROM "Promotion" P LIMIT 1);
		IF(v_step > 0)
		THEN
			v_data_promotion := (with tmp AS(SELECT P."PromotionCodeId",P."PromotionCode" FROM "PromotionCode" P WHERE P."Date" = NOW()::DATE AND P."IsUsed" IS FALSE LIMIT v_step) SELECT array_agg(T."PromotionCode") FROM tmp T )::TEXT;
			v_data_promotion_id := (with tmp AS(SELECT P."PromotionCodeId",P."PromotionCode" FROM "PromotionCode" P WHERE P."Date" = NOW()::DATE AND P."IsUsed" = FALSE  LIMIT v_step) SELECT array_agg(T."PromotionCodeId") FROM tmp T )::TEXT;
		END IF;
		

		
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP

			INSERT INTO "SalePointLog"(
				"SalePointId",
				"LotteryDate",
				"LotteryChannelId",
				"Quantity",
				"LotteryTypeId",
				"LotteryPriceId",
				"TotalValue",
				"ActionBy",
				"ActionByName",
				"ShiftDistributeId",
				"GuestId",
				"HistoryOfOrderId",
				"FourLastNumber"
			) VALUES(
				v_sale_point_id,
				(CASE WHEN (ele ->> 'LotteryTypeId')::INT = 3 THEN NULL::DATE ELSE (ele ->> 'LotteryDate')::DATE END),
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'Quantity')::INT,
				(ele ->> 'LotteryTypeId')::INT,
				null,
				(ele ->> 'Quantity')::INT * (ele ->> 'Price')::INT,
				p_action_by,
				p_action_by_name,
				v_shift_dis_id,
				COALESCE(p_guest_id,NULL),
				p_order_id,
				(ele->>'FourLastNumber')::VARCHAR
			) RETURNING "SalePointLogId" INTO v_tmp;
			IF (ele ->> 'LotteryChannelId')::INT < 1000
			THEN
				SELECT * INTO v_mess FROM crm_update_reward_lottery(NOW()::DATE,v_tmp, v_data_promotion, v_data_promotion_id);
			END IF;

			
			v_array := array_append(v_array, v_tmp);
			
			IF (ele ->> 'LotteryTypeId')::INT = 1 THEN 
			
				UPDATE "Inventory"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSEIF (ele ->> 'LotteryTypeId')::INT = 2 THEN
			
				UPDATE "Inventory"
				SET
					"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalDupRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSE
			
				UPDATE "Scratchcard"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
					
			
			END IF;
			
	
		END LOOP;
		
		UPDATE "HistoryOfOrder" 
		SET 
			"SalePointLogIds" = v_array
		WHERE "HistoryOfOrderId" = p_order_id;
		v_id := v_sale_point_id;
		v_mess := 'Lưu thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess, p_order_id, v_data_promotion;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sell_lottery_v5(p_shift_dis_id integer, p_user_role_id integer, p_action_by integer, p_action_by_name character varying, p_data text, p_guest_id integer DEFAULT NULL::integer, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer, "PromotionCode" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	v_sale_point_id INT;
	v_user_id INT;
	ele JSON;
	v_shift_dis_id INT;
	v_is_super_admin BOOL;
	v_is_manager BOOL;
	v_is_staff BOOL;
	v_check INT := 0;
	v_time TIMESTAMP DEFAULT NOW();
	v_array INT[] := '{}'::INT[];
	v_tmp INT;
	v_step INT;
	v_quantity INT;
	v_promotioncode TEXT;
	v_data_promotion TEXT;
	v_data_promotion_id TEXT;
BEGIN	
	SELECT 
		f."IsSuperAdmin",
		f."IsManager",
		f."IsStaff",
		f."SalePointId",
		f."ShiftDistributeId"
	INTO v_is_super_admin, v_is_manager, v_is_staff, v_sale_point_id, v_shift_dis_id
	FROM fn_get_shift_info(p_user_role_id) f;
	
		--Tạo hoá đơn khi chưa có
	IF p_order_id IS NULL THEN
		
		INSERT INTO "HistoryOfOrder" (
			"SalePointId",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"IsDeleted",
			"ShiftDistributeId"
		)
		VALUES (
			v_sale_point_id,
			p_action_by,
			p_action_by_name,
			v_time,
			FALSE,
			v_shift_dis_id
		) RETURNING "HistoryOfOrderId" INTO p_order_id;
		
	END IF;
	
	--Lấy danh sách id bán hàng
	SELECT "SalePointLogIds" INTO v_array FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = p_order_id;
	IF v_array IS NULL THEN 
		v_array := '{}'::INT[];
	END IF;
	
	
	
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
			IF COALESCE(p_guest_id,NULL) IS NOT NULL
			THEN
				v_quantity:=0;
			ELSE
				v_quantity= COALESCE(v_quantity,0) + (ele ->> 'Quantity')::INT;
			END IF;
		END LOOP;
		
		v_step := (SELECT (v_quantity::INT / P."Step") FROM "Promotion" P LIMIT 1);
		IF(v_step > 0)
		THEN
			v_data_promotion := (with tmp AS(SELECT P."PromotionCodeId",P."PromotionCode" FROM "PromotionCode" P WHERE P."Date" = NOW()::DATE AND P."IsUsed" IS FALSE LIMIT v_step) SELECT array_agg(T."PromotionCode") FROM tmp T )::TEXT;
			v_data_promotion_id := (with tmp AS(SELECT P."PromotionCodeId",P."PromotionCode" FROM "PromotionCode" P WHERE P."Date" = NOW()::DATE AND P."IsUsed" = FALSE  LIMIT v_step) SELECT array_agg(T."PromotionCodeId") FROM tmp T )::TEXT;
		END IF;
		
		raise notice 'v_data_promotion, %', v_data_promotion;
		
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
			INSERT INTO "SalePointLog"(
				"SalePointId",
				"LotteryDate",
				"LotteryChannelId",
				"Quantity",
				"LotteryTypeId",
				"LotteryPriceId",
				"TotalValue",
				"ActionBy",
				"ActionByName",
				"ShiftDistributeId",
				"GuestId",
				"HistoryOfOrderId",
				"FourLastNumber"
			) VALUES(
				v_sale_point_id,
				(CASE WHEN (ele ->> 'LotteryTypeId')::INT = 3 THEN NULL::DATE ELSE (ele ->> 'LotteryDate')::DATE END),
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'Quantity')::INT,
				(ele ->> 'LotteryTypeId')::INT,
				(ele ->> 'LotteryPriceId')::INT,
				(SELECT (CASE WHEN (ele ->> 'Quantity')::INT >= 110 AND (ele ->> 'LotteryPriceId')::INT = 6 THEN  CEIL((ele ->> 'Quantity')::INT * LP."Price") + ((ele ->> 'Quantity')::INT /100) ELSE CEIL((ele ->> 'Quantity')::INT * LP."Price") END) FROM "LotteryPrice" LP WHERE LP."LotteryPriceId" = (ele ->> 'LotteryPriceId')::INT),
				p_action_by,
				p_action_by_name,
				v_shift_dis_id,
				COALESCE(p_guest_id,NULL),
				p_order_id,
				(ele->>'FourLastNumber')::VARCHAR
			) RETURNING "SalePointLogId" INTO v_tmp;

			SELECT * INTO v_mess FROM crm_update_reward_lottery(NOW()::DATE,v_tmp, v_data_promotion, v_data_promotion_id);

			
			v_array := array_append(v_array, v_tmp);
			
			IF (ele ->> 'LotteryTypeId')::INT = 1 THEN 
			
				UPDATE "Inventory"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSEIF (ele ->> 'LotteryTypeId')::INT = 2 THEN
			
				UPDATE "Inventory"
				SET
					"TotalDupRemaining" = "TotalDupRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryDate" = (ele ->> 'LotteryDate')::DATE
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalDupRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
			
			ELSE
			
				UPDATE "Scratchcard"
				SET
					"TotalRemaining" = "TotalRemaining" - (ele ->> 'Quantity')::INT
				WHERE "SalePointId" = v_sale_point_id
					AND "LotteryChannelId" = (ele ->> 'LotteryChannelId')::INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Số lượng vé đã thay đổi không đủ vé để bán';
				END IF;
					
			
			END IF;
			
	
		END LOOP;
		
		UPDATE "HistoryOfOrder" 
		SET 
			"SalePointLogIds" = v_array
		WHERE "HistoryOfOrderId" = p_order_id;
		v_id := v_sale_point_id;
		v_mess := 'Lưu thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess, p_order_id, v_data_promotion;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_shift_transfer(p_user_role_id integer, p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	v_sale_point_id INT;
	v_user_id INT;
	ele JSON;
	v_shift_dis_id INT;
	v_shift_id INT;
BEGIN
	
	SELECT 
		UR."UserId" INTO v_user_id
	FROM "UserRole" UR WHERE UR."UserRoleId" = p_user_role_id;
	
	SELECT
		SD."SalePointId", SD."ShiftDistributeId", SD."ShiftId" INTO v_sale_point_id, v_shift_dis_id, v_shift_id
	FROM "ShiftDistribute" SD 
	WHERE SD."UserId" = v_user_id 
		AND SD."DistributeDate" = NOW()::DATE;
		
	IF COALESCE(v_sale_point_id, 0) > 0 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
			INSERT INTO "ShiftTransfer"(
				"ShiftId",
				"UserId",
				"TotalReceived",
				"TotalRemaining",
				"LotteryChannelId",
				"LotteryDate",
				"ActionBy",
				"ActionByName",
				"ActionDate",
				"TotalDupReceived",
				"TotalDupRemaining",
				"SalePointid"
			) VALUES (
				v_shift_id,
				v_user_id,
				(ele ->> 'TotalReceived')::INT,
				(ele ->> 'TotalRemaining')::INT,
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'LotteryDate')::DATE,
				p_action_by,
				p_action_by_name,
				NOW(),
				(ele ->> 'TotalDupReceived')::INT,
				(ele ->> 'TotalDupRemaining')::INT,
				v_sale_point_id
			) RETURNING "ShiftTransferId" INTO v_id;	
			
		END LOOP;

		v_mess := 'Ghi nhận chuyển ca thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;

	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_shift_transfer_v2(p_user_role_id integer, p_shift_distribute integer, p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
