				"LotteryTypeId",
				"ActionBy",
				"ActionByName",
				"ActionDate",
				"LotteryDate",
				"TotalStocks",
				"TotalReceived",
				"TotalTrans",
				"TotalReturns",
				"TotalRemaining",
				"TotalSold",
				"TotalSoldMoney",
				"TotalRetail",
				"TotalRetailMoney",
				"TotalWholesale",
				"TotalWholesaleMoney",
				"SalePointid"
			) VALUES (
				v_shift_id,
				v_user_id,
				v_shift_dis_id,
				(ele ->> 'LotteryChannelId')::INT,
				(ele ->> 'LotteryTypeId')::INT,
				p_action_by,
				p_action_by_name,
				NOW(),
				(ele ->> 'LotteryDate')::DATE,
				(ele ->> 'TotalStocks')::INT,
				(ele ->> 'TotalReceived')::INT,
				(ele ->> 'TotalTrans')::INT,
				(ele ->> 'TotalReturns')::INT,
				(ele ->> 'TotalRemaining')::INT,
				(ele ->> 'TotalSold')::INT,
				(ele ->> 'TotalSoldMoney')::NUMERIC,
				(ele ->> 'TotalRetail')::INT,
				(ele ->> 'TotalRetailMoney')::NUMERIC,
				(ele ->> 'TotalWholesale')::INT,
				(ele ->> 'TotalWholesaleMoney')::NUMERIC,
				v_sale_point_id
			) RETURNING "ShiftTransferId" INTO v_id;	
			
		END LOOP;

		v_mess := 'Ghi nhận lưu thành công';
	
	ELSE 
 
		v_id := 0;
		v_mess := 'Nhân viên không trong ca làm việc';
 
	END IF;

	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


