CREATE OR REPLACE FUNCTION public.crm_activity_sold_log(p_shift_distribute_id integer)
 RETURNS TABLE("RowNumber" bigint, "ActionDate" timestamp without time zone, "TotalQuantity" bigint, "TotalPrice" numeric, "DetailData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	
BEGIN

	RETURN QUERY
	WITH tmp AS(
		SELECT
			SL."ActionDate",
			SUM(SL."Quantity") AS "TotalQuantity",
			SUM(SL."TotalValue") AS "TotalPrice"
		FROM "SalePointLog" SL 
		WHERE SL."ShiftDistributeId" = p_shift_distribute_id
			AND SL."IsDeleted" IS FALSE
		GROUP BY
			SL."ActionDate"
	)
	SELECT 
		ROW_NUMBER() OVER(ORDER BY t."ActionDate" DESC) AS "RowNumber",
		t."ActionDate",
		t."TotalQuantity",
		t."TotalPrice",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					SL."SalePointLogId",
					SL."LotteryDate",
					SL."LotteryChannelId",
					LC."LotteryChannelName",
					SL."Quantity",
					SL."TotalValue",
					SL."LotteryTypeId",
					LT."LotteryTypeName"
				FROM "SalePointLog" SL
					JOIN "LotteryType" LT ON LT."LotteryTypeId" = SL."LotteryTypeId"
					LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SL."LotteryChannelId"
				WHERE SL."ActionDate" = t."ActionDate"
					AND SL."IsDeleted" IS FALSE
				ORDER BY SL."SalePointLogId"
			) r
		)::TEXT AS "DetailData"
	FROM tmp t;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_sold_log_v1(p_shift_distribute_id integer, p_page_number integer, p_page_size integer)
 RETURNS TABLE("RowNumber" bigint, "ActionDate" timestamp without time zone, "TotalQuantity" bigint, "TotalPrice" numeric, "DetailData" text, "TotalValues" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
	v_total_count INT8;
BEGIN

	RETURN QUERY
	WITH tmp AS(
		SELECT
			SL."ActionDate",
			SUM(SL."Quantity") AS "TotalQuantity",
			SUM(SL."TotalValue") AS "TotalPrice"
		FROM "SalePointLog" SL 
		WHERE SL."ShiftDistributeId" = p_shift_distribute_id
			AND SL."IsDeleted" IS FALSE
		GROUP BY
			SL."ActionDate"
	)
	SELECT 
		ROW_NUMBER() OVER(ORDER BY t."ActionDate" DESC) AS "RowNumber",
		t."ActionDate",
		t."TotalQuantity",
		t."TotalPrice",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					SL."SalePointLogId",
					SL."LotteryDate",
					SL."LotteryChannelId",
					LC."LotteryChannelName",
					SL."Quantity",
					SL."TotalValue",
					SL."LotteryTypeId",
					LT."LotteryTypeName"
				FROM "SalePointLog" SL
					JOIN "LotteryType" LT ON LT."LotteryTypeId" = SL."LotteryTypeId"
					LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SL."LotteryChannelId"
				WHERE SL."ActionDate" = t."ActionDate"
					AND SL."IsDeleted" IS FALSE
				ORDER BY SL."SalePointLogId"
			) r
		)::TEXT AS "DetailData",
		(
			SELECT COUNT(1) FROM tmp t1 GROUP BY t1."TotalQuantity"
		)::INT AS "Totalvalues"
	FROM tmp t
	OFFSET v_offset_row
	LIMIT p_page_size;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_trans_log(p_shift_distribute_id integer)
 RETURNS TABLE("RowNumber" bigint, "TransitionDate" timestamp without time zone, "TransitionTypeId" integer, "TransitionTypeName" character varying, "ManagerId" integer, "ManagerName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying, "ConfirmDate" timestamp without time zone, "FromSalePointId" integer, "FromSalePointName" character varying, "ToSalePointId" integer, "ToSalePointName" character varying, "DetailData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	
BEGIN

	RETURN QUERY
	SELECT 
		ROW_NUMBER() OVER(ORDER BY T."TransitionDate" DESC) AS "RowNumber",
		T."TransitionDate",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."ManagerId",
		U."FullName" AS "ManagerName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName",
		T."ConfirmDate",
		T."FromSalePointId",
		(CASE WHEN T."FromSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "FromSalePointName",
		T."ToSalePointId",
		(CASE WHEN T."ToSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "ToSalePointName",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					R."LotteryChannelId",
					LC."LotteryChannelName",
					R."TotalTrans",
					R."TotalTransDup",
					R."IsScratchcard"
				FROM "Transition" R
					JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = R."LotteryChannelId"
				WHERE R."TransitionDate" = T."TransitionDate"
				ORDER BY LC."LotteryChannelTypeId"
			) r
		)::TEXT AS "DetailData"
	FROM "Transition" T
		JOIN "TransitionType" TT ON TT."TransitionTypeId" = T."TransitionTypeId"
		JOIN "ConfirmStatus" CS ON CS."ConfirmStatusId" = T."ConfirmStatusId"
		JOIN "SalePoint" SP ON (SP."SalePointId" = T."ToSalePointId" OR SP."SalePointId" = T."FromSalePointId")
		JOIN "User" U ON U."UserId" = T."ManagerId"
	WHERE T."ShiftDistributeId" = p_shift_distribute_id
	GROUP BY
		T."TransitionDate",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."ManagerId",
		U."FullName",
		T."ConfirmStatusId",
		T."ConfirmDate",
		CS."ConfirmStatusName",
		T."FromSalePointId",
		T."ToSalePointId",
		SP."SalePointName";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_update_const_price(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
BEGIN

	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		
		IF NOT EXISTS(
			SELECT 1 FROM "Constant" C
			WHERE C."ConstId" = (ele->>'ConstId')::INT
				AND C."Price" = (ele->>'Price')::NUMERIC
			ORDER BY C."CreatedDate" DESC
			LIMIT 1
		) THEN
			
			INSERT INTO "Constant"(
				"ConstantName",
				"Price",
				"ConstId",
				"CreatedBy",
				"CreatedByName",
				"CreatedDate"
			)
			VALUES(
				(ele->>'ConstantName')::VARCHAR,
				(ele->>'Price')::NUMERIC,
				(ele->>'ConstId')::INT,
				p_action_by,
				p_action_by_name,
				v_time
			);
		END IF;
	
		v_id := 1;
		v_mess := 'Cập nhật thành công';
	END LOOP;

	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_update_debt(p_user_id bigint, p_payed_debt numeric, p_month character varying, p_salepoint_id bigint DEFAULT 0, p_total_debt numeric DEFAULT 0, p_flag boolean DEFAULT false)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT;
    v_mess TEXT;
    ele JSON;
    v_time TIMESTAMP := NOW();
		v_check DECIMAL := (SELECT SUM(D."TotalDebt") + COALESCE(p_total_debt,0) FROM "Debt" D WHERE D."UserId" = p_user_id AND D."IsAdded" IS TRUE) ;
		-- - (SELECT SUM(D."PayedDebt") FROM "Debt" D WHERE D."UserId" = p_user_id ) - COALESCE(p_payed_debt,0);
BEGIN
			IF( v_check < 0 ) THEN 
				v_mess := 'Trả quá số nợ cần trả, vui lòng nhập lại';
				v_id := 0;
			ELSE
				RAISE NOTICE 'viet%',v_check;
				INSERT INTO "Debt" ("UserId","TotalDebt","PayedDebt","Month","SalePointId","SalePointName","IsAdded","ActionDate","DebtDone")
				VALUES (p_user_id,COALESCE(p_total_debt,(SELECT SUM(D."TotalDebt") - p_payed_debt FROM "Debt" D WHERE D."UserId" = p_user_id AND D."IsAdded" = TRUE)),p_payed_debt + p_total_debt,p_month,p_salepoint_id,(SELECT S."SalePointName" FROM "SalePoint" S WHERE S."SalePointId" = p_salepoint_id),p_flag,NOW(), p_payed_debt );
				v_mess := 'Thêm nợ thành công';
				v_id := 1;
			END IF;
    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_update_isdeleted_salepoint_log(p_action_by integer, p_action_by_name character varying, p_count integer, p_salepoint_id integer, p_number character varying, p_lottery_channel_id integer, p_day timestamp without time zone)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
v_lenght_number INT := LENGTH(p_number);
BEGIN   
 		v_id := -1;
    v_mess := 'Update khong thanh cong';

	IF p_count < (SELECT 
									COUNT(*)
								FROM "SalePointLog" S
								WHERE (S."LotteryDate" BETWEEN (p_day::DATE - INTERVAL'5 day') AND p_day::DATE )
										AND right(S."FourLastNumber", p_count_number) = right(p_number, p_count_number)
										AND right(S."FourLastNumber", p_count_number+1) != right(p_number, p_count_number+1)
										AND S."IsDeleted" = FALSE
										AND S."SalePointId" = p_salepoint_id
										AND S."LotteryChannelId" = p_lottery_channel_id
					LIMIT 1)
	THEN
		FOR ele IN  1..(p_count) LOOP
			WITH tmp AS
			(		SELECT 
						S."SalePointLogId"
					FROM "SalePointLog" S
					WHERE (S."LotteryDate" BETWEEN (p_day::DATE - INTERVAL'5 day') AND p_day::DATE )
							AND right(S."FourLastNumber", v_lenght_number) = p_number
							AND right(S."FourLastNumber", v_lenght_number+1) != p_number
							AND S."IsDeleted" = FALSE
							AND S."SalePointId" = p_salepoint_id
							AND S."LotteryChannelId" = p_lottery_channel_id
					LIMIT 1
			)
		
			UPDATE "SalePointLog"
				SET 
					"IsDeleted" = TRUE,
					"ActionBy" = p_action_by,
					"ActionByName" = p_action_by_name
			 WHERE "SalePointLogId" = (SELECT * FROM tmp);
		END LOOP;

    v_id := 1;
    v_mess := 'Update thanh cong';
	ELSE
		v_id := -1;
    v_mess := 'Update khong thanh cong';
	END IF;
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_activity_update_lottery_price_agency(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
BEGIN
	--INSERT UPDATE
	IF p_action_type = 1 THEN
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
					INSERT INTO "LotteryPriceAgency"(
						"AgencyId",
						"LotteryChannelId",
						"Price",
						"CreatedBy",
						"CreatedByName",
						"CreatedDate",
						"IsDeleted" 
					)
					VALUES(
						(ele->>'AgencyId')::INT,
						(ele->>'LotteryChannelId')::INT,
						(ele->>'Price')::NUMERIC,
						p_action_by,
						p_action_by_name,
						v_time,
						FALSE
					);
					
		END LOOP;
		
		v_id := 1;
		v_mess := 'INSERT thành công';
		
	--DELETE
	ELSEIF p_action_type = 3 THEN
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "LotteryPriceAgency" 
			SET "IsDeleted" = TRUE
			WHERE "LotteryPriceAgencyId" = (ele->>'LotteryPriceAgencyId')::INT;
		END LOOP;
		v_id := 1;
		v_mess := 'Xoá thành công';
	ELSE
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "LotteryPriceAgency" 
				SET "Price" = (ele->>'Price')::NUMERIC
			WHERE "LotteryPriceAgencyId" = (ele->>'LotteryPriceAgencyId')::INT;
		END LOOP;
		v_id := 1;
		v_mess := 'Cập nhật thành công';
	END IF;

	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


