CREATE OR REPLACE FUNCTION public.crm_auth_change_password(p_user_id integer, p_current_password character varying, p_new_password character varying)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_log_content VARCHAR;
BEGIN

	SELECT 
		U."UserId"
	INTO
		v_id
	FROM "User" U
	WHERE U."UserId" = p_user_id AND U."Password" = p_current_password
		AND U."IsActive" IS TRUE AND U."IsDeleted" IS FALSE;
	
	IF COALESCE(v_id, 0) <> 0 THEN
		
		UPDATE "User"
		SET
			"Password" = p_new_password
		WHERE "UserId" = v_id;
		
		v_log_content := 'Mật khẩu cũ: '||p_current_password||', Mật khẩu mới: '||p_new_password;
		INSERT INTO "AuthLog"(
			"UserId",
			"ActionType",
			"LogContent"
		) VALUES(
			v_id,
			'Đổi mật khẩu',
			v_log_content
		);
		
		v_mess := 'Đổi mật khẩu thành công';
		
	ELSE
		
		v_mess := 'Mật khẩu cũ không đúng';
	
	END IF;
	
	RETURN QUERY 
	SELECT COALESCE(v_id, 0), v_mess;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_auth_checklogin(p_account character varying, p_password character varying, p_mac_address character varying, p_ip_address character varying)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	--
	v_user_role_id INT;
	v_sale_point_id INT;
	v_shift_dis_id INT;
BEGIN
	
	SELECT 
		U."UserId"
	INTO
		v_id
	FROM "User" U
	WHERE (LOWER(U."Email") = LOWER(p_account) OR LOWER(U."Account") = LOWER(p_account)) AND U."Password" = p_password
		AND U."IsActive" IS TRUE AND U."IsDeleted" IS FALSE;
	
	IF COALESCE(v_id, 0) <> 0 THEN
		
		SELECT UR."UserRoleId" INTO v_user_role_id
		FROM "UserRole" UR
		WHERE UR."UserId" = v_id;
		
		SELECT 
			"SalePointId",
			"ShiftDistributeId"
		INTO 
			v_sale_point_id,
			v_shift_dis_id
		FROM fn_get_shift_info(v_user_role_id);
		
		INSERT INTO "AuthLog"(
			"UserId",
			"ActionType",
			"LogContent",
			"SalePointId",
			"ShiftDistributeId"
		) VALUES(
			v_id,
			'Đăng nhập',
			'Đăng nhập thành công',
			v_sale_point_id,
			v_shift_dis_id
		);
		
		v_mess := 'Đăng nhập thành công';
		
	ELSE
		
		v_mess := 'Sai tên đăng nhập hoặc mậu khẩu';
	
	END IF;
	
	RETURN QUERY 
	SELECT COALESCE(v_id, -1), v_mess;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_auth_forgot_password(p_email character varying, p_new_password character varying)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_old_password VARCHAR;
	v_log_content VARCHAR;
BEGIN
	
	SELECT U."UserId", U."Password" 
	INTO v_id, v_old_password 
	FROM "User" U 
	WHERE U."IsActive" IS TRUE AND U."IsDeleted" IS FALSE
		AND LOWER("Email") = LOWER(p_email);
	
	IF COALESCE(v_id, 0) <> 0 THEN
		
		UPDATE "User"
		SET
			"Password" = p_new_password
		WHERE "UserId" = v_id;
		
		v_log_content := 'Mật khẩu cũ: '||v_old_password||', Mật khẩu cấp lại: '||p_new_password;
		INSERT INTO "AuthLog"(
			"UserId",
			"ActionType",
			"LogContent"
		) VALUES(
			v_id,
			'Cấp lại mật khẩu',
			v_log_content
		);
		
		v_mess := 'Cấp lại mật khẩu mới thành công';
		
	ELSE
		
		v_mess := 'Email không tồn tại trong hệ thống';
	
	END IF;
	
	RETURN QUERY 
	SELECT COALESCE(v_id, 0), v_mess;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_auth_get_permission(p_user_role_id integer)
 RETURNS TABLE("RoleDisplayName" character varying, "RoleName" character varying, "ActionName" character varying, "IsShowMenu" boolean, "PermissionName" character varying, "ControllerName" character varying, "CssIcon" text, "IsSubMenu" boolean, "Sort" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_title_id INT := (SELECT "UserTitleId" FROM "UserRole" WHERE "UserRoleId" = p_user_role_id);
	v_array_title INT[] := (SELECT "SubUserTitleId" FROM "UserRole" WHERE "UserRoleId" = p_user_role_id);
BEGIN 
	v_array_title := array_append(v_array_title, v_user_title_id);
	RETURN QUERY
  SELECT DISTINCT
		PR."RoleDisplayName",
		PR."RoleName",
		PR."ActionName",
		PR."IsShowMenu",
		P."PermissionName",
		P."ControllerName",
		P."CssIcon",
		PR."IsSubMenu",
		PR."Sort"
	FROM "PermissionRoleTitles" PRT
		JOIN "PermissionRole" PR ON PR."PermissionRoleId" = PRT."PermissionRoleId"
		JOIN "Permission" P ON P."PermissionId" = PR."PermissionId"
	WHERE PRT."UserTitleId" = ANY(v_array_title)
		AND PR."IsDelete" IS FALSE
		AND PR."IsActive" IS TRUE
	ORDER BY PR."Sort";
	
END; $function$


CREATE OR REPLACE FUNCTION public.crm_base_get_type_name_ddl(p_transaction_type_id integer DEFAULT 0)
 RETURNS TABLE("TypeNameId" integer, "Name" character varying, "TransactionTypeId" integer, "TransactionTypeName" character varying, "Price" numeric, "RequireSalePoint" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS(
		SELECT 
			C."Price",
			C."ConstId"
		FROM "Constant" C 
		WHERE C."ConstId" IN (7,8,9)
			AND C."CreatedDate" >= ALL (
				SELECT 
					A."CreatedDate"
				FROM "Constant" A
				WHERE A."ConstId" = C."ConstId"
			)
	)
	SELECT
		TN."TypeNameId",
		TN."Name",
		TN."TransactionTypeId",
		TT."TransactionTypeName",
		T."Price",
		TN."RequireSalePoint"
	FROM "TypeName" TN 
		JOIN "TransactionType" TT ON TN."TransactionTypeId" = TT."TransactionTypeId"
		LEFT JOIN tmp T ON T."ConstId" = TN."ConstId"
	WHERE TN."IsActive" IS TRUE
		AND (TN."TransactionTypeId" = p_transaction_type_id OR COALESCE(p_transaction_type_id, 0) = 0)
	ORDER BY TN."TransactionTypeId", TN."TypeNameId";
END;
$function$


