CREATE OR REPLACE FUNCTION public.crm_delete_shift_transfer(p_date date, p_shift_id integer, p_sale_point integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_shift_dis INT;
	v_shift_dis_after INT;
BEGIN
	IF p_shift_id = 1 THEN 
		SELECT 
			"ShiftDistributeId" INTO v_shift_dis
		FROM "ShiftDistribute"
		WHERE "SalePointId" = p_sale_point
			AND "ShiftId" = p_shift_id
			AND "DistributeDate" :: DATE = p_date::DATE;
		
		SELECT 
			"ShiftDistributeId" INTO v_shift_dis_after
		FROM "ShiftDistribute"
		WHERE "SalePointId" = p_sale_point
			AND "ShiftId" = 2
			AND "DistributeDate" :: DATE = (p_date::DATE);
			
		IF NOT EXISTS(SELECT 1 FROM "ShiftTransfer" WHERE "ShiftDistributeId" = v_shift_dis_after) 
			AND NOT EXISTS(SELECT 1 FROM "ShiftTransfer" WHERE "ActionDate"::DATE >= ((p_date::DATE) + INTERVAL '1 day')::DATE)
		THEN 
			IF EXISTS(SELECT 1 FROM "ShiftTransfer" WHERE "ShiftDistributeId" = v_shift_dis) THEN
				DELETE FROM "ShiftTransfer" WHERE "SalePointid" = p_sale_point AND "ShiftDistributeId" = v_shift_dis;
				v_id := 0;
				v_mess := 'Xoá thành công';
			ELSE
				v_id := 0;
				v_mess := 'Không có ca làm này trong kết ca';
			END IF;
		ELSE
			v_id := 0;
			v_mess := 'Ca làm sau đã kết thúc nên không thể xoá lịch sử kết ca';
		END IF;
	ELSE 
		SELECT 
			"ShiftDistributeId" INTO v_shift_dis
		FROM "ShiftDistribute"
		WHERE "SalePointId" = p_sale_point
			AND "ShiftId" = p_shift_id
			AND "DistributeDate" :: DATE = p_date::DATE;
		
		IF NOT EXISTS(SELECT 1 FROM "ShiftTransfer" WHERE "ActionDate"::DATE >= ((p_date::DATE) + INTERVAL '1 day')::DATE) THEN
			IF EXISTS(SELECT 1 FROM "ShiftTransfer" WHERE "ShiftDistributeId" = v_shift_dis) THEN
				DELETE FROM "ShiftTransfer" WHERE "SalePointid" = p_sale_point AND "ShiftDistributeId" = v_shift_dis;
				v_id := 0;
				v_mess := 'Xoá thành công';
			ELSE
				v_id := 0;
				v_mess := 'Không có ca làm này trong kết ca';
			END IF;
		ELSE
			v_id := 0;
			v_mess := 'Ca làm sau đã kết thúc nên không thể xoá lịch sử kết ca';
		END IF;
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_all_shift_of_one_employee(p_user_role integer, p_month character varying)
 RETURNS TABLE("UserId" integer, "DistributeDate" date, "SalePointId" integer, "SalePointName" character varying, "ShiftId" integer, "ShiftName" character varying, "ShiftTypeId" integer, "ShiftTypeName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_user_id INT;
BEGIN
	SELECT UR."UserId" INTO v_user_id FROM "UserRole" UR WHERE UR."UserRoleId" = p_user_role;
	RETURN QUERY
		SELECT 
			SD."UserId",
			SD."DistributeDate",
			SD."SalePointId",
			SP."SalePointName",
			SD."ShiftId",
			S."ShiftName",
			SD."ShiftTypeId",
			ST."ShiftTypeName"
		FROM "ShiftDistribute" SD
			JOIN "SalePoint" SP ON SD."SalePointId" = SP."SalePointId"
			JOIN "Shift" S ON SD."ShiftId" = S."ShiftId"
			JOIN "ShiftType" ST ON SD."ShiftTypeId" = ST."ShiftTypeId"
		WHERE SD."UserId" = v_user_id AND TO_CHAR(SD."DistributeDate", 'YYYY-MM')=p_month
		ORDER BY SD."DistributeDate", SP."SalePointId", S."ShiftId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_average_lottery_price_in_date(p_lottery_channel_id integer, p_date timestamp without time zone DEFAULT now())
 RETURNS numeric
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_result NUMERIC;
BEGIN
	
	--Vé thường
	IF NOT EXISTS (SELECT 1 FROM "LotteryChannel" LC WHERE LC."LotteryChannelId" = p_lottery_channel_id AND LC."IsScratchcard" IS TRUE) THEN
		
		WITH tmp AS (
			SELECT 
				ROW_NUMBER() OVER(PARTITION BY LP."AgencyId", LP."LotteryChannelId" ORDER BY LP."CreatedDate" DESC) AS "Id",
				LP."AgencyId",
				LP."Price"
			FROM "LotteryPriceAgency" LP
			WHERE LP."IsDeleted" IS FALSE
				AND (p_date::DATE IS NULL OR LP."CreatedDate" <= p_date::DATE)
				AND LP."LotteryChannelId" = p_lottery_channel_id
		),
		tmp2 AS (
			SELECT
				A."AgencyId",
				A."Price"
			FROM tmp A
			WHERE A."Id" = 1
		),
		tmp3 AS (
			SELECT
				I."AgencyId",
				SUM(I."TotalReceived") AS "TotalReceived"
			FROM "InventoryFull" I
			WHERE I."LotteryChannelId" = p_lottery_channel_id
				AND I."LotteryDate" = p_date::DATE
			GROUP BY 
				I."AgencyId"
		)
		SELECT
			COALESCE(SUM(T."TotalReceived" * A."Price"), 0) /(  CASE WHEN COALESCE(SUM(T."TotalReceived"), 0) = 0 THEN 1 END) INTO v_result
		FROM tmp3 T
			LEFT JOIN tmp2 A ON T."AgencyId" = A."AgencyId";
	--Vé cào
	ELSE
	
		v_result := (
			SELECT 
				C."Price"
			FROM "Constant" C 
			WHERE C."CreatedDate"::DATE <= p_date::DATE
				AND C."ConstId" = 12
				AND C."CreatedDate" >= ALL(
					SELECT 
						CC."CreatedDate"
					FROM "Constant" CC
					WHERE CC."ConstId" = C."ConstId"
						AND CC."CreatedDate"::DATE <= p_date::DATE
				)
		)::NUMERIC;
		
	END IF;
	
	RETURN v_result;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_average_lottery_price_in_date_of_month(p_month character varying)
 RETURNS TABLE("LotteryChannelId" integer, "Date" date, "Price" numeric)
 LANGUAGE plpgsql
AS $function$
DECLARE
		v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
		v_time TIMESTAMP := NOW();
BEGIN
	RETURN QUERY
	--Lấy danh sách đài theo ngày
	WITH tmp0 AS (
		SELECT 
			(p_month||'-'||T."Date")::DATE AS "Date",
			UNNEST((SELECT ARRAY_AGG(A."Id") FROM crm_lottery_channel_ddl(2, (p_month||'-'||T."Date")::TIMESTAMP) A)::INT[]) AS "LotteryChannelId"
		FROM (
			SELECT * FROM generate_series(1, v_total_date) AS "Date"
		) T
		UNION ALL
		SELECT 
			NULL::DATE AS "Date",
			LC."LotteryChannelId"
		FROM "LotteryChannel" LC
		WHERE LC."IsScratchcard" IS TRUE 
	)
	SELECT 
		T."LotteryChannelId",
		T."Date",
		CASE WHEN 
		(SELECT * FROM crm_get_average_lottery_price_in_date(T."LotteryChannelId", T."Date"::TIMESTAMP))::INT <> 0 THEN (SELECT * FROM crm_get_average_lottery_price_in_date(T."LotteryChannelId", T."Date"::TIMESTAMP))::NUMERIC
		ELSE 1 
		END
	FROM tmp0 T;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_current_store_of_sale_point_by_channel(p_sale_point_id integer, p_channel_id integer, p_lottery_type_id integer, p_lottery_date date)
 RETURNS integer
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	IF p_lottery_type_id = 1 THEN
		RETURN (SELECT I."TotalRemaining"
						FROM "Inventory" I
						WHERE I."LotteryDate" = p_lottery_date
							AND I."LotteryChannelId" = p_channel_id
							AND I."SalePointId" = p_sale_point_id);
	ELSEIF p_lottery_type_id = 2 THEN
		RETURN (SELECT I."TotalDupRemaining"
						FROM "Inventory" I
						WHERE I."LotteryDate" = p_lottery_date
							AND I."LotteryChannelId" = p_channel_id
							AND I."SalePointId" = p_sale_point_id);
	ELSE 
		RETURN (SELECT S."TotalRemaining"
						FROM "Scratchcard" S
						WHERE S."SalePointId" = p_sale_point_id);
	END IF;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_every_payed_debt_and_new_debt_all_time(p_user_id integer)
 RETURNS TABLE("UserId" integer, "PayedDebtData" text, "DebtData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	with tmp AS(
		SELECT
			D."UserId",
			D."TotalDebt",
			D."ActionDate"
		FROM "Debt" D
			LEFT JOIN "UserRole" UR ON UR."UserId" = D."UserId"
		WHERE D."IsAdded" IS TRUE AND D."UserId" = p_user_id
	), tmp1 AS(
		SELECT
			D."UserId",
			D."DebtDone"
		FROM "Debt" D
			LEFT JOIN "UserRole" UR ON UR."UserId" = D."UserId"
		WHERE D."UserId" = p_user_id
	),tmp2 AS(
		SELECT 
			T1."UserId",
			json_agg(json_build_object('PayedData',T1."DebtDone"))::TEXT AS "PayedData" 
		FROM tmp1 T1 
		GROUP BY T1."UserId"
	),tmp3 AS(
		SELECT 
			T."UserId",
			array_to_json(array_agg(json_build_object(
				'TotalDebt',
				T."TotalDebt",
				'Date',
				T."ActionDate")))::TEXT AS "DebtData" 
		FROM tmp T
		GROUP BY T."UserId"
	)
	SELECT T."UserId",T."PayedData",T1."DebtData" FROM tmp2 T LEFT JOIN tmp3 T1 ON T."UserId" = T1."UserId"
	;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_history_scratch_card_full_log(p_date date, p_page_size integer, p_page_number integer)
 RETURNS TABLE("ScratchcardFullLogId" integer, "AgencyName" character varying, "ActionByName" character varying, "ActionDate" character varying, "TotalReceive" integer, "LotteryChannelName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
BEGIN

	RETURN QUERY
	SELECT
		SFL."ScratchcardFullLogId",
		A."AgencyName",
		SFL."ActionByName",
		TO_CHAR(SFL."ActionDate",'YYYY-MM-DD HH:MM')::VARCHAR(255),
		SFL."TotalReceived",
		LC."LotteryChannelName"
	FROM "ScratchcardFullLog" SFL 
	LEFT JOIN "Agency" A ON SFL."AgencyId" = A."AgencyId"
	LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SFL."LotteryChannelId"
	WHERE SFL."ActionDate"::DATE = p_date
	ORDER BY SFL."ScratchcardFullLogId" 
	OFFSET v_offset_row LIMIT p_page_size;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_inventory_inday_of_all_salepoint(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "TotalReceived" bigint, "TotalRemaining" bigint, "TotalDupReceived" bigint, "TotalDupRemaining" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	WITH tmp AS(
		SELECT
			I."SalePointId",
			I."LotteryDate",
			SUM(I."TotalReceived") "TotalReceived", 
			SUM(I."TotalRemaining") "TotalRemaining", 
			SUM(I."TotalDupReceived") "TotalDupReceived", 
			SUM(I."TotalDupRemaining") "TotalDupRemaining" 
		FROM "Inventory" I
		WHERE TO_CHAR(I."LotteryDate",'YYYY-MM') = p_month
		GROUP BY I."SalePointId", I."LotteryDate"
	)
	SELECT 
		SP."SalePointId",
		SP."SalePointName",
		A."LotteryDate",
		A."TotalReceived",
		A."TotalRemaining",
		A."TotalDupReceived",
		A."TotalDupRemaining"			
	FROM "SalePoint" SP 
		JOIN tmp A ON SP."SalePointId" = A."SalePointId";
		 
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_inventory_inday_of_all_salepoint_v2(p_month character varying, p_sale_point_id integer)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "TotalReceived" bigint, "TotalRemaining" bigint, "TotalDupReceived" bigint, "TotalDupRemaining" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	WITH tmp AS(
		SELECT
			I."SalePointId",
			I."LotteryDate",
			SUM(I."TotalReceived") "TotalReceived", 
			SUM(I."TotalRemaining") "TotalRemaining", 
			SUM(I."TotalDupReceived") "TotalDupReceived", 
			SUM(I."TotalDupRemaining") "TotalDupRemaining" 
		FROM "Inventory" I
		WHERE TO_CHAR(I."LotteryDate",'YYYY-MM') = p_month
		AND I."LotteryDate" BETWEEN (date_trunc('MONTH',(p_month || '-01')::DATE)::DATE) AND NOW()::DATE
			AND (COALESCE(p_sale_point_id, 0) = 0 OR I."SalePointId" = p_sale_point_id)
		GROUP BY I."SalePointId", I."LotteryDate"
	)
	SELECT 
		SP."SalePointId",
		SP."SalePointName",
		A."LotteryDate",
		A."TotalReceived",
		A."TotalRemaining",
		A."TotalDupReceived",
		A."TotalDupRemaining"			
	FROM "SalePoint" SP 
		JOIN tmp A ON SP."SalePointId" = A."SalePointId";
		 
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_inventory_inmonth_of_all_salepoint(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "TotalReceived" bigint, "TotalRemaining" bigint, "TotalDupReceived" bigint, "TotalDupRemaining" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
			SP."SalePointId",
			SP."SalePointName",
			A."TotalReceived",
			A."TotalRemaining",
			A."TotalDupReceived",
			A."TotalDupRemaining"			
	FROM "SalePoint" SP LEFT JOIN (
		SELECT
			I."SalePointId",
			SUM(I."TotalReceived") "TotalReceived", 
			SUM(I."TotalRemaining") "TotalRemaining", 
			SUM(I."TotalDupReceived") "TotalDupReceived", 
			SUM(I."TotalDupRemaining") "TotalDupRemaining" 
		FROM "Inventory" I
		WHERE TO_CHAR(I."LotteryDate",'YYYY-MM') = p_month
		GROUP BY I."SalePointId") A ON SP."SalePointId" = A."SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_criteria_ddl(p_user_title_id integer DEFAULT 0)
 RETURNS TABLE("CriteriaId" integer, "CriteriaName" character varying, "Coef" numeric, "MaxValue" numeric, "UserTitleId" integer, "SalePointId" integer)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	SELECT
		C."CriteriaId",
		C."CriteriaName",
		C."Coef",
		C."MaxValue",
		C."UserTitleId",
		U."SalePointId"
	FROM "Criteria" C
	LEFT JOIN "UserRole" UR ON UR."UserTitleId" = C."UserTitleId"
	LEFT JOIN "User" U ON U."UserId" = UR."UserId"
	WHERE C."IsActive" IS TRUE
		AND (C."UserTitleId" = p_user_title_id OR p_user_title_id = 0)
	ORDER BY
		C."CriteriaId",
		U."SalePointId",
		C."UserTitleId" DESC;	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_guest_ddl(p_sale_point_id integer DEFAULT 0, p_guest_id integer DEFAULT 0)
 RETURNS TABLE("GuestId" integer, "FullName" character varying, "Phone" character varying, "SalePointId" integer, "WholesalePriceId" integer, "WholesalePrice" numeric, "ScratchPriceId" integer, "ScratchPrice" numeric, "Debt" numeric, "CanBuyWholesale" boolean)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	WITH tmp AS (
-- 		SELECT
-- 			GA."GuestId",
-- 			GA."SalePointId",
-- 			SUM((CASE WHEN GA."GuestActionTypeId" = 1 THEN GA."TotalPrice" 
-- 								WHEN GA."GuestActionTypeId" = 2 OR GA."GuestActionTypeId" = 3 THEN -GA."TotalPrice"
-- 								ELSE 0 END)) AS "Debt"
-- 		FROM "GuestAction" GA
-- 		GROUP BY
-- 			GA."GuestId",
-- 			GA."SalePointId"
		SELECT 
			SPL."GuestId",
			SPL."SalePointId",
			SUM(SPL."TotalValue") AS "Debt"
		FROM "SalePointLog" SPL
		WHERE (SPL."GuestId" IS NOT NULL AND SPL."GuestId" <> 0)
		GROUP BY 
			SPL."SalePointId",
			SPL."GuestId"
	),
	tmp2 AS (
		SELECT
			GA."GuestId",
			GA."SalePointId",
			COALESCE((SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2)), 0) - COALESCE((SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 3)), 0) AS "Debt"
		FROM "GuestAction" GA
		GROUP BY
			GA."GuestId",
			GA."SalePointId"
	)
	SELECT 
		G."GuestId",
		G."FullName",
		G."Phone",
		G."SalePointId",
		G."WholesalePriceId",
		LT."Price" AS "WholesalePrice",
		G."ScratchPriceId",
		LT2."Price" AS "ScratchPrice",
		COALESCE(T."Debt", 0) - COALESCE(TT."Debt", 0) AS "Debt",
		(CASE WHEN G."WholesalePriceId" IS NOT NULL AND G."ScratchPriceId" IS NOT NULL THEN TRUE ELSE FALSE END) AS "CanBuyWholesale"
	FROM "Guest" G
		LEFT JOIN "LotteryPrice" LT ON G."WholesalePriceId" = LT."LotteryPriceId"
		LEFT JOIN "LotteryPrice" LT2 ON G."ScratchPriceId" = LT2."LotteryPriceId"
		LEFT JOIN tmp T ON T."GuestId" = G."GuestId" AND T."SalePointId" = G."SalePointId"
		LEFT JOIN tmp2 TT ON TT."GuestId" = G."GuestId" AND TT."SalePointId" = G."SalePointId"
	WHERE (COALESCE(p_guest_id, 0) = 0 OR G."GuestId" = p_guest_id)
		AND (COALESCE(p_sale_point_id, 0) = 0 OR G."SalePointId" = p_sale_point_id)
	ORDER BY G."FullName";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_guest_ddl_v2(p_sale_point_id integer DEFAULT 0, p_guest_id integer DEFAULT 0)
 RETURNS TABLE("GuestId" integer, "FullName" character varying, "Phone" character varying, "SalePointId" integer, "WholesalePrice" integer, "ScratchPrice" integer, "Debt" numeric, "CanBuyWholesale" boolean)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	WITH tmp AS (
-- 		SELECT
-- 			GA."GuestId",
-- 			GA."SalePointId",
-- 			SUM((CASE WHEN GA."GuestActionTypeId" = 1 THEN GA."TotalPrice" 
-- 								WHEN GA."GuestActionTypeId" = 2 OR GA."GuestActionTypeId" = 3 THEN -GA."TotalPrice"
-- 								ELSE 0 END)) AS "Debt"
-- 		FROM "GuestAction" GA
-- 		GROUP BY
-- 			GA."GuestId",
-- 			GA."SalePointId"
		SELECT 
			SPL."GuestId",
			SPL."SalePointId",
			SUM(SPL."TotalValue") AS "Debt"
		FROM "SalePointLog" SPL
		WHERE (SPL."GuestId" IS NOT NULL AND SPL."GuestId" <> 0)
		GROUP BY 
			SPL."SalePointId",
			SPL."GuestId"
	),
	tmp2 AS (
		SELECT
			GA."GuestId",
			GA."SalePointId",
			COALESCE((SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2)), 0) - COALESCE((SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 3)), 0) AS "Debt"
		FROM "GuestAction" GA
		GROUP BY
			GA."GuestId",
			GA."SalePointId"
	)
	SELECT 
		G."GuestId",
		G."FullName",
		G."Phone",
		G."SalePointId",
-- 		G."WholesalePriceId",
		G."WholesalePrice" AS "WholesalePrice",
-- 		G."ScratchPriceId",
		G."ScratchPrice" AS "ScratchPrice",
		COALESCE(T."Debt", 0) - COALESCE(TT."Debt", 0) AS "Debt",
		(CASE WHEN G."WholesalePrice" IS NOT NULL AND G."ScratchPrice" IS NOT NULL THEN TRUE ELSE FALSE END) AS "CanBuyWholesale"
	FROM "Guest" G
-- 		LEFT JOIN "LotteryPrice" LT ON G."WholesalePriceId" = LT."LotteryPriceId"
-- 		LEFT JOIN "LotteryPrice" LT2 ON G."ScratchPriceId" = LT2."LotteryPriceId"
		LEFT JOIN tmp T ON T."GuestId" = G."GuestId" AND T."SalePointId" = G."SalePointId"
		LEFT JOIN tmp2 TT ON TT."GuestId" = G."GuestId" AND TT."SalePointId" = G."SalePointId"
	WHERE (COALESCE(p_guest_id, 0) = 0 OR G."GuestId" = p_guest_id)
		AND (COALESCE(p_sale_point_id, 0) = 0 OR G."SalePointId" = p_sale_point_id)
	ORDER BY G."FullName";
