$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_item_ddl(p_item_id integer DEFAULT 0)
 RETURNS TABLE("ItemId" integer, "ItemName" character varying, "UnitId" integer, "UnitName" character varying, "Price" integer, "Quotation" integer, "TypeOfItemId" integer, "TypeName" character varying)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	SELECT 
		I."ItemId",
		I."ItemName",
		I."UnitId",
		U."UnitName",
		I."Price",
		I."Quotation",
		I."TypeOfItemId",
		TI."TypeName"
	FROM "Item" I
		JOIN "Unit" U ON I."UnitId" = U."UnitId"
		JOIN "TypeOfItem" TI ON I."TypeOfItemId" = TI."TypeOfItemId"
	WHERE COALESCE(p_item_id, 0) = 0 OR I."ItemId" = p_item_id
	ORDER BY I."TypeOfItemId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_payment_for_confirm(p_page_size integer, p_page_number integer, p_salepoint_id integer, p_date timestamp without time zone)
 RETURNS TABLE("ArrayGuestActionId" text, "SalePointId" integer, "SalePointName" character varying, "GuestId" integer, "FullName" character varying, "CreatedDate" timestamp without time zone, "Note" character varying, "TotalPrice" numeric, "GuestActionTypeId" integer, "TypeName" character varying, "GuestInfo" text, "DoneTransfer" boolean)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT
			JSON_AGG(GA."GuestActionId")::TEXT AS "ArrayGuestActionId",
			GA."SalePointId",
			SP."SalePointName",
			GA."GuestId",
			G."FullName",
			MAX(GA."CreatedDate") AS "CreatedDate",
			GA."Note",
			SUM(GA."TotalPrice") AS "TotalPrice",
			GA."GuestActionTypeId",
			GA."GuestInfo",
			GA."DoneTransfer"
		FROM "GuestAction" GA
			LEFT JOIN "Guest" G ON GA."GuestId" = G."GuestId"
			JOIN "SalePoint" SP ON GA."SalePointId" = SP."SalePointId"
		WHERE GA."FormPaymentId" = 2
			AND (GA."SalePointId" = p_salepoint_id OR COALESCE(p_salepoint_id, 0) = 0)
			AND (COALESCE(p_date, NULL) IS NULL OR GA."CreatedDate"::DATE = p_date::DATE)
			AND GA."IsDeleted" IS FALSE
		GROUP BY 
			GA."Note",
			GA."SalePointId",
			SP."SalePointName",
			GA."GuestId",
			G."FullName",
			GA."DoneTransfer",
			GA."GuestActionTypeId",
			GA."GuestInfo"
	)	
	SELECT
		T."ArrayGuestActionId",
		T."SalePointId",
		T."SalePointName",
		T."GuestId",
		T."FullName",
		T."CreatedDate",
		T."Note",
		T."TotalPrice",
		T."GuestActionTypeId",
		GA."TypeName",
		T."GuestInfo",
		T."DoneTransfer"
	FROM tmp T
		JOIN "GuestActionType" GA ON T."GuestActionTypeId" = GA."GuestActionTypeId"
	ORDER BY
		T."DoneTransfer",
		T."CreatedDate" DESC
	OFFSET v_offset_row LIMIT p_page_size;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_percent_salepoint_in_month(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "MainUserId" integer[], "PercentMainUserId" numeric[])
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
		SPL."SalePointId",
		SPL."MainUserId",
		SPL."PercentMainUserId"
	FROM "SalePointPercentLog" SPL 
	WHERE SPL."ActionDate"::DATE <= (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day')::DATE
		AND SPL."ActionDate" >= ALL(
			SELECT
				T."ActionDate"
			FROM "SalePointPercentLog" T
			WHERE T."ActionDate"::DATE <= (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day')::DATE
				AND T."SalePointId" = SPL."SalePointId"
		);
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_get_list_sale_point(p_id integer DEFAULT 0)
 RETURNS TABLE("Id" integer, "Name" character varying, "IsActive" boolean, "ActionBy" integer, "ActionByName" character varying, "FullAddress" character varying, "Note" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY 
	SELECT
		S."SalePointId",
		S."SalePointName",
		S."IsActive",
		COALESCE(S."ModifyBy", S."ActionBy"),
		COALESCE(S."ModifyByName", S."ActionByName"),
