CREATE OR REPLACE FUNCTION public.crm_insert_update_confirm_for_manager_borrow(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_check INT;
	ele JSON := (p_data::JSON);
	elele JSON;
	v_data TEXT;
	v_user_id INT;
	v_time TIMESTAMP := NOW();
BEGIN
	--INSERT
	IF p_action_type = 1 THEN 
		INSERT INTO "ConfirmLog" (
			"Data",
			"ActionDate",
			"ActionBy",
			"ActionByName",
			"ConfirmStatusId",
			"ConfirmFor",
			"DataActionInfo"
		) VALUES (
			ele::TEXT,
			v_time,
			p_action_by,
			p_action_by_name,
			1,
			5,
			(ele->>'UserId')::TEXT
		) RETURNING "ConfirmLogId" INTO v_id;
				
		v_mess := 'Thêm thành công';
		
	--UPDATE
	ELSEIF p_action_type = 2 THEN
		
		SELECT 
			CL."ConfirmStatusId",  
			CL."Data",
			CL."DataActionInfo"::INT
		INTO
			v_check,
			v_data,
			v_user_id
		FROM "ConfirmLog" CL
		WHERE CL."ConfirmLogId" = (ele->>'ConfirmLogId')::INT;
		
		IF v_check = 1 THEN
			UPDATE "ConfirmLog"
			SET
				"ConfirmStatusId" = 2,
				"ConfirmDate" = v_time
			WHERE "ConfirmLogId" = (ele->>'ConfirmLogId')::INT;
 	
			FOR elele IN SELECT * FROM json_array_elements(((v_data::JSON)->>'DataForConfirm')::JSON) LOOP
				INSERT INTO "ManagerBorrow"(
					"UserId",
					"FormPaymentId",
					"Price",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"Note",
					"IsDeleted"
				)
				VALUES(
					v_user_id,
					(elele->>'FormPaymentId')::INT,
					(elele->>'Price')::NUMERIC,
					p_action_by,
					p_action_by_name,
					v_time,
					((v_data::JSON)->>'Note')::VARCHAR,
					FALSE
				);	
			END LOOP;
						
		ELSE 
			RAISE 'Yêu cầu đã được xác nhận trước đó';
		END IF;
		
		v_id := 1;
		v_mess := 'Xác nhận thành công';
		
	--DELETE
	ELSEIF p_action_type = 3 THEN
	
		SELECT 
			CL."ConfirmStatusId",  
			CL."Data",
			CL."DataActionInfo"::INT
		INTO
			v_check,
			v_data,
			v_user_id
		FROM "ConfirmLog" CL
		WHERE CL."ConfirmLogId" = (ele->>'ConfirmLogId')::INT;
		
		IF v_check = 1 THEN
			UPDATE "ConfirmLog"
			SET
				"ConfirmStatusId" = 3,
				"ConfirmDate" = v_time
			WHERE "ConfirmLogId" = (ele->>'ConfirmLogId')::INT;
		ELSE 
			RAISE 'Yêu cầu đã được xác nhận trước đó';
		END IF;
		
		v_id := 1;
		v_mess := 'Huỷ thành công';
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_intern_by_title()
 RETURNS TABLE("Id" integer, "Name" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	SELECT 
		U."UserId",
		U."FullName"
	FROM "User" U
		JOIN "UserRole" UR ON UR."UserId" = U."UserId"
		JOIN "UserTitle" UT ON UT."UserTitleId" = UR."UserTitleId"
	WHERE U."IsActive" IS TRUE
		AND UR."UserTitleId" = 5
		AND U."IsIntern" = TRUE;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_item_create_or_update(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	ele JSON := p_data::JSON;
BEGIN
		IF(COALESCE((ele ->> 'ItemId')::INT,0)=0 ) THEN
			INSERT INTO "Item"(
				"ItemName",
				"Price",
				"UnitId",
				"CreateBy",
				"CreateByName",
				"CreatedDate",
				"Quotation",
				"TypeOfItemId"
			)
			VALUES(
				(ele ->> 'ItemName'):: VARCHAR,
				(ele ->> 'Price'):: INT,
				(ele ->> 'UnitId'):: INT,
				p_action_by,
				p_action_by_name,
				NOW(),
				(ele ->> 'Quotation'):: INT,
				COALESCE((ele ->> 'TypeOfItemId'):: INT, 1)
			) RETURNING "ItemId" INTO v_id;
			v_mess := 'Thao tác tạo thành công';
		ELSE
			v_id:=(ele ->> 'ItemId')::INT ;
			UPDATE "Item"
			SET 
				"ItemName" = COALESCE((ele ->> 'ItemName'):: VARCHAR,"ItemName") ,
				"Price" =  COALESCE((ele ->> 'Price'):: INT,"Price"),
				"UnitId" =  COALESCE((ele ->> 'UnitId'):: INT,"UnitId"),
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate" = NOW(),
				"IsActive" = COALESCE((ele ->> 'IsActive'):: BOOLEAN,"IsActive"),
				"Quotation" = COALESCE((ele ->> 'Quotation'):: INT,"Quotation"),
				"TypeOfItemId" = COALESCE((ele ->> 'TypeOfItemId'):: INT,"TypeOfItemId") 
			WHERE "ItemId" = v_id;
			v_mess := 'Thao tác tạo thành công';
		END IF;
	 
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_item_get_list_confirm(p_month character varying, p_sale_point_id integer DEFAULT 0, p_confirm_for_type_id integer DEFAULT 1)
 RETURNS TABLE("ItemConfirmLogId" integer, "ConfirmForTypeName" character varying, "Data" text, "TypeActionId" integer, "ItemTypeName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying, "RequestBy" integer, "RequestByName" character varying, "RequestDate" timestamp without time zone, "ConfirmBy" integer, "ConfirmByName" character varying, "ConfirmDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	
BEGIN
	RETURN QUERY 
		SELECT 
			IL."ConfirmLogId" AS "ItemConfirmLogId",
			CFT."ConfirmForTypeName",
			(
				SELECT 
					array_to_json(array_agg(
						A.*::JSONB 
						||('{"ItemName": "'||(SELECT I."ItemName" FROM "Item" I WHERE I."ItemId" = (A->>'ItemId')::INT)::TEXT||'"}')::JSONB
						||('{"UnitId": '||(SELECT I."UnitId" FROM "Item" I WHERE I."ItemId" = (A->>'ItemId')::INT)::INT||'}')::JSONB
						||('{"UnitName": "'||(SELECT U."UnitName" FROM "Item" I JOIN "Unit" U ON I."UnitId" = U."UnitId" WHERE I."ItemId" = (A->>'ItemId')::INT)::TEXT||'"}')::JSONB
					))
				FROM json_array_elements(IL."Data"::JSON) A
			)::TEXT,
			IL."TypeActionId",
			(CASE 
				WHEN COALESCE(p_confirm_for_type_id,1) = 1 THEN  IT."ItemTypeName"
				WHEN p_confirm_for_type_id = 2 THEN  GT."TypeName"
			END) ,
			IL."ConfirmStatusId",
			CS."ConfirmStatusName",
			IL."ActionBy" AS "RequestBy",
			IL."ActionByName" AS "RequestByName",
			Il."ActionDate" AS "RequestDate",
			IL."ConfirmBy",
			IL."ConfirmByName",
			IL."ConfirmDate"
		FROM "ConfirmLog" IL 
			JOIN "ConfirmStatus" CS ON IL."ConfirmStatusId" = CS."ConfirmStatusId"
			JOIN "ItemType" IT ON IT."ItemTypeId" =IL."TypeActionId"
			JOIN "GuestActionType" GT ON GT."GuestActionTypeId" = IL."TypeActionId"
			JOIN "ConfirmForType" CFT ON CFT."ConfirmForTypeId" = IL."ConfirmFor"
		WHERE 
 			(COALESCE(p_month, NULL) IS NULL OR TO_CHAR(IL."ActionDate",'YYYY-MM') = p_month)
			AND IL."ConfirmFor" = COALESCE(p_confirm_for_type_id,1)
			AND (COALESCE(p_sale_point_id,0) = 0 OR p_sale_point_id = (((IL."Data" ::JSON)->> 0)::JSON ->> 'SalePointId')::INT)
		ORDER BY IL."ActionDate" DESC;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_item_get_list_confirm_v1(p_month character varying, p_sale_point_id integer DEFAULT 0, p_confirm_for_type_id integer DEFAULT 1)
 RETURNS TABLE("ItemConfirmLogId" integer, "ConfirmForTypeName" character varying, "Data" text, "TypeActionId" integer, "ItemTypeName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying, "RequestBy" integer, "RequestByName" character varying, "RequestDate" timestamp without time zone, "ConfirmBy" integer, "ConfirmByName" character varying, "ConfirmDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	
BEGIN
	RETURN QUERY 
		SELECT 
			IL."ConfirmLogId" AS "ItemConfirmLogId",
			CFT."ConfirmForTypeName",
			(
				SELECT 
					array_to_json(array_agg(
						A.*::JSONB 
						||('{"ItemName": "'||(SELECT I."ItemName" FROM "Item" I WHERE I."ItemId" = (A->>'ItemId')::INT )::TEXT||'"}')::JSONB
						||('{"UnitId": '||(SELECT I."UnitId" FROM "Item" I WHERE I."ItemId" = (A->>'ItemId')::INT)::INT||'}')::JSONB
						||('{"UnitName": "'||(SELECT U."UnitName" FROM "Item" I JOIN "Unit" U ON I."UnitId" = U."UnitId" WHERE I."ItemId" = (A->>'ItemId')::INT )::TEXT||'"}')::JSONB
					))
				FROM json_array_elements(IL."Data"::JSON) A
-- 				WHERE   (A->>'ItemId')::INT <> 13 AND  (A->>'ItemId')::INT <> 27
			)::TEXT AS "Data",
			IL."TypeActionId",
			(CASE 
				WHEN COALESCE(p_confirm_for_type_id,1) = 1 THEN  IT."ItemTypeName"
				WHEN p_confirm_for_type_id = 2 THEN  GT."TypeName"
			END) ,
			IL."ConfirmStatusId",
			CS."ConfirmStatusName",
			IL."ActionBy" AS "RequestBy",
			IL."ActionByName" AS "RequestByName",
			Il."ActionDate" AS "RequestDate",
			IL."ConfirmBy",
			IL."ConfirmByName",
			IL."ConfirmDate"
		FROM "ConfirmLog" IL 
			JOIN "ConfirmStatus" CS ON IL."ConfirmStatusId" = CS."ConfirmStatusId"
			JOIN "ItemType" IT ON IT."ItemTypeId" =IL."TypeActionId"
			JOIN "GuestActionType" GT ON GT."GuestActionTypeId" = IL."TypeActionId"
			JOIN "ConfirmForType" CFT ON CFT."ConfirmForTypeId" = IL."ConfirmFor"
		WHERE 
 			(COALESCE(p_month, NULL) IS NULL OR TO_CHAR(IL."ActionDate",'YYYY-MM') = p_month)
			AND IL."ConfirmFor" = COALESCE(p_confirm_for_type_id,1)
			AND (COALESCE(p_sale_point_id,0) = 0 OR p_sale_point_id = (((IL."Data" ::JSON)->> 0)::JSON ->> 'SalePointId')::INT)
-- 			AND (IL."Data"::JSON ->> 'ItemId')::INT8 <> 27 OR (IL."Data"::JSON ->> 'ItemId')::INT8 <> 13 
		ORDER BY IL."ActionDate" DESC;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_lottery_channel_ddl(p_region_id integer, p_date timestamp without time zone)
 RETURNS TABLE("Id" integer, "Name" character varying, "ShortName" character varying, "ChannelTypeShortName" character varying, "IsScratchcard" boolean, "DayIds" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_dow INT := EXTRACT(ISODOW FROM p_date);
BEGIN
	
	RETURN QUERY 
	SELECT 
		LC."LotteryChannelId",
		LC."LotteryChannelName",
		LC."ShortName",
		LT."ShortName" AS "ChannelTypeShortName",
		LC."IsScratchcard",
		TRANSLATE(LC."DayIds" ::TEXT, '{}', '[]')
	FROM "LotteryChannel" LC
		JOIN "LotteryChannelType" LT ON LT."LotteryChannelTypeId" = LC."LotteryChannelTypeId"
	WHERE LC."IsActive" IS TRUE 
		AND LC."IsDeleted" IS FALSE
		AND LC."RegionId" IN (p_region_id, 3)
		AND (p_date IS NULL OR v_dow = ANY(LC."DayIds"))
	ORDER BY LT."LotteryChannelTypeId", LC."LotteryChannelId";
	
END;
$function$


