CREATE OR REPLACE FUNCTION public.crm_permission_get_by_title(p_title integer)
 RETURNS TABLE("RoleId" integer, "RoleName" character varying, "PermissionId" integer, "PermissionName" character varying, "ControllerName" character varying, "IsCheck" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN 
	
	RETURN QUERY
	SELECT 
		PR."PermissionRoleId",
		COALESCE(PR."RoleDisplayName", PR."RoleName"),
		P."PermissionId",
		P."PermissionName",
		P."ControllerName",
		(CASE WHEN PTR."PermissionRoleId" IS NULL THEN FALSE ELSE TRUE END)
	FROM "PermissionRole" PR
		JOIN "Permission" P ON P."PermissionId" = PR."PermissionId"
		LEFT JOIN "PermissionRoleTitles" PTR ON PR."PermissionRoleId" = PTR."PermissionRoleId" AND PTR."UserTitleId" = p_title
		JOIN "UserTitle" UT ON UT."UserTitleId" = p_title
	WHERE PR."IsDelete" IS FALSE
		AND PR."IsActive" IS TRUE
	ORDER BY 
		P."Sort" ASC, 
		P."PermissionId" ASC, 
		PR."Sort" ASC, 
		PR."PermissionRoleId" ASC; 
			
END; 
$function$


CREATE OR REPLACE FUNCTION public.crm_permission_get_first_page_show(p_user_role_id integer)
 RETURNS TABLE("ActionName" character varying, "ControllerName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_title_id INT := (SELECT "UserTitleId" FROM "UserRole" WHERE "UserRoleId" = p_user_role_id);
BEGIN

	RETURN QUERY
	SELECT 
		PR."ActionName",
		P."ControllerName"
	FROM "PermissionRole" PR
		JOIN "Permission" P ON P."PermissionId" = PR."PermissionId"
		JOIN "PermissionRoleTitles" PTR ON PR."PermissionRoleId" = PTR."PermissionRoleId" AND PTR."UserTitleId" = v_user_title_id
	WHERE PR."IsShowMenu" IS TRUE
	ORDER BY P."Sort" ASC, P."PermissionId" ASC, PR."Sort" ASC, PR."PermissionRoleId" ASC
	LIMIT 1;

END; $function$


CREATE OR REPLACE FUNCTION public.crm_permission_update_title_role(p_action_by integer, p_action_by_name character varying, p_title integer, p_list_role text)
 RETURNS TABLE(id integer, message text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT4;
	v_msg TEXT;
	v_list_role JSON;
	v_role JSON;
	v_ischeck BOOL;
	v_role_id INT4;
BEGIN 

	v_list_role := p_list_role::json;
	
	FOR v_role IN SELECT * FROM json_array_elements(v_list_role) ele LOOP
		
		v_ischeck := (v_role->>'IsCheck')::BOOL;
		v_role_id := (v_role->>'RoleId')::INT4;
		
		IF v_ischeck IS TRUE AND NOT EXISTS (SELECT 1 FROM "PermissionRoleTitles" WHERE "PermissionRoleId" = v_role_id AND "UserTitleId" = p_title) THEN
		
			INSERT INTO "PermissionRoleTitles"(
				"PermissionRoleId",
				"UserTitleId",
				"CreatedBy",
				"CreatedByName"
			)
			VALUES(
				v_role_id,
				p_title,
				p_action_by,
				p_action_by_name
			);
			
		ELSEIF v_ischeck IS FALSE AND EXISTS (SELECT 1 FROM "PermissionRoleTitles" WHERE "PermissionRoleId" = v_role_id AND "UserTitleId" = p_title) THEN
			
			DELETE FROM "PermissionRoleTitles" WHERE "PermissionRoleId" = v_role_id AND "UserTitleId" = p_title;
			
		END IF;
		
	END LOOP;
	
	v_id := 1;
	v_msg := 'Update permission successful!';
	
	RETURN QUERY 
	SELECT 	v_id AS "Id", v_msg AS "Message";
					
	EXCEPTION WHEN others THEN
	BEGIN
		v_id := -1;
		v_msg := sqlerrm;	
	END;
	
	RETURN QUERY 
	SELECT 	v_id AS "Id", v_msg AS "Message";
			
END; $function$


CREATE OR REPLACE FUNCTION public.crm_potential_get_total_and_other_parts_of_potential_v1(p_user_id integer)
 RETURNS TABLE("ActivityCode" integer, "ActivityName" character varying, "TotalPotential" bigint)
 LANGUAGE plpgsql
AS $function$
DECLARE
-- 	v_user_role_id INT := (SELECT UR."UserRoleId" FROM "UserRole" UR WHERE UR."UserId" = v_user_id);
-- 	v_user_title_ids INT[] := (SELECT UR."UserTitleIds" FROM "UserRole" UR WHERE UR."UserId" = v_user_id);
	v_list_activity_code INT[] := ARRAY[100, 2006, 2002, 2001, 2009];
BEGIN
	RETURN QUERY

		SELECT
				A."ActivityCode",
				A."ActivityName",
				COUNT(1) FILTER (WHERE P."PotentialId" IS NOT NULL) AS "TotalPotential"
			FROM "Activity" A
				LEFT JOIN "Potential" P ON A."ActivityCode" = P."ActivityCode" 
				LEFT JOIN "Student" S ON S."PotentialId" = P."PotentialId"
				LEFT JOIN "User" U ON U."UserId" = P."AssignToUserId"
				WHERE  A."ActivityCode"  = ANY (v_list_activity_code)  AND U."UserId" = p_user_id or p_user_id = 0 AND A."ActivityCode"  = ANY (v_list_activity_code)
				AND A."IsActive" IS TRUE 
			GROUP BY 
				A."ActivityCode",
				A."ActivityName"
			ORDER BY 
				SUBSTRING(A."ActivityName", 1, 2)::INT;
	
	
	

	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_average_lottery_sell_of_user_to_current_date(p_month character varying, p_userid integer, p_lottery_type integer)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "Average" numeric)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	WITH tmp AS(
		SELECT 
			U."UserId",
			U."FullName",
			U."TypeUserId"
		FROM "User" U 
			JOIN "UserRole" UR ON U."UserId" = UR."UserId"
			JOIN "UserTitle" UT ON UR."UserTitleId" = UT."UserTitleId"
		WHERE UT."IsStaff" IS TRUE AND U."IsDeleted" IS FALSE AND U."IsActive" IS TRUE
	),
	tmp1 AS (
		SELECT 
			SPL."ActionBy",
			SPL."SalePointId",
			SPL."LotteryTypeId",
			LT."LotteryTypeName",
			TO_CHAR(COALESCE(SPL."LotteryDate", SPL."ActionDate"), 'YYYY-MM-DD') AS "DateSell",
			SUM(SPL."Quantity") AS "TotalLottery"
		FROM "SalePointLog" SPL, "LotteryType" LT
		WHERE (SPL."ActionDate" <= NOW())
			AND (TO_CHAR(SPL."ActionDate", 'YYYY-MM') =  p_month) 
			AND SPL."LotteryTypeId" = LT."LotteryTypeId"
			AND (COALESCE(p_lottery_type, 0) = 0 
				OR (p_lottery_type = 3 AND SPL."LotteryTypeId" = p_lottery_type) 
				OR (p_lottery_type <> 3 AND SPL."LotteryTypeId" IN(1,2)))
			AND SPL."IsDeleted" IS FALSE
		GROUP BY 
			SPL."ActionBy",
			SPL."SalePointId",
			SPL."LotteryTypeId",
			LT."LotteryTypeName",
			TO_CHAR(COALESCE(SPL."LotteryDate", SPL."ActionDate"), 'YYYY-MM-DD')
	),
	tmp2 AS (
		SELECT 
			TT."ActionBy", 
			SP."SalePointId",
			SP."SalePointName", 
			TT."LotteryTypeId",
			TT."LotteryTypeName",
			TT."DateSell",
			TT."TotalLottery"
		FROM "SalePoint" SP 
			JOIN tmp1 TT ON SP."SalePointId" = TT."SalePointId"
	), 
	tmp3 AS (
		SELECT 
			U."UserId",
			U."FullName",
			U."TypeUserId",
			SUM(A."TotalLottery") AS "Total"
		FROM tmp U 
			LEFT JOIN tmp2 A ON U."UserId" = A."ActionBy"
		WHERE COALESCE(p_userId, 0) = 0 OR U."UserId" = p_userId
		GROUP BY U."UserId", U."FullName", U."TypeUserId"
	),
	tmp4 AS (
		SELECT
			SD."UserId",
			COUNT(1) FILTER (WHERE TO_CHAR(SD."DistributeDate", 'YYYY-MM') = p_month ) AS "TotalShift"
		FROM "ShiftDistribute" SD	
		WHERE (COALESCE(p_userId, 0) = 0 OR SD."UserId" = p_userId)
			AND ((SD."DistributeDate" :: DATE) :: TIMESTAMP + (((CASE WHEN SD."ShiftId" = 1 THEN '6' ELSE '13' END)||' hour') :: INTERVAL))  <= NOW()
		GROUP BY SD."UserId" 
	)
	SELECT 
		tmp3."UserId",
		tmp3."FullName",
		ROUND(COALESCE(tmp3."Total", 0)/(CASE WHEN COALESCE(tmp4."TotalShift", 0) = 0 THEN 1 ELSE COALESCE(tmp4."TotalShift", 1) END), 0) AS "Average" 
	FROM tmp3 
		LEFT JOIN tmp4 ON tmp3."UserId" = tmp4."UserId"
	ORDER BY tmp3."TypeUserId", tmp3."UserId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_check_update_kpi(p_user_id integer, p_week_id integer, p_month character varying)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
BEGIN   
	UPDATE "KPILog"
	SET
		"IsSumKPI" = FALSE
	WHERE "UserId" = p_user_id AND "WeekId" = p_week_id AND "Month" = p_month;
    
    v_id := 1;
    v_mess := 'Update successful';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift(p_user_role integer)
 RETURNS TABLE("LotteryDate" date, "LotteryChannelId" integer, "LotteryChannelName" character varying, "LotteryTypeId" integer, "LotteryTypeName" character varying, "TotalStocks" integer, "TotalTrans" bigint, "TotalReceived" bigint, "TotalReturns" bigint, "TotalRemaining" integer, "TotalSold" integer, "TotalSoldMoney" numeric, "ShiftDistributeId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_id INT;
	v_shift_dis_id INT;
	v_salepoint_id INT;
	v_shift_id INT;
	v_quantity INT;
	v_total_money NUMERIC;
BEGIN
		
	SELECT FN."UserId", FN."SalePointId", FN."ShiftDistributeId" INTO v_user_id, v_salepoint_id, v_shift_dis_id  FROM fn_get_shift_info(p_user_role) FN;
	SELECT SD."ShiftId" INTO v_shift_id FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  v_shift_dis_id;

	RETURN QUERY
		WITH LoadData AS 
				(
				SELECT 
					(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
					SP."SalePointName",
					T."LotteryDate",
					T."LotteryChannelId",
					LC."LotteryChannelName",
					LT."LotteryTypeId",
					LT."LotteryTypeName",
					SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 1 THEN T."TotalTrans" 
									 WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
					SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 1 THEN T."TotalTrans" 
									 WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
					SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 1 THEN T."TotalTrans" 
									 WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
					T."ShiftDistributeId"
				FROM "Transition" T 
							JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
							JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId",
							"LotteryType" LT	
				WHERE LT."LotteryTypeId" <> 3	AND T."ShiftDistributeId" = v_shift_dis_id
				GROUP BY 
					(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), 
					SP."SalePointName",
					T."LotteryChannelId",
					LC."LotteryChannelName",
					T."LotteryDate",
					LT."LotteryTypeId",
					LT."LotteryTypeName",
					T."ShiftDistributeId"
				ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
			),
			GetInventory AS 
			(
				SELECT 
					I."SalePointId",
					SP."SalePointName",
					I."LotteryDate",
					LT."LotteryTypeId",
					LT."LotteryTypeName",
					LC."LotteryChannelId",
					LC."LotteryChannelName"
				FROM "Inventory" I, "LotteryType" LT, "SalePoint" SP, "LotteryChannel" LC
				WHERE (I."LotteryDate" = NOW() ::DATE OR I."LotteryDate" = (NOW() + INTERVAL '1 DAY') ::DATE) 
					AND LT."LotteryTypeId" <> 3
					AND I."SalePointId" = v_salepoint_id
					AND SP."SalePointId" = I."SalePointId"
					AND I."LotteryChannelId" = LC."LotteryChannelId"
				ORDER BY I."LotteryDate", I."LotteryChannelId"
			)	,
			GetData AS 
			(
				SELECT 
					GI."LotteryDate",
					GI."SalePointId",
					GI."SalePointName",
					GI."LotteryChannelId",
					GI."LotteryChannelName",
					GI."LotteryTypeId",
					GI."LotteryTypeName",
					COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
					COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
					COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
					v_shift_dis_id AS "ShiftDistributeId"			
				FROM GetInventory GI LEFT JOIN LoadData L ON GI."LotteryDate" = L."LotteryDate"
																										AND GI."LotteryChannelId" = L."LotteryChannelId"
																										AND GI."LotteryTypeId" = L."LotteryTypeId"
			),
			MoneyData AS
			(
				SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, v_shift_dis_id)
			)				
				SELECT * FROM 
						((SELECT 
						G."LotteryDate",
						G."LotteryChannelId",
						G."LotteryChannelName",
						G."LotteryTypeId",
						G."LotteryTypeName",
						COALESCE(crm_get_previous_remaining(G."ShiftDistributeId", G."LotteryChannelId", G."LotteryTypeId", G."LotteryDate"), 0) AS "FirstStore",
						G."LotteryTrans",
						G."LotteryReceive",
						G."LotteryReturn" ,
						COALESCE(crm_get_current_store_of_sale_point_by_channel(G."SalePointId",G."LotteryChannelId",G."LotteryTypeId",G."LotteryDate"), 0) 
						AS "Last",
						COALESCE(M."Quantity", 0) AS "TotalSell",
						COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
						G."ShiftDistributeId"					
					FROM GetData G 
					  FULL JOIN MoneyData M ON (G."LotteryTypeId" = M."LotteryTypeId"
																AND G."LotteryChannelId" = M."LotteryChannelId"
																AND G."LotteryDate" = M."LotteryDate")
					WHERE G."SalePointId" = v_salepoint_id AND G."ShiftDistributeId" = v_shift_dis_id
					ORDER BY G."LotteryDate")
					UNION
					(SELECT
						NULL,
						NULL,
						NULL,
						LT."LotteryTypeId",
						LT."LotteryTypeName",
						COALESCE(crm_get_previous_remaining(v_shift_dis_id, 0, LT."LotteryTypeId", NULL), 0) AS "FirstStore",
						0,
						0,
						0,
						COALESCE(crm_get_current_store_of_sale_point_by_channel(v_salepoint_id,0,LT."LotteryTypeId",NULL), 0) 
						AS "Last",
						COALESCE(M."Quantity", 0) AS "TotalSell",
						COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
						v_shift_dis_id
					FROM "LotteryType" LT LEFT JOIN MoneyData M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = 0)
					WHERE LT."LotteryTypeId" = 3)) A
				ORDER BY A."LotteryDate", A."LotteryChannelId", A."LotteryTypeId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift_v2(p_user_role integer, p_shift_dis_id integer, p_lottery_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("LotteryDate" date, "LotteryChannelId" integer, "LotteryChannelName" character varying, "LotteryTypeId" integer, "LotteryTypeName" character varying, "TotalStocks" integer, "TotalTrans" bigint, "TotalReceived" bigint, "TotalReturns" bigint, "TotalRemaining" bigint, "TotalSold" integer, "TotalSoldMoney" numeric, "TotalRetail" integer, "TotalRetailMoney" numeric, "TotalWholesale" integer, "TotalWholesaleMoney" numeric, "ShiftDistributeId" integer, "ShortName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_id INT;
	v_salepoint_id INT;
	v_shift_id INT;
	v_quantity INT;
	v_total_money NUMERIC;
BEGIN
		
	SELECT 
		SD."ShiftId", SD."UserId", SD."SalePointId"
	INTO v_shift_id, v_user_id, v_salepoint_id
	FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  p_shift_dis_id;

	RETURN QUERY
	WITH LoadData AS 
	(
		SELECT
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
			SP."SalePointName",
			T."LotteryDate",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."IsScratchcard",
			SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
			SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
			SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
			T."ShiftDistributeId"
		FROM "Transition" T 
			JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
			JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
			JOIN "LotteryType" LT	ON (CASE WHEN T."IsScratchcard" IS TRUE THEN LT."LotteryTypeId" = 3 ELSE LT."LotteryTypeId" <> 3 END)
		WHERE T."ShiftDistributeId" = p_shift_dis_id AND T."ConfirmStatusId" = 2
		GROUP BY
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END),
			SP."SalePointName",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			T."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."ShiftDistributeId",
			T."IsScratchcard"
		ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
	),
	GetInventory AS 
	(
		SELECT 
			I."SalePointId",
			SP."SalePointName",
			I."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			LC."LotteryChannelId",
			LC."LotteryChannelName"
		FROM "Inventory" I, "LotteryType" LT, "SalePoint" SP, "LotteryChannel" LC
		WHERE (I."LotteryDate" = p_lottery_date::DATE OR I."LotteryDate" = (p_lottery_date + INTERVAL '1 DAY') ::DATE) 
			AND LT."LotteryTypeId" <> 3
			AND I."SalePointId" = v_salepoint_id
			AND SP."SalePointId" = I."SalePointId"
			AND I."LotteryChannelId" = LC."LotteryChannelId"
		ORDER BY I."LotteryDate", I."LotteryChannelId"
	),
	GetData AS 
	(
		SELECT 
			GI."LotteryDate",
			GI."SalePointId",
			GI."SalePointName",
			GI."LotteryChannelId",
			GI."LotteryChannelName",
			GI."LotteryTypeId",
			GI."LotteryTypeName",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			p_shift_dis_id AS "ShiftDistributeId"			
		FROM GetInventory GI 
			LEFT JOIN LoadData L ON GI."LotteryDate" = L."LotteryDate" 
				AND L."IsScratchcard" IS FALSE
				AND GI."LotteryChannelId" = L."LotteryChannelId"
				AND GI."LotteryTypeId" = L."LotteryTypeId"
	),
	MoneyData AS
	(
		SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, p_shift_dis_id)
	)				
	SELECT 
		A."LotteryDate",
		A."LotteryChannelId",
		A."LotteryChannelName",
		A."LotteryTypeId",
		A."LotteryTypeName",
		A."FirstStore",
		A."LotteryTrans",
		A."LotteryReceive",
		A."LotteryReturn" ,
		(A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" - A."TotalSell") AS "TotalRemaining",
		A."TotalSell",
		A."TotalSoldMoney",
		A."TotalRetail",
		A."TotalRetailMoney",
		A."TotalWholesale",
		A."TotalWholesaleMoney",
		A."ShiftDistributeId",
		COALESCE(LC."ShortName",'Vé cào')
	FROM 
	(
		SELECT 
			G."LotteryDate",
			G."LotteryChannelId",
			G."LotteryChannelName",
			G."LotteryTypeId",
			G."LotteryTypeName",
			COALESCE(crm_get_previous_remaining_v2(G."ShiftDistributeId", G."LotteryChannelId", G."LotteryTypeId", G."LotteryDate", p_lottery_date::DATE), 0) AS "FirstStore",
			G."LotteryTrans",
			G."LotteryReceive",
			G."LotteryReturn" ,
			--COALESCE(crm_get_current_store_of_sale_point_by_channel(G."SalePointId",G."LotteryChannelId",G."LotteryTypeId",G."LotteryDate"), 0) 
			COALESCE(M."Quantity", 0) AS "TotalSell",
			COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
			COALESCE(M."TotalRetail", 0) AS "TotalRetail",
			COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
			COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
			COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
			G."ShiftDistributeId"
		FROM GetData G 
			FULL JOIN MoneyData M ON G."LotteryTypeId" = M."LotteryTypeId" AND G."LotteryChannelId" = M."LotteryChannelId" AND G."LotteryDate" = M."LotteryDate"
		WHERE G."SalePointId" = v_salepoint_id AND G."ShiftDistributeId" = p_shift_dis_id
		
		UNION
		
		SELECT
			NULL,
			LC."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			COALESCE(crm_get_previous_remaining_v2(p_shift_dis_id, SC."LotteryChannelId", 3, p_lottery_date::DATE, p_lottery_date::DATE), 0) AS "FirstStore",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			--COALESCE(crm_get_current_store_of_sale_point_by_channel(v_salepoint_id,0,LT."LotteryTypeId",NULL), 0) 
			COALESCE(M."Quantity", 0) AS "TotalSell",
			COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
			COALESCE(M."TotalRetail", 0) AS "TotalRetail",
			COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
			COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
			COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
			p_shift_dis_id
		FROM "Scratchcard" SC
			FULL JOIN LoadData L ON L."IsScratchcard" IS TRUE
				AND SC."LotteryChannelId" = L."LotteryChannelId"
				AND L."LotteryTypeId" = 3
			LEFT JOIN "LotteryType" LT ON LT."LotteryTypeId" = 3 AND SC."SalePointId" = v_salepoint_id
			LEFT JOIN MoneyData M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = SC."LotteryChannelId")
			LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SC."LotteryChannelId"
		WHERE SC."SalePointId" = v_salepoint_id
	) A
	LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
	WHERE A."FirstStore" <> 0 OR A."LotteryTrans" <> 0 OR A."LotteryReceive" <> 0 OR A."TotalSell" <> 0 OR A."LotteryTypeId" = 1
	ORDER BY A."LotteryDate",A."LotteryTypeId" DESC, LC."DayIds", LC."LotteryChannelTypeId";

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift_v3(p_user_role integer, p_shift_dis_id integer, p_lottery_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("MoneyData" text, "LotteryData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_id INT;
	v_salepoint_id INT;
	v_shift_id INT;
	v_quantity INT;
	v_total_money NUMERIC;
BEGIN
		
	SELECT 
		SD."ShiftId", SD."UserId", SD."SalePointId"
	INTO v_shift_id, v_user_id, v_salepoint_id
	FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  p_shift_dis_id;

	RETURN QUERY
	--Lấy tổng chuyển nhận
	WITH tmp AS (
		SELECT
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
			SP."SalePointName",
			T."LotteryDate",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."IsScratchcard",
			SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
			SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
			SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
			T."ShiftDistributeId"
		FROM "Transition" T 
			JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
			JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
			JOIN "LotteryType" LT	ON (CASE WHEN T."IsScratchcard" IS TRUE THEN LT."LotteryTypeId" = 3 ELSE LT."LotteryTypeId" <> 3 END)
		WHERE T."ShiftDistributeId" = p_shift_dis_id AND T."ConfirmStatusId" = 2
		GROUP BY
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END),
			SP."SalePointName",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			T."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."ShiftDistributeId",
			T."IsScratchcard"
		ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
	),
	--Lấy master từ kho
	tmp2 AS (--GetInventory
		SELECT 
			I."SalePointId",
			SP."SalePointName",
			I."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			LC."LotteryChannelId",
			LC."LotteryChannelName"
		FROM "Inventory" I, "LotteryType" LT, "SalePoint" SP, "LotteryChannel" LC
		WHERE (I."LotteryDate" = p_lottery_date::DATE OR I."LotteryDate" = (p_lottery_date + INTERVAL '1 DAY') ::DATE) 
			AND LT."LotteryTypeId" <> 3
			AND I."SalePointId" = v_salepoint_id
			AND SP."SalePointId" = I."SalePointId"
			AND I."LotteryChannelId" = LC."LotteryChannelId"
		ORDER BY I."LotteryDate", I."LotteryChannelId"
	),
	--Hợp hai bảng tạm 
	tmp3 AS (--GetData
	
		SELECT 
			GI."LotteryDate",
			GI."SalePointId",
			GI."SalePointName",
			GI."LotteryChannelId",
			GI."LotteryChannelName",
			GI."LotteryTypeId",
			GI."LotteryTypeName",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			p_shift_dis_id AS "ShiftDistributeId"			
		FROM tmp2 GI 
			LEFT JOIN tmp L ON GI."LotteryDate" = L."LotteryDate" 
				AND L."IsScratchcard" IS FALSE
				AND GI."LotteryChannelId" = L."LotteryChannelId"
				AND GI."LotteryTypeId" = L."LotteryTypeId"
	),
	--Lấy tổng bán hàng
	tmp4 AS ( --MoneyData
		SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, p_shift_dis_id)
	),
	--Tổng hợp lại
	tmp5 AS (
		SELECT 
			A."LotteryDate",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."LotteryTypeId",
			A."LotteryTypeName",
			A."FirstStore" AS "TotalStocks",
			A."LotteryTrans" AS "TotalTrans",
			A."LotteryReceive" AS "TotalReceived",
			A."LotteryReturn" AS "TotalReturns",
			(A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" - A."TotalSell") AS "TotalRemaining",
			A."TotalSell" AS "TotalSold",
			A."TotalSoldMoney",
			A."TotalRetail",
			A."TotalRetailMoney",
			A."TotalWholesale",
			A."TotalWholesaleMoney",
			A."ShiftDistributeId",
			COALESCE(LC."ShortName",'Vé cào') AS "ShortName"
		FROM 
		(
			SELECT 
				G."LotteryDate",
				G."LotteryChannelId",
				G."LotteryChannelName",
				G."LotteryTypeId",
				G."LotteryTypeName",
				COALESCE(crm_get_previous_remaining_v3(G."ShiftDistributeId", G."LotteryChannelId", G."LotteryTypeId", G."LotteryDate", p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
				G."LotteryTrans",
				G."LotteryReceive",
				G."LotteryReturn" ,
				COALESCE(M."Quantity", 0) AS "TotalSell",
				COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
				COALESCE(M."TotalRetail", 0) AS "TotalRetail",
				COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
				COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
				COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
				G."ShiftDistributeId"
			FROM tmp3 G 
				FULL JOIN tmp4 M ON G."LotteryTypeId" = M."LotteryTypeId" AND G."LotteryChannelId" = M."LotteryChannelId" AND G."LotteryDate" = M."LotteryDate"
			WHERE G."SalePointId" = v_salepoint_id AND G."ShiftDistributeId" = p_shift_dis_id
			
			UNION
			
			SELECT
				NULL,
				LC."LotteryChannelId",
				LC."LotteryChannelName",
				LT."LotteryTypeId",
				LT."LotteryTypeName",
				COALESCE(crm_get_previous_remaining_v3(p_shift_dis_id, SC."LotteryChannelId", 3, p_lottery_date::DATE, p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
				COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
				COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
				COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
				COALESCE(M."Quantity", 0) AS "TotalSell",
				COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
				COALESCE(M."TotalRetail", 0) AS "TotalRetail",
				COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
				COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
				COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
				p_shift_dis_id
			FROM "Scratchcard" SC
				FULL JOIN tmp L ON L."IsScratchcard" IS TRUE
					AND SC."LotteryChannelId" = L."LotteryChannelId"
					AND L."LotteryTypeId" = 3
				LEFT JOIN "LotteryType" LT ON LT."LotteryTypeId" = 3 AND SC."SalePointId" = v_salepoint_id
				LEFT JOIN tmp4 M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = SC."LotteryChannelId")
				LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SC."LotteryChannelId"
			WHERE SC."SalePointId" = v_salepoint_id
		) A
		LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
		WHERE A."FirstStore" <> 0 OR A."LotteryTrans" <> 0 OR A."LotteryReceive" <> 0 OR A."TotalSell" <> 0 OR A."LotteryTypeId" = 1
		ORDER BY A."LotteryDate",A."LotteryTypeId" DESC, LC."DayIds", LC."LotteryChannelTypeId"
	),
	--Khách nợ
	tmp6 AS (
		SELECT 
			SUM(SPL."TotalValue") AS "TotalGuestDebt"
		FROM "SalePointLog" SPL 
		WHERE SPL."GuestId" IS NOT NULL	
			AND SPL."IsDeleted" IS FALSE
			AND SPL."ShiftDistributeId" = p_shift_dis_id
	),
	--Khách trả
	tmp7 AS (
		SELECT
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalTranferForGuest",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestPay",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestTransferConfirm",
				SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS FALSE) AS "TotalGuestTransferNotConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 3 AND GA."DoneTransfer" IS TRUE) AS "TotalPayForGuest"
		FROM "GuestAction" GA
		WHERE GA."IsDeleted" IS FALSE
			AND GA."ShiftDistributeId" = p_shift_dis_id
	),
	tmp8 AS (
		SELECT
			W."WinningTypeId",
			WT."WinningTypeName",
			SUM(W."WinningPrice") AS "WinningPrice"
		FROM "Winning" W
			JOIN "WinningType" WT ON WT."WinningTypeId" = W."WinningTypeId"
		WHERE W."ShiftDistributeId" = p_shift_dis_id
		GROUP BY
			W."WinningTypeId",
			WT."WinningTypeName"
	),
	tmp9 AS (
		SELECT
			SUM(W."WinningPrice") AS "WinningPrice",
			array_to_json(array_agg(W.*)) AS "WinningData"
		FROM tmp8 W
	),
	tmp10 AS (
		SELECT 
			SUM(T."TotalSoldMoney") AS "TotalSoldMoney"
		FROM tmp5 T
	),
	tmp11 AS (
		SELECT 
			SUM(T."TotalPrice") AS "TotalFeeOutSite"
		FROM crm_sale_point_get_list_fee_outsite(NOW()::TIMESTAMP, 0, p_shift_dis_id) T
	),
	tmp12 AS (
		SELECT
			0 AS "TotalGuestDebt",
			COALESCE(tmp7."TotalTranferForGuest", 0) AS "TotalTranferForGuest",
			COALESCE(tmp7."TotalGuestPay", 0) AS "TotalGuestPay",
			COALESCE(tmp7."TotalGuestTransferConfirm", 0) AS "TotalGuestTransferConfirm",
			COALESCE(tmp7."TotalGuestTransferNotConfirm", 0) AS "TotalGuestTransferNotConfirm",
			COALESCE(tmp7."TotalPayForGuest", 0) AS "TotalPayForGuest",
			COALESCE(tmp9."WinningPrice", 0) AS "WinningPrice",
			COALESCE(tmp11."TotalFeeOutSite", 0) AS "TotalFeeOutSite",
			tmp9."WinningData",
			COALESCE(tmp10."TotalSoldMoney", 0) AS "TotalSoldMoney"
		FROM tmp6, tmp7, tmp9, tmp10, tmp11
	),
	tmp13 AS (
		SELECT 
			T.*,
			(T."TotalSoldMoney"
			- T."TotalGuestDebt"
			+ T."TotalTranferForGuest"
			+ T."TotalGuestPay"
			- T."TotalPayForGuest"
		
			- T."TotalFeeOutSite"
			- T."WinningPrice"
			) AS "TotalMoneyReturn"
		FROM tmp12 T
	)
	SELECT 
	(
		SELECT 
			TO_JSONB(T.*)
		FROM tmp13 T
	)::TEXT AS "MoneyData",
	(
		SELECT
			array_to_json(array_agg(TO_JSONB(T.*)))
		FROM tmp5 T
	)::TEXT AS "LotteryData";
	

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift_v4(p_user_role integer, p_shift_dis_id integer, p_lottery_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("LotteryDate" date, "LotteryChannelId" integer, "LotteryChannelName" character varying, "LotteryTypeId" integer, "LotteryTypeName" character varying, "TotalStocks" integer, "TotalTrans" bigint, "TotalReceived" bigint, "TotalReturns" bigint, "TotalRemaining" bigint, "TotalSold" integer, "TotalSoldMoney" numeric, "TotalRetail" integer, "TotalRetailMoney" numeric, "TotalWholesale" integer, "TotalWholesaleMoney" numeric, "ShiftDistributeId" integer, "ShortName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_id INT;
	v_salepoint_id INT;
	v_shift_id INT;
	v_quantity INT;
	v_total_money NUMERIC;
	v_is_shift INT;
BEGIN
		
	SELECT 
		SD."ShiftId", SD."UserId", SD."SalePointId"
	INTO v_shift_id, v_user_id, v_salepoint_id
	FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  p_shift_dis_id;
	RETURN QUERY
	WITH LoadData AS 
	(
		SELECT
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
			SP."SalePointName",
			T."LotteryDate",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."IsScratchcard",
			SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
			SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
			SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
			T."ShiftDistributeId"
		FROM "Transition" T 
			JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
			JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
			JOIN "LotteryType" LT	ON (CASE WHEN T."IsScratchcard" IS TRUE THEN LT."LotteryTypeId" = 3 ELSE LT."LotteryTypeId" <> 3 END)
		WHERE T."ShiftDistributeId" = p_shift_dis_id AND T."ConfirmStatusId" = 2
		GROUP BY
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END),
			SP."SalePointName",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			T."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."ShiftDistributeId",
			T."IsScratchcard"
		ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
	),
	GetInventory AS 
	(
		SELECT 
			I."SalePointId",
			SP."SalePointName",
			I."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			LC."LotteryChannelId",
			LC."LotteryChannelName"
		FROM "Inventory" I, "LotteryType" LT, "SalePoint" SP, "LotteryChannel" LC
		WHERE (I."LotteryDate" = p_lottery_date::DATE OR I."LotteryDate" = (p_lottery_date + INTERVAL '1 DAY') ::DATE) 
			AND LT."LotteryTypeId" <> 3
			AND I."SalePointId" = v_salepoint_id
			AND SP."SalePointId" = I."SalePointId"
			AND I."LotteryChannelId" = LC."LotteryChannelId"
		ORDER BY I."LotteryDate", I."LotteryChannelId"
	),
	GetData AS 
	(
		SELECT 
			GI."LotteryDate",
			GI."SalePointId",
			GI."SalePointName",
			GI."LotteryChannelId",
			GI."LotteryChannelName",
			GI."LotteryTypeId",
			GI."LotteryTypeName",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			p_shift_dis_id AS "ShiftDistributeId"			
		FROM GetInventory GI 
			LEFT JOIN LoadData L ON GI."LotteryDate" = L."LotteryDate" 
				AND L."IsScratchcard" IS FALSE
				AND GI."LotteryChannelId" = L."LotteryChannelId"
				AND GI."LotteryTypeId" = L."LotteryTypeId"
	),
	MoneyData AS
	(
		SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, p_shift_dis_id)
	)				
	SELECT 
		A."LotteryDate",
		A."LotteryChannelId",
		A."LotteryChannelName",
		A."LotteryTypeId",
		A."LotteryTypeName",
		(CASE WHEN A."LotteryTypeId" = 3 
		THEN
			A."FirstStore" - fn_totalreceived(v_salepoint_id, p_lottery_date,A."LotteryChannelId" , v_shift_id) 
		ELSE
			A."FirstStore"
		END) AS "FirstStore",
		A."LotteryTrans",
		(CASE WHEN A."LotteryTypeId" = 3
		THEN
			A."LotteryReceive" + fn_totalreceived(v_salepoint_id, p_lottery_date, A."LotteryChannelId",v_shift_id)
		ELSE
			A."LotteryReceive"
		END) AS "LotteryReceive",
		A."LotteryReturn" ,
		(CASE WHEN v_shift_id = 1
		THEN
			(A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" - A."TotalSell" - fn_totalreceived(v_salepoint_id, p_lottery_date,A."LotteryChannelId" ,2))
		ELSE
			(A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" - A."TotalSell")
		END ) AS "TotalRemaining",
		A."TotalSell",
		A."TotalSoldMoney",
		A."TotalRetail",
		A."TotalRetailMoney",
		A."TotalWholesale",
		A."TotalWholesaleMoney",
		A."ShiftDistributeId",
		COALESCE(LC."ShortName",'Vé cào')
	FROM 
	(
		SELECT 
			G."LotteryDate",
			G."LotteryChannelId",
			G."LotteryChannelName",
			G."LotteryTypeId",
			G."LotteryTypeName",
			COALESCE(crm_get_previous_remaining_v2(G."ShiftDistributeId", G."LotteryChannelId", G."LotteryTypeId", G."LotteryDate", p_lottery_date::DATE), 0) AS "FirstStore",
			G."LotteryTrans",
			G."LotteryReceive",
			G."LotteryReturn" ,
			--COALESCE(crm_get_current_store_of_sale_point_by_channel(G."SalePointId",G."LotteryChannelId",G."LotteryTypeId",G."LotteryDate"), 0) 
			COALESCE(M."Quantity", 0) AS "TotalSell",
			COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
			COALESCE(M."TotalRetail", 0) AS "TotalRetail",
			COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
			COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
			COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
			G."ShiftDistributeId"
		FROM GetData G 
			FULL JOIN MoneyData M ON G."LotteryTypeId" = M."LotteryTypeId" AND G."LotteryChannelId" = M."LotteryChannelId" AND G."LotteryDate" = M."LotteryDate"
		WHERE G."SalePointId" = v_salepoint_id AND G."ShiftDistributeId" = p_shift_dis_id
		
		UNION
		
		SELECT
			NULL,
			LC."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			COALESCE(crm_get_previous_remaining_v2(p_shift_dis_id, SC."LotteryChannelId", 3, p_lottery_date::DATE, p_lottery_date::DATE), 0) AS "FirstStore",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			--COALESCE(crm_get_current_store_of_sale_point_by_channel(v_salepoint_id,0,LT."LotteryTypeId",NULL), 0) 
			COALESCE(M."Quantity", 0) AS "TotalSell",
			COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
			COALESCE(M."TotalRetail", 0) AS "TotalRetail",
			COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
			COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
			COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
			p_shift_dis_id
		FROM "Scratchcard" SC
			FULL JOIN LoadData L ON L."IsScratchcard" IS TRUE
				AND SC."LotteryChannelId" = L."LotteryChannelId"
				AND L."LotteryTypeId" = 3
			LEFT JOIN "LotteryType" LT ON LT."LotteryTypeId" = 3 AND SC."SalePointId" = v_salepoint_id
			LEFT JOIN MoneyData M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = SC."LotteryChannelId")
			LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SC."LotteryChannelId"
		WHERE SC."SalePointId" = v_salepoint_id
	) A
	LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
	WHERE A."FirstStore" <> 0 OR A."LotteryTrans" <> 0 OR A."LotteryReceive" <> 0 OR A."TotalSell" <> 0 OR A."LotteryTypeId" = 1
	ORDER BY A."LotteryDate",A."LotteryTypeId" DESC, LC."DayIds", LC."LotteryChannelTypeId";

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift_v5(p_user_role integer, p_shift_dis_id integer, p_lottery_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("MoneyData" text, "LotteryData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_id INT;
	v_salepoint_id INT;
	v_shift_id INT;
	v_quantity INT;
	v_total_money NUMERIC;
BEGIN
		
	SELECT 
		SD."ShiftId", SD."UserId", SD."SalePointId"
	INTO v_shift_id, v_user_id, v_salepoint_id
	FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  p_shift_dis_id;

	RETURN QUERY
	--Lấy tổng chuyển nhận
	WITH tmp AS (
		SELECT
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
			SP."SalePointName",
			T."LotteryDate",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."IsScratchcard",
			SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
			SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
			SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
			T."ShiftDistributeId"
		FROM "Transition" T 
			JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
			JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
			JOIN "LotteryType" LT	ON (CASE WHEN T."IsScratchcard" IS TRUE THEN LT."LotteryTypeId" = 3 ELSE LT."LotteryTypeId" <> 3 END)
		WHERE T."ShiftDistributeId" = p_shift_dis_id AND T."ConfirmStatusId" = 2
		GROUP BY
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END),
			SP."SalePointName",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			T."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."ShiftDistributeId",
			T."IsScratchcard"
		ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
	),
	--Lấy master từ kho
	tmp2 AS (--GetInventory
		SELECT 
			I."SalePointId",
			SP."SalePointName",
			I."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			LC."LotteryChannelId",
			LC."LotteryChannelName"
		FROM "Inventory" I, "LotteryType" LT, "SalePoint" SP, "LotteryChannel" LC
		WHERE (I."LotteryDate" = p_lottery_date::DATE OR I."LotteryDate" = (p_lottery_date + INTERVAL '1 DAY') ::DATE) 
			AND LT."LotteryTypeId" <> 3
			AND I."SalePointId" = v_salepoint_id
			AND SP."SalePointId" = I."SalePointId"
			AND I."LotteryChannelId" = LC."LotteryChannelId"
		ORDER BY I."LotteryDate", I."LotteryChannelId"
	),
	--Hợp hai bảng tạm 
	tmp3 AS (--GetData
	
		SELECT 
			GI."LotteryDate",
			GI."SalePointId",
			GI."SalePointName",
			GI."LotteryChannelId",
			GI."LotteryChannelName",
			GI."LotteryTypeId",
			GI."LotteryTypeName",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			p_shift_dis_id AS "ShiftDistributeId"			
		FROM tmp2 GI 
			LEFT JOIN tmp L ON GI."LotteryDate" = L."LotteryDate" 
				AND L."IsScratchcard" IS FALSE
				AND GI."LotteryChannelId" = L."LotteryChannelId"
				AND GI."LotteryTypeId" = L."LotteryTypeId"
	),
	--Lấy tổng bán hàng
	tmp4 AS ( --MoneyData
		SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, p_shift_dis_id)
	),
	--Tổng hợp lại
	tmp5 AS (
		SELECT 
			A."LotteryDate",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."LotteryTypeId",
			A."LotteryTypeName",
			A."FirstStore"+ A."TotalSell"  AS "TotalStocks",
			A."LotteryTrans" AS "TotalTrans",
			A."LotteryReceive" AS "TotalReceived",
			A."LotteryReturn" AS "TotalReturns",
			(A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" ) AS "TotalRemaining",
			A."TotalSell" AS "TotalSold",
			A."TotalSoldMoney",
			A."TotalRetail",
			A."TotalRetailMoney",
			A."TotalWholesale",
			A."TotalWholesaleMoney",
			A."ShiftDistributeId",
			COALESCE(LC."ShortName",'Vé cào') AS "ShortName"
		FROM 
		(
			SELECT 
				G."LotteryDate",
				G."LotteryChannelId",
				G."LotteryChannelName",
				G."LotteryTypeId",
				G."LotteryTypeName",
				COALESCE(crm_get_previous_remaining_v3(G."ShiftDistributeId", G."LotteryChannelId", G."LotteryTypeId", G."LotteryDate", p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
				G."LotteryTrans",
				G."LotteryReceive",
				G."LotteryReturn" ,
				COALESCE(M."Quantity", 0) AS "TotalSell",
				COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
				COALESCE(M."TotalRetail", 0) AS "TotalRetail",
				COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
				COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
				COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
				G."ShiftDistributeId"
			FROM tmp3 G 
				FULL JOIN tmp4 M ON G."LotteryTypeId" = M."LotteryTypeId" AND G."LotteryChannelId" = M."LotteryChannelId" AND G."LotteryDate" = M."LotteryDate"
			WHERE G."SalePointId" = v_salepoint_id AND G."ShiftDistributeId" = p_shift_dis_id
			
			UNION
			
			SELECT
				NULL,
				LC."LotteryChannelId",
				LC."LotteryChannelName",
				LT."LotteryTypeId",
				LT."LotteryTypeName",
				COALESCE(crm_get_previous_remaining_v3(p_shift_dis_id, SC."LotteryChannelId", 3, p_lottery_date::DATE, p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
				COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
				COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
				COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
				COALESCE(M."Quantity", 0) AS "TotalSell",
				COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
				COALESCE(M."TotalRetail", 0) AS "TotalRetail",
				COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
				COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
				COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
				p_shift_dis_id
			FROM "Scratchcard" SC
				FULL JOIN tmp L ON L."IsScratchcard" IS TRUE
					AND SC."LotteryChannelId" = L."LotteryChannelId"
					AND L."LotteryTypeId" = 3
				LEFT JOIN "LotteryType" LT ON LT."LotteryTypeId" = 3 AND SC."SalePointId" = v_salepoint_id
				LEFT JOIN tmp4 M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = SC."LotteryChannelId")
				LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SC."LotteryChannelId"
			WHERE SC."SalePointId" = v_salepoint_id
		) A
		LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
		WHERE A."FirstStore" <> 0 OR A."LotteryTrans" <> 0 OR A."LotteryReceive" <> 0 OR A."TotalSell" <> 0 OR A."LotteryTypeId" = 1
		ORDER BY A."LotteryDate",A."LotteryTypeId" DESC, LC."DayIds", LC."LotteryChannelTypeId"
	),
	--Khách nợ
	tmp6 AS (
		SELECT 
			SUM(SPL."TotalValue") AS "TotalGuestDebt"
		FROM "SalePointLog" SPL 
		WHERE SPL."GuestId" IS NOT NULL	
			AND SPL."IsDeleted" IS FALSE
			AND SPL."ShiftDistributeId" = p_shift_dis_id
	),
	--Khách trả
	tmp7 AS (
		SELECT
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS FALSE) AS "TotalTransferForGuestNotConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalTransferForGuestConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestPay",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestTransferConfirm",
				SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS FALSE) AS "TotalGuestTransferNotConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 3 AND GA."DoneTransfer" IS TRUE) AS "TotalPayForGuest"
		FROM "GuestAction" GA
		WHERE GA."IsDeleted" IS FALSE
			AND GA."ShiftDistributeId" = p_shift_dis_id
	),
	tmp8 AS (
		SELECT
			W."WinningTypeId",
			WT."WinningTypeName",
			SUM(W."WinningPrice") AS "WinningPrice"
		FROM "Winning" W
			JOIN "WinningType" WT ON WT."WinningTypeId" = W."WinningTypeId"
		WHERE W."ShiftDistributeId" = p_shift_dis_id
		GROUP BY
			W."WinningTypeId",
			WT."WinningTypeName"
	),
	tmp9 AS (
		SELECT
			SUM(W."WinningPrice") AS "WinningPrice",
			array_to_json(array_agg(W.*)) AS "WinningData"
		FROM tmp8 W
	),
	tmp10 AS (
		SELECT 
			SUM(T."TotalSoldMoney") AS "TotalSoldMoney"
		FROM tmp5 T
	),
	tmp11 AS (
		SELECT 
			SUM(T."TotalPrice") AS "TotalFeeOutSite"
		FROM crm_sale_point_get_list_fee_outsite(NOW()::TIMESTAMP, 0, p_shift_dis_id) T
	),
	tmp12 AS (
		SELECT
			0 AS "TotalGuestDebt",
			COALESCE(tmp7."TotalTransferForGuestConfirm", 0) AS "TotalTransferForGuestConfirm",
			COALESCE(tmp7."TotalTransferForGuestNotConfirm", 0) AS "TotalTransferForGuestNotConfirm",
			COALESCE(tmp7."TotalGuestPay", 0) AS "TotalGuestPay",
			COALESCE(tmp7."TotalGuestTransferConfirm", 0) AS "TotalGuestTransferConfirm",
			COALESCE(tmp7."TotalGuestTransferNotConfirm", 0) AS "TotalGuestTransferNotConfirm",
			COALESCE(tmp7."TotalPayForGuest", 0) AS "TotalPayForGuest",
			COALESCE(tmp9."WinningPrice", 0) AS "WinningPrice",
			COALESCE(tmp11."TotalFeeOutSite", 0) AS "TotalFeeOutSite",
			tmp9."WinningData",
			COALESCE(tmp10."TotalSoldMoney", 0) AS "TotalSoldMoney"
		FROM tmp6, tmp7, tmp9, tmp10, tmp11
	),
	tmp13 AS (
		SELECT 
			T.*,
			(T."TotalSoldMoney"
			- T."TotalGuestDebt"
			+ T."TotalTransferForGuestConfirm"
			+ T."TotalGuestPay"
			- T."TotalPayForGuest"
		
			- T."TotalFeeOutSite"
			- T."WinningPrice"
			) AS "TotalMoneyReturn"
		FROM tmp12 T
	)
	SELECT 
	(
		SELECT 
			TO_JSONB(T.*)
		FROM tmp13 T
	)::TEXT AS "MoneyData",
	(
		SELECT
			array_to_json(array_agg(TO_JSONB(T.*)))
		FROM tmp5 T
	)::TEXT AS "LotteryData";

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift_v5_reuse(p_user_role integer, p_shift_dis_id integer, p_lottery_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("MoneyData" text, "LotteryData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_user_id INT;
    v_salepoint_id INT;
    v_shift_id INT;
    v_quantity INT;
    v_total_money NUMERIC;
BEGIN
    SELECT 
        SD."ShiftId", SD."UserId", SD."SalePointId"
    INTO v_shift_id, v_user_id, v_salepoint_id
    FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  p_shift_dis_id;
    RETURN QUERY
    --Lấy tổng chuyển nhận
    WITH tmp AS (
        SELECT
            (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
            SP."SalePointName",
            T."LotteryDate",
            T."LotteryChannelId",
            LC."LotteryChannelName",
            LT."LotteryTypeId",
            LT."LotteryTypeName",
            T."IsScratchcard",
            SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
                            WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
            SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
                            WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
            SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
                            WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
            T."ShiftDistributeId"
        FROM "Transition" T 
            JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
            JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
            JOIN "LotteryType" LT   ON (CASE WHEN T."IsScratchcard" IS TRUE THEN LT."LotteryTypeId" = 3 ELSE LT."LotteryTypeId" <> 3 END)
        WHERE T."ShiftDistributeId" = p_shift_dis_id AND T."ConfirmStatusId" = 2 
        GROUP BY
            (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END),
            SP."SalePointName",
            T."LotteryChannelId",
            LC."LotteryChannelName",
            T."LotteryDate",
            LT."LotteryTypeId",
            LT."LotteryTypeName",
            T."ShiftDistributeId",
            T."IsScratchcard"
        ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
    ),
    --Lấy tổng bán hàng
    tmp4 AS ( --MoneyData
        SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, p_shift_dis_id)
    ),
    --Tổng hợp lại
    tmp5 AS (
        SELECT 
            A."LotteryDate",
            A."LotteryChannelId",
            A."LotteryChannelName",
            A."LotteryTypeId",
            A."LotteryTypeName",
            A."FirstStore" AS "TotalStocks",
            A."LotteryTrans" AS "TotalTrans",
            A."LotteryReceive" AS "TotalReceived",
            A."LotteryReturn" AS "TotalReturns",
            (A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" - A."TotalSell") AS "TotalRemaining",
            A."TotalSell" AS "TotalSold",
            A."TotalSoldMoney",
            A."TotalRetail",
            A."TotalRetailMoney",
            A."TotalWholesale",
            A."TotalWholesaleMoney",
            A."ShiftDistributeId",
            COALESCE(LC."ShortName",'Vé cào') AS "ShortName"
        FROM 
        (
            SELECT
                NULL AS "LotteryDate",
                LC."LotteryChannelId",
                LC."LotteryChannelName",
                LT."LotteryTypeId",
                LT."LotteryTypeName",
                COALESCE(crm_get_previous_remaining_v3(p_shift_dis_id, SC."LotteryChannelId", 3, p_lottery_date::DATE, p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
                COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
                COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
                COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
                COALESCE(M."Quantity", 0) AS "TotalSell",
                COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
                COALESCE(M."TotalRetail", 0) AS "TotalRetail",
                COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
                COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
                COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
                p_shift_dis_id AS "ShiftDistributeId"
            FROM "Scratchcard" SC
                FULL JOIN tmp L ON L."IsScratchcard" IS TRUE
                    AND SC."LotteryChannelId" = L."LotteryChannelId"
                    AND L."LotteryTypeId" = 3
                LEFT JOIN "LotteryType" LT ON LT."LotteryTypeId" = 3 AND SC."SalePointId" = v_salepoint_id
                LEFT JOIN tmp4 M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = SC."LotteryChannelId")
                LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SC."LotteryChannelId"
            WHERE SC."SalePointId" = v_salepoint_id
        ) A
        LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
        WHERE A."FirstStore" <> 0 OR A."LotteryTrans" <> 0 OR A."LotteryReceive" <> 0 OR A."TotalSell" <> 0 OR A."LotteryTypeId" = 1
        ORDER BY A."LotteryDate",A."LotteryTypeId" DESC, LC."DayIds", LC."LotteryChannelTypeId"
    ),
    --Khách nợ
    tmp6 AS (
        SELECT 
            SUM(SPL."TotalValue") AS "TotalGuestDebt"
        FROM "SalePointLog" SPL 
        WHERE SPL."GuestId" IS NOT NULL 
            AND SPL."IsDeleted" IS FALSE
            AND SPL."ShiftDistributeId" = p_shift_dis_id
    ),
    --Khách trả
    tmp7 AS (
        SELECT
            SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS FALSE) AS "TotalTransferForGuestNotConfirm",
            SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalTransferForGuestConfirm",
            SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestPay",
            SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestTransferConfirm",
                SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS FALSE) AS "TotalGuestTransferNotConfirm",
            SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 3 AND GA."DoneTransfer" IS TRUE) AS "TotalPayForGuest"
        FROM "GuestAction" GA
        WHERE GA."IsDeleted" IS FALSE
            AND GA."ShiftDistributeId" = p_shift_dis_id
    ),
    tmp8 AS (
        SELECT
            W."WinningTypeId",
            WT."WinningTypeName",
            SUM(W."WinningPrice") AS "WinningPrice"
        FROM "Winning" W
            JOIN "WinningType" WT ON WT."WinningTypeId" = W."WinningTypeId"
        WHERE W."ShiftDistributeId" = p_shift_dis_id
        GROUP BY
            W."WinningTypeId",
            WT."WinningTypeName"
    ),
    tmp9 AS (
        SELECT
            SUM(W."WinningPrice") AS "WinningPrice",
            array_to_json(array_agg(W.*)) AS "WinningData"
        FROM tmp8 W
    ),
    tmp10 AS (
        SELECT 
            SUM(SL."TotalValue") AS "TotalSoldMoney"
        FROM "SalePointLog" SL WHERE SL."ShiftDistributeId" = p_shift_dis_id AND SL."IsDeleted" IS FALSE
    ),
    tmp11 AS (
        SELECT 
            SUM(T."TotalPrice") AS "TotalFeeOutSite"
        FROM crm_sale_point_get_list_fee_outsite(NOW()::TIMESTAMP, 0, p_shift_dis_id) T
    ),
    tmp12 AS (
        SELECT
            0 AS "TotalGuestDebt",
            COALESCE(tmp7."TotalTransferForGuestConfirm", 0) AS "TotalTransferForGuestConfirm",
            COALESCE(tmp7."TotalTransferForGuestNotConfirm", 0) AS "TotalTransferForGuestNotConfirm",
            COALESCE(tmp7."TotalGuestPay", 0) AS "TotalGuestPay",
            COALESCE(tmp7."TotalGuestTransferConfirm", 0) AS "TotalGuestTransferConfirm",
            COALESCE(tmp7."TotalGuestTransferNotConfirm", 0) AS "TotalGuestTransferNotConfirm",
            COALESCE(tmp7."TotalPayForGuest", 0) AS "TotalPayForGuest",
            COALESCE(tmp9."WinningPrice", 0) AS "WinningPrice",
            COALESCE(tmp11."TotalFeeOutSite", 0) AS "TotalFeeOutSite",
            tmp9."WinningData",
            COALESCE(tmp10."TotalSoldMoney", 0) AS "TotalSoldMoney"
        FROM tmp6, tmp7, tmp9, tmp10, tmp11
    ),
    tmp13 AS (
        SELECT 
            T.*,
            (T."TotalSoldMoney"
            - T."TotalGuestDebt"
            + T."TotalTransferForGuestConfirm"
            + T."TotalGuestPay"
            - T."TotalPayForGuest"
            - T."TotalFeeOutSite"
            - T."WinningPrice"
            ) AS "TotalMoneyReturn"
        FROM tmp12 T
    )
    SELECT 
    (
        SELECT 
            TO_JSONB(T.*)
        FROM tmp13 T
    )::TEXT AS "MoneyData",
    (
        SELECT
            array_to_json(array_agg(TO_JSONB(T.*)))
        FROM tmp5 T WHERE T."LotteryDate" IS NULL
    )::TEXT AS "LotteryData";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_data_finish_shift_v6(p_user_role integer, p_shift_dis_id integer, p_lottery_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("MoneyData" text, "LotteryData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_id INT;
	v_salepoint_id INT;
	v_shift_id INT;
	v_quantity INT;
	v_total_money NUMERIC;
BEGIN
		
	SELECT 
		SD."ShiftId", SD."UserId", SD."SalePointId"
	INTO v_shift_id, v_user_id, v_salepoint_id
	FROM "ShiftDistribute" SD WHERE SD."ShiftDistributeId" =  p_shift_dis_id;

	RETURN QUERY
	--Lấy tổng chuyển nhận
	WITH tmp AS (
		SELECT
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END) AS "SalePointId", 
			SP."SalePointName",
			T."LotteryDate",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."IsScratchcard",
			SUM(CASE WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 1 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryTrans",
			SUM(CASE WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 2 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReceive",
			SUM(CASE WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" IN (1, 3) THEN T."TotalTrans" 
							WHEN T."TransitionTypeId" = 3 AND LT."LotteryTypeId" = 2 THEN T."TotalTransDup" ELSE 0 END) AS "LotteryReturn",
			T."ShiftDistributeId"
		FROM "Transition" T 
			JOIN "SalePoint" SP ON (T."ToSalePointId" = SP."SalePointId" OR T."FromSalePointId" = SP."SalePointId")
			JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
			JOIN "LotteryType" LT	ON (CASE WHEN T."IsScratchcard" IS TRUE THEN LT."LotteryTypeId" = 3 ELSE LT."LotteryTypeId" <> 3 END)
		WHERE T."ShiftDistributeId" = p_shift_dis_id AND T."ConfirmStatusId" = 2 
		GROUP BY
			(CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END),
			SP."SalePointName",
			T."LotteryChannelId",
			LC."LotteryChannelName",
			T."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			T."ShiftDistributeId",
			T."IsScratchcard"
		ORDER BY (CASE WHEN T."FromSalePointId" = 0 THEN T."ToSalePointId" ELSE T."FromSalePointId" END), T."LotteryChannelId"
	),
	--Lấy master từ kho
	tmp2 AS (--GetInventory
		SELECT 
			I."SalePointId",
			SP."SalePointName",
			I."LotteryDate",
			LT."LotteryTypeId",
			LT."LotteryTypeName",
			LC."LotteryChannelId",
			LC."LotteryChannelName"
		FROM "Inventory" I, "LotteryType" LT, "SalePoint" SP, "LotteryChannel" LC
		WHERE (I."LotteryDate" = p_lottery_date::DATE OR I."LotteryDate" = (p_lottery_date + INTERVAL '1 DAY') ::DATE) 
			AND LT."LotteryTypeId" <> 3
			AND I."SalePointId" = v_salepoint_id
			AND SP."SalePointId" = I."SalePointId"
			AND I."LotteryChannelId" = LC."LotteryChannelId"
		ORDER BY I."LotteryDate", I."LotteryChannelId"
	),
	--Hợp hai bảng tạm 
	tmp3 AS (--GetData
	
		SELECT 
			GI."LotteryDate",
			GI."SalePointId",
			GI."SalePointName",
			GI."LotteryChannelId",
			GI."LotteryChannelName",
			GI."LotteryTypeId",
			GI."LotteryTypeName",
			COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
			COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
			COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
			p_shift_dis_id AS "ShiftDistributeId"			
		FROM tmp2 GI 
			LEFT JOIN tmp L ON GI."LotteryDate" = L."LotteryDate" 
				AND L."IsScratchcard" IS FALSE
				AND GI."LotteryChannelId" = L."LotteryChannelId"
				AND GI."LotteryTypeId" = L."LotteryTypeId"
	),
	--Lấy tổng bán hàng
	tmp4 AS ( --MoneyData
		SELECT * FROM crm_get_total_sale_of_sale_point_by_shiftdis(v_salepoint_id, p_shift_dis_id)
	),
	--Tổng hợp lại
	tmp5 AS (
		SELECT 
			A."LotteryDate",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."LotteryTypeId",
			A."LotteryTypeName",
			A."FirstStore" AS "TotalStocks",
			A."LotteryTrans" AS "TotalTrans",
			A."LotteryReceive" AS "TotalReceived",
			A."LotteryReturn" AS "TotalReturns",
			(A."FirstStore" - A."LotteryTrans" + A."LotteryReceive" - A."TotalSell") AS "TotalRemaining",
			A."TotalSell" AS "TotalSold",
			A."TotalSoldMoney",
			A."TotalRetail",
			A."TotalRetailMoney",
			A."TotalWholesale",
			A."TotalWholesaleMoney",
			A."ShiftDistributeId",
			COALESCE(LC."ShortName",'Vé cào') AS "ShortName"
		FROM 
		(
			SELECT 
				G."LotteryDate",
				G."LotteryChannelId",
				G."LotteryChannelName",
				G."LotteryTypeId",
				G."LotteryTypeName",
				COALESCE(crm_get_previous_remaining_v3(G."ShiftDistributeId", G."LotteryChannelId", G."LotteryTypeId", G."LotteryDate", p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
				G."LotteryTrans",
				G."LotteryReceive",
				G."LotteryReturn" ,
				COALESCE(M."Quantity", 0) AS "TotalSell",
				COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
				COALESCE(M."TotalRetail", 0) AS "TotalRetail",
				COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
				COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
				COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
				G."ShiftDistributeId"
			FROM tmp3 G 
				FULL JOIN tmp4 M ON G."LotteryTypeId" = M."LotteryTypeId" AND G."LotteryChannelId" = M."LotteryChannelId" AND G."LotteryDate" = M."LotteryDate"
			WHERE G."SalePointId" = v_salepoint_id AND G."ShiftDistributeId" = p_shift_dis_id
			
			UNION
			
			SELECT
				NULL,
				LC."LotteryChannelId",
				LC."LotteryChannelName",
				LT."LotteryTypeId",
				LT."LotteryTypeName",
				COALESCE(crm_get_previous_remaining_v3(p_shift_dis_id, SC."LotteryChannelId", 3, p_lottery_date::DATE, p_lottery_date::DATE,p_user_role), 0) AS "FirstStore",
				COALESCE(L."LotteryTrans", 0) AS "LotteryTrans",
				COALESCE(L."LotteryReceive", 0) AS "LotteryReceive",
				COALESCE(L."LotteryReturn", 0) AS "LotteryReturn",
				COALESCE(M."Quantity", 0) AS "TotalSell",
				COALESCE(M."TotalSoldMoney", 0) AS "TotalSoldMoney",
				COALESCE(M."TotalRetail", 0) AS "TotalRetail",
				COALESCE(M."TotalRetailMoney", 0) AS "TotalRetailMoney",
				COALESCE(M."TotalWholesale", 0) AS "TotalWholesale",
				COALESCE(M."TotalWholesaleMoney", 0) AS "TotalWholesaleMoney",
				p_shift_dis_id
			FROM "Scratchcard" SC
				FULL JOIN tmp L ON L."IsScratchcard" IS TRUE
					AND SC."LotteryChannelId" = L."LotteryChannelId"
					AND L."LotteryTypeId" = 3
				LEFT JOIN "LotteryType" LT ON LT."LotteryTypeId" = 3 AND SC."SalePointId" = v_salepoint_id
				LEFT JOIN tmp4 M ON (LT."LotteryTypeId" = M."LotteryTypeId" AND M."LotteryChannelId" = SC."LotteryChannelId")
				LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = SC."LotteryChannelId"
			WHERE SC."SalePointId" = v_salepoint_id
		) A
		LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
		WHERE A."FirstStore" <> 0 OR A."LotteryTrans" <> 0 OR A."LotteryReceive" <> 0 OR A."TotalSell" <> 0 OR A."LotteryTypeId" = 1
		ORDER BY A."LotteryDate",A."LotteryTypeId" DESC, LC."DayIds", LC."LotteryChannelTypeId"
	),
	--Khách nợ
	tmp6 AS (
		SELECT 
			SUM(SPL."TotalValue") AS "TotalGuestDebt"
		FROM "SalePointLog" SPL 
		WHERE SPL."GuestId" IS NOT NULL	
			AND SPL."IsDeleted" IS FALSE
			AND SPL."ShiftDistributeId" = p_shift_dis_id
	),
	--Khách trả
	tmp7 AS (
		SELECT
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS FALSE) AS "TotalTransferForGuestNotConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalTransferForGuestConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 1 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestPay",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS TRUE) AS "TotalGuestTransferConfirm",
				SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 2 AND GA."FormPaymentId" = 2 AND GA."DoneTransfer" IS FALSE) AS "TotalGuestTransferNotConfirm",
			SUM(GA."TotalPrice") FILTER (WHERE GA."GuestActionTypeId" = 3 AND GA."DoneTransfer" IS TRUE) AS "TotalPayForGuest"
		FROM "GuestAction" GA
		WHERE GA."IsDeleted" IS FALSE
			AND GA."ShiftDistributeId" = p_shift_dis_id
	),
	tmp8 AS (
		SELECT
			W."WinningTypeId",
			WT."WinningTypeName",
			SUM(W."WinningPrice") AS "WinningPrice"
		FROM "Winning" W
			JOIN "WinningType" WT ON WT."WinningTypeId" = W."WinningTypeId"
		WHERE W."ShiftDistributeId" = p_shift_dis_id
		GROUP BY
			W."WinningTypeId",
			WT."WinningTypeName"
	),
	tmp9 AS (
		SELECT
			SUM(W."WinningPrice") AS "WinningPrice",
			array_to_json(array_agg(W.*)) AS "WinningData"
		FROM tmp8 W
	),
	tmp10 AS (
		SELECT 
			SUM(T."TotalSoldMoney") AS "TotalSoldMoney"
		FROM tmp5 T
	),
	tmp11 AS (
		SELECT 
			SUM(T."TotalPrice") AS "TotalFeeOutSite"
		FROM crm_sale_point_get_list_fee_outsite(NOW()::TIMESTAMP, 0, p_shift_dis_id) T
	),
	tmp12 AS (
		SELECT
			0 AS "TotalGuestDebt",
			COALESCE(tmp7."TotalTransferForGuestConfirm", 0) AS "TotalTransferForGuestConfirm",
			COALESCE(tmp7."TotalTransferForGuestNotConfirm", 0) AS "TotalTransferForGuestNotConfirm",
			COALESCE(tmp7."TotalGuestPay", 0) AS "TotalGuestPay",
			COALESCE(tmp7."TotalGuestTransferConfirm", 0) AS "TotalGuestTransferConfirm",
			COALESCE(tmp7."TotalGuestTransferNotConfirm", 0) AS "TotalGuestTransferNotConfirm",
			COALESCE(tmp7."TotalPayForGuest", 0) AS "TotalPayForGuest",
			COALESCE(tmp9."WinningPrice", 0) AS "WinningPrice",
			COALESCE(tmp11."TotalFeeOutSite", 0) AS "TotalFeeOutSite",
			tmp9."WinningData",
			COALESCE(tmp10."TotalSoldMoney", 0) AS "TotalSoldMoney"
		FROM tmp6, tmp7, tmp9, tmp10, tmp11
	),
	tmp13 AS (
		SELECT 
			T.*,
			(T."TotalSoldMoney"
			- T."TotalGuestDebt"
			+ T."TotalTransferForGuestConfirm"
			+ T."TotalGuestPay"
			- T."TotalPayForGuest"
		
			- T."TotalFeeOutSite"
			- T."WinningPrice"
			) AS "TotalMoneyReturn"
		FROM tmp12 T
	)
	SELECT 
	(
		SELECT 
			TO_JSONB(T.*)
		FROM tmp13 T
	)::TEXT AS "MoneyData",
	(
		SELECT
			array_to_json(array_agg(TO_JSONB(T.*)))
		FROM tmp5 T WHERE T."LotteryDate" IS NULL
	)::TEXT AS "LotteryData";

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_delete_shift_transfer(p_action_by integer, p_action_by_name character varying, p_shift_distribute_id integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_check INT;
	v_user_id INT;
	v_user_name VARCHAR;
	v_salepoint_id INT;
BEGIN
	SELECT "ShiftDistributeId"
		INTO v_check
		FROM "ShiftTransfer" WHERE "ShiftDistributeId"= p_shift_distribute_id LIMIT 1;
	
	IF(COALESCE(v_check, 0)=0) THEN
		v_id := -1;
		v_mess := 'Ca làm việc chưa kết thúc HOẶC đã được huỷ kết ca';
	ELSE
		--GET DATA
		SELECT
			SD."SalePointId",
			SD."UserId",
			U."FullName"
		INTO
			v_salepoint_id,
			v_user_id,
			v_user_name
		FROM "ShiftDistribute" SD 
			JOIN "User" U ON U."UserId" = Sd."UserId"
			WHERE SD."ShiftDistributeId"  = v_check;
		--DELETE
		DELETE FROM "ShiftTransfer" 
			WHERE "ShiftDistributeId" = v_check;
		--INSERT LOG
		INSERT INTO "ShiftTransferLog" (
			"ActionBy",
			"ActionByName",
			"ActionDate",
			"ShiftDistributeId",
			"SalePointId",
			"UserId",
			"UserName"
		)VALUES(
			p_action_by,
			p_action_by_name,
			NOW(),
			v_check,
			v_salepoint_id,
			v_user_id,
			v_user_name
		);
		 
		v_id := 1;
		v_mess := 'Thao tác thành công';
	END IF;
	

	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_all_lottery_data_of_staff(p_month text)
 RETURNS TABLE("TotalProfitInAMonth" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	with tmp AS(
		SELECT SUM(SPL."TotalValue") AS "TotalLotteryValue" FROM "SalePointLog" SPL 
			WHERE TO_CHAR(SPL."LotteryDate",'YYYY-MM') = p_month AND "LotteryTypeId" = 1
	), tmp2 AS(
		SELECT SPL."TotalValue" AS "TotalSratchLotteryValue" FROM "SalePointLog" SPL 
			WHERE TO_CHAR(SPL."LotteryDate",'YYYY-MM') = p_month AND "LotteryTypeId" = 3
	), tmp3 AS(
		SELECT SUM(T."Price") AS "TotalVietlot" FROM "Transaction" T 
			WHERE TO_CHAR(T."Date",'YYYY-MM') = p_month AND T."TransactionTypeId" = 2
	), tmp4 AS(
		SELECT SUM(T."Price") AS "TotalLoto" FROM "Transaction" T 
			WHERE TO_CHAR(T."Date",'YYYY-MM') = p_month AND T."TransactionTypeId" = 3
	)
	SELECT json_build_object('TotalVietlot',T."TotalVietlot")::TEXT FROM tmp3 T
	UNION
	SELECT json_build_object('TotalLotteryValue',T."TotalLotteryValue")::TEXT FROM tmp T
	UNION 
	SELECT json_build_object('TotalSratchLotteryValue',T."TotalSratchLotteryValue")::TEXT FROM tmp2 T
	UNION
	SELECT json_build_object('TotalLoto',T."TotalLoto")::TEXT FROM tmp4 T;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_data_item_in_month(p_month character varying)
 RETURNS TABLE("ItemId" integer, "SalePointId" integer, "TotalReceive" integer, "TotalRemaining" integer, "Import" integer, "Export" integer, "Use" integer, "ImportPrice" integer, "ExportPrice" integer, "UsePrice" integer, "Quotation" integer, "Note" text, "ActionBy" integer, "ActionByName" character varying, "ActionDate" timestamp without time zone, "BalancePrice" integer, "AVGPrice" numeric, "TypeOfItemId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_last_date DATE := ((p_month||'-01' )::date  + interval '1 month' - interval '1 day')::date;
	v_pre_last_date DATE := ((p_month||'-01' )::date - interval '1 day')::date;
	v_check BOOLEAN:= (SELECT COALESCE(IL."TotalRemaining",0)::BOOLEAN FROM "ItemFullLog" IL WHERE IL."Month"::date <= ((p_month||'-01' )::date  + interval '1 month' - interval '1 day')::date AND  IL."Month"::date >= (p_month||'-01' )::date LIMIT 1);
BEGIN
	

	--Lấy số tồn cuối tháng 
	
		
	IF v_check IS NOT NULL THEN 
	RETURN QUERY
	WITH tmp AS(
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY IL."SalePointId", IL."ItemId" ORDER BY "CreateDate"::TIMESTAMP DESC) AS "Id",
				IL."ItemId",
				IL."SalePointId",
				IL."TotalRemaining",
				IL."CreateDate",
				IL."Quantity",
				IL."ItemTypeId",
				IL."TotalPrice",
				IL."Month",
				COALESCE(IL."ModifyBy", IL."CreateBy") AS "ActionBy",
				COALESCE(IL."ModifyByName", IL."CreateByName") AS "ActionByName",
				COALESCE(IL."ModifyDate", IL."CreateDate") AS "ActionDate",
				IL."BalancePrice"
		FROM "ItemFullLog" IL
		WHERE IL."Month"::date <= ((p_month||'-01' )::date  + interval '1 month' - interval '1 day')::date
		AND  IL."Month"::date >= (p_month||'-01' )::date
	
	),

		--Lấy số tồn đầu tháng 
	tmp1 AS(
			SELECT 
				ROW_NUMBER() OVER(PARTITION BY IL."SalePointId", IL."ItemId" ORDER BY "CreateDate" DESC) AS "Id",
					IL."ItemId",
					IL."SalePointId",
					IL."TotalRemaining",
					IL."CreateDate",
					IL."Month"
			FROM "ItemFullLog" IL
			WHERE IL."Month"::date <= ((p_month||'-01' )::date - interval '1 day')::date
	),
	--Tính tổng sử dụng trong tháng
	tmp2 AS(
		SELECT 
			tm."SalePointId",
			tm."ItemId",
			SUM(tm."Quantity") FILTER(WHERE tm."ItemTypeId" =1) AS "Import",
			SUM(tm."Quantity") FILTER(WHERE tm."ItemTypeId" =2) AS "Export",
			SUM(tm."Quantity") FILTER(WHERE tm."ItemTypeId" =3) AS "Use",
			SUM(tm."TotalPrice") FILTER(WHERE tm."ItemTypeId" =1) AS "ImportPrice",
			SUM(tm."TotalPrice") FILTER(WHERE tm."ItemTypeId" =2) AS "ExportPrice",
			SUM(tm."TotalPrice") FILTER(WHERE tm."ItemTypeId" =3) AS "UsePrice"
		FROM tmp tm
		WHERE date_part('month',  tm."Month")  = date_part('month',  to_date(p_month, 'YYYY-MM'))
		GROUP BY tm."SalePointId", tm."ItemId"
),
	tmp3 AS(
		SELECT
			COALESCE(A."ItemId",B."ItemId") AS "ItemId",
			COALESCE(A."SalePointId",B."SalePointId") AS "SalePointId",
			COALESCE(B."TotalRemaining",0) ::INT AS "TotalReceive",
			COALESCE(A."TotalRemaining",0) ::INT AS "TotalRemaining",
			COALESCE(C."Import",0)::INT AS "Import",
 			COALESCE(C."Export",0)::INT  AS "Export",
 			COALESCE(C."Use",0)::INT  AS "Use",
			COALESCE(C."ImportPrice",0)::INT  AS "ImportPrice",
 			COALESCE(C."ExportPrice",0)::INT  AS "ExportPrice",
 			COALESCE(C."UsePrice",0)::INT  AS "UsePrice",
			I."Quotation",
			(CASE WHEN COALESCE(A."TotalRemaining",0) / I."Quotation" = 0 THEN 'Cần bổ sung gấp'
					WHEN COALESCE(A."TotalRemaining",0) ::NUMERIC /I."Quotation" > 0.5 THEN 'Đủ Dùng'
					ELSE 'Cần bổ sung gấp'
				END ) AS "Note",
			A."ActionBy",
			A."ActionByName",
			A."ActionDate",
			A."BalancePrice",
			(CASE WHEN A."TotalRemaining"= 0 THEN I."Price" ELSE A."BalancePrice" / A."TotalRemaining" END):: NUMERIC AS AVGPrice,
			TOI."TypeOfItemId"
		FROM tmp A
			FULL JOIN tmp1 B ON A."SalePointId"= B."SalePointId" AND A."ItemId"= B."ItemId" AND A."Id"= B."Id"
			LEFT JOIN tmp2 C ON COALESCE(A."SalePointId",B."SalePointId") = C."SalePointId" AND COALESCE(A."ItemId",B."ItemId")= C."ItemId"
			LEFT JOIN "Item" I ON  A."ItemId"=I."ItemId" 
			JOIN "TypeOfItem" TOI ON  TOI."TypeOfItemId"= I."TypeOfItemId" 
		WHERE 
			A."Id" =1 OR B."Id" = 1
	)
		SELECT * FROM tmp3;
ELSE 
	RETURN QUERY
	WITH tmp AS(
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY IL."SalePointId", IL."ItemId" ORDER BY "CreateDate"::TIMESTAMP DESC) AS "Id",
				IL."ItemId",
				IL."SalePointId",
				IL."TotalRemaining",
				IL."CreateDate",
				IL."Quantity",
				IL."ItemTypeId",
				IL."TotalPrice",
				IL."Month",
				COALESCE(IL."ModifyBy", IL."CreateBy") AS "ActionBy",
				COALESCE(IL."ModifyByName", IL."CreateByName") AS "ActionByName",
				COALESCE(IL."ModifyDate", IL."CreateDate") AS "ActionDate",
				IL."BalancePrice"
		FROM "ItemFullLog" IL
		WHERE IL."Month"::date <= ((p_month||'-01' )::date  + interval '1 month' - interval '1 day')::date
	
	),

		--Lấy số tồn đầu tháng 
	tmp1 AS(
			SELECT 
				ROW_NUMBER() OVER(PARTITION BY IL."SalePointId", IL."ItemId" ORDER BY "CreateDate" DESC) AS "Id",
					IL."ItemId",
					IL."SalePointId",
					IL."TotalRemaining",
					IL."CreateDate",
					IL."Month"
			FROM "ItemFullLog" IL
			WHERE IL."Month"::date <= ((p_month||'-01' )::date - interval '1 day')::date
	),
	--Tính tổng sử dụng trong tháng
	tmp2 AS(
		SELECT 
			tm."SalePointId",
			tm."ItemId",
			SUM(tm."Quantity") FILTER(WHERE tm."ItemTypeId" =1) AS "Import",
			SUM(tm."Quantity") FILTER(WHERE tm."ItemTypeId" =2) AS "Export",
			SUM(tm."Quantity") FILTER(WHERE tm."ItemTypeId" =3) AS "Use",
			SUM(tm."TotalPrice") FILTER(WHERE tm."ItemTypeId" =1) AS "ImportPrice",
			SUM(tm."TotalPrice") FILTER(WHERE tm."ItemTypeId" =2) AS "ExportPrice",
			SUM(tm."TotalPrice") FILTER(WHERE tm."ItemTypeId" =3) AS "UsePrice"
		FROM tmp tm
		WHERE date_part('month',  tm."Month")  = date_part('month',  to_date(p_month, 'YYYY-MM'))
		GROUP BY tm."SalePointId", tm."ItemId"
),
	tmp3 AS(
		SELECT
			COALESCE(A."ItemId",B."ItemId") AS "ItemId",
			COALESCE(A."SalePointId",B."SalePointId") AS "SalePointId",
			COALESCE(B."TotalRemaining",0) ::INT AS "TotalReceive",
			COALESCE(A."TotalRemaining",0) ::INT AS "TotalRemaining",
			COALESCE(C."Import",0)::INT AS "Import",
 			COALESCE(C."Export",0)::INT  AS "Export",
 			COALESCE(C."Use",0)::INT  AS "Use",
			COALESCE(C."ImportPrice",0)::INT  AS "ImportPrice",
 			COALESCE(C."ExportPrice",0)::INT  AS "ExportPrice",
 			COALESCE(C."UsePrice",0)::INT  AS "UsePrice",
			I."Quotation",
			(CASE WHEN COALESCE(A."TotalRemaining",0) / I."Quotation" = 0 THEN 'Cần bổ sung gấp'
					WHEN COALESCE(A."TotalRemaining",0) ::NUMERIC /I."Quotation" > 0.5 THEN 'Đủ Dùng'
					ELSE 'Cần bổ sung gấp'
				END ) AS "Note",
			A."ActionBy",
			A."ActionByName",
			A."ActionDate",
			A."BalancePrice",
			(CASE WHEN A."TotalRemaining"= 0 THEN I."Price" ELSE A."BalancePrice" / A."TotalRemaining" END):: NUMERIC AS AVGPrice,
			TOI."TypeOfItemId"
		FROM tmp A
			FULL JOIN tmp1 B ON A."SalePointId"= B."SalePointId" AND A."ItemId"= B."ItemId" AND A."Id"= B."Id"
			LEFT JOIN tmp2 C ON COALESCE(A."SalePointId",B."SalePointId") = C."SalePointId" AND COALESCE(A."ItemId",B."ItemId")= C."ItemId"
			LEFT JOIN "Item" I ON  A."ItemId"=I."ItemId" 
			JOIN "TypeOfItem" TOI ON  TOI."TypeOfItemId"= I."TypeOfItemId" 
		WHERE 
			A."Id" =1 OR B."Id" = 1
	)
		SELECT * FROM tmp3;
END IF;
END;
$function$


