			WHERE (COALESCE(p_salepoint_id, 0 ) = 0 OR p_salepoint_id = WW."SalePointId")
			GROUP BY
				WW."SalePointId",
				SPS."SalePointName";
	END IF;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_list_exempt_kpi(p_month character varying)
