CREATE OR REPLACE FUNCTION public.crm_report_get_list_report_sales(p_date character varying)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "TotalShiftRegister" integer, "TotalShiftFinished" integer, "TotalCall" integer, "TotalTrial" integer, "TotalSales" integer)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	WITH tmp AS
	(
			-- lấy ra các ca làm trong tháng
			SELECT 
				CH."RequestFromUserId",
				CH."ShiftId",
				CH."CallDateTime"::DATE,
				U."UserId",
				COALESCE(U."FullName", U."FullNameEN") AS "FullName",
				COUNT(CH."ShiftId") AS "Count",
				SUM("CallDuration") AS "TotalCallDuration",
				U."CallRequireTypeId"
			FROM "CallHistory" CH 
			LEFT JOIN "User" U ON U."UserId" = substring(CH."RequestFromUserId" FROM '\d+')::INT4
			LEFT JOIN "UserRole" UR ON UR."UserId" = U."UserId"
			LEFT JOIN "UserTitle" UT ON UT."UserTitleId" = ANY(UR."UserTitleIds") AND UT."UserTitleId" IN (2,9)
			WHERE to_char(CH."CallDateTime", 'YYYY-MM') = p_date
				AND UT."UserTitleId" IN (2,9)
			GROUP BY 
				CH."ShiftId",
				CH."CallDateTime"::DATE,
				CH."RequestFromUserId",
				U."UserId"
			ORDER BY CH."RequestFromUserId"
	), tmp1 AS
	(
			-- tổng số ca đi làm
			SELECT 
				T."UserId",
				T."FullName",
				COUNT(T."UserId") AS "TotalShiftRegister",
				COUNT(T."UserId") FILTER (WHERE T."Count" >= CR."CallRequire" AND T."TotalCallDuration" >= CR."CallDurationRequire") AS "TotalShiftFinished"
			FROM tmp T
			LEFT JOIN "CallRequireType" CR ON T."CallRequireTypeId" = CR."CallRequireTypeId"
			GROUP BY 
				T."RequestFromUserId",
				T."UserId",
				T."FullName" 
	),tmp2 AS 
	(	
			-- Lấy ra tổng số cuộc gọi trong tháng
			SELECT 
				U."UserId",
				COALESCE(U."FullName",U."FullNameEN") AS "FullName",
				COUNT( CH."CallHistoryId")AS "Total_Call"
			FROM "CallHistory" CH
			LEFT JOIN "User" U ON U."UserId" = substring(CH."RequestFromUserId" FROM '\d+')::INT4
			LEFT JOIN "UserRole" UR ON UR."UserId" = U."UserId"
			LEFT JOIN "UserTitle" UT ON UT."UserTitleId" = ANY(UR."UserTitleIds")
			WHERE to_char(CH."CallDateTime", 'YYYY-MM') = p_date
				AND UT."UserTitleId" IN (2,9)
			GROUP 
			BY "RequestFromUserId",
			U."UserId"
	), tmp3 AS 
	(
			-- tổng số trial/tổng số sales
			SELECT 
				C."SaleId" AS "UserId",
				COUNT(*) FILTER (WHERE C."ClassTypeId" = 1) AS "TotalTrial",
				COUNT(*) FILTER (WHERE C."ClassTypeId" = 2) AS "TotalSales"
			FROM "Class" C
			WHERE to_char(C."CreatedDate", 'YYYY-MM') = p_date 
			GROUP BY C."SaleId"
	)
	SELECT 
	 T1."UserId",
	 T1."FullName",
	 COALESCE(T1."TotalShiftRegister",0)::INT4,
	 COALESCE(T1."TotalShiftFinished",0)::INT4,
	 COALESCE(T2."Total_Call",0)::INT4,
	 COALESCE(T3."TotalTrial",0)::INT4,
	 COALESCE(T3."TotalSales",0)::INT4
	FROM tmp1 T1
	LEFT JOIN tmp2 T2 ON T1."UserId" = T2."UserId"
	LEFT JOIN tmp3 T3 ON T3."UserId" = T1."UserId"
	ORDER BY T1."UserId";
	
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_list_sale_for_update(p_sale_point_id integer, p_shift_id integer, p_date timestamp without time zone)
 RETURNS TABLE("LotteryInfo" text, "SalePointInfo" text, "HistoryInfo" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_shift_dis_id INT := (
					SELECT 
						SD."ShiftDistributeId"
					FROM "ShiftDistribute" SD
					WHERE
						SD."ShiftId"=p_shift_id AND SD."SalePointId"= p_sale_point_id AND SD."DistributeDate"=p_date :: DATE) ::INT;
BEGIN
RETURN QUERY
	WITH tmp0 AS
	(
		SELECT array_to_json(ARRAY_AGG(R)) :: TEXT
		FROM
			(SELECT *FROM
				(
					(
						SELECT 
							LC."LotteryChannelTypeId",
							I."SalePointId",
							I."LotteryDate",
							LC."LotteryChannelId",
							LC."ShortName",
							I."TotalRemaining",
							I."TotalDupRemaining",
							FALSE AS "IsScratchcard"
						FROM "Inventory" I,  "LotteryChannel" LC
						WHERE 
							(I."LotteryDate" = p_date ::DATE OR I."LotteryDate" = (p_date ::DATE + INTERVAL '1 DAY') ::DATE)
							AND I."SalePointId" = p_sale_point_id
							AND I."LotteryChannelId" = LC."LotteryChannelId"
							AND (I."TotalRemaining" > 0 OR I."TotalDupRemaining" > 0)
					) 
					UNION
					(
						SELECT 
							LC."LotteryChannelTypeId",
							SC."SalePointId",
							'9999-01-01' AS "LotteryDate",
							LC."LotteryChannelId",
							LC."ShortName",
							SC."TotalRemaining" ,
							0 AS "TotalDupRemaining",
							TRUE AS "IsScratchcard"
						FROM "Scratchcard" SC
							JOIN "LotteryChannel" LC ON LC."LotteryChannelId"= SC."LotteryChannelId"
						WHERE SC."SalePointId"= p_sale_point_id
					
					)
				) A
				ORDER BY  A."LotteryDate", A."LotteryChannelTypeId"
			)R
	),
	tmp1 AS
	(
			SELECT ROW_TO_JSON(R) :: TEXT
			FROM
				(
					SELECT 
						SD."ShiftDistributeId",
						SD."UserId",
						SD."SalePointId",
						SD."ShiftId",
						COALESCE((SELECT TRUE FROM "ShiftTransfer" WHERE "ShiftDistributeId" = SD."ShiftDistributeId" LIMIT 1),FALSE) AS "IsEndOfShift"
					FROM "ShiftDistribute" SD
					WHERE
						SD."ShiftId"=p_shift_id 
						AND SD."SalePointId"= p_sale_point_id 
						AND SD."DistributeDate"=p_date :: DATE
				) R
	),
	tmp2 AS
	(
		SELECT array_to_json(ARRAY_AGG(R)) :: TEXT
		FROM
			(
				SELECT 
					SPL."ShiftDistributeId",
					SPL."SalePointLogId",
					SPL."LotteryDate",
					SPL."LotteryChannelId",
					SPL."LotteryTypeId",
					LT."LotteryTypeName",
					LC."ShortName",
					SPL."Quantity",
					SPL."TotalValue",
					SPL."ActionDate"
				FROM	"SalePointLog" SPL
					JOIN "LotteryChannel" LC ON SPL."LotteryChannelId" = LC."LotteryChannelId"
					JOIN "LotteryType" LT ON LT."LotteryTypeId"= SPL."LotteryTypeId"
				WHERE SPL."ShiftDistributeId" = v_shift_dis_id 
				  AND SPL."IsDeleted" IS FALSE 
				ORDER BY SPL."LotteryDate"
			)R
	)
	SELECT 
		*
	FROM tmp0, tmp1, tmp2;
		

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_list_total_number_of_tickets_of_each_manager(p_month character varying)
 RETURNS TABLE("FullName" character varying, "LotteryDate" date, "SalePointId" integer, "TotalReceived" bigint, "ManagerId" integer)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	WITH tmp AS
	(
			SELECT
				I."LotteryDate",
				I."SalePointId",
				SUM(I."TotalDupReceived") + SUM(I."TotalReceived") AS "TotalReceived",
				(SELECT FN."ManagerId" FROM crm_sale_point_manage_v2(I."LotteryDate") FN
					WHERE FN."SalePointId" = I."SalePointId" AND FN."ManagerId" IS NOT NULL
					LIMIT 1) AS "ManagerId"
			FROM "InventoryLog" I 
			WHERE TO_CHAR( I."LotteryDate", 'YYYY-MM' ) = p_month
			GROUP BY 
				I."LotteryDate",
				I."SalePointId"
	)
	SELECT
		U."FullName",
		T.*
	FROM tmp T 
	LEFT JOIN "User" U ON U."UserId" = T."ManagerId"
	GROUP BY 
		T."LotteryDate",
		T."SalePointId",
		T."ManagerId",
		T."TotalReceived",
		U."FullName";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_list_unsold_lottery_ticket(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "TotalRemaining" bigint)
 LANGUAGE plpgsql
AS $function$

BEGIN
	
	RETURN QUERY
	SELECT
		I."SalePointId",
		COALESCE( S."SalePointName",'Kho') AS "SalePointName",
		I."LotteryDate",
		(SUM(I."TotalRemaining") + SUM(I."TotalDupRemaining")) AS "TotalRemaining"
	FROM "Inventory" I 
	LEFT JOIN "SalePoint" S ON I."SalePointId" = S."SalePointId"
	WHERE 
		TO_CHAR(I."LotteryDate", 'YYYY-MM') = p_month
		AND I."SalePointId" > 0
	GROUP BY 
		I."LotteryDate",
		I."SalePointId",
		S."SalePointName"
	ORDER BY I."LotteryDate";
		
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_log_distribute_for_salepoint(p_date timestamp without time zone, p_sale_point_id integer DEFAULT 0)
 RETURNS TABLE("LotteryDate" date, "LotteryChannelId" integer, "TotalReceived" bigint, "TotalDupReceived" bigint, "SalePointId" integer, "ActionDate" timestamp without time zone, "ShortName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
RETURN QUERY

SELECT
	IL."LotteryDate",
	IL."LotteryChannelId",
	SUM(IL."TotalReceived"),
	SUM(IL."TotalDupReceived"),
	IL."SalePointId",
	IL."ActionDate",
	LC."ShortName"
FROM "InventoryDetailLog" IL 
	JOIN "LotteryChannel" LC ON LC."LotteryChannelId"= IL."LotteryChannelId"
WHERE IL."LotteryDate" = p_date::DATE 
	AND ((COALESCE(p_sale_point_id, 0) = 0) OR IL."SalePointId" = p_sale_point_id)
GROUP BY
	IL."LotteryDate",
	IL."LotteryChannelId",
	IL."ActionDate",
	IL."SalePointId",
	LC."ShortName",
	LC."LotteryChannelTypeId"
ORDER BY
	IL."ActionDate" DESC,
	LC."LotteryChannelTypeId";
	
		
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_lottery_sell_in_month(p_month character varying)
 RETURNS TABLE("VT" text, "VTr" text, "VC" text, "SUM" text, "SUMF" text, "Month" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_last_date DATE := ((p_month||'-01' )::date  + interval '1 month' - interval '1 day')::date;
	v_pre_last_date DATE := ((p_month||'-01' )::date - interval '1 day')::date;

BEGIN
	RETURN QUERY
	
		WITH tmp AS(
			SELECT 
				SPL."SalePointId",
				SPL."ActionDate" :: DATE,
				SPL."LotteryTypeId",
				SUM(SPL."Quantity") AS "TotalQuatity",
				SUM(SPL."TotalValue") AS "TotalValue",
				COALESCE(SUM(SPL."Quantity") FILTER (WHERE SPL."LotteryPriceId"= 1),0) AS "Wholesale",
				COALESCE(SUM(SPL."Quantity") FILTER (WHERE SPL."LotteryPriceId"<> 1),0) AS "Retail",
				COALESCE(SUM(SPL."TotalValue") FILTER (WHERE SPL."LotteryPriceId"= 1),0) AS "TotalWholesale",
				COALESCE(SUM(SPL."TotalValue") FILTER (WHERE SPL."LotteryPriceId"<> 1),0) AS "TotalRetail"
			FROM "SalePointLog" SPL 
			WHERE TO_CHAR("ActionDate",'YYYY-MM')= p_month
			GROUP BY SPL."SalePointId", SPL."ActionDate":: DATE, SPL."LotteryTypeId"
			ORDER BY SPL."SalePointId", SPL."ActionDate":: DATE, SPL."LotteryTypeId"
		), 
		tmp1 AS(
			SELECT array_to_json(array_agg(tm))::TEXT AS "VT"
			FROM (
				SELECT * FROM tmp
				WHERE "LotteryTypeId" = 1
			) tm 
		),
		tmp2 AS(
			SELECT array_to_json(array_agg(tm))::TEXT AS "VTr"
			FROM (
				SELECT * FROM tmp
				WHERE "LotteryTypeId" = 2
			) tm 
		),
		tmp3 AS(
			SELECT array_to_json(array_agg(tm))::TEXT AS "VC"
			FROM (
				SELECT * FROM tmp
				WHERE "LotteryTypeId" = 3
			) tm 
		),
		tmp4 AS(
			SELECT array_to_json(array_agg(sm))::TEXT AS "SUM"
			FROM (
				SELECT					
					tm."SalePointId",
					tm."ActionDate",
					SUM(tm."TotalQuatity") AS "TotalQuatity",
					SUM(tm."TotalValue") AS "TotalValue",
					SUM(tm."Wholesale") AS "Wholesale",
					SUM(tm."Retail") AS "Retail",
					SUM(tm."TotalWholesale") AS "TotalWholesale",
					SUM(tm."TotalRetail") AS "TotalRetail"
				FROM tmp tm
				GROUP BY
					tm."SalePointId", tm."ActionDate":: DATE
				ORDER BY
					tm."SalePointId", tm."ActionDate":: DATE
			) sm 
		),
		tmp5 AS(
			SELECT array_to_json(array_agg(sm))::TEXT AS "SUMF"
			FROM (
				SELECT					
					tm."SalePointId",
					SUM(tm."TotalQuatity") AS "TotalQuatity",
					SUM(tm."TotalValue") AS "TotalValue",
					SUM(tm."Wholesale") AS "Wholesale",
					SUM(tm."Retail") AS "Retail",
					SUM(tm."TotalWholesale") AS "TotalWholesale",
					SUM(tm."TotalRetail") AS "TotalRetail"
				FROM tmp tm
				GROUP BY
					tm."SalePointId"
				ORDER BY
					tm."SalePointId"
			) sm 
		)
		
		SELECT 
			tmp1."VT",
			tmp2."VTr",
			tmp3."VC", 
			tmp4."SUM",
			tmp5."SUMF",
			p_month
		FROM tmp1, tmp2, tmp3, tmp4, tmp5;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_remain_of_all_salepoint_in_date()
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "TotalRemainingTheNorth" bigint, "TotalRemainingTheCentral" bigint, "TotalRemainingTheSouth" bigint)
 LANGUAGE plpgsql
AS $function$

BEGIN
	
	RETURN QUERY
	WITH Total AS (
	SELECT 
		SP."SalePointId", 
		SP."SalePointName", 
		SUM(I."TotalRemaining" + I."TotalDupRemaining") FILTER (WHERE L."RegionId" = 1) AS "TotalRemainingTheNorth",
		SUM(I."TotalRemaining" + I."TotalDupRemaining") FILTER (WHERE L."RegionId" = 3) AS "TotalRemainingTheCentral",
		SUM(I."TotalRemaining" + I."TotalDupRemaining") FILTER (WHERE L."RegionId" = 2) AS "TotalRemainingTheSouth"
	FROM "SalePoint" SP JOIN "Inventory" I ON SP."SalePointId" = I."SalePointId"
	LEFT JOIN "LotteryChannel" L ON L."LotteryChannelId" = I."LotteryChannelId"
	LEFT JOIN "Region" R ON R."RegionId" = L."RegionId"
	WHERE TO_CHAR(I."LotteryDate", 'YYYY-MM-DD') :: DATE = NOW()::DATE 
	--		OR TO_CHAR(I."LotteryDate", 'YYYY-MM-DD') :: DATE = (NOW() + INTERVAL '1 DAY')::DATE ) 
	GROUP BY SP."SalePointId", SP."SalePointName"
	ORDER BY SP."SalePointId")
	SELECT 
		SP."SalePointId", 
		SP."SalePointName",
		COALESCE( T."TotalRemainingTheNorth",0) AS "TotalRemainingTheNorth",
		COALESCE(T."TotalRemainingTheCentral",0) AS "TotalRemainingTheCentral",
		COALESCE( T."TotalRemainingTheSouth",0) AS "TotalRemainingTheSouth"
	FROM "SalePoint" SP LEFT JOIN Total T ON SP."SalePointId" = T."SalePointId"
	WHERE SP."IsActive" IS TRUE
	ORDER BY SP."SalePointId";	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_remain_of_salepoint_in_date(p_salepoint_id integer, p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryDate" date, "TotalRemaining" integer, "TotalDupRemaining" integer)
 LANGUAGE plpgsql
AS $function$

BEGIN
	
	RETURN QUERY
	SELECT 
		SP."SalePointId", 
		SP."SalePointName", 
		I."LotteryDate", 
		I."TotalRemaining", 
		I."TotalDupRemaining"
	FROM "SalePoint" SP JOIN "Inventory" I ON SP."SalePointId" = I."SalePointId"
	WHERE TO_CHAR(I."LotteryDate", 'YYYY-MM') = p_month 
		AND (COALESCE(p_salepoint_id, 0) = 0 OR I."SalePointId" = p_salepoint_id)
	ORDER BY SP."SalePointId", I."LotteryDate";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_salary_of_user(p_user_id bigint, p_month character varying)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "UserTitleId" integer, "UserTitleName" character varying, "SalaryData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	SELECT F.* FROM crm_get_salary_of_user_by_month(p_month) F
	WHERE F."UserId" = p_user_id or p_user_id = -1 AND F."UserId" <> 0;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_not_sell_by_date(p_date timestamp without time zone, p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryChannelId" integer, "LotteryChannelName" character varying, "TotalRemaining" bigint, "TotalDupRemaining" bigint, "DataShift" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	IF p_month IS NULL THEN
		RETURN QUERY
		WITH tmp AS (
			SELECT 
				I."SalePointId", 
				SPP."SalePointName",
				I."LotteryChannelId",
				LC."LotteryChannelName", 
				COALESCE(SUM(CASE WHEN SFF."LotteryTypeId"=1 THEN SFF."TotalReturns" END), 0) AS "TotalRemaining",
				COALESCE(SUM(CASE WHEN SFF."LotteryTypeId"=2 THEN SFF."TotalReturns" END), 0) AS "TotalDupRemaining",
				(
					SELECT array_to_json(ARRAY_AGG (r))
					FROM
					(
						SELECT
							SD."ShiftId", 
							SD."ShiftDistributeId",
							I."LotteryChannelId",
							COALESCE(SUM(SF."TotalTrans"),0) AS "TotalTrans"
						FROM "ShiftDistribute" SD
							LEFT JOIN "ShiftTransfer" SF ON (SD."ShiftDistributeId" = SF."ShiftDistributeId" AND SF."LotteryChannelId" =  I."LotteryChannelId")
						WHERE SD."DistributeDate" = p_date :: DATE
							AND SD."SalePointId" = I."SalePointId"	
						GROUP BY 
							SD."ShiftId", 
							SD."ShiftDistributeId",
							SF."LotteryChannelId"
						ORDER BY SD."ShiftId"
					) r
				) :: TEXT AS "DataShift"
			FROM "SalePoint" SP
				JOIN "ShiftDistribute" SDD ON (SP."SalePointId" = SDD."SalePointId" AND SDD."DistributeDate" = p_date :: DATE)
				JOIN "ShiftTransfer" SFF ON (SDD."ShiftDistributeId" = SFF."ShiftDistributeId" AND SFF."LotteryTypeId" <> 3)
				RIGHT JOIN "Inventory" I ON (SFF."LotteryChannelId" = I."LotteryChannelId"
																		AND SFF."LotteryDate" = I."LotteryDate"
																		AND I."SalePointId" = SP."SalePointId")
				JOIN "LotteryChannel" LC ON I."LotteryChannelId" = LC."LotteryChannelId"
				JOIN "SalePoint" SPP ON I."SalePointId" = SPP."SalePointId"

			WHERE I."SalePointId" <> 0
				AND (I."LotteryDate" = p_date::DATE OR I."LotteryDate" = ((p_date ::DATE) + INTERVAL '1 DAY')) 
				AND SP."IsActive" IS TRUE
			GROUP BY 
				I."SalePointId",
				SPP."SalePointName",
				I."LotteryChannelId",
				LC."LotteryChannelName"
			ORDER BY I."SalePointId", I."LotteryChannelId"
		)
		SELECT
			A."SalePointId",
			A."SalePointName",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."TotalRemaining",
			A."TotalDupRemaining",
			A."DataShift"
		FROM tmp A
			JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
		ORDER BY A."SalePointId", LC."LotteryChannelTypeId", A."LotteryChannelId";
	ELSE
		RETURN QUERY
		WITH tmp AS (
			SELECT 
				I."SalePointId", 
				SPP."SalePointName",
				I."LotteryChannelId",
				LC."LotteryChannelName", 
				COALESCE(SUM(CASE WHEN SFF."LotteryTypeId"=1 THEN SFF."TotalReturns" END), 0) AS "TotalRemaining",
				COALESCE(SUM(CASE WHEN SFF."LotteryTypeId"=2 THEN SFF."TotalReturns" END), 0) AS "TotalDupRemaining",
				NULL AS "DataShift"
			FROM "SalePoint" SP
				JOIN "ShiftDistribute" SDD ON (SP."SalePointId" = SDD."SalePointId" AND TO_CHAR(SDD."DistributeDate",'YYYY-MM') = p_month)
				JOIN "ShiftTransfer" SFF ON (SDD."ShiftDistributeId" = SFF."ShiftDistributeId" AND SFF."LotteryTypeId" <> 3)
				RIGHT JOIN "Inventory" I ON (SFF."LotteryChannelId" = I."LotteryChannelId"
																		AND SFF."LotteryDate" = I."LotteryDate"
																		AND I."SalePointId" = SP."SalePointId")
				JOIN "LotteryChannel" LC ON I."LotteryChannelId" = LC."LotteryChannelId"
				JOIN "SalePoint" SPP ON I."SalePointId" = SPP."SalePointId"

			WHERE I."SalePointId" <> 0 AND (TO_CHAR(I."LotteryDate", 'YYYY-MM')= p_month) AND SP."IsActive" IS TRUE
			GROUP BY 
				I."SalePointId",
				SPP."SalePointName",
				I."LotteryChannelId",
				LC."LotteryChannelName"
			ORDER BY I."SalePointId", I."LotteryChannelId"
		)
		SELECT
			A."SalePointId",
			A."SalePointName",
			A."LotteryChannelId",
			A."LotteryChannelName",
			A."TotalRemaining",
			A."TotalDupRemaining",
			A."DataShift"
		FROM tmp A
			JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = A."LotteryChannelId"
		ORDER BY A."SalePointId", LC."LotteryChannelTypeId", A."LotteryChannelId";
	END IF;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_of_all_agency_in_day(p_agency_id integer, p_month character varying)
 RETURNS TABLE("AgencyId" integer, "Info" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
RETURN QUERY
-- 	SELECT 
-- 		I."AgencyId",
-- 		A."AgencyName", 
-- 		I."ActionDate"::DATE,
-- 		SUM(I."TotalReceived") AS "TotalReceived", 
-- 		SUM(I."TotalDupReceived") AS "TotalDupReceived"
-- 	FROM "InventoryLog" I, "Agency" A
-- 	WHERE TO_CHAR(I."ActionDate", 'YYYY-MM') = p_month
-- 		AND A."AgencyId" = I."AgencyId"	
-- 	GROUP BY I."AgencyId", A."AgencyName", I."ActionDate"::DATE
-- 	ORDER BY  I."AgencyId";
WITH tmp AS(
		SELECT 
			I."AgencyId",
			A."AgencyName",
			LC."LotteryChannelId",
			LC."LotteryChannelName", 
			I."LotteryDate"::DATE,
			LC."ShortName",
			SUM(I."TotalReceived") AS "Total"
		FROM "InventoryFull" I, "Agency" A, "LotteryChannel" LC
	WHERE I."LotteryDate" BETWEEN (date_trunc('month', (p_month || '-01')::date))::DATE AND (date_trunc('month', (p_month|| '-01')::date) + interval '1 month' - interval '1 day')::DATE
		AND A."AgencyId" = I."AgencyId"
		AND LC."LotteryChannelId" = I."LotteryChannelId"
		AND A."AgencyId" = p_agency_id
	GROUP BY I."AgencyId", A."AgencyName", I."LotteryDate"::DATE, I."TotalReceived",LC."LotteryChannelId",LC."LotteryChannelName"
	ORDER BY   I."LotteryDate" DESC,LC."LotteryChannelId" DESC
	)
		SELECT 
			T."AgencyId", 
			array_to_json(array_agg(json_build_object( 
				'LotteryId',
				T."LotteryChannelId",
				'LotteryChannelName',
				T."LotteryChannelName", 
				'ShortName',
				T."ShortName",
				'LotteryDate',
				T."LotteryDate"::DATE,
				'Quantity',
				T."Total"
			)))::TEXT AS "AgencyInfo"
		FROM tmp T GROUP BY T."AgencyId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_of_all_agency_in_month(p_month character varying)
 RETURNS TABLE("AgencyId" integer, "AgencyName" character varying, "ActionDate" date, "TotalReceived" bigint, "TotalDupReceived" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN
RETURN QUERY
-- 	SELECT 
-- 		I."AgencyId",
-- 		A."AgencyName", 
-- 		I."ActionDate"::DATE,
-- 		SUM(I."TotalReceived") AS "TotalReceived", 
-- 		SUM(I."TotalDupReceived") AS "TotalDupReceived"
-- 	FROM "InventoryLog" I, "Agency" A
-- 	WHERE TO_CHAR(I."ActionDate", 'YYYY-MM') = p_month
-- 		AND A."AgencyId" = I."AgencyId"
-- 	GROUP BY I."AgencyId", A."AgencyName", I."ActionDate"::DATE
-- 	ORDER BY  I."AgencyId";
	SELECT 
		I."AgencyId",
		A."AgencyName", 
		I."LotteryDate"::DATE,
		SUM(I."TotalReceived") AS "TotalReceived", 
		0 :: INT8
	FROM "InventoryFull" I, "Agency" A
	WHERE TO_CHAR(I."LotteryDate", 'YYYY-MM') = p_month
		AND A."AgencyId" = I."AgencyId"
	GROUP BY I."AgencyId", A."AgencyName", I."LotteryDate"::DATE
	ORDER BY  I."AgencyId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_return_in_month(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "DateReturn" date, "TotalReturn" bigint)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	SELECT 
		T."FromSalePointId",
		SP."SalePointName", 
		T."LotteryDate", 
		SUM(T."TotalTrans") AS "TotalTrans"
	FROM "Transition" T, "SalePoint" SP
	WHERE TO_CHAR(T."LotteryDate", 'YYYY-MM') = p_month 
		AND T."TransitionTypeId" = 3
		AND SP."SalePointId" = T."FromSalePointId"
	GROUP BY T."FromSalePointId", T."LotteryDate", SP."SalePointName";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_return_in_month_v2(p_month character varying, p_sale_point_id integer)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "DateReturn" date, "TotalReturn" bigint)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	SELECT 
		T."FromSalePointId",
		SP."SalePointName", 
		T."LotteryDate", 
		SUM(T."TotalTrans") AS "TotalTrans"
	FROM "Transition" T
		LEFT JOIN "SalePoint" SP ON SP."SalePointId" = T."FromSalePointId"
	WHERE TO_CHAR(T."LotteryDate", 'YYYY-MM') = p_month 
		AND T."TransitionTypeId" = 3
		AND (COALESCE(p_sale_point_id, 0) = 0 OR SP."SalePointId" = p_sale_point_id)
	GROUP BY T."FromSalePointId", T."LotteryDate", SP."SalePointName";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_sell_of_some_salepoint_is_managed(p_user_role integer, p_month character varying, p_date timestamp without time zone)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "LotteryTypeId" integer, "LotteryTypeName" character varying, "ActionDate" date, "TotalSold" bigint)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_is_leader BOOL;
	v_user_id INT;
BEGIN

	SELECT UT."IsLeader", UR."UserId" INTO v_is_leader, v_user_id
	FROM "UserRole" UR 
		JOIN "UserTitle" UT ON UR."UserTitleId" = UT."UserTitleId"
	WHERE UR."UserRoleId" = p_user_role;
	
	IF v_is_leader IS TRUE THEN	
		IF p_month IS NOT NULL THEN
			RETURN QUERY
				WITH LIST AS (SELECT * FROM crm_get_list_salepoint_of_leader(v_user_id))
				SELECT 
					SPL."SalePointId",
					SP."SalePointName",
					SPL."LotteryTypeId",
					LT."LotteryTypeName",
					SPL."ActionDate" :: DATE,
					COALESCE(SUM(SPL."Quantity"),0) AS "TotalSold"
				FROM "SalePointLog" SPL 
					JOIN "SalePoint" SP ON SPL."SalePointId" = SP."SalePointId"
					JOIN "LotteryType" LT ON SPL."LotteryTypeId" = LT."LotteryTypeId"	
					JOIN LIST L ON SP."SalePointId" = L."SalePointId"
				WHERE TO_CHAR(SPL."ActionDate", 'YYYY-MM') = p_month AND SPL."IsDeleted" IS FALSE
				GROUP BY 
					SPL."SalePointId", 
					SP."SalePointName", 
					SPL."LotteryTypeId",
					LT."LotteryTypeName",
					SPL."ActionDate" :: DATE
				ORDER BY SPL."SalePointId",SPL."ActionDate"::DATE, SPL."LotteryTypeId";
			ELSE
				RETURN QUERY 
					WITH LIST AS (SELECT * FROM crm_get_list_salepoint_of_leader(v_user_id))
					SELECT 
						SPL."SalePointId",
						SP."SalePointName",
						SPL."LotteryTypeId",
						LT."LotteryTypeName",
						SPL."ActionDate" :: DATE,
						COALESCE(SUM(SPL."Quantity"),0) AS "TotalSold"
					FROM "SalePointLog" SPL 
						JOIN "SalePoint" SP ON SPL."SalePointId" = SP."SalePointId"
						JOIN "LotteryType" LT ON SPL."LotteryTypeId" = LT."LotteryTypeId"	
					WHERE (SPL."ActionDate" :: DATE) = (p_date :: DATE) AND SPL."IsDeleted" IS FALSE
					GROUP BY 
						SPL."SalePointId", 
						SP."SalePointName", 
						SPL."LotteryTypeId",
						LT."LotteryTypeName",
						SPL."ActionDate"::DATE
					ORDER BY SPL."SalePointId", SPL."ActionDate"::DATE, SPL."LotteryTypeId";
			END IF;
		END IF;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_lottery_sell_of_some_salepoint_is_managed_(p_user_role integer, p_month character varying, p_date timestamp without time zone)
