					SP."SalePointName", 
					SPL."LotteryTypeId",
					LT."LotteryTypeName",
					SPL."ActionDate" :: DATE
				ORDER BY SPL."SalePointId",SPL."ActionDate"::DATE, SPL."LotteryTypeId";
			ELSE
				RETURN QUERY 
					WITH LIST AS (SELECT * FROM crm_get_list_salepoint_of_leader(v_user_id))
