						JOIN "LotteryType" LT ON SPL."LotteryTypeId" = LT."LotteryTypeId"	
					WHERE (SPL."ActionDate" :: DATE) = (p_date :: DATE)
					GROUP BY 
						SPL."SalePointId", 
						SP."SalePointName", 
						SPL."LotteryTypeId",
