CREATE OR REPLACE FUNCTION public.crm_report_get_total_shift_of_all_user_in_month(p_month character varying)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "ShiftTypeId" integer, "ShiftTypeName" character varying, "SalePointId" integer, "Quantity" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmpSDD AS (
		SELECT 
			SD."UserId", 
			SD."ShiftTypeId",
			ST."ShiftTypeName",
			SD."SalePointId",
			COUNT(SD."ShiftTypeId") AS "Quantity"
		FROM "ShiftDistribute" SD JOIN "ShiftType" ST ON SD."ShiftTypeId" = ST."ShiftTypeId"
		WHERE SD."IsActive" IS TRUE 
		AND
			CASE WHEN TO_CHAR(NOW(),'YYYY-MM') = p_month
				THEN
					TO_CHAR(SD."DistributeDate",'YYYY-MM-DD') <= TO_CHAR(NOW(),'YYYY-MM-DD')
					AND TO_CHAR(SD."DistributeDate",'YYYY-MM') = TO_CHAR(NOW(),'YYYY-MM')
				ELSE
					TO_CHAR(SD."DistributeDate",'YYYY-MM') = p_month 
				END 
		GROUP BY SD."UserId", SD."ShiftTypeId", ST."ShiftTypeName",
			SD."SalePointId"),
		tmp2 AS(
			SELECT COUNT(1) AS "DaysInAMonth"
			FROM   generate_series(date ('2023-03'||'-01')::DATE, (date_trunc('month', ('2023-03'||'-10')::date) + interval '1 month' - interval '1 day')::date, '1 day') day
		)
			
	SELECT 
		U."UserId",
		U."FullName",
		SDD."ShiftTypeId",
		SDD."ShiftTypeName",
		SDD."SalePointId",
		CASE WHEN COALESCE(SDD."Quantity",0) > ( SELECT T."DaysInAMonth" - 2 from tmp2 T ) THEN COALESCE(SDD."Quantity",0) + 1 ELSE COALESCE(SDD."Quantity",0) END
	FROM "User" U JOIN tmpSDD SDD ON U."UserId" = SDD."UserId"
	ORDER BY U."UserId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_shift_of_all_user_in_month_of_salepoint(p_month character varying, p_salepointid integer)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "SalePointId" integer, "SalePointName" character varying, "ShiftTypeId" integer, "ShiftTypeName" character varying, "Quantity" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN
	IF p_salePointId <> 0 THEN
		RETURN QUERY
		WITH tmpSDD AS (
			SELECT 
				SD."UserId", 
				SD."ShiftTypeId",
				ST."ShiftTypeName",
				SP."SalePointId",
				SP."SalePointName",
				COUNT(SD."ShiftTypeId") AS "Quantity"
			FROM "ShiftDistribute" SD JOIN "ShiftType" ST ON SD."ShiftTypeId" = ST."ShiftTypeId", "SalePoint" SP
			WHERE SD."IsActive" = 't' 
				AND TO_CHAR(SD."DistributeDate",'YYYY-MM') = p_month 
				AND SP."SalePointId" = SD."SalePointId" 
				AND SD."SalePointId" = p_salePointId
			GROUP BY SD."UserId", SD."ShiftTypeId", ST."ShiftTypeName", SP."SalePointId", SP."SalePointName")
		SELECT 
			U."UserId",
			U."FullName",
			SDD."SalePointId",
			SDD."SalePointName",
			SDD."ShiftTypeId",
			SDD."ShiftTypeName",
			COALESCE(SDD."Quantity",0)	
		FROM "User" U JOIN tmpSDD SDD ON U."UserId" = SDD."UserId"
		ORDER BY U."UserId";
	ELSE
		RETURN QUERY
			WITH tmpSDD AS (
				SELECT 
					SD."UserId", 
					SD."ShiftTypeId",
					ST."ShiftTypeName",
					SP."SalePointId",
					SP."SalePointName",
					COUNT(SD."ShiftTypeId") AS "Quantity"
				FROM "ShiftDistribute" SD JOIN "ShiftType" ST ON SD."ShiftTypeId" = ST."ShiftTypeId", "SalePoint" SP
				WHERE SD."IsActive" = 't' 
					AND TO_CHAR(SD."DistributeDate",'YYYY-MM') = p_month 
					AND SP."SalePointId" = SD."SalePointId"
				GROUP BY SD."UserId", SD."ShiftTypeId", ST."ShiftTypeName", SP."SalePointId", SP."SalePointName")
			SELECT 
				U."UserId",
				U."FullName",
				SDD."SalePointId",
				SDD."SalePointName",
				SDD."ShiftTypeId",
				SDD."ShiftTypeName",
				COALESCE(SDD."Quantity",0)	
			FROM "User" U JOIN tmpSDD SDD ON U."UserId" = SDD."UserId"
			ORDER BY U."UserId";
	END IF;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_total_shift_of_all_user_in_month_v1(p_month character varying)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "ShiftTypeId" integer, "ShiftTypeName" character varying, "Quantity" bigint)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmpSDD AS (
		SELECT 
			SD."UserId", 
			SD."ShiftTypeId",
			ST."ShiftTypeName",
			COUNT(SD."ShiftTypeId") AS "Quantity"
		FROM "ShiftDistribute" SD JOIN "ShiftType" ST ON SD."ShiftTypeId" = ST."ShiftTypeId"
		WHERE SD."IsActive" IS TRUE AND TO_CHAR(SD."DistributeDate",'YYYY-MM-DD') <= p_month
		GROUP BY SD."UserId", SD."ShiftTypeId", ST."ShiftTypeName")
	SELECT 
		U."UserId",
		U."FullName",
		SDD."ShiftTypeId",
		SDD."ShiftTypeName",
		COALESCE(SDD."Quantity",0)
	FROM "User" U JOIN tmpSDD SDD ON U."UserId" = SDD."UserId"
	ORDER BY U."UserId";
END;
$function$


