		),tmp3 AS
		(
			SELECT 
				U."UserId",
				U."FullName",
				SDD."ShiftTypeId",
				SDD."ShiftTypeName",
				SDD."SalePointId",
				COALESCE(SDD."Quantity",0) AS "Quantity"
			FROM "User" U JOIN tmpSDD SDD ON U."UserId" = SDD."UserId"
			ORDER BY U."UserId"
		),
		tmp4 AS
		(
			SELECT 
				T3."UserId",
				SUM(T3."Quantity") AS "Sum",
				U."SalePointId"
			FROM tmp3 T3 
			LEFT JOIN "User" U ON U."UserId" = T3."UserId"
			GROUP BY T3."UserId",U."SalePointId"
		)--,
		--tmp5 AS
		--(
				SELECT 
					T3."UserId",
					T3."FullName",
					--T3."ShiftTypeId",
					--T3."ShiftTypeName",
					T3."SalePointId",
					SUM(COALESCE(T3."Quantity",0))::INT8 AS "Quantity",
					T4."Sum"::INT8,
					T4."SalePointId" AS "MainSalePointId"
				FROM tmp3 T3 
				LEFT JOIN tmp4 T4 ON T3."UserId" = T4."UserId"
				GROUP BY 
					T3."UserId",
					T3."FullName",
					T3."SalePointId",
					T4."Sum",
					T4."SalePointId";
		--)
		--SELECT 
			--(CASE WHEN T5."Sum" > ( SELECT T."DaysInAMonth" - 2 from tmp2 T ) AND T5."SalePointId" = T5."MainSalePointId"
			--THEN
				
		--FROM tmp5 T5;


END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_get_transiton_type_offset(p_month character varying)
 RETURNS TABLE("SalePointName" character varying, "SalePointId" integer, "TransitionDate" date, "TransferTicket" bigint, "TicketsReceived" bigint, "Offset" bigint)
 LANGUAGE plpgsql
AS $function$
DECLARE

BEGIN

	RETURN QUERY
	WITH tmp AS(
			SELECT
				S."SalePointName",
				S."SalePointId",
				T."TransitionDate"::DATE,
				COALESCE( SUM(T."TotalTrans") FILTER (WHERE T."TransitionTypeId" = 1),0) AS "TransferTicket",
				COALESCE( SUM(T."TotalTrans") FILTER (WHERE T."TransitionTypeId" = 2),0) AS "TicketsReceived"
			FROM "Transition" T 
			LEFT JOIN "SalePoint" S ON T."FromSalePointId" = S."SalePointId" OR T."ToSalePointId" = S."SalePointId"
			WHERE --T."TransitionDate"::DATE = p_day::DATE
			--AND 
			TO_CHAR(T."TransitionDate",'YYYY-MM') = p_month
			GROUP BY 
				T."TransitionTypeId",
				S."SalePointName",
				S."SalePointId",
				T."TransitionDate"::DATE
			ORDER BY S."SalePointId"
	)
	SELECT
		T."SalePointName",
		T."SalePointId",
		T."TransitionDate"::DATE,
		SUM(T."TransferTicket")::INT8 AS "TransferTicket",
		SUM(T."TicketsReceived")::INT8 AS "TicketsReceived",
		(SUM(T."TransferTicket") - SUM(T."TicketsReceived"))::INT8 AS "Offset"
	FROM tmp T 
	GROUP BY 
		T."SalePointId", 
		T."SalePointName",
		T."TransitionDate"::DATE
	ORDER BY 
		T."SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_lottery_in_a_day(p_sale_point_id integer, p_date timestamp without time zone, p_shift_id bigint)
 RETURNS TABLE("Data" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	with tmp AS(SELECT
		RL.* 
	FROM "ReportLottery" RL
	LEFT JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = RL."LotteryChannelId"
	WHERE RL."Date" = p_date AND (RL."ShiftId" = p_shift_id or p_shift_id = 0) AND RL."SalePointId" = p_sale_point_id 
	ORDER BY RL."LotteryDate",LC."LotteryChannelTypeId")
	SELECT array_to_json(array_agg(json_build_object(
		'ReportLotteryId',
		T."ReportLotteryId",
		'ShiftId',
		T."ShiftId",
		'Date',
		T."Date",
		'LotteryChannelId',
		T."LotteryChannelId",
		'SalePointId',
		T."SalePointId",
		'Stock',
		T."Stock",
		'SoldRetail',
		T."SoldRetail",
		'Remaining',
		T."Remaining",
		'LotteryTypeId',
		T."LotteryTypeId",
		'Transfer',
		T."Transfer",
		'Received',
		T."Received",
		'LotteryDate',
		T."LotteryDate",
		'OrdinalNum',
		T."OrdinalNum",
		'SoldRetailMoney',
		T."SoldRetailMoney",
		'SoldWholeSale',
		T."SoldWholeSale",
		'SoldWholeSaleMoney',
		T."SoldWholeSaleMoney",
		'ReceivedDup',
		T."ReceivedDup",
		'TransferDup',
		T."TransferDup",
		'RemainingDup',
		T."RemainingDup",
		'StockDup',
		T."StockDup",
		'SoldRetailDup',
		T."SoldRetailDup",
		'SoldRetailMoneyDup',
		T."SoldRetailMoneyDup",
		'SoldWholeSaleDup',
		T."SoldWholeSaleDup",
		'SoldWholeSaleMoneyDup',
		T."SoldWholeSaleMoneyDup"
	)))::TEXT AS "Data" FROM tmp T;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_manager_overall(p_user_role_id integer, p_lottery_date timestamp without time zone, p_sale_point_id integer)
 RETURNS TABLE("ScratchcardData" text, "LotteryData" text, "RepaymentData" text, "WinningData" text)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY 
	SELECT
	(
		SELECT array_to_json(
			ARRAY_AGG (r))
		FROM
		(
			SELECT
				ST."ShiftId",
				S."ShiftName",
				ST."TotalReceived",
				ST."TotalRemaining",
				ST."TotalSold",
				ST."TotalSoldMoney"
			FROM "ShiftTransfer" ST
				JOIN "Shift" S ON S."ShiftId" = ST."ShiftId"
			WHERE "LotteryTypeId" = 3 
				AND "ActionDate"::DATE = p_lottery_date::DATE
				AND (COALESCE(p_sale_point_id, 0)= 0 OR ST."SalePointid" = p_sale_point_id)
		) r
	)::TEXT AS "ScratchcardData",
	(
		SELECT array_to_json(
			ARRAY_AGG (r))
		FROM
		(
			SELECT
				ST."ShiftId",
				S."ShiftName",
				ST."UserId",
				U."FullName",
				SUM(ST."TotalReceived") AS "TotalReceived",
				SUM(ST."TotalRemaining") AS "TotalRemaining",
				ST."LotteryChannelId",
				LC."ShortName",
				ST."LotteryDate",
				SUM(ST."TotalTrans") AS "TotalTrans",
				SUM(ST."TotalReturns") AS "TotalReturns",
				SUM(ST."TotalStocks") AS "TotalStocks",
				SUM(ST."TotalSold") AS "TotalSold",
				SUM(ST."TotalSoldMoney") AS "TotalSoldMoney",
				ST."SalePointid",
				SP."SalePointName"
			FROM "ShiftTransfer" ST
				JOIN "Shift" S ON S."ShiftId" = ST."ShiftId"
				JOIN "LotteryChannel" LC ON LC."LotteryChannelId" = ST."LotteryChannelId"
				JOIN "User" U ON U."UserId" = ST."UserId"
				JOIN "SalePoint" SP ON SP."SalePointId" = ST."SalePointid"
			WHERE ST."LotteryTypeId" <> 3 
				AND ST."ActionDate"::DATE = p_lottery_date::DATE
				AND (COALESCE(p_sale_point_id, 0)= 0 OR ST."SalePointid" = p_sale_point_id)
			GROUP BY
				ST."ShiftId",
				S."ShiftName",
				ST."UserId",
				U."FullName",
				ST."LotteryChannelId",
				LC."ShortName",
				ST."LotteryDate",
				ST."SalePointid",
				SP."SalePointName",
				LC."DayIds",
				LC."LotteryChannelTypeId"
			ORDER BY ST."ShiftId", ST."LotteryDate", LC."DayIds", LC."LotteryChannelTypeId"
		) r
	)::TEXT AS "LotteryData",
	(
		SELECT array_to_json(
			ARRAY_AGG (r))
		FROM
		(
			SELECT
				R."SalePointId",
				SP."SalePointName",
				SD."ShiftId",
				S."ShiftName",
				SUM(R."Amount") AS "TotalRepay"
			FROM "Repayment" R
				JOIN "ShiftDistribute" SD ON SD."ShiftDistributeId" = R."ShiftDistributeId"
				JOIN "Shift" S ON S."ShiftId" = SD."ShiftId"
				JOIN "SalePoint" SP ON SP."SalePointId" = R."SalePointId"
			WHERE R."ActionDate"::DATE = p_lottery_date::DATE
				AND (COALESCE(p_sale_point_id, 0)= 0 OR R."SalePointId" = p_sale_point_id)
			GROUP BY
				R."SalePointId",
				SP."SalePointName",
				SD."ShiftId",
				S."ShiftName"
		) r
	)::TEXT AS "RepaymentData",
	(
		SELECT array_to_json(
			ARRAY_AGG (r))
		FROM
		(
			SELECT
				W."WinningTypeId",
				WT."WinningTypeName",
				S."ShiftId",
				S."ShiftName",
				SUM(W."Quantity") AS "TotalQuantity",
				SUM(W."WinningPrice") AS "TotalPrice"
			FROM "Winning" W
				JOIN "ShiftDistribute" SD ON SD."ShiftDistributeId" = W."ShiftDistributeId"
				JOIN "Shift" S ON S."ShiftId" = SD."ShiftId"
				JOIN "SalePoint" SP ON SP."SalePointId" = W."SalePointId"
				JOIN "WinningType" WT ON WT."WinningTypeId" = W."WinningTypeId"
			WHERE W."ActionDate"::DATE = p_lottery_date::DATE
				AND (COALESCE(p_sale_point_id, 0)= 0 OR W."SalePointId" = p_sale_point_id)
			GROUP BY
				W."WinningTypeId",
				WT."WinningTypeName",
				S."ShiftId",
				S."ShiftName"
		) r
	)::TEXT AS "WinningData";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_report_manager_overall_v2(p_user_role_id integer, p_lottery_date timestamp without time zone, p_sale_point_id integer)
 RETURNS TABLE("ScratchcardData" text, "LotteryData" text, "RepaymentData" text, "WinningData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_role_1 INT;
	v_shift_dis_1 INT;
	v_role_2 INT;
	v_shift_dis_2 INT;
BEGIN
	--Lấy ca 1
	SELECT UR."UserRoleId", SD."ShiftDistributeId"
