CREATE OR REPLACE FUNCTION public.crm_salepoint_confirm_list_payment(p_action_by integer, p_action_by_name character varying, p_confirm_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data ::JSON;
	ele JSON;
	v_check INT;
	v_confirm_status INT;
BEGIN
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
		SELECT 
			"ConfirmLogId",
			"ConfirmStatusId"
		INTO
			v_check,
			v_confirm_status
		FROM "ConfirmLog"
		WHERE "ConfirmLogId" = (ele ->>'ConfirmLogId')::INT;
		
		IF COALESCE(v_check,0) =0 THEN
			RAISE 'Không tồn tại ConfirmLogId';
		ELSEIF COALESCE(v_confirm_status,1) <> 1 THEN
			RAISE 'Yêu cầu đã được thao tác';
		ELSE
			SELECT
				f."Id",
				f."Message"
			INTO
				v_id,
				v_mess
			FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 0,'',v_check,p_confirm_type,9999) f;
		
		END IF;
   
  END LOOP;

	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_confirm_list_payment_v2(p_action_by integer, p_action_by_name character varying, p_confirm_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_data JSON := p_data ::JSON;
	ele JSON;
	v_check INT;
	v_confirm_status INT;
	v_order_id INT;
BEGIN
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
		SELECT 
			"ConfirmLogId",
			"ConfirmStatusId",
			"HistoryOfOrderId"
		INTO
			v_check,
			v_confirm_status,
			v_order_id
		FROM "ConfirmLog"
		WHERE "ConfirmLogId" = (ele ->>'ConfirmLogId')::INT;
		
		IF COALESCE(v_check,0) = 0 THEN
			RAISE 'Không tồn tại ConfirmLogId';
		ELSEIF COALESCE(v_confirm_status,1) <> 1 THEN
			RAISE 'Yêu cầu đã được thao tác';
		ELSE
			SELECT
				f."Id",
				f."Message"
			INTO
				v_id,
				v_mess
			FROM crm_salepoint_update_cornfirm_item_v2(p_action_by, p_action_by_name, 0,'',v_check,p_confirm_type,9999, NULL, NULL, NULL, v_order_id) f;
		
		END IF;
   
  END LOOP;

	RETURN QUERY
	SELECT v_id, v_mess, v_order_id;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess, v_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_create_list_confirm_payment(p_action_by integer, p_action_by_name character varying, p_guest_id integer DEFAULT NULL::integer, p_lottery_data text DEFAULT NULL::text, p_lottery_data_info text DEFAULT NULL::text, p_payment_data text DEFAULT NULL::text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := -1;
	v_mess TEXT := 'Dữ liệu chưa';
	v_now TIMESTAMP :=NOW();
	ele JSON;
BEGIN
  IF p_lottery_data IS NOT NULL THEN
		--LOTTERY
		SELECT f."Id", f."Message"
		INTO v_id, v_mess
		FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name,3, p_lottery_data, NULL, 1, 3 ,p_lottery_data_info,p_guest_id,v_now) f ;
		
		IF v_id <= 0 THEN
			RAISE '%', v_mess;
		END IF;
	END IF; 
	
	IF p_payment_data IS NOT NULL THEN
		--PAYMENT
		SELECT f."Id", f."Message"
		INTO v_id, v_mess
		FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name,2, p_payment_data, NULL, 1, 2 ,NULL,p_guest_id,v_now) f ;
		IF v_id <= 0 THEN
			RAISE '%', v_mess;
		END IF;
		
	END IF; 
	

	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_create_list_confirm_payment_v2(p_action_by integer, p_action_by_name character varying, p_guest_id integer DEFAULT NULL::integer, p_lottery_data text DEFAULT NULL::text, p_lottery_data_info text DEFAULT NULL::text, p_payment_data text DEFAULT NULL::text, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := -1;
	v_mess TEXT := 'Dữ liệu chưa';
	v_now TIMESTAMP :=NOW();
	ele JSON;
	v_sale_point_id INT;
	v_shift_dis INT;
BEGIN
	v_sale_point_id := (p_lottery_data_info::JSON->>'SalePointId')::INT;
	v_shift_dis := (p_lottery_data_info::JSON->>'ShiftDistributeId')::INT;
		--Tạo hoá đơn khi chưa có
	IF p_order_id IS NULL THEN
		raise notice 'TH 1';
		INSERT INTO "HistoryOfOrder" (
			"SalePointId",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"IsDeleted",
			"ShiftDistributeId"
		)
		VALUES (
			v_sale_point_id,
			p_action_by,
			p_action_by_name,
			v_now,
			FALSE,
			v_shift_dis
		) RETURNING "HistoryOfOrderId" INTO p_order_id;
		
	END IF;

  IF p_lottery_data IS NOT NULL THEN
		raise notice 'TH 2';
		--LOTTERY
		SELECT f."Id", f."Message"
		INTO v_id, v_mess
		FROM crm_salepoint_update_cornfirm_item_v2(p_action_by, p_action_by_name, 3, p_lottery_data, NULL, 1, 3 , p_lottery_data_info, p_guest_id, v_now, p_order_id) f ;
		
		IF v_id <= 0 THEN
			RAISE '%', v_mess;
		END IF;
	END IF; 
	
	IF p_payment_data IS NOT NULL THEN
		raise notice 'TH 3';
		--PAYMENT
		SELECT f."Id", f."Message"
		INTO v_id, v_mess
		FROM crm_salepoint_update_cornfirm_item_v2(p_action_by, p_action_by_name, 2, p_payment_data, NULL, 1, 2 , p_lottery_data_info, p_guest_id, v_now, p_order_id) f ;
		IF v_id <= 0 THEN
			RAISE '%', v_mess;
		END IF;
		
	END IF; 
	

	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_create_or_update_guest(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_phone TEXT;
	ele JSON;
BEGIN
	
	-- THÊM
	IF p_action_type = 1 THEN 
		ele := p_data::JSON;
		-- Kiểm tra SĐT
		v_phone := fn_phoneconvert_11_to_10(ele ->> 'Phone');
		IF LENGTH(v_phone) <> 10 THEN
			RAISE 'Số điện thoại không đúng cú pháp';
		END IF;
		-- Kiểm tra xem đã có số điện thoại và loại khách này chưa
		IF NOT EXISTS(SELECT 1 FROM "Guest" G WHERE fn_phoneconvert_11_to_10(G."Phone") = v_phone AND G."SalePointId" = (ele ->> 'SalePointId')::INT) THEN 
			INSERT INTO "Guest"(
				"FullName",
				"Phone",
				"SalePointId",
				"WholesalePriceId",
				"ScratchPriceId",
				"CreatedBy",
				"CreatedByName"
			)
			VALUES(
				(ele->>'FullName')::VARCHAR,
				v_phone,
				(ele->>'SalePointId')::INT,
				(ele->>'WholesalePriceId')::INT,
				(ele->>'ScratchPriceId')::INT,
				p_action_by,
				p_action_by_name
			) RETURNING "GuestId" INTO v_id;
								
		ELSE
			RAISE 'Đã tồn tại khách với số điện thoại này trong hệ thống';
		END IF;
		
		v_mess := 'Thêm thành công';
	-- Sửa
	ELSEIF p_action_type = 2 THEN
		ele := p_data::JSON;
		v_phone := fn_phoneconvert_11_to_10(ele ->> 'Phone');
		IF LENGTH(v_phone) <> 10 THEN
			RAISE 'Số điện thoại không đúng cú pháp';
		END IF;
		
		UPDATE "Guest"
		SET
			"FullName" = COALESCE((ele ->> 'FullName')::VARCHAR, "FullName"),
			"Phone" = COALESCE(fn_phoneconvert_11_to_10((ele ->> 'Phone')::VARCHAR) , "Phone"),
			"WholesalePriceId" = COALESCE((ele ->> 'WholesalePriceId')::INT, "WholesalePriceId"),
			"ScratchPriceId" = COALESCE((ele ->> 'ScratchPriceId')::INT, "ScratchPriceId"),
			"ModifyBy" = p_action_by,
			"ModifyByName" = p_action_by_name,
			"ModifyDate" = NOW()
		WHERE 
			"GuestId" = (ele ->> 'GuestId')::INT;
		v_id := 1;
		v_mess := 'Cập nhật thành công';
	-- Xóa
	ELSEIF p_action_type = 3 THEN
		ele := p_data::JSON;
		UPDATE "Guest"
		SET
			"IsActive" = FALSE,
			"ModifyBy" = p_action_by,
			"ModifyByName" = p_action_by_name,
			"ModifyDate" = NOW()
		WHERE 
			"GuestId" = (ele ->> 'GuestId')::INT;
		v_id := 1;
		v_mess := 'Xóa thành công';
	END IF;

	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


