CREATE OR REPLACE FUNCTION public.crm_salepoint_get_fee_outsite_in_month(p_month character varying, p_user_id integer DEFAULT 0)
 RETURNS TABLE("Date" date, "UserId" integer, "FullName" character varying, "ShiftId" integer, "ShiftDistributeId" integer, "SalePointId" integer, "SalePointName" character varying, "Data" text, "TotalPrice" numeric)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
BEGIN
	RETURN QUERY
	WITH tmp0 AS (
		SELECT 
			T."SalePointId",
			T."SalePointName"
		FROM crm_get_list_salepoint_of_leader(0) T
	),
	tmp AS (
		SELECT
			(p_month||'-'||"I")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "I"
	),
	tmp1 AS (
		SELECT
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SD."SalePointId",
			SP."SalePointName",
			json_agg(json_build_object('Note',TT."Note",'TotalPrice',TT."TotalPrice"))::TEXT AS "Data",
			SUM(TT."TotalPrice")::NUMERIC AS "TotalPrice"
		FROM tmp T
			JOIN "ShiftDistribute" SD ON SD."DistributeDate" = T."Date"::DATE
			JOIN "User" U ON SD."UserId" = U."UserId"
			JOIN tmp0 SP ON SD."SalePointId" = SP."SalePointId"
			LEFT JOIN "Transaction" TT ON TT."Date" = T."Date" 
					AND TT."TransactionTypeId" = 1
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
		GROUP BY 
			TT."TransactionTypeId",
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SD."SalePointId",
			SP."SalePointName"
		ORDER BY 
			T."Date",
			SD."ShiftId"
	)
	SELECT 
		T."Date",
		T."UserId",
		T."FullName",
		T."ShiftId",
		T."ShiftDistributeId",
		T."SalePointId",
		T."SalePointName",
		T."Data",
		T."TotalPrice"
	FROM tmp1 T
	ORDER BY 
		T."SalePointId",
		T."Date",
		T."ShiftId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_fee_outsite_in_month_in_total(p_month character varying, p_salepoint_id bigint)
 RETURNS TABLE("Month" character varying, "UserId" integer, "FullName" character varying, "SalePointId" integer, "SalePointName" character varying, "Data" text, "TotalPrice" numeric)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
BEGIN
	RETURN QUERY
	WITH tmp0 AS (
		SELECT 
			T."SalePointId",
			T."SalePointName"
		FROM crm_get_list_salepoint_of_leader(0) T
	),
	tmp AS (
		SELECT
			(p_month||'-'||"I")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "I"
	),
	tmp1 AS (
		SELECT
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SD."SalePointId",
			SP."SalePointName",
			json_agg(json_build_object('Note',TT."Note",'TotalPrice',TT."Price"))::TEXT AS "Data",
			SUM(TT."TotalPrice")::NUMERIC AS "TotalPrice"
		FROM tmp T
			JOIN "ShiftDistribute" SD ON SD."DistributeDate" = T."Date"::DATE
			JOIN "User" U ON SD."UserId" = U."UserId"
			JOIN tmp0 SP ON SD."SalePointId" = SP."SalePointId"
			LEFT JOIN "Transaction" TT ON TT."Date" = T."Date" 
					AND TT."TransactionTypeId" = 1
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
		GROUP BY 
			TT."TransactionTypeId",
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SD."SalePointId",
			SP."SalePointName"
		ORDER BY 
			T."Date",
			SD."ShiftId"
	)
	SELECT 
		p_month,
		T."UserId",
		T."FullName",
		T."SalePointId",
		T."SalePointName",
		array_agg(T."Data") ::TEXT,
		SUM(T."TotalPrice")
	FROM tmp1 T
	WHERE T."SalePointId" = p_salepoint_id
	GROUP BY T."UserId",T."FullName",T."SalePointId",T."SalePointName"
	ORDER BY 
		T."SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_history_lottery_of_guest(p_guest_id integer, p_date timestamp without time zone)
 RETURNS TABLE("LotteryChannelId" integer, "LotteryChannelName" character varying, "IsScratchcard" boolean, "TotalReceived" bigint, "TotalValueReceived" numeric, "TotalReturn" bigint, "TotalValueReturn" numeric, "TotalCanReturn" bigint, "TotalValueCanReturn" numeric)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			SPL."LotteryChannelId",
			SPL."LotteryTypeId",
			SPL."LotteryDate",
			SPL."Quantity",
			SPL."TotalValue",
			(CASE WHEN SPL."Quantity" >= 0 THEN 1 ELSE -1 END) AS "Type"
		FROM "SalePointLog" SPL
		WHERE SPL."GuestId" = p_guest_id
		AND (SPL."LotteryDate" = p_date::DATE OR SPL."LotteryTypeId" = 3)
	),
	tmp2 AS (
		SELECT 
			SPL."LotteryChannelId",
			SPL."LotteryDate",
			SUM(SPL."Quantity") AS "Quantity",
			SUM(SPL."TotalValue") AS "TotalValue"
		FROM tmp SPL
		WHERE SPL."Type" = 1
		GROUP BY 
			SPL."LotteryChannelId",
			SPL."LotteryDate"
	),
	tmp3 AS (
		SELECT 
			SPL."LotteryChannelId",
			SPL."LotteryDate",
			SUM(-SPL."Quantity") AS "Quantity",
			SUM(-SPL."TotalValue") AS "TotalValue"
		FROM tmp SPL
		WHERE SPL."Type" = -1
		GROUP BY 
			SPL."LotteryChannelId",
			SPL."LotteryDate"
	)
	SELECT 
		T."LotteryChannelId",
		LC."LotteryChannelName",
		LC."IsScratchcard",
		T."Quantity" AS "TotalReceived",
		T."TotalValue" AS "TotalValueReceived",
		S."Quantity" AS "TotalReturn",
		S."TotalValue" AS "TotalValueReturn",
		(COALESCE(T."Quantity", 0) - COALESCE(S."Quantity", 0)) AS "TotalCanReturn",
		(COALESCE(T."TotalValue", 0) - COALESCE(S."TotalValue", 0)) AS "TotalValueCanReturn"
	FROM tmp2 T
		FULL JOIN tmp3 S ON T."LotteryChannelId" = S."LotteryChannelId"
		JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
	ORDER BY T."LotteryChannelId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_confirm_payment(p_sale_point_id integer DEFAULT NULL::integer, p_guest_id integer DEFAULT NULL::integer, p_page_size integer DEFAULT 100, p_page_number integer DEFAULT 1)
 RETURNS TABLE("ActionDate" timestamp without time zone, "ActionBy" integer, "ActionByName" character varying, "GuestId" integer, "GuestName" character varying, "CanBuyWholeSale" boolean, "DataConfirm" text, "SalePointId" integer, "SalePointName" character varying)
 LANGUAGE plpgsql
AS $function$ 
DECLARE
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
BEGIN
	RETURN QUERY 
	SELECT 
		CL."ActionDate",
		CL."ActionBy",
		CL."ActionByName",
		CL."GuestId",
		G."FullName" AS "GuestName",
		(CASE WHEN G."WholesalePriceId" IS NOT NULL AND G."ScratchPriceId" IS NOT NULL THEN TRUE ELSE FALSE END) AS "CanBuyWholeSale",
		(SELECT json_agg(t) FROM (
				SELECT 
					CL1."ConfirmLogId",
					CL1."ConfirmFor",
					CT."ConfirmForTypeName",
					CS."ConfirmStatusId",
					CS."ConfirmStatusName",
					CL1."Data",
					CL1."DataActionInfo"
				FROM "ConfirmLog" CL1
				JOIN "ConfirmForType" CT ON CT."ConfirmForTypeId" = CL1."ConfirmFor"
				JOIN "ConfirmStatus" CS ON CS."ConfirmStatusId" = CL1."ConfirmStatusId"
				WHERE (CL1."ConfirmFor" = 2 OR CL1."ConfirmFor" = 3)
					AND CL1."ActionDate" = CL."ActionDate"
		) t ) ::TEXT AS "DataConfirm",
		SP."SalePointId",
		SP."SalePointName"
	FROM "ConfirmLog" CL 
		JOIN "Guest" G ON (G."GuestId"= CL."GuestId" AND (COALESCE(p_sale_point_id,0) = 0 OR G."SalePointId" = p_sale_point_id) AND (COALESCE(p_guest_id, 0)=0 OR G."GuestId" = p_guest_id))
		JOIN "SalePoint" SP ON SP."SalePointId"= G."SalePointId"
	WHERE CL."ConfirmFor" = 2 OR CL."ConfirmFor" = 3		
	GROUP BY 
		CL."ActionDate", 
		CL."ActionBy", 
		CL."ActionByName", 
		CL."GuestId",
		G."FullName", 
		SP."SalePointId",
		SP."SalePointName", 
		G."WholesalePriceId", 
		G."ScratchPriceId"
	ORDER BY  CL."ActionDate" DESC
	OFFSET v_offset_row LIMIT p_page_size;	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_fee_of_commission(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "Date" date, "TotalValue" bigint, "Fee" numeric, "ActionBy" integer, "ActionByName" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
		C."SalePointId",
		C."Date",
		C."TotalValue",
		C."Fee",
		C."UserId",
		U."FullName"
	FROM "Commission" C
		JOIN "User" U ON C."UserId" = U."UserId"
	WHERE C."IsDeleted" IS FALSE
		AND TO_CHAR(C."Date", 'YYYY-MM') = p_month
		AND COALESCE(C."Fee", 0) > 0
	ORDER BY 
		C."SalePointId",
		C."Date",
		C."CreatedDate";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_fee_outsite(p_month character varying, p_user_id integer DEFAULT 0)
 RETURNS TABLE("TransactionId" integer, "Date" date, "UserId" integer, "SalePointId" integer, "SalePointName" character varying, "FullName" character varying, "Note" character varying, "TotalPrice" numeric)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp0 AS (
		SELECT 
			T."SalePointId",
			T."SalePointName"
		FROM crm_get_list_salepoint_of_leader(p_user_id) T
	)
	SELECT 
		T."TransactionId",
		T."Date",
		T."UserId",
		T."SalePointId",
		SP."SalePointName",
		U."FullName",
		T."Note",
		T."TotalPrice"
	FROM tmp0 SP
		JOIN "Transaction" T ON T."SalePointId" = SP."SalePointId"
		JOIN "User" U ON T."UserId" = U."UserId"
	WHERE T."TransactionTypeId" = 1
		AND TO_CHAR(T."Date", 'YYYY-MM') = p_month
		AND T."IsDeleted" IS FALSE;	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_guest_for_confirm(p_sale_point_id integer DEFAULT NULL::integer)
 RETURNS TABLE("ActionDate" timestamp without time zone, "ActionBy" integer, "ActionByName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying, "DataConfirm" text, "SalePointId" integer, "SalePointName" character varying)
 LANGUAGE plpgsql
AS $function$ 
BEGIN
	
	RETURN QUERY 
	SELECT 
		CL."ActionDate",
		CL."ActionBy",
		CL."ActionByName",
		CL."ConfirmStatusId",
		CS."ConfirmStatusName",
		CL."Data" AS "DataConfirm",
		SP."SalePointId",
		SP."SalePointName"
	FROM "ConfirmLog" CL 
		JOIN "ConfirmStatus" CS ON CL."ConfirmStatusId" = CS."ConfirmStatusId"
		JOIN "SalePoint" SP ON SP."SalePointId" = (CL."DataActionInfo"::JSON ->> 'SalePointId')::INT
	WHERE CL."ConfirmFor" = 4
		AND (SP."SalePointId" = p_sale_point_id OR COALESCE(p_sale_point_id, 0) = 0)
	ORDER BY CL."ActionDate" DESC;

	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_history_of_guest(p_guest_id integer, p_page_size integer DEFAULT 100, p_page_number integer DEFAULT 1)
 RETURNS TABLE("RowNumber" bigint, "TotalCount" bigint, "HistoryOfOrderId" integer, "SalePointId" integer, "Data" text, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
BEGIN
	RETURN QUERY
	SELECT 
		ROW_NUMBER() OVER (ORDER BY H."CreatedDate" DESC) "RowNumber",
		COUNT(1) OVER() AS "TotalCount",
		H."HistoryOfOrderId",
		H."SalePointId",
		json_build_object(
			'SalePointLogData',
			(
				SELECT 
					array_to_json(array_agg(R))
				FROM (
					SELECT 
						SPL."SalePointLogId",
						SPL."LotteryChannelId",
						LC."LotteryChannelName",
						SPL."LotteryDate",
						SPL."Quantity",
						SPL."LotteryTypeId",
						LT."LotteryTypeName",
						SPL."TotalValue",
						SPL."FourLastNumber"
					FROM "SalePointLog" SPL 
						JOIN "LotteryChannel" LC ON SPL."LotteryChannelId" = LC."LotteryChannelId"
						JOIN "LotteryType" LT ON SPL."LotteryTypeId" = LT."LotteryTypeId"
					WHERE SPL."SalePointLogId" = ANY(H."SalePointLogIds")
						AND SPL."IsDeleted" IS FALSE
					ORDER BY 
						SPL."SalePointLogId"
				) R
			),
			'GuestData',
			(
				SELECT
					json_build_object(
						'GuestId',
						G."GuestId",
						'FullName',
						G."FullName"
					)
				FROM "Guest" G
				WHERE G."GuestId" = H."GuestId"
			),
			'GuestActionData',
			(
				SELECT
					array_to_json(array_agg(R))
				FROM (
					SELECT
						GA."GuestActionId",
						GA."GuestActionTypeId",
						GAT."TypeName",
						GA."FormPaymentId",
						F."PaymentName",
						GA."TotalPrice"
					FROM "GuestAction" GA 
						JOIN "GuestActionType" GAT ON GA."GuestActionTypeId" = GAT."GuestActionTypeId" 
						JOIN "FormPayment" F ON GA."FormPaymentId" = F."FormPaymentId"
					WHERE GA."IsDeleted" IS FALSE
						AND GA."GuestActionId" = ANY(H."GuestActionIds")
				)R
			),
			'LastData',
			H."Data"
		)::TEXT,
		H."CreatedDate"
	FROM "HistoryOfOrder" H
	WHERE H."IsDeleted" IS FALSE
		AND H."GuestId" = p_guest_id
	ORDER BY H."CreatedDate" DESC
	OFFSET v_offset_row LIMIT p_page_size;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_history_order(p_date timestamp without time zone, p_sale_point_id integer DEFAULT 0, p_shift_distribute_id integer DEFAULT 0)
 RETURNS TABLE("HistoryOfOrderId" integer, "SalePointId" integer, "PrintTimes" integer, "ListPrint" text, "Data" text, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN

	RETURN QUERY
	SELECT 
		H."HistoryOfOrderId",
		H."SalePointId",
		H."PrintTimes",
		TRANSLATE(H."ListPrint"::TEXT, '{}', '[]') AS "ListPrint",
		json_build_object(
			'SalePointLogData',
			(
				SELECT 
					array_to_json(array_agg(R))
				FROM (
					SELECT 
						SPL."SalePointLogId",
						SPL."LotteryChannelId",
						LC."LotteryChannelName",
						SPL."LotteryDate",
						SPL."Quantity",
						SPL."LotteryTypeId",
						LT."LotteryTypeName",
						SPL."TotalValue",
						SPL."FourLastNumber"
					FROM "SalePointLog" SPL 
						JOIN "LotteryChannel" LC ON SPL."LotteryChannelId" = LC."LotteryChannelId"
						JOIN "LotteryType" LT ON SPL."LotteryTypeId" = LT."LotteryTypeId"
					WHERE SPL."SalePointLogId" = ANY(H."SalePointLogIds")
						AND SPL."IsDeleted" IS FALSE
					ORDER BY 
						SPL."SalePointLogId"
				) R
			),
			'GuestData',
			(
				SELECT
					json_build_object(
						'GuestId',
						G."GuestId",
						'FullName',
						G."FullName"
					)
				FROM "Guest" G
				WHERE G."GuestId" = H."GuestId"
			),
			'GuestActionData',
			(
				SELECT
					array_to_json(array_agg(R))
				FROM (
					SELECT
						GA."GuestActionId",
						GA."GuestActionTypeId",
						GAT."TypeName",
						GA."FormPaymentId",
						F."PaymentName",
						GA."TotalPrice"
					FROM "GuestAction" GA 
						JOIN "GuestActionType" GAT ON GA."GuestActionTypeId" = GAT."GuestActionTypeId" 
