					WHERE GA."IsDeleted" IS FALSE
						AND GA."GuestActionId" = ANY(H."GuestActionIds")
				)R
			),
			'LastData',
			H."Data"
		)::TEXT,
		H."CreatedDate"
	FROM "HistoryOfOrder" H
	WHERE H."IsDeleted" IS FALSE
		AND H."ShiftDistributeId" = p_shift_distribute_id 
-- 			OR(
-- 				H."CreatedDate"::DATE = p_date::DATE
-- 				AND (H."SalePointId" = p_sale_point_id OR p_sale_point_id = 0)
-- 			)
	ORDER BY H."CreatedDate" DESC;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_history_order_v1(p_page_size integer, p_page_number integer, p_date timestamp without time zone, p_sale_point_id integer DEFAULT 0, p_shift_distribute_id integer DEFAULT 0)
 RETURNS TABLE("HistoryOfOrderId" integer, "TotalCount" bigint, "SalePointId" integer, "PrintTimes" integer, "ListPrint" text, "Data" text, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
