	FROM "HistoryOfOrder" H
	WHERE H."IsDeleted" IS FALSE
		AND H."ShiftDistributeId" = p_shift_distribute_id 
-- 			OR(
-- 				H."CreatedDate"::DATE = p_date::DATE
-- 				AND (H."SalePointId" = p_sale_point_id OR p_sale_point_id = 0)
