	ORDER BY H."CreatedDate" DESC
	OFFSET v_offset_row LIMIT p_page_size;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_leader_attendent(p_date timestamp without time zone DEFAULT now())
 RETURNS TABLE("GroupSalePointData" text, "LeaderAttendentData" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			LA."CreatedDate",
			(SELECT json_agg(value order by (value->>'UserId')::INT)
			FROM json_array_elements(json_agg(json_build_object('UserId', LA."UserId", 'FullName', U."FullName", 'TriggerSalePoint', LA."TriggerSalePoint")::JSON)))::TEXT AS "ListLeader"
		FROM "LeaderAttendent" LA 
			JOIN "User" U ON LA."UserId" = U."UserId"
		WHERE U."IsActive" IS TRUE
		GROUP BY 
			LA."CreatedDate"
		HAVING LA."CreatedDate" >= ALL(
			SELECT 
				LAA."CreatedDate"
			FROM "LeaderAttendent" LAA
			GROUP BY LAA."CreatedDate"
		)
	),
	tmp1 AS (
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY GS."Option" ORDER BY GS."CreatedDate" DESC) AS "Id",
			GS.*
		FROM "GroupSalePoint" GS
		WHERE GS."Option" <= json_array_length((SELECT T."ListLeader" FROM tmp T LIMIT 1)::JSON)
			AND GS."CreatedDate"::DATE <= NOW()::DATE
	),
	tmp2 AS (
		SELECT 
			T."GroupSalePointId",
			T."UserId",
			U."FullName",
-- 			(
-- 					SELECT 
-- 						array_to_json(array_agg(
-- 								(
-- 									'{"SalePointId": ' 
-- 									|| "SPId"
-- 									||',"SalePointName": "'
-- 									||(SELECT SP."SalePointName" FROM "SalePoint" SP WHERE SP."SalePointId" = "SPId")::TEXT
-- 									||'"}'
-- 								)::JSONB
-- 							)
-- 						)::TEXT
-- 					FROM UNNEST(T."SalePointIds") AS "SPId"
-- 			)::TEXT AS "SalePointIds",
			TRANSLATE(T."SalePointIds"::TEXT, '{}', '[]') AS "SalePointIds",
			T."Option"
		FROM tmp1 T
			JOIN "User" U ON T."UserId" = U."UserId"
		WHERE T."Id" = ANY(SELECT generate_series(0, json_array_length((SELECT T."ListLeader" FROM tmp T LIMIT 1)::JSON))) 
		ORDER BY 
			T."Option",
			T."UserId"
	),
	tmp3 AS (
		SELECT 
			array_to_json(array_agg(T.*))::TEXT AS "GroupSalePointData"
		FROM tmp2 T
	),
	tmp4 AS (
		SELECT 
			T."ListLeader"::TEXT AS "LeaderAttendentData"
		FROM tmp T
	)
	SELECT 
		tmp3."GroupSalePointData",
		tmp4."LeaderAttendentData"
	FROM tmp3 JOIN tmp4 ON TRUE;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_lottery_award_expected(p_date timestamp without time zone DEFAULT now(), p_salepoint_id integer DEFAULT 0)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "DataGroupType" text, "PriceLottery" numeric, "PriceLotteryDB" numeric, "DataWinning" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_date DATE := p_date::DATE;
	v_day_id INT := (CASE WHEN EXTRACT(DOW FROM p_date) =0 THEN 7 ELSE EXTRACT(DOW FROM p_date) END);
	v_two INT := 4;
	v_three INT := 2;
	v_four INT := 3;
BEGIN
	RETURN QUERY
	--Lấy danh sách xổ số
	WITH tmp AS (
		SELECT
			RL."LotteryChannelId",
			RL."LotteryDate",
			RL."TypeAwardId",
			TA."TypeAwardName",
			RL."ListNumber",
			TA."Price"
		FROM "ResultLottery" RL
			JOIN "TypeAward" TA ON TA."TypeAwardId" = RL."TypeAwardId"
		WHERE RL."IsDeleted" IS FALSE
			AND RL."LotteryDate" = v_date
	),
	--Lấy danh sách bán hàng theo ngày 
	tmp1 AS (
		SELECT 
			SPl."SalePointLogId",
			SPL."SalePointId",
			SPL."Quantity",
			SPL."LotteryPriceId",
			SPL."LotteryChannelId",
			SPL."FourLastNumber"
		FROM "SalePointLog" SPL
			LEFT JOIN "Guest" G ON SPL."GuestId" = G."GuestId"
		WHERE SPL."IsDeleted" IS FALSE
			AND SPL."LotteryTypeId" <> 3
			AND (G."GuestId" IS NULL OR (G."GuestId" IS NOT NULL AND G."ScratchPriceId" IS NULL AND G."WholesalePriceId" IS NULL))
			AND SPL."FourLastNumber" IS NOT NULL
			AND SPL."LotteryDate" = p_date::DATE
	),
	--Lấy danh sách masterdata thưởng 
	tmp2 AS (
		SELECT
			WT."WinningTypeId",
			WT."WinningTypeName",
			WT."WinningPrize",
			LC."LotteryChannelId",
			T."ListNumber"
		FROM "WinningType" WT
			JOIN "LotteryChannel" LC ON LC."RegionId" = 2 AND LC."IsActive" IS TRUE AND LC."IsDeleted" IS FALSE AND array_position(LC."DayIds", v_day_id) > 0
			JOIN tmp T ON T."LotteryChannelId" = LC."LotteryChannelId" aND T."TypeAwardId" = 9
		WHERE WT."WinningPrize" IS NOT NULL
			AND WT."WinningTypeId" IN (2,3,4)
	),
	--Quy đổi thưởng
	tmp3 AS (
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY T."SalePointLogId" ORDER BY COALESCE(RS."Price", RSS."WinningPrize", 0) DESC) AS "Id",
			T."SalePointId",
			T."LotteryChannelId",
			T."Quantity",
			T."LotteryPriceId",
			LC."LotteryChannelName",
			T."FourLastNumber",
			RS."TypeAwardId",
			RS."TypeAwardName",
			RS."ListNumber",
			RS."Price",
			RSS."ListNumber" AS "ListNumberDB",
			RSS."WinningTypeId",
			RSS."WinningTypeName",
			RSS."WinningPrize"
		FROM tmp1 T
			JOIN "LotteryChannel" LC ON T."LotteryChannelId" = LC."LotteryChannelId"
			LEFT JOIN tmp RS ON RS."LotteryChannelId" = T."LotteryChannelId" AND T."FourLastNumber" LIKE ANY(SELECT '%'||LS FROM UNNEST(RS."ListNumber") LS)
			LEFT JOIN tmp2 RSS ON RSS."LotteryChannelId" = T."LotteryChannelId" AND  
				T."FourLastNumber" LIKE ANY(
					SELECT '%'||RIGHT(LSS, CASE WHEN RSS."WinningTypeId" = v_two THEN 2 
																			WHEN RSS."WinningTypeId" = v_three THEN 3
																			WHEN RSS."WinningTypeId" = v_four THEN 4 
																			ELSE 0 END) 
					FROM UNNEST(RSS."ListNumber") LSS
				)
		WHERE COALESCE(RS."TypeAwardId", RSS."WinningTypeId", -1) <> -1
	),
	-- lấy danh sách điểm bán
	tmp4 AS (
		SELECT
			SP."SalePointId",
			SP."SalePointName"
		FROM "SalePoint" SP 
		WHERE SP."IsActive" IS TRUE
			AND SP."IsDeleted" IS FALSE
			AND (SP."SalePointId" = p_salepoint_id OR p_salepoint_id = 0)
	),
	tmp5 AS (
		SELECT 
			SP."SalePointId",
			T."LotteryChannelId",
			COALESCE(T."TypeAwardId", T."WinningTypeId" + 10000) AS "WinningTypeId",
			COALESCE(T."TypeAwardName", T."WinningTypeName") AS "WinningTypeName",
			COALESCE(SUM(COALESCE(T."Price", T."WinningPrize", 0) * T."Quantity"), 0)  AS "PriceLottery"
		FROM tmp4 SP
			LEFT JOIN tmp3 T ON T."SalePointId" = SP."SalePointId" AND T."Id" = 1
		GROUP BY
			SP."SalePointId",
			COALESCE(T."TypeAwardId", T."WinningTypeId" + 10000),
			COALESCE(T."TypeAwardName", T."WinningTypeName"),
			T."LotteryChannelId"
	),
	tmp6 AS (
		SELECT
			T."SalePointId",
			(CASE WHEN COUNT(T."WinningTypeId") > 0 THEN array_to_json(array_agg(TO_JSONB(T.*))) ELSE '[]' END) AS "DataGroupType"
		FROM tmp5 T
		GROUP BY
			T."SalePointId"
	)
	SELECT 
		SP."SalePointId",
		SP."SalePointName",
		S."DataGroupType"::TEXT,
		COALESCE(SUM(COALESCE(T."Price", 0) * T."Quantity") FILTER (WHERE T."TypeAwardId" IS NOT NULL), 0) AS "PriceLottery",
		COALESCE(SUM(COALESCE(T."WinningPrize", 0) * T."Quantity") FILTER (WHERE T."TypeAwardId" IS NULL AND T."WinningTypeId" IS NOT NULL), 0)  AS "PriceLotteryDB",
		(CASE WHEN (COUNT(T."TypeAwardId") + COUNT(T."WinningTypeId")) > 0 
				THEN array_to_json(array_agg(json_build_object(
					'Quantity',
					T."Quantity",
					'Number',
					T."FourLastNumber",
					'LotteryChannelId',
					T."LotteryChannelId",					
					'LotteryChannelName',
					T."LotteryChannelName",
					'WinningTypeId',
					COALESCE(T."TypeAwardId", T."WinningTypeId" + 10000),
					'TypeAwardName',
					COALESCE(T."TypeAwardName", T."WinningTypeName"),
					'WinningPrice',
					COALESCE(T."Price", T."WinningPrize"),
					'Number', 	COALESCE(T."FourLastNumber", '')		
				)))
				ELSE '[]' END)::TEXT
		 AS "DataWinning"
	FROM tmp4 SP
		LEFT JOIN tmp3 T ON T."SalePointId" = SP."SalePointId" AND T."Id" = 1
		LEFT JOIN tmp6 S ON S."SalePointId" = SP."SalePointId"
	GROUP BY
		SP."SalePointId",
		SP."SalePointName",
		S."DataGroupType"::TEXT
	ORDER BY
		SP."SalePointId";
END
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_sale_of_vietlott_in_date(p_date timestamp without time zone DEFAULT now(), p_sale_point_id integer DEFAULT 0)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "SalePointData" text, "SumData" numeric, "RealData" numeric, "IsEqual" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT	
			SP."SalePointId",
			SP."SalePointName"
		FROM "SalePoint" SP 
		WHERE SP."IsActive" IS TRUE
			AND SP."IsDeleted" IS FALSE
			AND (p_sale_point_id = 0 OR SP."SalePointId" = p_sale_point_id)
	),
	tmp1 AS (
		SELECT 
			T."SalePointId",
			T."ShiftDistributeId",
			SD."ShiftId",
			T."UserId",
			T."TotalPrice"
		FROM "Transaction" T
			JOIN "ShiftDistribute" SD ON SD."ShiftDistributeId" = T."ShiftDistributeId"
		WHERE T."TransactionTypeId" = 2
			AND T."Date" = p_date::DATE
			AND T."IsDeleted" IS FALSE
	),
	tmp2 AS (
		SELECT 
			T."SalePointId",
			T."ShiftDistributeId",
			T."UserId",
			T."TotalPrice"
		FROM "Transaction" T
		WHERE T."TransactionTypeId" = 10
			AND T."Date" = p_date::DATE
			AND T."IsDeleted" IS FALSE
	),
	tmp3 AS (
		SELECT
			SP.*,
			(
				SELECT array_to_json(array_agg(R))
				FROM (
					SELECT 
						SD."SalePointId",
						SD."ShiftDistributeId",
						SD."ShiftId",
						SD."UserId",
						U."FullName",
						T."TotalPrice"
					FROM "ShiftDistribute" SD
						JOIN "User" U ON SD."UserId" = U."UserId"
						LEFT JOIN tmp1 T ON SD."UserId" = T."UserId" AND SD."ShiftDistributeId" = T."ShiftDistributeId"
					WHERE SD."SalePointId" = SP."SalePointId"
						AND SD."DistributeDate" = p_date::DATE
				) R
			)::TEXT AS "SalePointData",
			(
				SELECT 
					SUM(T."TotalPrice")
				FROM tmp1 T
				WHERE T."SalePointId" = SP."SalePointId"
			)::NUMERIC AS "SumData",
			(
				SELECT T."TotalPrice" FROM tmp2 T WHERE T."SalePointId" = SP."SalePointId"
			)::NUMERIC AS "RealData",
			(
				SELECT SUM(T."TotalPrice") FROM tmp1 T WHERE T."SalePointId" = SP."SalePointId"
			)::NUMERIC AS "TotalSalePoint"
		FROM tmp SP
	)
	SELECT
		T."SalePointId",
		T."SalePointName",
		T."SalePointData",
		T."SumData",
		T."RealData",
		(CASE WHEN COALESCE(T."RealData", 0) = COALESCE(T."TotalSalePoint", 0) THEN TRUE ELSE FALSE END) AS "IsEqual"
	FROM tmp3 T;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_transaction(p_month character varying, p_user_id integer DEFAULT 0, p_type integer DEFAULT 0)
 RETURNS TABLE("TransactionId" integer, "TransactionTypeId" integer, "TransactionTypeName" character varying, "Note" character varying, "IsSum" boolean, "Quantity" integer, "Price" numeric, "TotalPrice" numeric, "SalePointId" integer, "SalePointName" character varying, "ShiftDistributeId" integer, "TypeName" character varying, "UserId" integer, "Date" date, "ActionBy" integer, "ActionByName" character varying, "ActionDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
		T."TransactionId",
		T."TransactionTypeId",
		TT."TransactionTypeName",
		T."Note",
		TT."IsSum",
		T."Quantity",
		T."Price",
		T."TotalPrice",
		T."SalePointId",
		SP."SalePointName",
		T."ShiftDistributeId",
		TN."Name" AS "TypeName",
		T."UserId",
		T."Date",
		T."ActionBy",
		T."ActionByName",
		T."ActionDate"
	FROM "Transaction" T
		JOIN "TransactionType" TT ON T."TransactionTypeId" = TT."TransactionTypeId"
		LEFT JOIN "SalePoint" SP ON SP."SalePointId" = T."SalePointId"
		LEFT JOIN "TypeName" TN ON T."TypeNameId" = TN."TypeNameId"
	WHERE T."IsDeleted" IS FALSE
		AND (CASE WHEN p_type = 1 THEN T."TransactionTypeId" IN (4,5,6,7) ELSE TRUE END)
		AND TO_CHAR(T."Date", 'YYYY-MM') = p_month
		AND (COALESCE(p_user_id, 0) = 0 OR T."ActionBy" = p_user_id)
	ORDER BY T."TransactionTypeId", T."Date" DESC;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_list_union_in_year(p_year integer)
 RETURNS TABLE("PriceUnionFirst" numeric, "TotalUnionSend" numeric, "TotalUse" numeric, "TotalRemain" numeric, "UserData" text, "DataUse" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	--Lấy danh sách user
	WITH tmp0 AS (
		SELECT 
			U."UserId",
			U."FullName",
			U."UserTitleId",
			U."SalePointId",
			U."ListSalePoint",
			SP."SalePointName"
		FROM crm_get_user_ddl(0, (p_year||'-01-01')::TIMESTAMP) U
			LEFT JOIN "SalePoint" SP ON SP."SalePointId" = U."SalePointId" 
		WHERE U."UserTitleId" IN (6, 4, 5)
	),
	--Lấy danh sách tháng trong năm
	tmp AS (
		SELECT
			TO_CHAR((p_year||'-'||T."Data"||'-01')::DATE, 'YYYY-MM') AS "Month"
		FROM (
			SELECT
				*
			FROM generate_series(1, 12) "Data"
		) T
	),
	--Lấy danh sách tiền nộp trước năm 
	tmp1 AS (
		SELECT
			SUM(((SC."Data"::JSON)->>'PriceUnion')::NUMERIC) AS "PriceUnion"
		FROM "SalaryConfirm" SC
		WHERE SC."IsDeleted" IS FALSE
			AND SUBSTRING(SC."Month", 1, 4)::INT < p_year
	),
	--Lấy danh sách trích quỹ trước năm
	tmp2 AS (
		SELECT
			SUM(T."TotalPrice") AS "TotalPrice"
		FROM "Transaction" T
		WHERE T."IsDeleted" IS FALSE
			AND T."TransactionTypeId" = 12
			AND TO_CHAR(T."Date", 'YYYY')::INT < p_year
	),
	--Lấy danh sách nộp trong năm theo user
	tmp3 AS (
		SELECT
			SC."UserId",
			SC."Month",
			SUM(((SC."Data"::JSON)->>'PriceUnion')::NUMERIC) AS "PriceUnion"
		FROM "SalaryConfirm" SC
		WHERE SC."IsDeleted" IS FALSE
			AND SUBSTRING(SC."Month", 1, 4)::INT = p_year
		GROUP BY 
			SC."UserId",
			SC."Month"
	),
	--Lấy danh sách nộp trong năm theo 12 tháng
	tmp4 AS (
		SELECT
			U."UserId",
			U."FullName",
			U."UserTitleId",
			U."SalePointId",
			U."ListSalePoint",
			U."SalePointName",
			SUM(COALESCE(P."PriceUnion", 0)) AS "TotalUnionSend",
			array_to_json(array_agg(json_build_object(
				'Month',
				M."Month",
				'PriceUnion',
				COALESCE(P."PriceUnion", 0)
			))) AS "DataSend"
		FROM tmp0 U
			JOIN tmp M ON TRUE
			LEFT JOIN tmp3 P ON U."UserId" = P."UserId" AND M."Month" = P."Month"
		GROUP BY
			U."UserId",
			U."FullName",
			U."UserTitleId",
			U."SalePointId",
			U."ListSalePoint",
			U."SalePointName"
		ORDER BY
			U."UserTitleId" IN (4, 6),
			U."SalePointId",
			U."UserId"
	),
	--Lấy danh sách trích quỹ trong năm
	tmp5 AS (
		SELECT
			SUM(T."TotalPrice") AS "TotalUse",
			array_to_json(array_agg(json_build_object(
				'TransactionId',
				T."TransactionId",
				'Date',
				T."Date",
				'Price',
				T."Price",
				'Note',
				T."Note"
			))) AS "DataUse"
		FROM "Transaction" T
		WHERE T."IsDeleted" IS FALSE
			AND T."TransactionTypeId" = 12
			AND TO_CHAR(T."Date", 'YYYY')::INT = p_year
	),
	--Tổng hợp tiền nộp user trong năm
	tmp6 AS (
		SELECT
			SUM(U."TotalUnionSend") AS "TotalUnionSend",
			array_to_json(array_agg(TO_JSONB(U.*))) "UserData"
		FROM tmp4 U
	),
	tmp7 AS (
		SELECT 
			COALESCE(P."PriceUnion", 0) - COALESCE(T."TotalPrice", 0) AS "PriceUnionFirst",
			COALESCE(U."TotalUnionSend", 0) AS "TotalUnionSend",
			COALESCE(A."TotalUse", 0) AS "TotalUse",	
			COALESCE(P."PriceUnion", 0) - COALESCE(T."TotalPrice", 0) + COALESCE(U."TotalUnionSend", 0) - COALESCE(A."TotalUse", 0) AS "TotalRemain",
			U."UserData",
			A."DataUse"
		FROM tmp1 P
			FULL JOIN tmp2 T ON TRUE
			FULL JOIN tmp6 U ON TRUE
			FULL JOIN tmp5 A ON TRUE
	)
	SELECT
		T."PriceUnionFirst",
		T."TotalUnionSend",
		T."TotalUse",
		T."TotalRemain",
		T."UserData"::TEXT,
		T."DataUse"::TEXT		
	FROM tmp7 T;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_percent(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "MainUserData" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT
			SP."SalePointId",
			SP."SalePointName"
		FROM "SalePoint" SP 
		WHERE SP."IsDeleted" IS FALSE
			AND SP."IsActive" IS TRUE
	),
	tmp1 AS (
		SELECT 	
			T."SalePointId",
			T."MainUserId",
			T."PercentMainUserId"
		FROM crm_get_list_percent_salepoint_in_month(p_month) T
	)
	SELECT
		SP."SalePointId",
		SP."SalePointName",
		--TRANSLATE(COALESCE(S."MainUserId", '{}'::INT[])::TEXT, '{}', '[]') AS "MainUserId",
		COALESCE((
			SELECT 
				array_to_json(array_agg(json_build_object(
					'UserId',
					I,
					'FullName',
					(SELECT U."FullName" FROM "User" U WHERE U."UserId" = I)::VARCHAR,
					'Percent',
					(S."PercentMainUserId"[array_position(S."MainUserId"::TEXT[], I::TEXT)])::NUMERIC		
				)))
			FROM UNNEST(S."MainUserId") I
		)::TEXT, '[]') AS "MainUserId"
		--TRANSLATE(COALESCE(S."PercentMainUserId", '{}'::NUMERIC[])::TEXT, '{}', '[]') AS "PercentMainUserId"
	FROM tmp SP
		LEFT JOIN tmp1 S ON SP."SalePointId" = S."SalePointId" 
	ORDER BY SP."SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_sale_loto_in_date(p_month character varying, p_sale_point_id integer DEFAULT 0)
 RETURNS TABLE("Date" date, "SalePointId" integer, "AllTotalPrice" numeric, "Data" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT
			(p_month||'-'||"I")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "I"
	),
	tmp1 AS (
		SELECT
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SP."SalePointId",
			(
				SELECT 
					TT."TotalPrice"
				FROM "Transaction" TT
				WHERE TT."Date" = T."Date"
					AND TT."TransactionTypeId" = 3
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
				LIMIT 1
			)::NUMERIC AS "TotalPrice"
		FROM tmp T
			JOIN "SalePoint" SP ON SP."IsDeleted" IS FALSE AND SP."IsActive" IS TRUE
			LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = SP."SalePointId" AND SD."DistributeDate" = T."Date"::DATE
			LEFT JOIN "User" U ON SD."UserId" = U."UserId"
		WHERE (SP."SalePointId" = p_sale_point_id OR p_sale_point_id = 0)
		ORDER BY 
			T."Date",
			SD."ShiftId"
	),
	tmp2 AS (
		SELECT
			T."Date",
			T."SalePointId",
			SUM(COALESCE(T."TotalPrice", 0)) AS "AllTotalPrice",
			array_to_json(ARRAY_AGG(TO_JSONB(T.*)))::TEXT AS "Data"
		FROM tmp1 T
		GROUP BY
			T."Date",
			T."SalePointId"
	)
	SELECT 
		T."Date",
		T."SalePointId",
		T."AllTotalPrice",
		T."Data"
	FROM tmp2 T
	ORDER BY 
		T."SalePointId",
		T."Date";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_sale_loto_in_date_1(p_month character varying, p_transaction_type integer, p_sale_point_id integer DEFAULT 0)
 RETURNS TABLE("Date" date, "SalePointId" integer, "AllTotalPrice" numeric, "Data" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT
			(p_month||'-'||"I")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "I"
	),
	tmp1 AS (
		SELECT
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SP."SalePointId",
			(
				SELECT 
					TT."TotalPrice"
				FROM "Transaction" TT
				WHERE TT."Date" = T."Date"
					AND TT."TransactionTypeId" = p_transaction_type
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
				LIMIT 1
			)::NUMERIC AS "TotalPrice"
		FROM tmp T
			JOIN "SalePoint" SP ON SP."IsDeleted" IS FALSE AND SP."IsActive" IS TRUE
			LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = SP."SalePointId" AND SD."DistributeDate" = T."Date"::DATE
			LEFT JOIN "User" U ON SD."UserId" = U."UserId"
		WHERE (SP."SalePointId" = p_sale_point_id OR p_sale_point_id = 0)
		ORDER BY 
			T."Date",
			SD."ShiftId"
	),
	tmp2 AS (
		SELECT
			T."Date",
			T."SalePointId",
			SUM(COALESCE(T."TotalPrice", 0)) AS "AllTotalPrice",
			array_to_json(ARRAY_AGG(TO_JSONB(T.*)))::TEXT AS "Data"
		FROM tmp1 T
		GROUP BY
			T."Date",
			T."SalePointId"
	)
	SELECT 
		T."Date",
		T."SalePointId",
		T."AllTotalPrice",
		T."Data"
	FROM tmp2 T
	ORDER BY 
		T."SalePointId",
		T."Date";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_sale_loto_in_date_v1(p_month character varying, p_transaction_type integer, p_sale_point_id integer DEFAULT 0)
 RETURNS TABLE("Date" date, "SalePointId" integer, "AllTotalPrice" numeric, "Data" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT
			(p_month||'-'||"I")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "I"
	),
	tmp1 AS (
		SELECT
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SP."SalePointId",
			(
				SELECT 
					TT."TotalPrice"
				FROM "Transaction" TT
				WHERE TT."Date" = T."Date"
					AND TT."TransactionTypeId" = p_transaction_type
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
				LIMIT 1
			)::NUMERIC AS "TotalPrice",
			(
				SELECT 
					TT."TransactionId"
				FROM "Transaction" TT
				WHERE TT."Date" = T."Date"
					AND TT."TransactionTypeId" = p_transaction_type
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
				LIMIT 1
			)::NUMERIC AS "TransactionId"
		FROM tmp T
			JOIN "SalePoint" SP ON SP."IsDeleted" IS FALSE AND SP."IsActive" IS TRUE
			LEFT JOIN "ShiftDistribute" SD ON SD."SalePointId" = SP."SalePointId" AND SD."DistributeDate" = T."Date"::DATE
			LEFT JOIN "User" U ON SD."UserId" = U."UserId"
		WHERE (SP."SalePointId" = p_sale_point_id OR p_sale_point_id = 0)
		ORDER BY 
			T."Date",
			SD."ShiftId"
	),
	tmp2 AS (
		SELECT
			T."Date",
			T."SalePointId",
			SUM(COALESCE(T."TotalPrice", 0)) AS "AllTotalPrice",
			array_to_json(ARRAY_AGG(TO_JSONB(T.*)))::TEXT AS "Data"
		FROM tmp1 T
		GROUP BY
			T."Date",
			T."SalePointId"
	)
	SELECT 
		T."Date",
		T."SalePointId",
		T."AllTotalPrice",
		T."Data"
	FROM tmp2 T
	ORDER BY 
		T."SalePointId",
		T."Date";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_sale_vietlott_in_date(p_month character varying, p_sale_point_id integer DEFAULT 0)
 RETURNS TABLE("Date" date, "UserId" integer, "FullName" character varying, "ShiftId" integer, "ShiftDistributeId" integer, "SalePointId" integer, "TotalPrice" numeric)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT
			(p_month||'-'||"I")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "I"
	),
	tmp1 AS (
		SELECT
			T."Date",
			SD."UserId",
			U."FullName",
			SD."ShiftId",
			SD."ShiftDistributeId",
			SD."SalePointId",
			(
				SELECT 
					TT."TotalPrice"
				FROM "Transaction" TT
				WHERE TT."Date" = T."Date"
					AND TT."TransactionTypeId" = 2
					AND TT."IsDeleted" IS FALSE
					AND TT."UserId" = SD."UserId"
					AND TT."ShiftDistributeId" = SD."ShiftDistributeId"
				LIMIT 1
			)::NUMERIC AS "TotalPrice"
		FROM tmp T
			JOIN "ShiftDistribute" SD ON (SD."SalePointId" = p_sale_point_id OR p_sale_point_id =0) AND SD."DistributeDate" = T."Date"::DATE
			JOIN "User" U ON SD."UserId" = U."UserId"
		ORDER BY 
			T."Date",
			SD."ShiftId"
	),
	tmp2 AS (
		SELECT
			T."Date",
			T."UserId",
			U."FullName",
			NULL::INT AS "ShiftId",
			NULL::INT AS "ShiftDistributeId",
			T."SalePointId",
			T."TotalPrice"
		FROM "Transaction" T
			JOIN "User" U ON T."UserId" = U."UserId"
		WHERE T."TransactionTypeId" = 10
			AND TO_CHAR(T."Date", 'YYYY-MM') = p_month
			AND T."IsDeleted" IS FALSE
			AND (T."SalePointId" = p_sale_point_id OR p_sale_point_id = 0)
			
	),
	tmp3 AS (
		SELECT 
			* 
		FROM tmp1
		UNION ALL
		SELECT 
			* 
		FROM tmp2	
	)
	SELECT 
		T.*
	FROM tmp3 T
	ORDER BY 
		T."SalePointId",
		T."Date",
		T."ShiftId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_static_fee()
 RETURNS TABLE("StaticFeeTypeId" integer, "StaticFeeTypeName" character varying, "StaticFeeTypeNameVIET" character varying)
