$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_total_commision(p_date text, p_salepoint_id bigint DEFAULT 0)
 RETURNS TABLE("CommissionId" integer, "User" text, "SalePointId" integer, "SalePointName" text, "Date" date, "TotalCommision" bigint, "Fee" numeric, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	--Lấy ra danh sách nhân viên được chia hoa hồng
	WITH tmp AS(
	SELECT 
		U."UserId",
		U."FullName"
	FROM "User" U 
		LEFT JOIN "UserRole" UR ON UR."UserId" = U."UserId"
	WHERE UR."UserTitleId" = 5  
	),tmp2 AS(
		SELECT 
			C."CommissionId",
			array_to_json(array_agg(json_build_object(
				'UserId',
				T."UserId",
				'UserName',
				T."FullName"
			)))::TEXT AS "Users"
		FROM tmp T 
		LEFT JOIN "Commission" C ON T."UserId" = ANY(C."ListStaff")
		LEFT JOIN "SalePoint" S ON S."SalePointId" = C."SalePointId"
		WHERE  (C."SalePointId" = p_salepoint_id OR p_salepoint_id = 0) AND date_part('month',C."Date") = date_part('month',CONCAT(p_date,'-01')::DATE)  
		GROUP BY C."CommissionId"
	)
	SELECT 
		C."CommissionId",
		T2."Users",
		C."SalePointId",
		S."SalePointName"::TEXT,
		C."Date",
		C."TotalValue",
		C."Fee",
		C."CreatedDate"
	FROM tmp T 
	LEFT JOIN "Commission" C ON T."UserId" = ANY(C."ListStaff")
	LEFT JOIN "SalePoint" S ON S."SalePointId" = C."SalePointId"
	LEFT JOIN tmp2 T2 ON T2."CommissionId" = C."CommissionId"
	WHERE  (C."SalePointId" = p_salepoint_id OR p_salepoint_id = 0) AND date_part('month',C."Date") = date_part('month',CONCAT(p_date,'-01')::DATE) AND C."IsDeleted" = FALSE
	GROUP BY
		C."CommissionId",
		C."SalePointId",
		S."SalePointName",
		C."Date",
		T2."Users"
	ORDER BY C."Date" DESC, C."CommissionId" DESC;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_get_user_by_date_and_salepoint(p_sale_point_id integer, p_date timestamp without time zone)
 RETURNS TABLE("ShiftDistributeId" integer, "UserId" integer, "FullName" character varying, "ShiftId" integer)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
		SD."ShiftDistributeId",
		SD."UserId",
		U."FullName",
		SD."ShiftId"
	FROM "ShiftDistribute" SD
		JOIN "User" U ON SD."UserId" = U."UserId"
	WHERE SD."SalePointId" = p_sale_point_id 
		AND SD."DistributeDate" = p_date :: DATE
		AND SD."IsActive" IS TRUE
	ORDER BY SD."ShiftId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_insert_update_transaction(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_transaction_type_id integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
BEGIN
	--INSERT
	IF p_action_type = 1 THEN 
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			INSERT INTO "Transaction"(
				"TransactionTypeId",
				"Note",
				"Quantity",
				"Price",
				"TotalPrice",
				"SalePointId",
				"ShiftDistributeId",
				"IsDeleted",
				"UserId",
				"TypeNameId",
				"Date",
				"ActionBy",
				"ActionByName",
				"ActionDate"
			)
			VALUES(
				p_transaction_type_id,
				(ele->>'Note')::VARCHAR,
				COALESCE((ele->>'Quantity')::INT, 1),
				(ele->>'Price')::NUMERIC,
				(COALESCE((ele->>'Price')::NUMERIC,0) * COALESCE((ele->>'Quantity')::INT,1)) :: NUMERIC,
				COALESCE((ele->>'SalePointId')::INT, (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = (ele->>'UserId')::INT)::INT),
				(ele->>'ShiftDistributeId')::INT,
				FALSE,
				COALESCE((ele->>'UserId')::INT, p_action_by),
				(ele->>'TypeNameId')::INT,
				COALESCE((ele->>'Date')::DATE, v_time::DATE),
				p_action_by,
				p_action_by_name,
				v_time
			) RETURNING "TransactionId" INTO v_id;
		END LOOP;
		
		v_mess := 'Thêm thành công';
		
	--UPDATE
	ELSEIF p_action_type = 2 THEN
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "Transaction" SET 
				"Note" = COALESCE((ele->>'Note')::VARCHAR, "Note"),
				"Quantity" = COALESCE((ele->>'Quantity')::INT, "Quantity"),
				"Price" =  COALESCE((ele->>'Price')::NUMERIC, "Price"),
				"TotalPrice" = COALESCE((COALESCE((ele->>'Price')::NUMERIC, "Price") * COALESCE((ele->>'Quantity')::INT, "Quantity")) :: NUMERIC, "TotalPrice"),
				"SalePointId" = COALESCE((ele->>'SalePointId')::INT, "SalePointId"),
				"ShiftDistributeId" = COALESCE((ele->>'ShiftDistributeId')::INT, "ShiftDistributeId"),
				"UserId" = COALESCE((ele->>'UserId')::INT, "UserId"),
				"TypeNameId" = COALESCE((ele->>'TypeNameId')::INT, "TypeNameId"),
				"Date" = COALESCE((ele->>'Date')::DATE, "Date"),
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate"= v_time
			WHERE "TransactionId" = (ele->>'TransactionId') :: INT;
		END LOOP;
		
		v_id := 1;
		v_mess := 'Cập nhật thành công';
		
	--DELETE
	ELSEIF p_action_type = 3 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "Transaction" SET 
				"IsDeleted" = TRUE,
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate"= v_time
			WHERE "TransactionId" = (ele->>'TransactionId') :: INT;
		END LOOP;
		
		v_id := 1;
		v_mess := 'Xóa thành công';
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_commission(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();

BEGIN
	-- INSERT
	IF p_action_type = 1 THEN
		ele := p_data::JSON;
		INSERT INTO "Commission"(
			"SalePointId",
			"UserId",
			"ListStaff",
			"TotalValue",
			"Date",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"IsDeleted",
			"Fee"
		)
		VALUES(
			(ele->>'SalePointId')::INT,
			p_action_by,
			translate((ele->>'ListStaff')::TEXT, '[]','{}')::INT[],
			(ele->>'Commission')::NUMERIC,
			(ele->>'Date')::DATE,
			p_action_by,
			p_action_by_name,
			v_time,
			FALSE,
			COALESCE((ele->>'Fee')::NUMERIC, 0)
		);
	
		v_id := 1;
		v_mess := 'Thêm thành công';
	-- UPDATE
	ELSEIF p_action_type = 2 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "Commission" 
			SET 
				"SalePointId" = COALESCE((ele->>'SalePointId')::INT, "SalePointId"),
				"UserId" = COALESCE(p_action_by, "UserId"),
				"ListStaff" = COALESCE(translate((ele->>'ListStaff')::TEXT, '[]','{}')::INT[], "ListStaff"),
				"TotalValue" = COALESCE((ele->>'TotalValue')::NUMERIC, "TotalValue"),
				"Date" = COALESCE((ele->>'Date')::DATE, "Date"),
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate" = v_time
			WHERE "CommissionId" = (ele ->> 'CommissionId') :: INT;
		END LOOP;
		
		v_id := 1;
		v_mess := 'Cập nhật thành công';
	-- DELETE
	ELSE 
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "Commission" 
			SET 
				"IsDeleted" = TRUE,
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate" = v_time
			WHERE "CommissionId" = (ele ->> 'CommissionId') :: INT;
		END LOOP;
		
		v_id := 1;
		v_mess := 'Xoá thành công';
		
	END IF;

	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_cornfirm_item(p_action_by integer, p_action_by_name character varying, p_type_action_id integer, p_data text, p_confirm_log_id integer DEFAULT 0, p_confirm_type integer DEFAULT 1, p_confirm_for integer DEFAULT 1, p_data_info text DEFAULT NULL::text, p_guest_id integer DEFAULT NULL::integer, p_time timestamp without time zone DEFAULT NULL::timestamp without time zone)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_cur_remain INT := 0; 
	v_check INT := 0;
	v_item_price INT;
	v_data TEXT;
	v_data_info TEXT;
	v_json_info JSON;
	v_change INT:= 0;
	v_confirm_for INT;
	v_type_action_id INT;
	v_guest_id INT;
	ele JSON;
BEGIN
	--TH1. Xử lý trạng thái dùng
	IF p_type_action_id = 3 AND p_confirm_for <> 3 THEN
		--1. ITEM
		IF COALESCE(p_confirm_for,1) = 1 THEN
			SELECT f."Id", f."Id",f."Message" 
			INTO v_check,v_id, v_mess 
				FROM crm_salepoint_update_item(p_action_by, p_action_by_name,p_type_action_id, p_data) f;
		--2. GUEST
		ELSEIF p_confirm_for = 2 THEN
			SELECT f."Id", f."Id",f."Message" 
			INTO v_check,v_id, v_mess 
				FROM crm_salepoint_update_guest_action(p_action_by, p_action_by_name, p_data) f;
		ELSE 
			v_id := -1;
			v_mess := 'NO.1: Trạng thái mới';
		END IF;
	
	--TH2. NOT EXISTS confirmLogId -> CREATE CONFIRM 
	ELSEIF COALESCE(p_confirm_log_id, 0) = 0 THEN
		--0. FULL
		INSERT INTO "ConfirmLog" (
			"Data",
			"TypeActionId",
			"ActionBy",
			"ActionByName",
			"ConfirmStatusId",
			"ConfirmFor",
			"DataActionInfo",
			"ActionDate",
			"GuestId"
		) VALUES (
			p_data,
			p_type_action_id,
			p_action_by,
			p_action_by_name,
			COALESCE(p_confirm_type, 1),
			COALESCE(p_confirm_for, 1),
			COALESCE(p_data_info, NULL),
			COALESCE(p_time, NOW()),
			COALESCE(p_guest_id, NULL)
		) 
		RETURNING "ConfirmLogId" INTO v_id;
		
		v_mess := 'Tạo mới thành công';
		
	--TH3. confirm susscess
	ELSEIF p_confirm_type = 2 THEN
		--0. GET Data
		SELECT 
			IL."Data", 
			IL."DataActionInfo",
			IL."ConfirmStatusId",
			IL."ConfirmFor",
			IL."TypeActionId",
			IL."GuestId"
		INTO 
			v_data,
			v_data_info, 
			v_change,
			v_confirm_for,
			v_type_action_id,
			v_guest_id
		FROM "ConfirmLog" IL 
		WHERE IL."ConfirmLogId" = COALESCE(p_confirm_log_id,0);
				
		--EXEC
		IF v_change = 1 THEN
			IF COALESCE(v_confirm_for, 1) = 1 THEN
			
			--1. ITEM
				SELECT f."Id",f."Message" ,f."Id"
				INTO v_check, v_mess ,v_id
				FROM crm_salepoint_update_item(p_action_by, p_action_by_name,v_type_action_id, v_data) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				
			ELSEIF v_confirm_for = 2 THEN
			
			--2. GUEST 
				SELECT f."Id",f."Message" ,f."Id"
				INTO v_check, v_mess ,v_id
				FROM crm_salepoint_update_guest_action(p_action_by, p_action_by_name, v_data) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				
			ELSEIF v_confirm_for = 3 THEN
			
			--3. LOTTERY 
				v_json_info := v_data_info :: JSON;
				IF COALESCE((v_json_info ->> 'ShiftDistributeId'):: INT,0) = 0
					OR COALESCE((v_json_info ->> 'UserRoleId'):: INT,0) = 0
				THEN 
				
					RAISE 'Không đủ thông tin để cập nhật';
					
				ELSE
				
					SELECT f."Id", f."Message",	f."Id"
					INTO v_check, v_mess,	v_id
					FROM crm_activity_sell_lottery_v2((v_json_info ->> 'ShiftDistributeId'):: INT, (v_json_info ->> 'UserRoleId'):: INT, p_action_by, p_action_by_name, v_data,COALESCE((v_json_info ->> 'GuestId'):: INT)) f;
					
					IF v_id <= 0 THEN
						RAISE '%', v_mess;
					END IF;
					
				END IF;
			
			--4. TẠO KHÁCH NỢ
			ELSEIF v_confirm_for = 4 THEN
			
				-- Tạo khách
				SELECT f."Id", f."Message"
				INTO v_id, v_mess
				FROM crm_salepoint_create_or_update_guest(p_action_by, p_action_by_name, 1, v_data_info::TEXT) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				

				-- Cập nhật lại data confirm
				v_data_info := (v_data_info::JSONB || ('{"GuestId": '||v_id||'}')::JSONB)::TEXT;
				
				-- Thêm dòng mới vào để confirm 
				SELECT f."Id", f."Message"
				INTO v_id, v_mess
				FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 1,	v_data::TEXT, NULL, NULL, 3, v_data_info::TEXT) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				

				-- Confirm dòng mới 
				SELECT f."Id", f."Message",	f."Id"
				INTO v_check, v_mess,	v_id
				FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 1, NULL, v_id, 2, 3) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
			
			ELSE
				v_id := -1;
				v_mess := 'NO.2: Trạng thái mới';
			END IF;
			
		ELSE
		
			v_id := -1;
			v_mess := 'Yêu cầu đã được xác nhận trước đó!';
			v_check := 0;
			
		END IF;
		
		--CHECK EXEC
		IF v_check > 0 THEN
		
				--0. FULL
				UPDATE "ConfirmLog"
				SET "ConfirmStatusId" = COALESCE(p_confirm_type, "ConfirmStatusId"),
						"ConfirmByName" = p_action_by_name,
						"ConfirmBy" = p_action_by,
						"ConfirmDate" = NOW()
				WHERE "ConfirmLogId" = COALESCE(p_confirm_log_id, 0);
				
		END IF;
		
	ELSEIF p_confirm_type = 3 THEN
	
	--TH4. cancel request
		--0. FULL
		SELECT IL."ConfirmStatusId" INTO v_change
		FROM "ConfirmLog" IL 
		WHERE IL."ConfirmLogId" = p_confirm_log_id;
			
		IF v_change = 1 THEN

			UPDATE "ConfirmLog"
			SET "ConfirmStatusId"= p_confirm_type,
					"ConfirmByName"= p_action_by_name,
					"ConfirmBy" = p_action_by,
					"ConfirmDate" = NOW()
			WHERE "ConfirmLogId" = COALESCE(p_confirm_log_id, 0);		
			
			v_id := 1;
			v_mess := 'Cập nhật thành công';
			
		ELSE
		
			v_id := -1;
			v_mess := 'Yêu cầu đã được xác nhận trước đó!';
			
		END IF;
	ELSE
			v_id := -1;
			v_mess := 'Dữ liệu chưa đúng định dạng';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_cornfirm_item_v2(p_action_by integer, p_action_by_name character varying, p_type_action_id integer, p_data text, p_confirm_log_id integer DEFAULT 0, p_confirm_type integer DEFAULT 1, p_confirm_for integer DEFAULT 1, p_data_info text DEFAULT NULL::text, p_guest_id integer DEFAULT NULL::integer, p_time timestamp without time zone DEFAULT NULL::timestamp without time zone, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_cur_remain INT := 0; 
	v_check INT := 0;
	v_item_price INT;
	v_data TEXT;
	v_data_info TEXT;
	v_json_info JSON;
	v_change INT:= 0;
	v_confirm_for INT;
	v_type_action_id INT;
	v_guest_id INT;
	ele JSON;
	v_array INT[];
BEGIN

	--TH1. Xử lý trạng thái dùng
	IF p_type_action_id = 3 AND p_confirm_for <> 3 THEN

		--1. ITEM
		IF COALESCE(p_confirm_for,1) = 1 THEN

			SELECT f."Id", f."Id",f."Message" 
			INTO v_check,v_id, v_mess 
				FROM crm_salepoint_update_item(p_action_by, p_action_by_name,p_type_action_id, p_data) f;
		--2. GUEST
		ELSEIF p_confirm_for = 2 THEN

			SELECT f."Id", f."Id",f."Message" 
			INTO v_check,v_id, v_mess 
				FROM crm_salepoint_update_guest_action_v2(p_action_by, p_action_by_name, p_data) f;
		ELSE 
			v_id := -1;
			v_mess := 'NO.1: Trạng thái mới';
		END IF;
	
	--TH2. NOT EXISTS confirmLogId -> CREATE CONFIRM 
	ELSEIF COALESCE(p_confirm_log_id, 0) = 0 THEN
		
		--Tạo giỏ hàng nếu là tương tác từ khách hàng
		IF p_confirm_for = 2 OR p_confirm_for = 3 THEN 
			IF p_order_id IS NULL THEN
				INSERT INTO "HistoryOfOrder" (
					"SalePointId",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"IsDeleted",
					"ShiftDistributeId"
				)
				VALUES (
					((p_data_info::JSON)->>'SalePointId')::INT,
					p_action_by,
					p_action_by_name,
					v_time,
					FALSE,
					((p_data_info::JSON)->>'ShiftDistributeId')::INT
				) RETURNING "HistoryOfOrderId" INTO p_order_id;
			END IF;

			--Lấy danh sách id bán hàng
			SELECT "ConfirmLogIds" INTO v_array FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = p_order_id;
			IF v_array IS NULL THEN 
				v_array := '{}'::INT[];
			END IF;
		
		END IF;
	
		--0. FULL
		INSERT INTO "ConfirmLog" (
			"Data",
			"TypeActionId",
			"ActionBy",
			"ActionByName",
			"ConfirmStatusId",
			"ConfirmFor",
			"DataActionInfo",
			"ActionDate",
			"GuestId",
			"HistoryOfOrderId"
		) VALUES (
			p_data,
			p_type_action_id,
			p_action_by,
			p_action_by_name,
			COALESCE(p_confirm_type, 1),
			COALESCE(p_confirm_for, 1),
			COALESCE(p_data_info, NULL),
			COALESCE(p_time, NOW()),
			COALESCE(p_guest_id, NULL),
			p_order_id
		) 
		RETURNING "ConfirmLogId" INTO v_id;
		
		v_array := array_append(v_array, v_id);
		
		UPDATE "HistoryOfOrder" 
		SET 
			"ConfirmLogIds" = v_array
		WHERE "HistoryOfOrderId" = p_order_id;
		
		v_mess := 'Tạo mới thành công';
		
	--TH3. confirm susscess
	ELSEIF p_confirm_type = 2 THEN
		--0. GET Data
		SELECT 
			IL."Data", 
			IL."DataActionInfo",
			IL."ConfirmStatusId",
			IL."ConfirmFor",
			IL."TypeActionId",
			IL."GuestId",
			IL."HistoryOfOrderId"
		INTO 
			v_data,
			v_data_info, 
			v_change,
			v_confirm_for,
			v_type_action_id,
			v_guest_id,
			p_order_id
		FROM "ConfirmLog" IL 
		WHERE IL."ConfirmLogId" = COALESCE(p_confirm_log_id,0);
				
		--EXEC
		IF v_change = 1 THEN
			IF COALESCE(v_confirm_for, 1) = 1 THEN
			
			--1. ITEM
				SELECT f."Id",f."Message" ,f."Id"
				INTO v_check, v_mess ,v_id
				FROM crm_salepoint_update_item(p_action_by, p_action_by_name,v_type_action_id, v_data) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				
			ELSEIF v_confirm_for = 2 THEN
			
			--2. GUEST 
				SELECT f."Id",f."Message" ,f."Id"
				INTO v_check, v_mess ,v_id
				FROM crm_salepoint_update_guest_action_v2(p_action_by, p_action_by_name, v_data, NULL, p_order_id,p_month) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				
			ELSEIF v_confirm_for = 3 THEN
			
			--3. LOTTERY 
				v_json_info := v_data_info :: JSON;
				IF COALESCE((v_json_info ->> 'ShiftDistributeId'):: INT,0) = 0
					OR COALESCE((v_json_info ->> 'UserRoleId'):: INT,0) = 0
				THEN 
				
					RAISE 'Không đủ thông tin để cập nhật';
					
				ELSE
				
					SELECT f."Id", f."Message",	f."Id"
					INTO v_check, v_mess,	v_id
					FROM crm_activity_sell_lottery_v4((v_json_info ->> 'ShiftDistributeId'):: INT, (v_json_info ->> 'UserRoleId'):: INT, p_action_by, p_action_by_name, v_data,COALESCE((v_json_info ->> 'GuestId'):: INT), p_order_id) f;
					
					IF v_id <= 0 THEN
						RAISE '%', v_mess;
					END IF;
					
				END IF;
			
			--4. TẠO KHÁCH NỢ
			ELSEIF v_confirm_for = 4 THEN
			
				-- Tạo khách
				SELECT f."Id", f."Message"
				INTO v_id, v_mess
				FROM crm_salepoint_create_or_update_guest(p_action_by, p_action_by_name, 1, v_data_info::TEXT) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				

				-- Cập nhật lại data confirm
				v_data_info := (v_data_info::JSONB || ('{"GuestId": '||v_id||'}')::JSONB)::TEXT;
				
				-- Thêm dòng mới vào để confirm 
				SELECT f."Id", f."Message"
				INTO v_id, v_mess
				FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 1,	v_data::TEXT, NULL, NULL, 3, v_data_info::TEXT) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				

				-- Confirm dòng mới 
				SELECT f."Id", f."Message",	f."Id"
				INTO v_check, v_mess,	v_id
				FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 1, NULL, v_id, 2, 3) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
			
			ELSE
				v_id := -1;
				v_mess := 'NO.2: Trạng thái mới';
			END IF;
			
		ELSE
		
			v_id := -1;
			v_mess := 'Yêu cầu đã được xác nhận trước đó!';
			v_check := 0;
			
		END IF;
		
		--CHECK EXEC
		IF v_check > 0 THEN
		
				--0. FULL
				UPDATE "ConfirmLog"
				SET "ConfirmStatusId" = COALESCE(p_confirm_type, "ConfirmStatusId"),
						"ConfirmByName" = p_action_by_name,
						"ConfirmBy" = p_action_by,
						"ConfirmDate" = NOW()
				WHERE "ConfirmLogId" = COALESCE(p_confirm_log_id, 0);
				
		END IF;
		
	ELSEIF p_confirm_type = 3 THEN 
	
	--TH4. cancel request
		--0. FULL
		SELECT IL."ConfirmStatusId" INTO v_change
		FROM "ConfirmLog" IL 
		WHERE IL."ConfirmLogId" = p_confirm_log_id;
			
		IF v_change = 1 THEN

			UPDATE "ConfirmLog"
			SET "ConfirmStatusId"= p_confirm_type,
					"ConfirmByName"= p_action_by_name,
					"ConfirmBy" = p_action_by,
					"ConfirmDate" = NOW()
			WHERE "ConfirmLogId" = COALESCE(p_confirm_log_id, 0);		
			
			v_id := 1;
			v_mess := 'Cập nhật thành công';
			
		ELSE
		
			v_id := -1;
			v_mess := 'Yêu cầu đã được xác nhận trước đó!';
			
		END IF;
	ELSE
			v_id := -1;
			v_mess := 'Dữ liệu chưa đúng định dạng';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_cornfirm_item_v3(p_action_by integer, p_action_by_name character varying, p_type_action_id integer, p_data text, p_confirm_log_id integer DEFAULT 0, p_confirm_type integer DEFAULT 1, p_confirm_for integer DEFAULT 1, p_data_info text DEFAULT NULL::text, p_guest_id integer DEFAULT NULL::integer, p_time timestamp without time zone DEFAULT NULL::timestamp without time zone, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_cur_remain INT := 0; 
	v_check INT := 0;
	v_item_price INT;
	v_data TEXT;
	v_data_info TEXT;
	v_json_info JSON;
	v_change INT:= 0;
	v_confirm_for INT;
	v_type_action_id INT;
	v_guest_id INT;
	ele JSON;
	v_array INT[];
BEGIN

	--TH1. Xử lý trạng thái dùng
	IF p_type_action_id = 3 AND p_confirm_for <> 3 THEN
		--1. ITEM
		IF COALESCE(p_confirm_for,1) = 1 THEN
			SELECT f."Id", f."Id",f."Message" 
			INTO v_check,v_id, v_mess 
				FROM crm_salepoint_update_item_v2(p_action_by, p_action_by_name,p_type_action_id, p_data) f;
		--2. GUEST
		ELSEIF p_confirm_for = 2 THEN
			SELECT f."Id", f."Id",f."Message" 
			INTO v_check,v_id, v_mess 
				FROM crm_salepoint_update_guest_action_v2(p_action_by, p_action_by_name, p_data) f;
		ELSE 
			v_id := -1;
			v_mess := 'NO.1: Trạng thái mới';
		END IF;
	
	--TH2. NOT EXISTS confirmLogId -> CREATE CONFIRM 
	ELSEIF COALESCE(p_confirm_log_id, 0) = 0 THEN
		
		--Tạo giỏ hàng nếu là tương tác từ khách hàng
		IF p_confirm_for = 2 OR p_confirm_for = 3 THEN 
			IF p_order_id IS NULL THEN
				INSERT INTO "HistoryOfOrder" (
					"SalePointId",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"IsDeleted",
					"ShiftDistributeId"
				)
				VALUES (
					((p_data_info::JSON)->>'SalePointId')::INT,
					p_action_by,
					p_action_by_name,
					v_time,
					FALSE,
					((p_data_info::JSON)->>'ShiftDistributeId')::INT
				) RETURNING "HistoryOfOrderId" INTO p_order_id;
			END IF;

			--Lấy danh sách id bán hàng
			SELECT "ConfirmLogIds" INTO v_array FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = p_order_id;
			IF v_array IS NULL THEN 
				v_array := '{}'::INT[];
			END IF;
		
		END IF;
	
		--0. FULL
		INSERT INTO "ConfirmLog" (
			"Data",
			"TypeActionId",
			"ActionBy",
			"ActionByName",
			"ConfirmStatusId",
			"ConfirmFor",
			"DataActionInfo",
			"ActionDate",
			"GuestId",
			"HistoryOfOrderId"
		) VALUES (
			p_data,
			p_type_action_id,
			p_action_by,
			p_action_by_name,
			COALESCE(p_confirm_type, 1),
			COALESCE(p_confirm_for, 1),
			COALESCE(p_data_info, NULL),
			COALESCE(p_time, NOW()),
			COALESCE(p_guest_id, NULL),
			p_order_id
		) 
		RETURNING "ConfirmLogId" INTO v_id;
		
		v_array := array_append(v_array, v_id);
		
		UPDATE "HistoryOfOrder" 
		SET 
			"ConfirmLogIds" = v_array
		WHERE "HistoryOfOrderId" = p_order_id;
		
		v_mess := 'Tạo mới thành công';
		
	--TH3. confirm susscess
	ELSEIF p_confirm_type = 2 THEN
		--0. GET Data
		SELECT 
			IL."Data", 
			IL."DataActionInfo",
			IL."ConfirmStatusId",
			IL."ConfirmFor",
			IL."TypeActionId",
			IL."GuestId",
			IL."HistoryOfOrderId"
		INTO 
			v_data,
			v_data_info, 
			v_change,
			v_confirm_for,
			v_type_action_id,
			v_guest_id,
			p_order_id
		FROM "ConfirmLog" IL 
		WHERE IL."ConfirmLogId" = COALESCE(p_confirm_log_id,0);
				
		--EXEC
		IF v_change = 1 THEN
			IF COALESCE(v_confirm_for, 1) = 1 THEN
		
			--1. ITEM
				SELECT f."Id",f."Message" ,f."Id"
				INTO v_check, v_mess ,v_id
				FROM crm_salepoint_update_item_v2(p_action_by, p_action_by_name,v_type_action_id, v_data) f;
					RAISE NOTICE 'vailoz,%',1;
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				
			ELSEIF v_confirm_for = 2 THEN
			
			--2. GUEST 
				SELECT f."Id",f."Message" ,f."Id"
				INTO v_check, v_mess ,v_id
				FROM crm_salepoint_update_guest_action_v2(p_action_by, p_action_by_name, v_data, NULL, p_order_id) f;
			
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				
			ELSEIF v_confirm_for = 3 THEN
			
			--3. LOTTERY 
				v_json_info := v_data_info :: JSON;
				IF COALESCE((v_json_info ->> 'ShiftDistributeId'):: INT,0) = 0
					OR COALESCE((v_json_info ->> 'UserRoleId'):: INT,0) = 0
				THEN 
				
					RAISE 'Không đủ thông tin để cập nhật';
					
				ELSE
				
					SELECT f."Id", f."Message",	f."Id"
					INTO v_check, v_mess,	v_id
					FROM crm_activity_sell_lottery_v3((v_json_info ->> 'ShiftDistributeId'):: INT, (v_json_info ->> 'UserRoleId'):: INT, p_action_by, p_action_by_name, v_data,COALESCE((v_json_info ->> 'GuestId'):: INT), p_order_id) f;
					
					IF v_id <= 0 THEN
						RAISE '%', v_mess;
					END IF;
					
				END IF;
			
			--4. TẠO KHÁCH NỢ
			ELSEIF v_confirm_for = 4 THEN
			
				-- Tạo khách
				SELECT f."Id", f."Message"
				INTO v_id, v_mess
				FROM crm_salepoint_create_or_update_guest(p_action_by, p_action_by_name, 1, v_data_info::TEXT) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				

				-- Cập nhật lại data confirm
				v_data_info := (v_data_info::JSONB || ('{"GuestId": '||v_id||'}')::JSONB)::TEXT;
				
				-- Thêm dòng mới vào để confirm 
				SELECT f."Id", f."Message"
				INTO v_id, v_mess
				FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 1,	v_data::TEXT, NULL, NULL, 3, v_data_info::TEXT) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
				

				-- Confirm dòng mới 
				SELECT f."Id", f."Message",	f."Id"
				INTO v_check, v_mess,	v_id
				FROM crm_salepoint_update_cornfirm_item(p_action_by, p_action_by_name, 1, NULL, v_id, 2, 3) f;
				
				IF v_id <= 0 THEN
					RAISE '%', v_mess;
				END IF;
			
			ELSE
				v_id := -1;
				v_mess := 'NO.2: Trạng thái mới';
			END IF;
			
		ELSE
		
			v_id := -1;
			v_mess := 'Yêu cầu đã được xác nhận trước đó!';
			v_check := 0;
			
		END IF;
		
		--CHECK EXEC
		IF v_check > 0 THEN
		
				--0. FULL
				UPDATE "ConfirmLog"
				SET "ConfirmStatusId" = COALESCE(p_confirm_type, "ConfirmStatusId"),
						"ConfirmByName" = p_action_by_name,
						"ConfirmBy" = p_action_by,
						"ConfirmDate" = NOW()
				WHERE "ConfirmLogId" = COALESCE(p_confirm_log_id, 0);
				
		END IF;
		
	ELSEIF p_confirm_type = 3 THEN
	
	--TH4. cancel request
		--0. FULL
		SELECT IL."ConfirmStatusId" INTO v_change
		FROM "ConfirmLog" IL 
		WHERE IL."ConfirmLogId" = p_confirm_log_id;
			
		IF v_change = 1 THEN

			UPDATE "ConfirmLog"
			SET "ConfirmStatusId"= p_confirm_type,
					"ConfirmByName"= p_action_by_name,
					"ConfirmBy" = p_action_by,
					"ConfirmDate" = NOW()
			WHERE "ConfirmLogId" = COALESCE(p_confirm_log_id, 0);		
			
			v_id := 1;
			v_mess := 'Cập nhật thành công';
			
		ELSE
		
			v_id := -1;
			v_mess := 'Yêu cầu đã được xác nhận trước đó!';
			
		END IF;
	ELSE
			v_id := -1;
			v_mess := 'Dữ liệu chưa đúng định dạng';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_guest_action(p_action_by integer, p_action_by_name character varying, p_data text, p_guest_action_type_id integer DEFAULT 2)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	ele JSON;
	v_tget_price NUMERIC := 0;
	v_price NUMERIC;
BEGIN
	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			IF 	COALESCE((ele ->> 'GuestActionId')::INT, 0) = 0 AND 
					--COALESCE((ele ->> 'GuestId')::INT, 0) <> 0 AND 
					COALESCE((ele ->> 'FormPaymentId')::INT, 0) <> 0
			THEN
				
				IF COALESCE((ele ->> 'FormPaymentId')::INT, 0) = 2 
					AND COALESCE((ele ->> 'Note')::VARCHAR, '') = '' THEN
					RAISE 'Thanh toán chuyển khoản phải có mã giao dịch';
				END IF;
				
				INSERT INTO "GuestAction" (
					"GuestId",
					"SalePointId",
					"GuestActionTypeId",
					"TotalPrice",
					"FormPaymentId",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"Note",
					"GuestInfo",
					"ModifyBy",
					"ModifyByName",
					"ModifyDate",
					"DoneTransfer",
					"ShiftDistributeId"
				)
				VALUES (
					COALESCE((ele ->> 'GuestId')::INT, 0),
					COALESCE((ele ->> 'SalePointId')::INT, 0),
					COALESCE((ele ->> 'GuestActionTypeId')::INT, p_guest_action_type_id),
					COALESCE((ele ->> 'TotalPrice')::NUMERIC, 0),
					COALESCE((ele ->> 'FormPaymentId')::INT, 1),
					COALESCE((ele ->> 'ActionBy')::INT, p_action_by),
					COALESCE((ele ->> 'ActionByName')::VARCHAR, p_action_by_name),
					COALESCE((ele ->> 'ActionDate')::TIMESTAMP, NOW()),
					COALESCE((ele ->> 'Note')::VARCHAR, NULL),
					COALESCE((ele ->> 'GuestInfo')::VARCHAR, NULL),
					p_action_by,
					p_action_by_name,
					NOW(),
					(CASE WHEN COALESCE((ele ->> 'FormPaymentId')::INT, 1) = 1 THEN TRUE ELSE FALSE	END),
					COALESCE((ele ->> 'ShiftDistributeId')::INT, 0)
				);
				v_id := 1;
				v_mess := 'Tạo mới thành công';
			ELSEIF COALESCE((ele ->> 'GuestActionId')::INT, 0) = 0 THEN 
				v_id := -1;
				v_mess := 'Không đủ thông tin cập nhật';
			ELSE
				UPDATE "GuestAction" 
				SET 
					"ModifyBy" = p_action_by,
					"ModifyByName" = p_action_by_name,
					"ModifyDate" = NOW(),
					"IsDeleted" = COALESCE((ele ->> 'IsDeleted')::BOOLEAN, "IsDeleted"),
					"FormPaymentId"= COALESCE((ele ->> 'FormPaymentId')::INT, "FormPaymentId"),
					"TotalPrice" = COALESCE((ele ->> 'TotalPrice')::NUMERIC, "TotalPrice"),
					"DoneTransfer" = COALESCE((ele ->> 'DoneTransfer')::BOOLEAN, "DoneTransfer")
				WHERE "GuestActionId" = (ele ->> 'GuestActionId')::INT;
				v_id := 1;
				v_mess := 'Cập nhật thành công';
				--Xu ly delete
			END IF;
	END LOOP;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_guest_action_v2(p_action_by integer, p_action_by_name character varying, p_data text, p_guest_action_type_id integer DEFAULT 2, p_order_id integer DEFAULT NULL::integer)
 RETURNS TABLE("Id" integer, "Message" text, "OrderId" integer)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	ele JSON;
	v_tget_price NUMERIC := 0;
	v_price NUMERIC;
	v_count INT := 0;
	v_array INT[];
	v_tmp INT;
BEGIN

	
	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			IF 	COALESCE((ele ->> 'GuestActionId')::INT, 0) = 0 AND 
					--COALESCE((ele ->> 'GuestId')::INT, 0) <> 0 AND 
					COALESCE((ele ->> 'FormPaymentId')::INT, 0) <> 0
			THEN
				
				IF v_count = 0 THEN 
					--Tạo hoá đơn khi chưa có
					IF p_order_id IS NULL THEN
						
						INSERT INTO "HistoryOfOrder" (
							"SalePointId",
							"CreatedBy",
							"CreatedByName",
							"CreatedDate",
							"IsDeleted",
							"ShiftDistributeId"
						)
						VALUES (
							(ele ->> 'SalePointId')::INT,
							p_action_by,
							p_action_by_name,
							v_time,
							FALSE,
							(ele ->> 'ShiftDistributeId')::INT
						) RETURNING "HistoryOfOrderId" INTO p_order_id;
						
					END IF;
	
					--Lấy danh sách id bán hàng
					SELECT "GuestActionIds" INTO v_array FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = p_order_id;
					IF v_array IS NULL THEN 
						v_array := '{}'::INT[];
					END IF;
				END IF;
				
				p_guest_action_type_id := COALESCE((ele->>'GuestActionTypeId')::INT, p_guest_action_type_id);
				
				IF COALESCE((ele ->> 'FormPaymentId')::INT, 0) = 2 
					AND COALESCE((ele ->> 'Note')::VARCHAR, '') = ''
					AND p_guest_action_type_id = 2 THEN
					RAISE 'Thanh toán chuyển khoản phải có mã giao dịch';
				END IF;
				
				INSERT INTO "GuestAction" (
					"GuestId",
					"SalePointId",
					"GuestActionTypeId",
					"TotalPrice",
					"FormPaymentId",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"Note",
					"GuestInfo",
					"ModifyBy",
					"ModifyByName",
					"ModifyDate",
					"DoneTransfer",
					"ShiftDistributeId",
					"HistoryOfOrderId"
				)
				VALUES (
					COALESCE((ele ->> 'GuestId')::INT, 0),
					COALESCE((ele ->> 'SalePointId')::INT, 0),
					COALESCE((ele ->> 'GuestActionTypeId')::INT, p_guest_action_type_id),
					COALESCE((ele ->> 'TotalPrice')::NUMERIC, 0),
					COALESCE((ele ->> 'FormPaymentId')::INT, 1),
					COALESCE((ele ->> 'ActionBy')::INT, p_action_by),
					COALESCE((ele ->> 'ActionByName')::VARCHAR, p_action_by_name),
					COALESCE((ele ->> 'ActionDate')::TIMESTAMP, NOW()),
					COALESCE((ele ->> 'Note')::VARCHAR, NULL),
					COALESCE((ele ->> 'GuestInfo')::VARCHAR, NULL),
					p_action_by,
					p_action_by_name,
					NOW(),
					(CASE WHEN COALESCE((ele ->> 'FormPaymentId')::INT, 1) = 1 THEN TRUE ELSE FALSE	END),
					COALESCE((ele ->> 'ShiftDistributeId')::INT, 0),
					p_order_id
				) RETURNING "GuestActionId" INTO v_tmp;
							
				v_array := array_append(v_array, v_tmp);
				
				UPDATE "HistoryOfOrder" 
				SET 
					"GuestActionIds" = v_array
				WHERE "HistoryOfOrderId" = p_order_id;
						
				v_id := 1;
				v_mess := 'Tạo mới thành công';
			ELSEIF COALESCE((ele ->> 'GuestActionId')::INT, 0) = 0 THEN 
				v_id := -1;
				v_mess := 'Không đủ thông tin cập nhật';
			ELSE
				UPDATE "GuestAction" 
				SET 
					"ModifyBy" = p_action_by,
					"ModifyByName" = p_action_by_name,
					"ModifyDate" = NOW(),
					"IsDeleted" = COALESCE((ele ->> 'IsDeleted')::BOOLEAN, "IsDeleted"),
					"FormPaymentId"= COALESCE((ele ->> 'FormPaymentId')::INT, "FormPaymentId"),
					"TotalPrice" = COALESCE((ele ->> 'TotalPrice')::NUMERIC, "TotalPrice"),
					"DoneTransfer" = COALESCE((ele ->> 'DoneTransfer')::BOOLEAN, "DoneTransfer")
				WHERE "GuestActionId" = (ele ->> 'GuestActionId')::INT;
				v_id := 1;
				v_mess := 'Cập nhật thành công';
				--Xu ly delete
			END IF;
			
			v_count := v_count + 1;
	END LOOP;
	
	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;

	RETURN QUERY
	SELECT v_id, v_mess, p_order_id;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_history_order(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON := p_data::JSON;
	v_time TIMESTAMP := NOW();
	v_tmp INT;
	v_arr TEXT;
BEGIN
	--INSERT
	IF (ele->>'ActionType')::INT = 1 THEN 
		INSERT INTO "HistoryOfOrder"(
			"SalePointId",
			"PrintTimes",
			"ListPrint",
			"Data",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"IsDeleted",
			"ShiftDistributeId",
			"GuestId"
		)
		VALUES(
			(ele->>'SalePointId')::INT,
			(CASE WHEN (ele->>'IsPrint')::BOOL IS TRUE THEN 1 ELSE 0 END),
			(CASE WHEN (ele->>'IsPrint')::BOOL IS TRUE THEN '{'||p_action_by||'}' ELSE '{}' END)::INT[],
			(ele->>'Data')::TEXT,
			p_action_by,
			p_action_by_name,
			v_time,
			FALSE,
			(ele->>'ShiftDistributeId')::INT,
			(ele->>'GuestId')::INT
		) RETURNING "HistoryOfOrderId" INTO v_id;
		
		v_mess := 'Thêm thành công';
	-- PRINT
	ELSEIF (ele->>'ActionType')::INT = 2 THEN
		
		UPDATE "HistoryOfOrder"
		SET 
			"PrintTimes" = "PrintTimes" + 1,
			"ListPrint" = array_append("ListPrint", p_action_by)
		WHERE "HistoryOfOrderId" = (ele->>'HistoryOfOrderId')::INT;
	
		v_id := 1;
		v_mess := 'In thành công';
	-- DELETE
	ELSEIF (ele->>'ActionType')::INT = 3 THEN
	
		UPDATE "HistoryOfOrder"
		SET 
			"IsDeleted" = TRUE
		WHERE "HistoryOfOrderId" = (ele->>'HistoryOfOrderId')::INT;
		
		--Xoá lịch sử bán hàng
		FOR v_tmp IN (SELECT UNNEST("SalePointLogIds") FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = (ele->>'HistoryOfOrderId')::INT) LOOP
			UPDATE "SalePointLog"
			SET "IsDeleted" = TRUE
			WHERE "SalePointLogId" = v_tmp;			
		END LOOP;
		--XOá lịch sử trả tiền
		FOR v_tmp IN (SELECT UNNEST("GuestActionIds") FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = (ele->>'HistoryOfOrderId')::INT) LOOP
			UPDATE "GuestAction"
			SET "IsDeleted" = TRUE
			WHERE "GuestActionId" = v_tmp;		
		END LOOP;
		--Xoá lịch sử confirm
		FOR v_tmp IN (SELECT UNNEST("ConfirmLogIds") FROM "HistoryOfOrder" WHERE "HistoryOfOrderId" = (ele->>'HistoryOfOrderId')::INT) LOOP
			UPDATE "ConfirmLog"
			SET "IsDeleted" = TRUE
			WHERE "ConfirmLogId" = v_tmp;			
		END LOOP;
		
		v_id := 1;
		v_mess := 'Xoá thành công';
	--Tự conftrm bill
	ELSEIF (ele->>'ActionType')::INT = 4 THEN
		SELECT 
			array_to_json(array_agg(jsonb_build_object('ConfirmLogId', A."ConfirmLogId")))::TEXT 
		INTO
			v_arr
		FROM (
			SELECT
				UNNEST(HOD."ConfirmLogIds") AS "ConfirmLogId"
			FROM "HistoryOfOrder" HOD
			WHERE HOD."HistoryOfOrderId" = (ele->>'HistoryOfOrderId')::INT
		) A;
		SELECT 
			T."Id",
			T."Message"
		INTO
			v_id,
			v_mess
		FROM crm_salepoint_confirm_list_payment_v2(0, 'System_CRM', 2, v_arr ) T;
	ELSE
		v_id := -1;
		v_mess := 'Không có gì để thực hiện';
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_item(p_action_by integer, p_action_by_name character varying, p_type_action integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_cur_remain INT := 0; 
	v_cur_price NUMERIC := 0;
	v_check INT := 0;
	v_item_price INT;
	ele JSON;
	v_avg_price NUMERIC;
BEGIN
	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP

			-- Lấy giá một món hàng
			SELECT 
				"Price" INTO v_item_price
			FROM "Item"
			WHERE "ItemId" = (ele ->> 'ItemId') :: INT;	
			
			-- Kiểm tra món hàng đã tồn tại hay chưa
-- 			IF v_item_price IS NULL THEN
-- 					RAISE 'Món hàng chưa được thêm vào kho';
-- 			END IF;
			v_item_price:= (ele ->> 'Price') :: INT;
				
			
			-- Tính giá trung bình của món hàng khi "Cho việc xuất và sử dụng" - ĐÚNG VỚI KHO
			SELECT 
				COALESCE("TotalPrice",0)/ (CASE WHEN "TotalRemaining" =0 THEN 1 ELSE "TotalRemaining" END)
				INTO v_avg_price
			FROM "ItemFull"
			WHERE "ItemId" = (ele ->> 'ItemId') :: INT AND "SalePointId" =0;
				
			-- Nếu trong kho không còn thì lấy gía ở DDL
			IF v_avg_price IS NULL OR v_avg_price =0 THEN
				SELECT 
						"Price" INTO v_avg_price
				FROM "Item"
				WHERE "ItemId" = (ele ->> 'ItemId') :: INT;	
			END IF;
						
			-- Lấy tồn kho hiện tại
			SELECT 
				COALESCE(IFL."TotalRemaining", 0) AS "TotalRemaining",COALESCE(I."TotalPrice", 0) AS "TotalPrice" INTO v_cur_remain, v_cur_price
			FROM "ItemFullLog" IFL
				LEFT JOIN "ItemFull" I ON I."SalePointId" = IFL."SalePointId" AND I."ItemId" = IFL."ItemId"
			WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
				AND I."ItemId" = (ele ->> 'ItemId') :: INT
				AND IFL."Month" = ( ele ->> 'Month'||'-01')::DATE
			ORDER BY IFL."CreateDate" DESC
			LIMIT 1;
			-- Thêm dòng vào Log
			INSERT INTO "ItemFullLog"(
				"ItemId",
				"SalePointId",
				"TotalRemaining",
				"Quantity",
				"ItemTypeId",
				"CreateBy",
				"CreateByName",
				"CreateDate",		
				"TotalPrice",
				"BalancePrice",
				"Month"
			)
			VALUES(
				(ele ->> 'ItemId') :: INT,	
				(ele ->> 'SalePointId') :: INT, 
				(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_remain, 0) + (ele ->> 'Quantity') :: INT 
							ELSE COALESCE(v_cur_remain, 0) - (ele ->> 'Quantity') :: INT END),
				(ele ->> 'Quantity') :: INT,
				p_type_action,
				p_action_by,
				p_action_by_name,
				v_time,
				((ele ->> 'Quantity') :: INT) * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END),
				(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: INT * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN  v_item_price ELSE v_avg_price END)
							ELSE COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: INT * v_avg_price END),
				(ele->>'Month'||'-01')::DATE
			) RETURNING "TotalRemaining" INTO v_check;
			UPDATE "ItemFullLog" 
			SET "TotalRemaining" = 	"TotalRemaining" + (ele ->> 'Quantity') :: INT 
				WHERE "Month" >= (ele->>'Month'||'-01')::DATE + interval '1 month'
				AND "SalePointId" = (ele ->> 'SalePointId') :: INT
				AND "ItemId" = (ele ->> 'ItemId') :: INT;
			IF v_check < 0 THEN
				RAISE 'Không đủ hàng hóa trong kho';
			END IF;				
			
			IF NOT EXISTS (
				SELECT 1 FROM "ItemFull" I
				WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT) 
				AND p_type_action = 1
			THEN 
			
				-- Thêm dòng nếu chưa có và là nhập kho
				INSERT INTO "ItemFull"(
					"ItemId",
					"SalePointId",
					"TotalRemaining",
					"CreateBy",
					"CreateByName",
					"CreateDate",
					"TotalPrice"
				)
				VALUES(
					(ele ->> 'ItemId') :: INT,	
					(ele ->> 'SalePointId') :: INT, 
					(ele ->> 'Quantity') :: INT,
					p_action_by,
					p_action_by_name,
					v_time,
					(ele ->> 'Quantity') :: INT * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END)
				
				);
				
			ELSEIF NOT EXISTS (
				SELECT 1 FROM "ItemFull" I
				WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT
			) THEN 				
				-- Báo lỗi vì trừ kho khi chưa có hàng
				RAISE 'Không đủ hàng hóa trong kho';
			ELSE 
				-- Cập nhật kho của điểm bán 
				UPDATE "ItemFull" 
				SET
					"ModifyDate" = v_time,
					"TotalRemaining" = (CASE WHEN p_type_action = 1 THEN "TotalRemaining"  + (ele ->> 'Quantity') :: INT 
																										ELSE "TotalRemaining" - (ele ->> 'Quantity') :: INT END),
					"TotalPrice" =(CASE WHEN p_type_action = 1 THEN "TotalPrice"  + (ele ->> 'Quantity') :: INT * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END)
																										ELSE "TotalPrice" - (ele ->> 'Quantity') :: INT * v_avg_price END)
																								 
				WHERE "SalePointId" = (ele ->> 'SalePointId') :: INT
					AND "ItemId" = (ele ->> 'ItemId') :: INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;				
					
			END IF;
			
			-- Cập nhật kho số 0 nếu là xuất, nhập kho
			IF (ele ->> 'SalePointId') :: INT <> 0 AND p_type_action <> 3 THEN
				
				SELECT 
					COALESCE(I."TotalRemaining", 0),COALESCE(I."TotalPrice", 0)  INTO v_cur_remain, v_cur_price
				FROM "ItemFull" I
				WHERE I."SalePointId" = 0 :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT;
				
				-- Thêm dòng nếu chưa có và là nhập kho
				INSERT INTO "ItemFullLog"(
					"ItemId",
					"SalePointId",
					"TotalRemaining",
					"Quantity",
					"ItemTypeId",
					"CreateBy",
					"CreateByName",
					"CreateDate",		
					"TotalPrice",
					"BalancePrice",
					"Month"
				)
				VALUES(
					(ele ->> 'ItemId') :: INT,	
					0, 
					(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_remain, 0) - (ele ->> 'Quantity') :: INT
								ELSE COALESCE(v_cur_remain, 0) + (ele ->> 'Quantity') :: INT END),
					(ele ->> 'Quantity') :: INT,
					(CASE WHEN p_type_action = 1 THEN 2 ELSE 1 END),
					p_action_by,
					p_action_by_name,
					v_time,
					((ele ->> 'Quantity') :: INT) * v_avg_price,
					(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: INT * v_avg_price
						ELSE COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: INT * v_avg_price END),
					to_date(p_month, 'YYYY-MM')
				) RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;
				
				UPDATE "ItemFull"
				SET
					"ModifyDate" = v_time,
					"TotalRemaining" = (CASE WHEN p_type_action = 1 THEN "TotalRemaining" - (ele ->> 'Quantity') :: INT 
																										ELSE "TotalRemaining" + (ele ->> 'Quantity') :: INT END),
																										
					"TotalPrice"	= (CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: INT * v_avg_price
																										ELSE COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: INT * v_avg_price END)
				WHERE "SalePointId" = 0 
					AND "ItemId" = (ele ->> 'ItemId') :: INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;								
			
			END IF;
			
	END LOOP;
	
	v_id := 1;
	v_mess := 'Cập nhật thành công';
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_item_v2(p_action_by integer, p_action_by_name character varying, p_type_action integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_cur_remain INT := 0; 
	v_cur_price NUMERIC := 0;
	v_check INT := 0;
	v_item_price INT;
	ele JSON;
	v_avg_price NUMERIC;
BEGIN
FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP

			-- Lấy giá một món hàng
			SELECT 
				"Price" INTO v_item_price
			FROM "Item"
			WHERE "ItemId" = (ele ->> 'ItemId') :: INT;	
			
			-- Kiểm tra món hàng đã tồn tại hay chưa
-- 			IF v_item_price IS NULL THEN
-- 					RAISE 'Món hàng chưa được thêm vào kho';
-- 			END IF;
			v_item_price:= (ele ->> 'Price') :: INT;
				
			
			-- Tính giá trung bình của món hàng khi "Cho việc xuất và sử dụng" - ĐÚNG VỚI KHO
			SELECT 
				COALESCE("TotalPrice",0)/ (CASE WHEN "TotalRemaining" =0 THEN 1 ELSE "TotalRemaining" END)
				INTO v_avg_price
			FROM "ItemFull"
			WHERE "ItemId" = (ele ->> 'ItemId') :: INT AND "SalePointId" =0;
				
			-- Nếu trong kho không còn thì lấy gía ở DDL
			IF v_avg_price IS NULL OR v_avg_price =0 THEN
				SELECT 
						"Price" INTO v_avg_price
				FROM "Item"
				WHERE "ItemId" = (ele ->> 'ItemId') :: INT;	
			END IF;
						
			-- Lấy tồn kho hiện tại
			SELECT 
				COALESCE(IFL."TotalRemaining", 0) AS "TotalRemaining",COALESCE(I."TotalPrice", 0) AS "TotalPrice" INTO v_cur_remain, v_cur_price
			FROM "ItemFullLog" IFL
				LEFT JOIN "ItemFull" I ON I."SalePointId" = IFL."SalePointId" AND I."ItemId" = IFL."ItemId"
			WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
				AND I."ItemId" = (ele ->> 'ItemId') :: INT
				AND  ( ele ->> 'Month'||'-01')::DATE >= IFL."Month"
			ORDER BY IFL."CreateDate" DESC
			LIMIT 1;
			-- Thêm dòng vào Log
			INSERT INTO "ItemFullLog"(
				"ItemId",
				"SalePointId",
				"TotalRemaining",
				"Quantity",
				"ItemTypeId",
				"CreateBy",
				"CreateByName",
				"CreateDate",		
				"TotalPrice",
				"BalancePrice",
				"Month"
			)
			VALUES(
				(ele ->> 'ItemId') :: INT,	
				(ele ->> 'SalePointId') :: INT, 
				(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_remain, 0) + (ele ->> 'Quantity') :: numeric 
							ELSE COALESCE(v_cur_remain, 0) - (ele ->> 'Quantity') :: numeric END),
				(ele ->> 'Quantity') :: numeric,
				CASE WHEN p_type_action = 1 THEN 1 WHEN p_type_action = 3 THEN 3 ELSE 2 END,
				p_action_by,
				p_action_by_name,
				v_time,
				((ele ->> 'Quantity') :: numeric) * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END),
				(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: numeric * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN  v_item_price ELSE v_avg_price END)
							ELSE COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: numeric * v_avg_price END),
				(ele->>'Month'||'-01')::DATE
			) RETURNING "TotalRemaining" INTO v_check;
			UPDATE "ItemFullLog" 
			SET "TotalRemaining" = 	"TotalRemaining" + (ele ->> 'Quantity') :: numeric 
				WHERE "Month" >= (ele->>'Month'||'-01')::DATE + interval '1 month'
				AND "SalePointId" = (ele ->> 'SalePointId') :: INT
				AND "ItemId" = (ele ->> 'ItemId') :: INT;
			IF v_check < 0 THEN
				RAISE 'Không đủ hàng hóa trong kho';
			END IF;				
			
			IF NOT EXISTS (
				SELECT 1 FROM "ItemFull" I
				WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT) 
				AND p_type_action = 1
			THEN 
			
				-- Thêm dòng nếu chưa có và là nhập kho
				INSERT INTO "ItemFull"(
					"ItemId",
					"SalePointId",
					"TotalRemaining",
					"CreateBy",
					"CreateByName",
					"CreateDate",
					"TotalPrice"
				)
				VALUES(
					(ele ->> 'ItemId') :: INT,	
					(ele ->> 'SalePointId') :: INT, 
					(ele ->> 'Quantity') :: numeric,
					p_action_by,
					p_action_by_name,
					v_time,
					(ele ->> 'Quantity') :: numeric * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END)
				
				);
				
			ELSEIF NOT EXISTS (
				SELECT 1 FROM "ItemFull" I
				WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT
			) THEN 				
				-- Báo lỗi vì trừ kho khi chưa có hàng
				RAISE 'Không đủ hàng hóa trong kho';
			ELSE 
				-- Cập nhật kho của điểm bán 
				UPDATE "ItemFull" 
				SET
					"ModifyDate" = v_time,
					"TotalRemaining" = (CASE WHEN p_type_action = 1 THEN "TotalRemaining"  + (ele ->> 'Quantity') ::numeric 
																										ELSE "TotalRemaining" - (ele ->> 'Quantity') :: numeric END),
					"TotalPrice" =(CASE WHEN p_type_action = 1 THEN "TotalPrice"  + (ele ->> 'Quantity') :: numeric * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END)
																										ELSE "TotalPrice" - (ele ->> 'Quantity') :: numeric * v_avg_price END)
																								 
				WHERE "SalePointId" = (ele ->> 'SalePointId') :: INT
					AND "ItemId" = (ele ->> 'ItemId') :: INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;				
					
			END IF;
			
			-- Cập nhật kho số 0 nếu là xuất, nhập kho
			IF (ele ->> 'SalePointId') :: INT <> 0 AND p_type_action <> 3 THEN
				
				SELECT 
					COALESCE(I."TotalRemaining", 0),COALESCE(I."TotalPrice", 0)  INTO v_cur_remain, v_cur_price
				FROM "ItemFull" I
				WHERE I."SalePointId" = 0 :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT;
				
				-- Thêm dòng nếu chưa có và là nhập kho
				INSERT INTO "ItemFullLog"(
					"ItemId",
					"SalePointId",
					"TotalRemaining",
					"Quantity",
					"ItemTypeId",
					"CreateBy",
					"CreateByName",
					"CreateDate",		
					"TotalPrice",
					"BalancePrice",
					"Month"
				)
				VALUES(
					(ele ->> 'ItemId') :: INT,	
					0, 
					(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_remain, 0) - (ele ->> 'Quantity') :: numeric
								ELSE COALESCE(v_cur_remain, 0) + (ele ->> 'Quantity') :: numeric END),
					(ele ->> 'Quantity') :: numeric,
					(CASE WHEN p_type_action = 1 THEN 2 WHEN p_type_action = 2 THEN 1 ELSE 4 END),
					p_action_by,
					p_action_by_name,
					v_time,
					((ele ->> 'Quantity') :: numeric) * v_avg_price,
					(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: numeric * v_avg_price
						ELSE COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: numeric * v_avg_price END),
					(ele ->> 'Month'||'-01')::DATE
				) RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;
				
				UPDATE "ItemFull"
				SET
					"ModifyDate" = v_time,
					"TotalRemaining" = (CASE WHEN p_type_action = 1 THEN "TotalRemaining" - (ele ->> 'Quantity') :: numeric 
																										ELSE "TotalRemaining" + (ele ->> 'Quantity') :: numeric END),
																										
					"TotalPrice"	= (CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: numeric * v_avg_price
																										ELSE COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: numeric * v_avg_price END)
				WHERE "SalePointId" = 0 
					AND "ItemId" = (ele ->> 'ItemId') :: INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;								
			
			END IF;
			
	END LOOP;
	
	v_id := 1;
	v_mess := 'Cập nhật thành công';
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_item_v3(p_action_by integer, p_action_by_name character varying, p_type_action integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_time TIMESTAMP := NOW();
	v_cur_remain INT := 0; 
	v_cur_price NUMERIC := 0;
	v_check INT := 0;
	v_item_price INT;
	ele JSON;
	v_avg_price NUMERIC;
BEGIN
FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP

			-- Lấy giá một món hàng
			SELECT 
				"Price" INTO v_item_price
			FROM "Item"
			WHERE "ItemId" = (ele ->> 'ItemId') :: INT;	
			
			-- Kiểm tra món hàng đã tồn tại hay chưa
-- 			IF v_item_price IS NULL THEN
-- 					RAISE 'Món hàng chưa được thêm vào kho';
-- 			END IF;
			v_item_price:= (ele ->> 'Price') :: INT;
				
			
			-- Tính giá trung bình của món hàng khi "Cho việc xuất và sử dụng" - ĐÚNG VỚI KHO
			SELECT 
				COALESCE("TotalPrice",0)/ (CASE WHEN "TotalRemaining" =0 THEN 1 ELSE "TotalRemaining" END)
				INTO v_avg_price
			FROM "ItemFull"
			WHERE "ItemId" = (ele ->> 'ItemId') :: INT AND "SalePointId" =0;
				
			-- Nếu trong kho không còn thì lấy gía ở DDL
			IF v_avg_price IS NULL OR v_avg_price =0 THEN
				SELECT 
						"Price" INTO v_avg_price
				FROM "Item"
				WHERE "ItemId" = (ele ->> 'ItemId') :: INT;	
			END IF;
						
			-- Lấy tồn kho hiện tại
			SELECT 
				COALESCE(IFL."TotalRemaining", 0) AS "TotalRemaining",COALESCE(I."TotalPrice", 0) AS "TotalPrice" INTO v_cur_remain, v_cur_price
			FROM "ItemFullLog" IFL
				LEFT JOIN "ItemFull" I ON I."SalePointId" = IFL."SalePointId" AND I."ItemId" = IFL."ItemId"
			WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
				AND I."ItemId" = (ele ->> 'ItemId') :: INT
				AND  ( ele ->> 'Month'||'-01')::DATE >= IFL."Month"
			ORDER BY IFL."CreateDate" DESC
			LIMIT 1;
			-- Thêm dòng vào Log
			INSERT INTO "ItemFullLog"(
				"ItemId",
				"SalePointId",
				"TotalRemaining",
				"Quantity",
				"ItemTypeId",
				"CreateBy",
				"CreateByName",
				"CreateDate",		
				"TotalPrice",
				"BalancePrice",
				"Month"
			)
			VALUES(
				(ele ->> 'ItemId') :: INT,	
				(ele ->> 'SalePointId') :: INT, 
				(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_remain, 0) + (ele ->> 'Quantity') :: numeric 
							ELSE COALESCE(v_cur_remain, 0) - (ele ->> 'Quantity') :: numeric END),
				(ele ->> 'Quantity') :: numeric,
				CASE WHEN p_type_action = 1 THEN 1 WHEN p_type_action = 3 THEN 3 ELSE 2 END,
				p_action_by,
				p_action_by_name,
				v_time,
				((ele ->> 'Quantity') :: numeric) * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END),
				(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: numeric * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN  v_item_price ELSE v_avg_price END)
							ELSE COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: numeric * v_avg_price END),
				(ele->>'Month'||'-01')::DATE
			) RETURNING "TotalRemaining" INTO v_check;
			UPDATE "ItemFullLog" 
			SET "TotalRemaining" = 	"TotalRemaining" + (ele ->> 'Quantity') :: numeric 
				WHERE "Month" >= (ele->>'Month'||'-01')::DATE + interval '1 month'
				AND "SalePointId" = (ele ->> 'SalePointId') :: INT
				AND "ItemId" = (ele ->> 'ItemId') :: INT;
			IF v_check < 0 THEN
				RAISE 'Không đủ hàng hóa trong kho';
			END IF;				
			
			IF NOT EXISTS (
				SELECT 1 FROM "ItemFull" I
				WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT) 
				AND p_type_action = 1
			THEN 
			
				-- Thêm dòng nếu chưa có và là nhập kho
				INSERT INTO "ItemFull"(
					"ItemId",
					"SalePointId",
					"TotalRemaining",
					"CreateBy",
					"CreateByName",
					"CreateDate",
					"TotalPrice"
				)
				VALUES(
					(ele ->> 'ItemId') :: INT,	
					(ele ->> 'SalePointId') :: INT, 
					(ele ->> 'Quantity') :: numeric,
					p_action_by,
					p_action_by_name,
					v_time,
					(ele ->> 'Quantity') :: numeric * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END)
				
				);
				
			ELSEIF NOT EXISTS (
				SELECT 1 FROM "ItemFull" I
				WHERE I."SalePointId" = (ele ->> 'SalePointId') :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT
			) THEN 				
				-- Báo lỗi vì trừ kho khi chưa có hàng
				RAISE 'Không đủ hàng hóa trong kho';
			ELSE 
				-- Cập nhật kho của điểm bán 
				UPDATE "ItemFull" 
				SET
					"ModifyDate" = v_time,
					"TotalRemaining" = (CASE WHEN p_type_action = 1 THEN "TotalRemaining"  + (ele ->> 'Quantity') ::numeric 
																										ELSE "TotalRemaining" - (ele ->> 'Quantity') :: numeric END),
					"TotalPrice" =(CASE WHEN p_type_action = 1 THEN "TotalPrice"  + (ele ->> 'Quantity') :: numeric * (CASE WHEN (ele ->> 'SalePointId') :: INT = 0 THEN v_item_price ELSE v_avg_price END)
																										ELSE "TotalPrice" - (ele ->> 'Quantity') :: numeric * v_avg_price END)
																								 
				WHERE "SalePointId" = (ele ->> 'SalePointId') :: INT
					AND "ItemId" = (ele ->> 'ItemId') :: INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;				
					
			END IF;
			
			-- Cập nhật kho số 0 nếu là xuất, nhập kho
			IF (ele ->> 'SalePointId') :: INT <> 0 AND p_type_action <> 3 THEN
				
				SELECT 
					COALESCE(I."TotalRemaining", 0),COALESCE(I."TotalPrice", 0)  INTO v_cur_remain, v_cur_price
				FROM "ItemFull" I
				WHERE I."SalePointId" = 0 :: INT
					AND I."ItemId" = (ele ->> 'ItemId') :: INT;
				
				-- Thêm dòng nếu chưa có và là nhập kho
				INSERT INTO "ItemFullLog"(
					"ItemId",
					"SalePointId",
					"TotalRemaining",
					"Quantity",
					"ItemTypeId",
					"CreateBy",
					"CreateByName",
					"CreateDate",		
					"TotalPrice",
					"BalancePrice",
					"Month"
				)
				VALUES(
					(ele ->> 'ItemId') :: INT,	
					0, 
					(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_remain, 0) - (ele ->> 'Quantity') :: numeric
								ELSE COALESCE(v_cur_remain, 0) + (ele ->> 'Quantity') :: numeric END),
					(ele ->> 'Quantity') :: numeric,
					(CASE WHEN p_type_action = 1 THEN 2 WHEN p_type_action = 2 THEN 1 ELSE 4 END),
					p_action_by,
					p_action_by_name,
					v_time,
					((ele ->> 'Quantity') :: numeric) * v_avg_price,
					(CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: numeric * v_avg_price
						ELSE COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: numeric * v_avg_price END),
					(ele ->> 'Month'||'-01')::DATE
				) RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;
				
				UPDATE "ItemFull"
				SET
					"ModifyDate" = v_time,
					"TotalRemaining" = (CASE WHEN p_type_action = 1 THEN "TotalRemaining" - (ele ->> 'Quantity') :: numeric 
																										ELSE "TotalRemaining" + (ele ->> 'Quantity') :: numeric END),
																										
					"TotalPrice"	= (CASE WHEN p_type_action = 1 THEN COALESCE(v_cur_price, 0) - (ele ->> 'Quantity') :: numeric * v_avg_price
																										ELSE COALESCE(v_cur_price, 0) + (ele ->> 'Quantity') :: numeric * v_avg_price END)
				WHERE "SalePointId" = 0 
					AND "ItemId" = (ele ->> 'ItemId') :: INT
				RETURNING "TotalRemaining" INTO v_check;
				
				IF v_check < 0 THEN
					RAISE 'Không đủ hàng hóa trong kho';
				END IF;								
			
			END IF;
			
	END LOOP;
	
	v_id := 1;
	v_mess := 'Cập nhật thành công';
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;


	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_leader_attendent(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	elele INT;
	v_time TIMESTAMP := NOW();
	v_id_tmp INT;
	v_array INT[] := '{}'::INT[];
BEGIN

	--Lấy bảng tạm để check dữ liệu cuối cùng
	CREATE TEMP TABLE tmpAttendent ON COMMIT DROP AS (
		SELECT 
			LA."LeaderAttendentId",
			LA."CreatedDate",
			LA."UserId",
			LA."TriggerSalePoint"
		FROM "LeaderAttendent" LA 
		WHERE LA."CreatedDate" >= ALL(
			SELECT 
				LAA."CreatedDate"
			FROM "LeaderAttendent" LAA
			GROUP BY LAA."CreatedDate"
		)
	);
	

		
	--Lặp qua list leader
	FOR ele IN SELECT * FROM json_array_elements(((p_data::JSON)->>'LeaderAttendentData')::JSON) LOOP
		
		--INSERT
		IF NOT EXISTS (
			SELECT 1 
			FROM tmpAttendent T 
			WHERE T."UserId" = (ele->>'UserId')::INT 
				AND T."TriggerSalePoint" = (ele->>'TriggerSalePoint')::INT
		) THEN
		
			INSERT INTO "LeaderAttendent"(
				"UserId",
				"TriggerSalePoint",
				"CreatedBy",
				"CreatedByName",
				"CreatedDate"
			)
			VALUES(
				(ele->>'UserId')::INT,
				(ele->>'TriggerSalePoint')::INT,
				p_action_by,
				p_action_by_name,
				v_time
			);
		
		--UPDATE
		ELSE 
		
			--Lấy id để update
			SELECT T."LeaderAttendentId" INTO v_id_tmp 
			FROM tmpAttendent T 
			WHERE T."UserId" = (ele->>'UserId')::INT 
			AND T."TriggerSalePoint" = (ele->>'TriggerSalePoint')::INT;
			
			--Update lại thời gian
			UPDATE "LeaderAttendent" 
			SET	
				"CreatedBy" = p_action_by,
				"CreatedByName" = p_action_by_name,
				"CreatedDate" = v_time
			WHERE "LeaderAttendentId" = v_id_tmp;
		
		END IF;
		
	END LOOP;
	
	--Lặp qua list lịch làm
	FOR ele IN SELECT * FROM json_array_elements(((p_data::JSON)->>'GroupSalePointData')::JSON) LOOP
		
		v_array := '{}'::INT[];
		
		FOR elele IN SELECT UNNEST(TRANSLATE((ele->>'SalePointIds'), '[]', '{}')::INT[]) LOOP			
			v_array := array_append(v_array, elele);			
		END LOOP;
		
		--INSERT
		IF COALESCE((ele->>'GroupSalePointId')::INT, 0) = 0 THEN
		
			INSERT INTO "GroupSalePoint"(
				"UserId",
				"SalePointIds",
				"Option",
				"CreatedBy",
				"CreatedByName",
				"CreatedDate"				
			)
			VALUES(
				(ele->>'UserId')::INT,
				v_array,
				(ele->>'Option')::INT,
				p_action_by,
				p_action_by_name,
				v_time
			);
		
		--UPDATE
		ELSE 
			
			UPDATE "GroupSalePoint" 
			SET
				"SalePointIds" = v_array,
				"Option" = (ele->>'Option')::INT,
				"CreatedBy" = p_action_by,
				"CreatedByName" = p_action_by_name,
				"CreatedDate"	= v_time	
			WHERE "GroupSalePointId" = (ele->>'GroupSalePointId')::INT;
					
		END IF;
		
	END LOOP;
	
	v_id := 1;
	v_mess := 'Cập nhật thành công';
	

	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_percent(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
	v_array INT[];
	v_percent NUMERIC[];
	v_total NUMERIC;
	elele JSON;
BEGIN

	
	--INSERT
	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		--Clear data tạm
		v_array := '{}'::INT[];
		v_percent := '{}'::NUMERIC[];
		v_total := 0;
		--Loop lấy các giá trị gửi xuống
		FOR elele IN SELECT * FROM json_array_elements((ele->>'MainUserData')::JSON) LOOP
			v_array := array_append(v_array, (elele->>'UserId')::INT);
			v_percent := array_append(v_percent, (elele->>'Percent')::NUMERIC);
			v_total := v_total +  (elele->>'Percent')::NUMERIC;
		END LOOP;
		
		IF v_total > 1 THEN
			RAISE 'Tỷ lệ phần trăm % đã nhập lớn hơn %', CONCAT(ROUND(100 * v_total, 0), '%'), CONCAT(100, '%');
		END IF;
		
		INSERT INTO "SalePointPercentLog"(
			"SalePointId",
			"MainUserId",
			"PercentMainUserId",
			"ActionBy",
			"ActionByName",
			"ActionDate"
		)
		VALUES(
			(ele->>'SalePointId')::INT,
			v_array,
			v_percent,
			p_action_by,
			p_action_by_name,
			v_time
		);
		
	END LOOP;
	
	v_id := 1;
	v_mess := 'Cập nhật thành công';
		
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_total_commision_and_fee(p_date timestamp without time zone, p_commissionid integer, p_commision bigint, p_fee bigint)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_id INT;
    v_mess TEXT;
    ele JSON;
    v_time TIMESTAMP := NOW();
BEGIN
		UPDATE 
			"Commission"
		SET 
		"TotalValue" = p_commision - p_fee,
		"Fee" = p_fee
		WHERE "Date" = p_date::DATE AND "CommissionId" = p_commissionId
		AND "IsDeleted" = FALSE;
		v_id:=1;
		v_mess:='Cập nhật thành công';
		
    RETURN QUERY
    SELECT v_id, v_mess;

    EXCEPTION WHEN OTHERS THEN
    BEGIN
        v_id := -1;
        v_mess := sqlerrm;
        RETURN QUERY
        SELECT v_id, v_mess;
    END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salepoint_update_transaction(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_transaction_type_id integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
BEGIN
	--INSERT
	IF p_action_type = 1 THEN 
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			INSERT INTO "Transaction"(
				"TransactionTypeId",
				"Note",
				"Quantity",
				"Price",
				"TotalPrice",
				"SalePointId",
				"ShiftDistributeId",
				"IsDeleted",
				"UserId",
				"TypeNameId",
				"ActionBy",
				"ActionByName",
				"ActionDate"
			)
			VALUES(
				p_transaction_type_id,
				(ele->>'Note')::VARCHAR,
				COALESCE((ele->>'Quantity')::INT, 1),
				(ele->>'Price')::NUMERIC,
				(COALESCE((ele->>'Price')::NUMERIC,0) * COALESCE((ele->>'Quantity')::INT,1)) :: NUMERIC,
				(ele->>'SalePointId')::INT,
				(ele->>'ShiftDistributeId')::INT,
				FALSE,
				COALESCE((ele->>'UserId')::INT, p_action_by),
				(ele->>'TypeNameId')::INT,
				p_action_by,
				p_action_by_name,
				v_time
			);
		END LOOP;
		
		v_id := 1;
		v_mess := 'Thêm thành công';
		
	--UPDATE
	ELSEIF p_action_type = 2 THEN
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "Transaction" SET 
				"Note" = COALESCE((ele->>'Note')::VARCHAR, "Note"),
				"Quantity" = COALESCE((ele->>'Quantity')::INT, "Quantity"),
				"Price" =  COALESCE((ele->>'Price')::NUMERIC, "Price"),
				"TotalPrice" = COALESCE((COALESCE((ele->>'Price')::NUMERIC, "Price") * COALESCE((ele->>'Quantity')::INT, "Quantity")) :: NUMERIC, "TotalPrice"),
				"SalePointId" = COALESCE((ele->>'SalePointId')::INT, "SalePointId"),
				"ShiftDistributeId" = COALESCE((ele->>'ShiftDistributeId')::INT, "ShiftDistributeId"),
				"UserId" = COALESCE((ele->>'UserId')::INT, "UserId"),
				"TypeNameId" = COALESCE((ele->>'TypeNameId')::INT, "TypeNameId"),
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate"= v_time
			WHERE "TransactionId" = (ele->>'TransactionId') :: INT;
		END LOOP;
		
		v_id := 1;
		v_mess := 'Cập nhật thành công';
		
	--DELETE
	ELSEIF p_action_type = 3 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "Transaction" SET 
				"IsDeleted" = TRUE,
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate"= v_time
			WHERE "TransactionId" = (ele->>'TransactionId') :: INT;
		END LOOP;
		
		v_id := 1;
		v_mess := 'Xóa thành công';
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_salpoint_get_list_pay_vietlott(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "TotalPrice" numeric, "ListHistory" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	--Lấy danh sách điểm bán
	WITH tmp AS (
		SELECT 
			SP."SalePointId",
			SP."SalePointName"
		FROM "SalePoint" SP 
		WHERE SP."IsActive" IS TRUE
			AND SP."IsDeleted" IS FALSE
	),
	tmp1 AS (
		SELECT
			T."TransactionId",
			T."Note",
			T."TotalPrice",
			T."SalePointId",
			T."ActionBy",
			T."ActionByName",
			T."Date"
		FROM "Transaction" T
		WHERE T."TransactionTypeId" = 9
			AND T."IsDeleted" IS FALSE
			AND TO_CHAR(T."Date", 'YYYY-MM') = p_month
		ORDER BY 
			T."Date" DESC
	)
	SELECT
		SP."SalePointId",
		SP."SalePointName",
		COALESCE(SUM(T."TotalPrice"), 0) AS "TotalPrice",
		(CASE WHEN COUNT(TO_JSONB(T.*)) > 0 THEN array_to_json(array_agg(TO_JSONB(T.*)))::TEXT ELSE '[]' END) AS "ListHistory"
	FROM tmp SP
		LEFT JOIN tmp1 T ON T."SalePointId" = SP."SalePointId" 
	GROUP BY
		SP."SalePointId",
		SP."SalePointName"
	ORDER BY
		SP."SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_scratch_card_full()
 RETURNS TABLE("Data" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	SELECT array_to_json(ARRAY_AGG(R)) ::TEXT
	FROM (
		SELECT 
			LC."LotteryChannelId",
			LC."ShortName",
			LC."LotteryChannelName",
			SF."TotalRemaining"
		FROM "ScratchcardFull" SF
			JOIN "LotteryChannel" LC ON SF."LotteryChannelId" = LC."LotteryChannelId"
		WHERE SF."AgencyId" = 0
		ORDER BY LC."LotteryChannelTypeId"
	) R;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_shift_distribute_get_by_date(p_date timestamp without time zone, p_user_id integer)
 RETURNS TABLE("ShiftDistributeId" integer, "SalePointId" integer, "SalePointName" character varying, "ShiftId" integer, "ShiftName" character varying, "WorkingTime" text, "UserId" integer, "FullName" character varying, "ShiftTypeId" integer, "ShiftTypeName" character varying, "CanClick" boolean, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_role_id INT;
	v_current_shift_dis INT;
BEGIN

	SELECT UR."UserRoleId" INTO v_user_role_id FROM "UserRole" UR WHERE UR."UserId" = p_user_id;

	SELECT f."ShiftDistributeId" INTO v_current_shift_dis FROM fn_get_shift_info(v_user_role_id) f;

	RETURN QUERY 
	SELECT 
		SD."ShiftDistributeId",
		SD."SalePointId",
		SP."SalePointName",
		SD."ShiftId",
		S."ShiftName",
		TO_CHAR(S."StartTime",'HH24:MI') || ' - ' || TO_CHAR(S."EndTime",'HH24:MI') AS "WorkingTime",
		SD."UserId",
		U."FullName",
		SD."ShiftTypeId",
		ST."ShiftTypeName",
		(CASE WHEN 
				(v_current_shift_dis <> SD."ShiftDistributeId" AND SD."ShiftId" = 1 AND EXISTS (SELECT 1 FROM "ShiftTransfer" ST WHERE ST."ActionDate"::DATE = SD."DistributeDate" AND ST."ShiftId" = 1 AND ST."ShiftDistributeId" = SD."ShiftDistributeId")) OR
				(v_current_shift_dis = SD."ShiftDistributeId" AND SD."ShiftId" = 1) OR 
				(v_current_shift_dis = SD."ShiftDistributeId" AND SD."ShiftId" = 2 AND EXISTS (SELECT 1 FROM "ShiftTransfer" ST WHERE ST."ActionDate"::DATE = SD."DistributeDate" AND ST."ShiftId" IN(1,2) AND ST."SalePointid" = SD."SalePointId"))
			THEN TRUE ELSE FALSE END) AS "CanClick",
		(CASE WHEN 
				(v_current_shift_dis <> SD."ShiftDistributeId" AND SD."ShiftId" = 1 AND EXISTS (SELECT 1 FROM "ShiftTransfer" ST WHERE ST."ActionDate"::DATE = SD."DistributeDate" AND ST."ShiftId" = 1 AND ST."ShiftDistributeId" = SD."ShiftDistributeId")) OR
				(v_current_shift_dis = SD."ShiftDistributeId" AND SD."ShiftId" = 1) OR 
				(v_current_shift_dis = SD."ShiftDistributeId" AND SD."ShiftId" = 2 AND EXISTS (SELECT 1 FROM "ShiftTransfer" ST WHERE ST."ActionDate"::DATE = SD."DistributeDate" AND ST."ShiftId" IN(1,2) AND ST."SalePointid" = SD."SalePointId"))
			THEN 'Ok' ELSE 'Ca trước chưa kết ca' END) AS "Message"
	FROM "ShiftDistribute" SD
		JOIN "SalePoint" SP ON SP."SalePointId" = SD."SalePointId"
		JOIN "Shift" S ON S."ShiftId" = SD."ShiftId"
		JOIN "ShiftType" ST ON ST."ShiftTypeId" = SD."ShiftTypeId"
		JOIN "User" U ON U."UserId" = SD."UserId"
	WHERE SD."DistributeDate" = p_date::DATE
		AND (COALESCE(p_user_id, 0) = 0 OR SD."UserId" = p_user_id)
	ORDER BY SD."ShiftId";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_student_get_real_time_studying_student()
 RETURNS TABLE("StudentName" character varying, "DayOfBirth" timestamp without time zone, "SC" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	SELECT S."StudentName", S."DOB"::TIMESTAMP ,C."CurrentSCName"
	FROM "Student" S 
		LEFT JOIN "StudentClassMapping" SCM ON SCM."StudentId" = S."StudentId"
		LEFT JOIN "Class" C ON C."ClassId" = SCM."ClassId"
	WHERE C."ClassStatusId" = 2;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_sub_agency_ddl()
 RETURNS TABLE("SubAgencyId" bigint, "SubAgencyName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN
	RETURN QUERY
	SELECT S."AgencyId", S."AgencyName" 
	FROM "SubAgency" S
	WHERE S."IsActive" = TRUE AND S."IsDelete" = FALSE;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_temp(p_sale_point_id integer, p_month character varying, p_total_commision numeric)
 RETURNS TABLE("FullName" character varying, "Percent" numeric, "TotalCommisionUser" numeric)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_temp_main_user_data JSON;
BEGIN
		v_temp_main_user_data:= (SELECT 
																T."MainUserData"
															FROM crm_salepoint_get_percent('2023-01') T
															WHERE T."SalePointId" = 1)::JSON;
	RETURN QUERY
	WITH tmp AS
	(
		select 
		* 
		from json_to_recordset('[{"UserId" : 30, "FullName" : "Việ12t Đập Đá", "Percent" : 0.3},{"UserId" : 31, "FullName" : "Việ1322t 23Đập Đá", "Percent" : 0.4}]') as x("UserId" int, "FullName" text, "Percent" NUMERIC)
		 
	)
	SELECT 
		T."FullName"::VARCHAR,
		T."Percent",
		 ROUND(T."Percent"::NUMERIC * p_total_commision,2) AS "TotalCommisionUser"
	FROM tmp T ;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_transition_get_list_confirm(p_page_size integer, p_page_number integer, p_date timestamp without time zone, p_sale_point_id integer, p_trans_type_id integer, p_user_role_id integer, p_user_id bigint)
 RETURNS TABLE("RowNumber" bigint, "TotalCount" bigint, "TransitionDate" timestamp without time zone, "ActionBy" integer, "ActionByName" character varying, "TransitionTypeId" integer, "TransitionTypeName" character varying, "FromSalePointId" integer, "FromSalePointName" character varying, "ToSalePointId" integer, "ToSalePointName" character varying, "ConfirmDate" timestamp without time zone, "ConfirmBy" integer, "ConfirmByName" character varying, "TransData" text, "ManagerId" integer, "ManagerName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
	v_user_id INT;
	v_user_id_temp INT;
BEGIN
	--Chọn ra người thế ca khi user nghỉ
	IF EXISTS(SELECT 1 FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" = p_date AND LOL."UserId" = p_user_id) THEN
		SELECT GSP."UserId" INTO v_user_id_temp FROM "GroupSalePoint" GSP
		 WHERE (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = p_user_id) = ANY (GSP."SalePointIds")
		 GROUP BY GSP."UserId", GSP."GroupSalePointId"
		 ORDER BY GSP."GroupSalePointId" DESC LIMIT 1;
	ELSE
		SELECT p_user_id INTO v_user_id;
		SELECT p_user_id INTO v_user_id_temp;
	END IF;
	RAISE NOTICE 'viet: %',v_user_id;
	RAISE NOTICE 'viet2: %',v_user_id_temp;
	RETURN QUERY 
	WITH tmp AS(
	SELECT 
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
		(CASE WHEN T."FromSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "FromSalePointName",
		T."ToSalePointId",
		(CASE WHEN T."ToSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "ToSalePointName",
		T."ConfirmDate",
		T."ConfirmBy",
		T."ConfirmByName",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					TT."TransitionId",
					TT."LotteryDate",	
					TT."LotteryChannelId",
					TT."TotalTrans",
					TT."ConfirmTrans",
					TT."LotteryDate",
					TT."TotalTransDup",
					TT."ConfirmTransDup",
					TT."IsScratchcard"
				FROM "Transition" TT 
					JOIN "LotteryChannel" LC ON TT."LotteryChannelId" = LC."LotteryChannelId" 
				WHERE TT."TransitionDate" = T."TransitionDate" AND TT."ActionBy" = T."ActionBy"
				ORDER BY 
					TT."IsScratchcard",
					TT."LotteryDate",
					LC."LotteryChannelTypeId"
			) r
		)::TEXT AS "TransData",
		T."ManagerId",
		U."FullName" AS "ManagerName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName"
	FROM "Transition" T
		JOIN "TransitionType" TT ON TT."TransitionTypeId" = T."TransitionTypeId"
		JOIN "SalePoint" SP ON (SP."SalePointId" = T."ToSalePointId" OR SP."SalePointId" = T."FromSalePointId")
		JOIN "ConfirmStatus" CS ON CS."ConfirmStatusId" = T."ConfirmStatusId"
		LEFT JOIN "User" U ON U."UserId" = T."ManagerId"
	WHERE T."TransitionDate"::DATE = p_date::DATE
		AND (T."FromSalePointId" = (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = p_user_id) OR T."FromSalePointId" = (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = v_user_id_temp) OR T."ToSalePointId" = (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = p_user_id) OR T."ToSalePointId" = (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = v_user_id_temp))
		AND (COALESCE(p_trans_type_id, 0) = 0 OR T."TransitionTypeId" = p_trans_type_id)
		AND (COALESCE(p_sale_point_id, 0) = 0 
			OR (COALESCE(p_trans_type_id, 0) = 0 AND SP."SalePointId" = p_sale_point_id) 
			OR (COALESCE(p_trans_type_id, 0) = 1 AND T."FromSalePointId" = p_sale_point_id)
			OR (COALESCE(p_trans_type_id, 0) = 2 AND T."ToSalePointId" = p_sale_point_id))
-- 		AND (COALESCE(v_user_id, 0) = 0  OR T."ManagerId" = v_user_id)
	GROUP BY
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
		T."ToSalePointId",
		T."ConfirmBy",
		T."ConfirmDate",
		T."ConfirmByName",
		SP."SalePointName",
		T."ManagerId",
		U."FullName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName"
	)
	SELECT
		ROW_NUMBER() OVER (ORDER BY t."TransitionDate" DESC) AS "RowNumber",
		COUNT(1) OVER() AS "TotalCount",
		t.*
	FROM tmp t
	OFFSET v_offset_row LIMIT p_page_size;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_transition_get_list_to_confirm(p_page_size integer, p_page_number integer, p_date timestamp without time zone, p_sale_point_id integer, p_trans_type_id integer, p_user_id bigint)
