	END IF;
	RAISE NOTICE 'viet: %',v_user_id;
	RAISE NOTICE 'viet2: %',v_user_id_temp;
	RETURN QUERY 
	WITH tmp AS(
	SELECT 
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
