CREATE OR REPLACE FUNCTION public.crm_transition_get_list_to_confirm_v2(p_page_size integer, p_page_number integer, p_date timestamp without time zone, p_sale_point_id integer, p_trans_type_id integer, p_user_role_id integer)
 RETURNS TABLE("RowNumber" bigint, "TotalCount" bigint, "TransitionDate" timestamp without time zone, "ActionBy" integer, "ActionByName" character varying, "TransitionTypeId" integer, "TransitionTypeName" character varying, "FromSalePointId" integer, "FromSalePointName" character varying, "ToSalePointId" integer, "ToSalePointName" character varying, "ConfirmDate" timestamp without time zone, "ConfirmBy" integer, "ConfirmByName" character varying, "TransData" text, "ManagerId" integer, "ManagerName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
	v_user_id INT;
	v_user_id_for_off INT;
	v_off_user INT;
BEGIN
SELECT "UserId" INTO v_user_id FROM fn_get_shift_info(p_user_role_id);

SELECT LOL."UserId" INTO v_off_user FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" = p_date AND LOL."UserId" <> v_user_id; 
	RAISE NOTICE 'vietBefore: %',v_user_id;
	IF EXISTS(SELECT 1 FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" = p_date AND LOL."UserId" <> v_user_id) THEN

		IF (	SELECT GSP."UserId"  FROM "GroupSalePoint" GSP
		 WHERE (SELECT U."SalePointId" FROM "User" U WHERE U."UserId" = v_off_user) = ANY (GSP."SalePointIds") AND GSP."UserId" <> v_off_user
	 GROUP BY GSP."UserId","GroupSalePointId" ORDER BY "GroupSalePointId" DESC LIMIT 1 ) = v_user_id THEN 
	 	SELECT LOL."UserId" INTO v_user_id_for_off FROM "LeaderOffLog" LOL WHERE LOL."WorkingDate" = p_date;
		ELSE 
		SELECT v_user_id INTO v_user_id_for_off;
		END IF;
	ELSE
		SELECT "UserId" INTO v_user_id_for_off FROM fn_get_shift_info(p_user_role_id);
	END IF;

	RETURN QUERY 
	WITH tmp AS(
	SELECT 
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
		(CASE WHEN T."FromSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "FromSalePointName",
		T."ToSalePointId",
		(CASE WHEN T."ToSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "ToSalePointName",
		T."ConfirmDate",
		T."ConfirmBy",
		T."ConfirmByName",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					TT."TransitionId",
					TT."LotteryDate",	
					TT."LotteryChannelId",
					TT."TotalTrans",
					TT."ConfirmTrans",
					TT."LotteryDate",
					TT."TotalTransDup",
					TT."ConfirmTransDup",
					TT."IsScratchcard"
				FROM "Transition" TT 
					JOIN "LotteryChannel" LC ON TT."LotteryChannelId" = LC."LotteryChannelId" 
				WHERE TT."TransitionDate" = T."TransitionDate" AND TT."ActionBy" = T."ActionBy"
				ORDER BY 
					TT."IsScratchcard",
					TT."LotteryDate",
					LC."LotteryChannelTypeId"
			) r
		)::TEXT AS "TransData",
		T."ManagerId",
		U."FullName" AS "ManagerName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName"
	FROM "Transition" T
		JOIN "TransitionType" TT ON TT."TransitionTypeId" = T."TransitionTypeId"
		JOIN "SalePoint" SP ON (SP."SalePointId" = T."ToSalePointId" OR SP."SalePointId" = T."FromSalePointId")
		JOIN "ConfirmStatus" CS ON CS."ConfirmStatusId" = T."ConfirmStatusId"
		LEFT JOIN "User" U ON U."UserId" = T."ManagerId" 
	WHERE T."TransitionDate"::DATE = p_date::DATE
		
		AND (COALESCE(p_trans_type_id, 0) = 0 OR T."TransitionTypeId" = p_trans_type_id)
		AND (COALESCE(p_sale_point_id, 0) = 0 
			OR (COALESCE(p_trans_type_id, 0) = 0 AND SP."SalePointId" = p_sale_point_id) 
			OR (COALESCE(p_trans_type_id, 0) = 1 AND T."FromSalePointId" = p_sale_point_id)
			OR (COALESCE(p_trans_type_id, 0) = 2 AND T."ToSalePointId" = p_sale_point_id))
	GROUP BY
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
		T."ToSalePointId",
		T."ConfirmBy",
		T."ConfirmDate",
		T."ConfirmByName",
		SP."SalePointName",
		T."ManagerId",
		U."FullName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName"
	)
	SELECT
		ROW_NUMBER() OVER (ORDER BY t."TransitionDate" DESC) AS "RowNumber",
		COUNT(1) OVER() AS "TotalCount",
		t.*
	FROM tmp t
	WHERE t."ManagerId" = v_user_id or (t."ManagerId" = v_user_id_for_off)
	OFFSET v_offset_row LIMIT p_page_size;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_transition_get_list_to_confirm_v3(p_page_size integer, p_page_number integer, p_date timestamp without time zone, p_sale_point_id integer, p_trans_type_id integer, p_user_role_id integer)
 RETURNS TABLE("RowNumber" bigint, "TotalCount" bigint, "TransitionDate" timestamp without time zone, "ActionBy" integer, "ActionByName" character varying, "TransitionTypeId" integer, "TransitionTypeName" character varying, "FromSalePointId" integer, "FromSalePointName" character varying, "ToSalePointId" integer, "ToSalePointName" character varying, "ConfirmDate" timestamp without time zone, "ConfirmBy" integer, "ConfirmByName" character varying, "TransData" text, "ManagerId" integer, "ManagerName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
	v_user_id INT;
BEGIN
SELECT "UserId" INTO v_user_id FROM fn_get_shift_info(p_user_role_id);
	RETURN QUERY 
	WITH tmp AS(
	SELECT 
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
		(CASE WHEN T."FromSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "FromSalePointName",
		T."ToSalePointId",
		(CASE WHEN T."ToSalePointId" = 0 THEN 'Kho' ELSE SP."SalePointName" END) AS "ToSalePointName",
		T."ConfirmDate",
		T."ConfirmBy",
		T."ConfirmByName",
		(
			SELECT array_to_json(
				ARRAY_AGG (r))
			FROM
			(
				SELECT
					TT."TransitionId",
					TT."LotteryDate",	
					TT."LotteryChannelId",
					TT."TotalTrans",
					TT."ConfirmTrans",
					TT."LotteryDate",
					TT."TotalTransDup",
					TT."ConfirmTransDup",
					TT."IsScratchcard"
				FROM "Transition" TT 
					JOIN "LotteryChannel" LC ON TT."LotteryChannelId" = LC."LotteryChannelId" 
				WHERE TT."TransitionDate" = T."TransitionDate" AND TT."ActionBy" = T."ActionBy"
				ORDER BY 
					TT."IsScratchcard",
					TT."LotteryDate",
					LC."LotteryChannelTypeId"
			) r
		)::TEXT AS "TransData",
		T."ManagerId",
		U."FullName" AS "ManagerName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName"
	FROM "Transition" T
		JOIN "TransitionType" TT ON TT."TransitionTypeId" = T."TransitionTypeId"
		JOIN "SalePoint" SP ON (SP."SalePointId" = T."ToSalePointId" OR SP."SalePointId" = T."FromSalePointId")
		JOIN "ConfirmStatus" CS ON CS."ConfirmStatusId" = T."ConfirmStatusId"
		LEFT JOIN "User" U ON U."UserId" = T."ManagerId"
	WHERE T."TransitionDate"::DATE = p_date::DATE
		
		AND (COALESCE(p_trans_type_id, 0) = 0 OR T."TransitionTypeId" = p_trans_type_id)
		AND (COALESCE(p_sale_point_id, 0) = 0 
			OR (COALESCE(p_trans_type_id, 0) = 0 AND SP."SalePointId" = p_sale_point_id) 
			OR (COALESCE(p_trans_type_id, 0) = 1 AND T."FromSalePointId" = p_sale_point_id)
			OR (COALESCE(p_trans_type_id, 0) = 2 AND T."ToSalePointId" = p_sale_point_id))
		
	GROUP BY
		T."TransitionDate",
		T."ActionBy",
		T."ActionByName",
		T."TransitionTypeId",
		TT."TransitionTypeName",
		T."FromSalePointId",
		T."ToSalePointId",
		T."ConfirmBy",
		T."ConfirmDate",
		T."ConfirmByName",
		SP."SalePointName",
		T."ManagerId",
		U."FullName",
		T."ConfirmStatusId",
		CS."ConfirmStatusName"
	)
	SELECT
		ROW_NUMBER() OVER (ORDER BY t."TransitionDate" DESC) AS "RowNumber",
		COUNT(1) OVER() AS "TotalCount",
		t.*
	FROM tmp t
	OFFSET v_offset_row LIMIT p_page_size;
	
END;
$function$


