 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
BEGIN   
	UPDATE "GuestAction"
	SET
		"ModifyBy" = p_action_by,
		"ModifyByName" = p_action_by_name,
		"ModifyDate" = NOW(),
		"IsDeleted"= TRUE
	WHERE "GuestActionId" = p_guest_action_id;
    
    v_id := 1;
    v_mess := 'Update thanh cong';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


