 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
arr integer[];
i integer;
BEGIN   
	arr := string_to_array(p_arr_guest_action_id, ',');
	FOR i IN 1..array_length(arr, 1)
	LOOP 
		raise notice 'for i: %', ((arr[i])::integer);
		UPDATE "GuestAction"
