CREATE OR REPLACE FUNCTION public.crm_user_by_title(p_usertitle_id integer)
 RETURNS TABLE("Id" integer, "Name" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	SELECT 
		U."UserId",
		U."FullName"
	FROM "User" U
		JOIN "UserRole" UR ON UR."UserId" = U."UserId"
		JOIN "UserTitle" UT ON UT."UserTitleId" = UR."UserTitleId"
	WHERE U."IsActive" IS TRUE
		AND (COALESCE(p_usertitle_id, 0) = 0 OR UT."UserTitleId" = p_usertitle_id);
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_by_title_v2(p_usertitle_id integer)
 RETURNS TABLE("Id" integer, "Name" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY
	SELECT 
		U."UserId",
		U."FullName"
	FROM "User" U
		JOIN "UserRole" UR ON UR."UserId" = U."UserId"
		JOIN "UserTitle" UT ON UT."UserTitleId" = UR."UserTitleId"
	WHERE U."IsActive" IS TRUE
		AND (COALESCE(p_usertitle_id, 0) = 0 OR UT."UserTitleId" = p_usertitle_id) AND U."IsIntern" is false;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_confirm_salary(p_action_by integer, p_action_by_name character varying, p_data text, p_action_type integer DEFAULT 1)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
BEGIN
	--INSERT
	IF p_action_type = 1 THEN 
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			
			IF (ele ->> 'SalaryConfirmId')::INT IS NOT NULL THEN 
				UPDATE "SalaryConfirm"
				SET
					"ModifyBy" = p_action_by,
					"ModifyByName" = p_action_by_name,
					"ModifyDate" = v_time,
					"Data" = COALESCE((ele ->> 'SalaryData')::TEXT, "Data")
				WHERE "SalaryConfirmId" = (ele ->> 'SalaryConfirmId')::INT;			
			ELSEIF NOT EXISTS (
				SELECT 1 FROM "SalaryConfirm" SC
				WHERE SC."UserId" = (ele ->> 'UserId')::INT 
					AND SC."Month" = (ele ->> 'Month')::VARCHAR
					AND SC."IsDeleted" IS FALSE
			) THEN
				INSERT INTO "SalaryConfirm"(
					"UserId",
					"Month",
					"Data",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"IsDeleted"
				)
				VALUES(
					(ele ->> 'UserId')::INT,
					(ele ->> 'Month')::VARCHAR,
					(ele ->> 'SalaryData')::TEXT,
					p_action_by,
					p_action_by_name,
					v_time,
					FALSE
				);
			
			END IF; 
			
		END LOOP;
		
		v_id := 1;
		v_mess := 'cập nhật thành công';
		
	--DELETE
	ELSEIF p_action_type = 3 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "SalaryConfirm"
			SET
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate" = v_time,
				"IsDeleted" = TRUE
			WHERE "SalaryConfirmId" = (ele ->> 'SalaryConfirmId')::INT;			
		END LOOP;
		
		v_id := 1;
		v_mess := 'Xóa thành công';
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_create(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	v_password VARCHAR = 'GjNJi5nlYpLw8no39S+hcg==';
	--
	ele JSON;
BEGIN
	
	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		IF(NOT EXISTS(SELECT 1 FROM "User" WHERE ("Account" = (ele->>'Account')::VARCHAR OR "Email" = (ele->>'Email')::VARCHAR) AND "IsActive" IS TRUE )) THEN
			INSERT INTO "User"(
				"Account",
				"Password",
				"FullName",
				"Phone",
				"Email",
				"SalePointId",
				"ActionBy",
				"ActionByName",
				"Address",
				"BankAccount",
				"NumberIdentity"
			)
			VALUES(
				(ele ->> 'Account'):: VARCHAR,
				v_password,
				(ele ->> 'FullName'):: VARCHAR,
				(ele ->> 'Phone'):: VARCHAR,
				(ele ->> 'Email'):: VARCHAR,
				(ele ->> 'SalePointId'):: INT,
				p_action_by,
				p_action_by_name,
				(ele ->> 'Address')::VARCHAR,
				(ele ->> 'BankAccount')::VARCHAR,
				(ele ->> 'NumberIdentity')::VARCHAR
			) RETURNING "UserId" INTO v_id;
			v_mess := 'User created';
		ELSE
			v_mess := 'Tài khoản hoặc email đã tồn tại';
			v_id := -1;
		END IF;
	 
		IF (v_id > 0) THEN
				INSERT INTO "UserRole"(
					"UserRoleId",
					"UserId",
					"UserTitleId"
				)
				VALUES(
					v_id,
					v_id,
					5
				);
		END IF;
	END LOOP;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


