			v_id := -1;
		END IF;
	 
		IF (v_id > 0) THEN
				INSERT INTO "UserRole"(
					"UserRoleId",
					"UserId",
					"UserTitleId"
				)
				VALUES(
					v_id,
					v_id,
					2
				);
		END IF;
	END LOOP;
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


