CREATE OR REPLACE FUNCTION public.crm_user_distribute_shift(p_action_by integer, p_action_by_name character varying, p_sale_point_id integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_data::JSON;
	ele JSON;
BEGIN
	
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
	
		IF NOT EXISTS (SELECT 1 FROM "ShiftDistribute" S WHERE S."SalePointId" = p_sale_point_id 
			AND S."DistributeDate" = (ele ->> 'DistributeDate')::DATE 
			AND S."ShiftId" = (ele ->> 'ShiftId')::INT) THEN	
			
			INSERT INTO "ShiftDistribute"(
				"DistributeDate",
				"SalePointId",
				"ShiftId",
				"UserId",
				"ShiftTypeId",
				"ActionBy",
				"ActionByName"
			) VALUES (
				(ele ->> 'DistributeDate')::DATE,
				p_sale_point_id,
				(ele ->> 'ShiftId')::INT,
				(ele ->> 'UserId')::INT,
				1,
				p_action_by,
				p_action_by_name
			);
			
		ELSE 
		
			UPDATE "ShiftDistribute" S
			SET
				"UserId" = (ele ->> 'UserId')::INT,
				"ActionBy" = p_action_by,
				"ActionByName" = p_action_by_name,
				"ActionDate" = NOW()
			WHERE S."DistributeDate" = (ele ->> 'DistributeDate')::DATE
				AND S."SalePointId" = p_sale_point_id
				AND S."ShiftId" = (ele ->> 'ShiftId')::INT;
			
		END IF;
	
	END LOOP;
	
	v_mess := 'Thao tác thành công';
	
	RETURN QUERY 
	SELECT v_id, v_mess;
	
	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_average_kpi_of_user_by_month(p_month character varying, p_user_id integer DEFAULT 0)
 RETURNS TABLE("UserId" integer, "SalePointId" integer, "KPI" numeric, "TotalWeek" bigint, "AverageKPI" numeric, "DataWeek" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_month DATE := (p_month||'-01')::DATE;
	v_total_month INT := (SELECT 
		date_part('week', date_trunc('month', v_month) + INTERVAL '1 month' - INTERVAL '1 day')
		- date_part('week', date_trunc('month', v_month))
		+1)::INT;
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY K."UserId", K."WeekId", K."SalePointId", K."CriteriaId" ORDER BY K."CreatedDate" DESC) AS "Id",
			K."UserId",
			K."WeekId",
			K."CriteriaId",
			K."SalePointId",
			(K."KPI" * C."Coef") AS "KPI"
		FROM "KPILog" K	
			JOIN "Criteria" C ON K."CriteriaId" = C."CriteriaId"
		WHERE K."IsDeleted" IS FALSE	
			AND K."Month" = p_month
	),
	tmp2 AS (
		SELECT 
			K."UserId",
			K."WeekId",
			K."CriteriaId",
			K."SalePointId",
			K."KPI"
		FROM tmp K
		WHERE K."Id" = 1
			AND (COALESCE(p_user_id, 0) = 0 OR p_user_id = K."UserId")
	),
	tmp3 AS (
		SELECT
			K."UserId",
			K."WeekId",
			K."SalePointId",
			SUM(K."KPI") AS "KPI"
		FROM tmp2 K
		GROUP BY 
			K."UserId",
			K."WeekId",
			K."SalePointId"
	)
	SELECT 
		T."UserId",
		T."SalePointId",
		SUM(T."KPI") AS "KPI",
		COUNT(T."WeekId") AS "TotalWeek",
		ROUND(SUM(T."KPI")::NUMERIC/ v_total_month, 2) AS "AverageKPI",
		(
			SELECT ARRAY_TO_JSON(ARRAY_AGG(R))
			FROM (
				SELECT 
					K.*
				FROM tmp3 K
				WHERE K."UserId" = T."UserId"
					AND K."SalePointId" = T."SalePointId"
			) R
		)::TEXT AS "DataWeek" 
	FROM tmp3 T
	GROUP BY 
		T."UserId",
		T."SalePointId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_data_distribute_month(p_distribute_month character varying)
 RETURNS TABLE("IsIntern" boolean, "DistributeDate" date, "SalePointId" integer, "ShiftId" integer, "UserId" integer, "ShiftTypeId" integer, "ShiftTypeName" character varying, "Note" text, "ShiftMain" character varying, "IsActive" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	WITH tmp AS
	(
			(SELECT 
				FALSE,
				S."DistributeDate",
				S."SalePointId",
				S."ShiftId",
				S."UserId",
				S."ShiftTypeId",
				ST."ShiftTypeName",
				S."Note",
				SM."MainUser",
				S."IsActive"
			FROM "ShiftDistribute" S
				JOIN "SalePoint" SP ON S."SalePointId" = SP."SalePointId"
				JOIN "ShiftType" ST ON ST."ShiftTypeId" = S."ShiftTypeId"
				LEFT JOIN "ShiftMain" SM ON S."ShiftMainId" = SM."ShiftMainId" AND S."SalePointId" = SM."SalePointId"
			WHERE S."UserId" IS NOT NULL AND TO_CHAR(S."DistributeDate",'YYYY-MM') = p_distribute_month AND SP."IsActive" IS TRUE
			ORDER BY S."DistributeDate")
	)
	SELECT *
	FROM tmp T 
	ORDER BY T."DistributeDate";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_data_distribute_month_of_intern(p_distribute_month character varying)
 RETURNS TABLE("DistributeDate" date, "SalePointId" integer, "ShiftId" integer, "UserId" integer, "ShiftTypeId" integer, "ShiftTypeName" character varying, "Note" text, "ShiftMain" character varying, "IsActive" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
