		JOIN "SalePoint" SP ON S."SalePointId" = SP."SalePointId"
		JOIN "ShiftType" ST ON ST."ShiftTypeId" = S."ShiftTypeId"
		LEFT JOIN "ShiftMain" SM ON S."ShiftMainId" = SM."ShiftMainId" AND S."SalePointId" = SM."SalePointId"
