CREATE OR REPLACE FUNCTION public.crm_user_get_intern_data_distribute_month(p_distribute_month text)
 RETURNS TABLE("ShiftDistributeId" integer, "DistributeDate" date, "SalePointId" integer, "ShiftId" integer, "UserId" integer, "ShiftTypeId" integer, "ShiftTypeName" character varying, "Note" text, "ShiftMain" character varying, "IsActive" boolean)
 LANGUAGE plpgsql
AS $function$
BEGIN
	
	RETURN QUERY 
	SELECT 
		S."ShiftDistributeId",
		S."DistributeDate",
		S."SalePointId",
		S."ShiftId",
		S."UserId",
		S."ShiftTypeId",
		ST."ShiftTypeName",
		S."Note",
		SM."MainUser",
		S."IsActive"
	FROM "ShiftDistributeForIntern" S
		JOIN "SalePoint" SP ON S."SalePointId" = SP."SalePointId"
		JOIN "ShiftType" ST ON ST."ShiftTypeId" = S."ShiftTypeId"
		LEFT JOIN "ShiftMain" SM ON S."ShiftMainId" = SM."ShiftMainId" AND S."SalePointId" = SM."SalePointId"
		LEFT JOIN "User" U ON U."UserId" = S."UserId" 
	WHERE S."UserId" IS NOT NULL AND TO_CHAR(S."DistributeDate",'YYYY-MM') = p_distribute_month AND SP."IsActive" IS TRUE AND U."IsIntern" = TRUE 
	ORDER BY S."DistributeDate";
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list(p_page_size integer, p_page_number integer, p_usertitle_id integer)
 RETURNS TABLE("RowNumber" bigint, "TotalCount" bigint, "UserId" integer, "Account" character varying, "Phone" character varying, "FullName" character varying, "Email" character varying, "IsActive" boolean, "IsDeleted" boolean, "StartDate" date, "EndDate" date, "UserTitleId" integer, "UserTitleName" character varying, "SalePointId" integer, "BasicSalary" bigint, "Address" character varying, "BankAccount" character varying, "NumberIdentity" character varying, "IsIntern" boolean)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
BEGIN
	
	RETURN QUERY 
	WITH tmp AS(
		SELECT
			ROW_NUMBER() OVER(PARTITION BY BS."UserId" ORDER BY BS."CreatedDate" DESC) AS "Id",
			BS."UserId",
			BS."Salary"
		FROM "BasicSalary" BS
	),
	tmp2 AS (
		SELECT * 
		FROM tmp 
		WHERE tmp."Id" = 1
	)
	SELECT 
		ROW_NUMBER() OVER (ORDER BY U."UserId") "RowNumber",
		COUNT(1) OVER() AS "TotalCount",
		U."UserId",
		U."Account",
		U."Phone",
		U."FullName",
		U."Email",
		U."IsActive",
		U."IsDeleted",
		U."StartDate",
		U."EndDate",
		UT."UserTitleId",
		UT."UserTitleName",
		U."SalePointId",
		COALESCE(T."Salary", 0) AS "BasicSalary",
		U."Address",
		U."BankAccount",
		U."NumberIdentity",
		U."IsIntern"
	FROM "User" U
		JOIN "UserRole" UR ON UR."UserId" = U."UserId"
		JOIN "UserTitle" UT ON UT."UserTitleId" = UR."UserTitleId"
		LEFT JOIN tmp2 T ON T."UserId" = U."UserId"
	WHERE (COALESCE(p_usertitle_id, 0) = 0 OR UT."UserTitleId" = p_usertitle_id)
		AND UT."UserTitleId" <> 1
-- 		AND U."IsActive" IS TRUE
-- 		AND U."IsIntern" IS FALSE
	ORDER BY 
		U."IsActive" DESC,
		UR."UserTitleId",
		U."SalePointId",
		U."UserId"
	OFFSET v_offset_row LIMIT 100000;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list_borrow_in_date(p_month character varying DEFAULT now(), p_user_id integer DEFAULT 0)
 RETURNS TABLE("Date" date, "TotalData" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
		v_total_date INT := (SELECT date_part('days', (date_trunc('month', (p_month||'-01')::DATE) + INTERVAL '1 month - 1 day'))) :: INT;
		v_time TIMESTAMP := NOW();
BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			U."UserId",
			U."FullName"
		FROM crm_get_user_ddl(2, v_time::DATE) U
		WHERE (U."UserId" = p_user_id OR p_user_id = 0)
	),
	--Lấy tất cả mượn trả từ trước đến giờ
	tmp1 AS (
		SELECT
			MB."UserId",
			MB."Price",
			MB."FormPaymentId",
			F."PaymentName",
			MB."Note",
			MB."CreatedDate",
			MB."IsPay"
		FROM "ManagerBorrow" MB
			JOIN "FormPayment" F ON MB."FormPaymentId" = F."FormPaymentId"
		WHERE MB."IsDeleted" IS FALSE
		ORDER BY 
			MB."CreatedDate"
	),
	--Tạo chuỗi ngày
	tmp3 AS (
		SELECT
			(p_month||'-'||"D")::DATE AS "Date"
		FROM generate_series(1, v_total_date) AS "D"
		WHERE (p_month||'-'||"D")::DATE <= v_time::DATE
	),
	tmp4 AS (
		SELECT
			U."CreatedDate"::DATE AS "Date",
			U."UserId",
			COALESCE(SUM(U."Price") FILTER (WHERE U."IsPay" IS FALSE), 0) AS "TotalBorrow",
			COALESCE(SUM(U."Price") FILTER (WHERE U."IsPay" IS TRUE), 0) AS "TotalPay",
			array_to_json(array_agg(jsonb_build_object(
				'CreatedDate',
				U."CreatedDate",
				'Price',
				U."Price",
				'Note',
				U."Note"
			))) AS "DataInDate"
		FROM tmp1 U
		GROUP BY 
			U."CreatedDate"::DATE,
			U."UserId"
	),
	tmp5 AS (
		SELECT 
			D."Date",
			U."UserId",
			U."FullName",		
			M."DataInDate",
			(
				SELECT
						COALESCE(SUM(T."Price") FILTER (WHERE T."IsPay" IS FALSE), 0) - COALESCE(SUM(T."Price") FILTER (WHERE T."IsPay" IS TRUE), 0) AS "FisrtDate"
				FROM tmp1 T 
				WHERE T."CreatedDate"::DATE < D."Date"
					AND T."UserId" = U."UserId"
			)::NUMERIC AS "FisrtDate",
			COALESCE(M."TotalBorrow", 0) AS "TotalBorrow",
			COALESCE(M."TotalPay", 0) AS "TotalPay"
		FROM tmp3 D 
			JOIN tmp U ON TRUE
			LEFT JOIN tmp4 M ON D."Date" = M."Date" AND U."UserId" = M."UserId"
	),
	tmp6 AS (
		SELECT
			U."Date",
			U."UserId",
			U."FullName",		
			U."DataInDate",
			U."FisrtDate",
			U."TotalBorrow",
			U."TotalPay",
			U."FisrtDate" + U."TotalBorrow" -	U."TotalPay" AS "TotalRemaining"
		FROM tmp5 U
		ORDER BY
			U."Date",
			U."UserId"
	)
	SELECT
		U."Date",
		array_to_json(array_agg(TO_JSONB(U.*)))::TEXT AS "TotalData"
	FROM tmp6 U
	GROUP BY 
		U."Date"
	ORDER BY 
		U."Date" DESC;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list_event_date(p_year integer, p_month integer)
 RETURNS TABLE("EventDayId" integer, "Date" date, "DateName" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
		EV."EventDayId",
		EV."Date",
		EV."Note" AS "DateName"
	FROM "EventDay" EV
	WHERE EV."IsDeleted" IS FALSE
		AND EV."Year" = p_year
		AND (COALESCE(p_month, 0) = 0 OR EV."Month" = p_month)
	ORDER BY EV."Date";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list_kpi_of_user_by_month(p_month character varying, p_user_id integer DEFAULT 0)
 RETURNS TABLE("KPILogId" integer, "UserId" integer, "FullName" character varying, "UserTitleId" integer, "WeekId" integer, "CriteriaId" integer, "CriteriaName" character varying, "SalePointId" integer, "SalePointName" character varying, "KPI" numeric, "CreatedDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
-- 	WITH tmp AS (
-- 		SELECT 
-- 			ROW_NUMBER() OVER(PARTITION BY K."UserId" ORDER BY K."CreatedDate" DESC) AS "Id",
-- 			K."KPILogId",
-- 			K."UserId",
-- 			K."KPI",
-- 			K."CreatedDate"
-- 		FROM "KPILog" K
-- 		WHERE K."IsDeleted" IS FALSE
-- 			AND TO_CHAR(K."CreatedDate", 'YYYY-MM') = p_month
-- 	),
-- 	tmp2 AS (
-- 		SELECT 
-- 			U."UserId",
-- 			U."FullName",
-- 			U."TypeUserId"
-- 		FROM "User" U 
-- 			JOIN "UserRole" UR ON U."UserId" = UR."UserId"
-- 		WHERE U."IsDeleted" IS FALSE	
-- 			AND U."IsActive" IS TRUE
-- 			AND UR."UserTitleId" IN (5)
-- 	)
-- 	SELECT 
-- 		U."UserId",
-- 		U."FullName",
-- 		S."KPILogId",
-- 		S."KPI",
-- 		S."CreatedDate"
-- 	FROM tmp2 U
-- 		LEFT JOIN tmp S ON U."UserId" = S."UserId" AND S."Id" = 1
-- 	WHERE (COALESCE(p_user_id, 0) = 0 OR p_user_id = U."UserId")
-- 	ORDER BY
-- 		U."TypeUserId",
-- 		U."UserId";
	WITH tmp AS (
		SELECT 
			ROW_NUMBER() OVER(PARTITION BY K."UserId", K."WeekId", K."SalePointId", K."CriteriaId" ORDER BY K."CreatedDate" DESC) AS "Id",
			K."KPILogId",
			K."UserId",
			U."FullName",
			UR."UserTitleId",
			K."WeekId",
			K."CriteriaId",
			C."CriteriaName",
			K."SalePointId",
			SP."SalePointName",
			K."KPI",
			K."CreatedDate",
			U."TypeUserId"
		FROM "KPILog" K	
			JOIN "Criteria" C ON K."CriteriaId" = C."CriteriaId"
			JOIN "User" U ON K."UserId" = U."UserId"
			JOIN "UserRole" UR ON U."UserId" = UR."UserId"
			LEFT JOIN "SalePoint" SP ON SP."SalePointId" = K."SalePointId"
		WHERE K."IsDeleted" IS FALSE	
			AND K."Month" = p_month
	)
	SELECT 
		K."KPILogId",
		K."UserId",
		K."FullName",
		K."UserTitleId",
		K."WeekId",
		K."CriteriaId",
		K."CriteriaName",
		K."SalePointId",
		(SELECT S."SalePointName" FROM "SalePoint" S WHERE S."SalePointId" = K."SalePointId"),
		K."KPI",
		K."CreatedDate"
	FROM tmp K
	WHERE K."Id" = 1
		AND (COALESCE(p_user_id, 0) = 0 OR p_user_id = K."UserId")
	ORDER BY 
		K."TypeUserId",
		K."UserId",	
		K."CriteriaId";
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list_manager_borrow_for_confirm(p_user_id integer DEFAULT 0, p_date timestamp without time zone DEFAULT now(), p_page_size integer DEFAULT 100, p_page_number integer DEFAULT 1)
 RETURNS TABLE("ConfirmLogId" integer, "ActionDate" timestamp without time zone, "ActionBy" integer, "ActionByName" character varying, "ConfirmStatusId" integer, "ConfirmStatusName" character varying, "DataConfirm" text, "Note" character varying, "UserId" integer, "FullName" character varying)
 LANGUAGE plpgsql
AS $function$ 
DECLARE
	v_offset_row INT8 := p_page_size * (p_page_number - 1);
BEGIN
	RETURN QUERY 
	SELECT 
		CL."ConfirmLogId",
		CL."ActionDate",
		CL."ActionBy",
		CL."ActionByName",
		CL."ConfirmStatusId",
		CS."ConfirmStatusName",
		(
			SELECT
				array_to_json(array_agg(T::JSONB ||('{"PaymentName": "'||(SELECT "PaymentName" FROM "FormPayment" WHERE "FormPaymentId" = (T->>'FormPaymentId')::INT)::TEXT||'"}')::JSONB))
			FROM json_array_elements(((CL."Data"::JSON)->>'DataForConfirm')::JSON) T
		)::TEXT AS "DataConfirm",
		(CL."Data"::JSON->>'Note')::VARCHAR AS "Note",
		CL."DataActionInfo"::INT AS "UserId",
		U."FullName"
	FROM "ConfirmLog" CL 
		JOIN "ConfirmStatus" CS ON CL."ConfirmStatusId" = CS."ConfirmStatusId"
		JOIN "User" U ON CL."DataActionInfo"::INT = U."UserId"
	WHERE CL."ConfirmFor" = 5
		AND (CL."DataActionInfo"::INT = p_user_id OR p_user_id = 0)
		AND CL."IsDeleted" IS FALSE
		AND CL."ActionDate"::DATE = p_date::DATE
	ORDER BY CL."ActionDate" DESC
	OFFSET v_offset_row LIMIT p_page_size;	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list_off_of_leader(p_month character varying)
 RETURNS TABLE("UserId" integer, "FullName" character varying, "Date" date, "Note" character varying, "ActionBy" integer, "ActionByName" character varying, "ActionDate" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	SELECT 
		LO."UserId",
		U."FullName",
		LO."WorkingDate",
		LO."Note",
		LO."ActionBy",
		LO."ActionByName",
		LO."ActionDate"
	FROM "LeaderOffLog" LO
		JOIN "User" U ON LO."UserId" = U."UserId"
	WHERE TO_CHAR(LO."WorkingDate", 'YYYY-MM') = p_month
		AND LO."IsDeleted" IS FALSE
	ORDER BY 
		LO."WorkingDate";
