$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_list_target()
 RETURNS TABLE("ResponsibilityLottery" text, "TargetVietlott" text, "TargetLottery" text, "TargetKPI" text, "TargetKPILeader" text)
 LANGUAGE plpgsql
AS $function$
BEGIN
	RETURN QUERY
	WITH tmp AS(
		SELECT 
			TD."TargetDataTypeId",
			TD."FromValue" AS "minTarget",
			TD."ToValue" AS "maxTarget",
			TD."Value" AS "commission"
		FROM "TargetData" TD
		WHERE TD."IsDeleted" IS FALSE
	),
	tmp2 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "ResponsibilityLottery"
		FROM (
			SELECT
				T."minTarget",
				T."maxTarget",
				T."commission"
			FROM tmp T
			WHERE T."TargetDataTypeId" = 1
			ORDER BY T."minTarget"
		) R
	),
	tmp3 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "TargetVietlott"
		FROM (
			SELECT
				T."minTarget",
				T."maxTarget",
				T."commission"
			FROM tmp T
			WHERE T."TargetDataTypeId" = 2
			ORDER BY T."minTarget"
		) R
	),
	tmp4 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "TargetLottery"
		FROM (
			SELECT
				T."minTarget",
				T."maxTarget",
				T."commission"
			FROM tmp T
			WHERE T."TargetDataTypeId" = 3
			ORDER BY T."minTarget"
		) R
	),
	tmp5 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "TargetKPI"
		FROM (
			SELECT
				T."minTarget",
				T."maxTarget",
				T."commission"
			FROM tmp T
			WHERE T."TargetDataTypeId" = 4
			ORDER BY T."minTarget"
		) R
	),
	tmp6 AS (
		SELECT ARRAY_TO_JSON(ARRAY_AGG(R))::TEXT AS "TargetKPILeader"
		FROM (
			SELECT
				T."minTarget",
				T."maxTarget",
				T."commission"
			FROM tmp T
			WHERE T."TargetDataTypeId" = 5
			ORDER BY T."minTarget"
		) R
	)
	SELECT
		tmp2."ResponsibilityLottery",
		tmp3."TargetVietlott",
		tmp4."TargetLottery",
		tmp5."TargetKPI",
		tmp6."TargetKPILeader"
	FROM tmp2, tmp3, tmp4, tmp5, tmp6;
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_session_info(p_user_id integer)
 RETURNS TABLE("UserId" integer, "UserRoleId" integer, "Account" character varying, "FullName" character varying, "Email" character varying, "UserTitleId" integer, "UserTitleName" character varying, "SubUserTitle" text, "IsSuperAdmin" boolean, "IsManager" boolean, "IsStaff" boolean, "SalePointId" integer, "ShiftDistributeId" integer, "IsIntern" boolean)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_user_role_id INT;
	v_user_title_id INT;
	v_user_title_name VARCHAR;
	v_sub_user_title INT[];
BEGIN
	
	SELECT 
		UR."UserRoleId",
		UT."UserTitleId",
		UT."UserTitleName",
		UR."SubUserTitleId" 
	INTO v_user_role_id, v_user_title_id, v_user_title_name, v_sub_user_title
	FROM "UserRole" UR 
		JOIN "UserTitle" UT ON UT."UserTitleId" = UR."UserTitleId"
	WHERE UR."UserId" = p_user_id;
	
	RETURN QUERY 
	WITH tmp AS(
		SELECT * FROM fn_get_shift_info(v_user_role_id)
	)
	SELECT 
		p_user_id,
		v_user_role_id,
		U."Account",
		U."FullName",
		U."Email",
		v_user_title_id,
		v_user_title_name,
		(
			CASE WHEN array_length(v_sub_user_title, 1) > 0 THEN
				(SELECT
					array_to_json(array_agg(
						(	
							SELECT 
								json_build_object(
									'UserTitleId',
									A,
									'UserTitleName',
									UT."UserTitleName"
								)
							FROM "UserTitle" UT 
							WHERE UT."UserTitleId" = A 
						)::JSON
					))
				FROM UNNEST(v_sub_user_title) A)::TEXT
			ELSE
				'[]'
			END
		)::TEXT AS "SubUserTitle",
		t."IsSuperAdmin",
		t."IsManager",
		t."IsStaff",
		t."SalePointId",
		t."ShiftDistributeId",
		U."IsIntern"
	FROM "User" U
		JOIN tmp t ON t."UserId" = U."UserId"
	WHERE U."IsActive" IS TRUE AND U."IsDeleted" IS FALSE AND U."UserId" = p_user_id;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_get_target_master(p_user_title_id integer DEFAULT 0)
 RETURNS TABLE("TargetMasterLevelData" text, "TargetMasterLevel" text, "TargetKPI" text, "TargetKPILeader" text)
 LANGUAGE plpgsql
AS $function$

BEGIN
	RETURN QUERY
	WITH tmp AS (
		SELECT 
			C."CriteriaId",
			C."CriteriaName",
			C."UserTitleId"
		FROM "Criteria" C
		WHERE (C."UserTitleId" = p_user_title_id OR p_user_title_id = 0)
		ORDER BY
			C."UserTitleId",
			C."CriteriaId"
	),
	tmp2 AS (
		SELECT
			T."CriteriaId",
			T."UserTitleId",
			array_to_json(
				array_agg(
					TO_JSONB(TM.*)
				)	
			) AS "TargetMaster"
		FROM tmp T
			JOIN "TargetMaster" TM ON T."CriteriaId" = TM."CriteriaId"
		GROUP BY 
			T."CriteriaId",
			T."UserTitleId"
	),
	tmp3 AS (
		SELECT DISTINCT 
			TM."LevelId", 
			T."UserTitleId",
			TM."TargetMasterName"
		FROM tmp T
			JOIN "TargetMaster" TM ON T."CriteriaId" = TM."CriteriaId"
		ORDER BY 
			TM."LevelId"
	),
	tmp4 AS (
		SELECT
			array_to_json(array_agg(R))::TEXT AS "TargetMasterLevelData"
		FROM (
			SELECT T.* 
			FROM tmp2 T
			ORDER BY 
				T."UserTitleId",
				T."CriteriaId"
		) R
	),
	tmp5 AS (
		SELECT
			array_to_json(array_agg(R))::TEXT AS "TargetMasterLevel"
		FROM (
			SELECT * FROM tmp3
		) R 
	),
	tmp6 AS (
		SELECT 
			T."TargetKPI",
			T."TargetKPILeader"
		FROM crm_user_get_list_target() T
	)
	SELECT 
		tmp4."TargetMasterLevelData",
		tmp5."TargetMasterLevel",
		tmp6."TargetKPI",
		tmp6."TargetKPILeader"
	FROM tmp4
		JOIN tmp5 ON TRUE
		JOIN tmp6 ON TRUE;	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_insert_promotioncode(p_date timestamp without time zone, p_total_number bigint)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;
BEGIN   
	raise notice '%', p_date;
	WITH tmp AS
	(
		SELECT 
			A::INT AS "Number",
			p_date::DATE AS "Date"
		FROM generate_series(1, p_total_number) A
	)
	INSERT INTO "PromotionCode" ("PromotionCode", "Date")
	SELECT 
		CONCAT( SUBSTRING('000000',1,(6 - LENGTH(T."Number"::VARCHAR(255)))), T."Number"::VARCHAR(255)), 
		T."Date"
	FROM tmp T
	ORDER BY RANDOM();

    v_id := 1;
    v_mess := 'Update successful';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_pay_for_borrow(p_action_by integer, p_action_by_name character varying, p_action_type integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON := p_data::JSON;
	v_time TIMESTAMP := NOW();
BEGIN
	--INSERT
	IF p_action_type = 1 THEN 
		
		INSERT INTO "ManagerBorrow"(
			"UserId",
			"FormPaymentId",
			"Price",
			"CreatedBy",
			"CreatedByName",
			"CreatedDate",
			"Note",
			"IsDeleted",
			"IsPay"
		)
		VALUES(
			(ele->>'UserId')::INT,
			COALESCE((ele->>'FormPaymentId')::INT, 1),
			(ele->>'Price')::NUMERIC,
			p_action_by,
			p_action_by_name,
			v_time,
			(ele->>'Note')::VARCHAR,
			FALSE,
			TRUE			
		)RETURNING "ManagerBorrowId" INTO v_id;
				
		v_mess := 'Thêm thành công';
		
	--UPDATE
	ELSEIF p_action_type = 2 THEN
		--DO SOMETHING
		
		v_id := 1;
		v_mess := 'Cập nhật thành công';
		
	--DELETE
	ELSEIF p_action_type = 3 THEN
	
		IF (ele->>'ManagerBorrowId')::INT IS NOT NULL THEN
		
			UPDATE "ManagerBorrow"
			SET	
				"IsDeleted" = TRUE,
				"CreatedBy" = p_action_by,
				"CreatedByName" = p_action_by_name,
				"CreatedDate" = v_time
			WHERE "ManagerBorrowId" = (ele->>'ManagerBorrowId')::INT;
			
			v_id := 1;
			v_mess := 'Xóa thành công';
		ELSE 
			RAISE 'Không có gì để xoá';
		END IF;
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_shift_distribute_month(p_action_by integer, p_action_by_name character varying, p_month character varying, p_distribute_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT := 1;
	v_mess TEXT;
	v_data JSON := p_distribute_data::JSON;
	ele JSON;
	v_shift_main_id INT;
	shift JSON;
BEGIN
	
	FOR ele IN SELECT * FROM json_array_elements(v_data) LOOP
		
		IF NOT EXISTS (SELECT 1 FROM "ShiftMain" WHERE "SalePointId" = (ele ->> 'SalePointId')::INT AND "Month" = p_month) THEN
			
			INSERT INTO "ShiftMain"(
				"SalePointId",
				"Month",
				"MainUser"
			) VALUES (
				(ele ->> 'SalePointId')::INT,
				p_month,
				(ele ->> 'MainShift')
			) RETURNING "ShiftMainId" INTO v_shift_main_id;
		
		ELSE

			UPDATE "ShiftMain"
			SET
				"MainUser" = (ele ->> 'MainShift')
			WHERE "SalePointId" = (ele ->> 'SalePointId')::INT AND "Month" = p_month
			RETURNING "ShiftMainId" INTO v_shift_main_id;
		
		END IF;
		
		FOR shift IN SELECT * FROM json_array_elements((ele ->> 'ShiftData')::JSON) LOOP
		
			IF NOT EXISTS (SELECT 1 FROM "ShiftDistribute" S WHERE S."SalePointId" = (ele ->> 'SalePointId')::INT 
				AND S."DistributeDate" = (shift ->> 'DistributeDate')::DATE 
				AND S."ShiftId" = (shift ->> 'ShiftId')::INT) THEN	
			
				INSERT INTO "ShiftDistribute"(
					"DistributeDate",
					"SalePointId",
					"ShiftId",
					"UserId",
					"ShiftTypeId",
					"Note",
					"ShiftMainId",
					"ActionBy",
					"ActionByName"
				) VALUES (
					(shift ->> 'DistributeDate')::DATE,
					(ele ->> 'SalePointId')::INT,
					(shift ->> 'ShiftId')::INT,
					(shift ->> 'UserId')::INT,
					(shift ->> 'ShiftTypeId')::INT,
					(shift ->> 'Note'),
					v_shift_main_id,
					p_action_by,
					p_action_by_name
				);
				
			ELSE 
			
				UPDATE "ShiftDistribute" S
				SET
					"UserId" = (shift ->> 'UserId')::INT,
					"Note" = (shift ->> 'Note'),
					"ShiftTypeId" = (shift ->> 'ShiftTypeId')::INT,
					"ActionBy" = p_action_by,
					"ActionByName" = p_action_by_name,
					"ActionDate" = NOW()
				WHERE S."DistributeDate" = (shift ->> 'DistributeDate')::DATE
					AND S."SalePointId" = (ele ->> 'SalePointId')::INT
					AND S."ShiftId" = (shift ->> 'ShiftId')::INT;
				
			END IF;
		
		END LOOP;
		
	END LOOP;
	
	v_mess := 'Thao tác thành công';
	
	RETURN QUERY 
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_shift_distribute_month_get_data(p_month character varying)
 RETURNS TABLE("SalePointId" integer, "SalePointName" character varying, "DistributeDate" date, "ShiftId" integer, "UserId" integer, "FullName" character varying, "ShiftTypeId" integer, "ShiftTypeName" character varying)
 LANGUAGE plpgsql
AS $function$
DECLARE
BEGIN

	RETURN QUERY
	SELECT 
		SD."SalePointId", 
		SP."SalePointName", 
		SD."DistributeDate", 
		SD."ShiftId", 
		SD."UserId", 
		u."FullName", 
		SD."ShiftTypeId", 
		ST."ShiftTypeName"
	FROM "SalePoint" SP
		LEFT JOIN "ShiftDistribute" SD ON SP."SalePointId"= SD."SalePointId"
		LEFT JOIN "ShiftType" ST ON SD."ShiftTypeId" = ST."ShiftTypeId"
		LEFT JOIN "User" U On U."UserId" = SD."UserId"
	WHERE TO_CHAR(SD."DistributeDate", 'YYYY-MM') = p_month;
	
END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_shift_distribute_month_intern(p_action_by integer, p_action_by_name character varying, p_month character varying, p_distribute_data text, p_attendance_data text)
