		IF NOT EXISTS (SELECT 1 FROM "ShiftMainForIntern" WHERE "SalePointId" = (ele ->> 'SalePointId')::INT AND "Month" = p_month FOR UPDATE) THEN
			
			INSERT INTO "ShiftMainForIntern"(
