				AND S."DistributeDate" = (shift ->> 'DistributeDate')::DATE 
				AND S."ShiftId" = (shift ->> 'ShiftId')::INT  FOR UPDATE) THEN	
			
				INSERT INTO "ShiftDistributeForIntern"(
