					p_action_by,
					p_action_by_name
				);
				
			ELSE 
			
				UPDATE "ShiftDistributeForIntern" S
				SET
					"UserId" = (shift ->> 'UserId')::INT,
					"Note" = (shift ->> 'Note'),
					"ShiftTypeId" = (shift ->> 'ShiftTypeId')::INT,
					"ActionBy" = p_action_by,
