CREATE OR REPLACE FUNCTION public.crm_user_update_event_date(p_action_by integer, p_action_by_name character varying, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	v_cur_time DATE := NOW()::DATE;
ele JSON;
BEGIN

	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		IF((ele->>'Date')::DATE <= v_cur_time) THEN 
			RAISE 'Không thể cập nhật ngày của quá khứ';
		END IF;
	END LOOP;

	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		--Xóa 
		IF((ele ->> 'IsDeleted')::BOOL IS TRUE) THEN 
			UPDATE "EventDay"
			SET "IsDeleted" = TRUE,
				"ModifyBy" = p_action_by,
				"ModifyByName" = p_action_by_name,
				"ModifyDate" = v_cur_time
			WHERE "Date" = (ele ->> 'Date')::DATE AND "IsDeleted" IS FAlSE;
			
			v_id := 1;
			v_mess := 'Xóa thành công';
		-- Nếu không xóa
		ELSEIF((ele ->> 'IsDeleted')::BOOL IS FALSE OR ((ele ->> 'IsDeleted')::BOOL IS NULL)) THEN
			-- Thêm nếu chưa có
			IF(NOT EXISTS(SELECT 1 FROM "EventDay" E WHERE E."Date" = (ele->>'Date')::DATE AND E."IsDeleted" IS FALSE)) THEN 
				INSERT INTO "EventDay"(
					"Year",
					"Date",
					"CreateBy",
					"CreateByName",
					"CreateDate",
					"Month",
					"IsDeleted",
					"Note"					
				)
				VALUES(
					EXTRACT(YEAR FROM (ele ->> 'Date')::DATE),
					(ele ->> 'Date')::DATE,
					p_action_by,
					p_action_by_name,
					v_cur_time,
					EXTRACT(MONTH FROM (ele ->> 'Date')::DATE),
					FALSE,
					(ele ->> 'Note')::VARCHAR
				);
				
				v_id := 1;
				v_mess := 'Thêm thành công';
			-- Cập nhật lại khi đã có và chưa bị xóa
			ELSE 
				UPDATE "EventDay"
				SET
					"Note" = (ele ->> 'Note')::VARCHAR, 
					"ModifyBy" = p_action_by,
					"ModifyByName" = p_action_by_name,
					"ModifyDate" = v_cur_time
				WHERE "Date" = (ele ->> 'Date')::DATE AND "IsDeleted" IS FALSE;
				
				v_id := 1;
				v_mess := 'Cập nhật thành công';
			END IF;
		END IF;
	END LOOP;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
	v_id := -1;
	v_mess := sqlerrm;
	RETURN QUERY
	SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_info(p_action_by integer, p_action_by_name character varying, p_user_id integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
	ele JSON := p_data::JSON;
	v_check BOOL;
	v_check_2 BOOL;
	v_account VARCHAR;
	v_email VARCHAR;
BEGIN	

	SELECT "IsActive", "IsDeleted", "Account", "Email" INTO v_check, v_check_2, v_account, v_email FROM "User" WHERE "UserId" = p_user_id;
	IF v_check IS FALSE OR v_check_2 IS TRUE THEN
		IF EXISTS (
			SELECT 1 FROM "User" U
			WHERE U."UserId" <> p_user_id
				AND (U."Account" = v_account OR U."Email" = v_email)
		) THEN
			RAISE 'Không thể cập nhật do tài khoản này đã được tạo mới trong hệ thống';
		END IF;
	END IF;

	UPDATE "User"
	SET
		"IsActive" = COALESCE((ele->> 'IsActive'):: BOOLEAN, "IsActive"),
		"FullName" = COALESCE((ele->> 'FullName'):: VARCHAR,"FullName"),
		"IsDeleted" = COALESCE((ele ->> 'IsDeleted')::BOOL, "IsDeleted"),
		"StartDate" = COALESCE((ele ->> 'StartDate')::DATE, "StartDate"),
		"EndDate" = (CASE WHEN (ele->> 'IsActive'):: BOOLEAN IS FALSE THEN COALESCE((ele ->> 'EndDate')::DATE, "EndDate") ELSE NULL::DATE END),
		"SalePointId" = COALESCE((ele->>'SalePointId')::INT, "SalePointId"),
		"ModifyBy"= p_action_by,	
		"ModifyByName" = p_action_by_name,
		"Address" = COALESCE((ele ->> 'Address')::VARCHAR,"Address"),
		"BankAccount" = COALESCE((ele ->> 'BankAccount')::VARCHAR,"BankAccount"),
		"NumberIdentity" = COALESCE((ele ->> 'NumberIdentity')::VARCHAR,"NumberIdentity"),
		"Phone" = COALESCE ((ele->>'Phone')::VARCHAR,"Phone"),
		"IsIntern" = COALESCE ((ele->>'IsIntern')::BOOL,"IsIntern")
	WHERE "UserId"= p_user_id;
	
	IF((ele->>'BasicSalary')::INT IS NOT NULL) THEN
		IF NOT EXISTS(
		SELECT 1 
		FROM "BasicSalary" C 
		WHERE C."UserId" = p_user_id
			AND C."Salary" = (ele->>'BasicSalary')::INT
			AND C."Salary" = ANY(
				SELECT B."Salary"
				FROM "BasicSalary" B 
				WHERE B."UserId" = p_user_id 
				ORDER BY B."CreatedDate" DESC
				LIMIT 1
			)
		) THEN 
			INSERT INTO "BasicSalary" (
				"UserId",
				"CreatedBy",
				"CreatedByName",
				"Salary"
			)
			VALUES(
				p_user_id,
				p_action_by,
				p_action_by_name,
				(ele->>'BasicSalary')::INT
			);
		END IF;
	END IF;
	
	v_id := 1;
	v_mess := 'Cập nhật người dùng thành công';
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_kpi_of_user(p_action_by integer, p_action_by_name character varying, p_data text, p_action_type integer DEFAULT 1)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
	DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	v_time TIMESTAMP := NOW();
	v_check NUMERIC;
BEGIN
	--INSERT
	IF p_action_type = 1 THEN 
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			IF ((ele->>'KPILogId')::INT IS NULL) THEN
				
				IF((ele->>'WeekId')::INT IS NULL OR (ele->>'CriteriaId')::INT IS NULL) THEN
					RAISE 'Không đủ thông tin để cập nhật';
				END IF;
				
				SELECT C."MaxValue" INTO v_check FROM "Criteria" C WHERE C."CriteriaId" = (ele->>'CriteriaId')::INT;

				IF((ele->>'KPI')::NUMERIC > v_check) THEN 
					RAISE 'Số KPI nhập vào % lớn hơn số KPI giới hạn %',(ele->>'KPI')::NUMERIC, v_check;
				END IF;
				
				INSERT INTO "KPILog"(
					"UserId",
					"KPI",
					"WeekId",
					"SalePointId",
					"CriteriaId",
					"Note",
					"Month",
					"CreatedBy",
					"CreatedByName",
					"CreatedDate",
					"IsDeleted" 
				)
				VALUES(
					(ele->>'UserId')::INT,
					(ele->>'KPI')::NUMERIC,
					(ele->>'WeekId')::INT,
					COALESCE((ele->>'SalePointId')::INT, 0),
					(ele->>'CriteriaId')::INT,
					(ele->>'Note')::VARCHAR,
					(ele->>'Month')::VARCHAR,
					p_action_by,
					p_action_by_name,
					v_time,
					FALSE
				);
				
			ELSE 
			
				UPDATE "KPILog" 
				SET 
					"KPI" = COALESCE((ele->>'KPI')::NUMERIC, "KPI"),
					"Note" = COALESCE((ele->>'Note')::VARCHAR, "Note"),
					"ModifyBy" = p_action_by,
					"ModifyByName" = p_action_by_name,
					"ModifyDate" = v_time
				WHERE "KPILogId" = (ele->>'KPILogId')::INT;
				
			END IF;	
			
		END LOOP;
		
		v_id := 1;
		v_mess := 'Cập nhật thành công';
	--DELETE
	ELSEIF p_action_type = 3 THEN
	
		FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
			UPDATE "KPILog"
			SET 
				"IsDeleted" = TRUE
			WHERE "KPILogId" = (ele->>'KPILogId')::INT;
	
		END LOOP;
			
		v_id := 1;
		v_mess := 'Xoá thành công';
		
	END IF;
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_scratch_card_full_log(p_acction_by integer, p_acction_by_name character varying, p_scratchcard_full_log_id integer, p_revision_number integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
v_number INT := (SELECT SFL."TotalReceived" FROM "ScratchcardFullLog" SFL WHERE SFL."ScratchcardFullLogId"= p_scratchcard_full_log_id) - p_revision_number;
BEGIN   
	UPDATE "ScratchcardFullLog"
	SET
			"TotalReceived" = p_revision_number,
			"IsActive" = FALSE
	WHERE "ScratchcardFullLogId"= p_scratchcard_full_log_id;
	UPDATE "ScratchcardFull" 
	SET "TotalRemaining" = ("TotalRemaining" - v_number)
	WHERE "LotteryChannelId" = (SELECT 
																SCFL."LotteryChannelId"
															FROM "ScratchcardFullLog" SCFL
															LEFT JOIN "ScratchcardFull" SCF
																ON SCF."LotteryChannelId" = SCFL."LotteryChannelId"
															WHERE SCFL."ScratchcardFullLogId"= p_scratchcard_full_log_id);
    
    v_id := 1;
    v_mess := 'Update thanh cong';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_scratchcard_full_log(p_action_by integer, p_action_by_name character varying, p_scratchcard_full_log_id integer, p_revision_number integer, p_is_deleted boolean)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
v_number INT := (SELECT SFL."TotalReceived" FROM "ScratchcardFullLog" SFL WHERE SFL."ScratchcardFullLogId"= p_scratchcard_full_log_id) - p_revision_number;
BEGIN   
	IF p_is_deleted IS FALSE
	THEN
		UPDATE "ScratchcardFullLog"
		SET
				"ActionBy" = p_action_by,
				"ActionByName" = p_action_by_name,
				"TotalReceived" = p_revision_number,
				"IsActive" = TRUE,
				"ActionDate" = NOW()
		WHERE "ScratchcardFullLogId"= p_scratchcard_full_log_id;
		UPDATE "ScratchcardFull" 
			SET 
				"TotalRemaining" = ("TotalRemaining" - v_number),
				"LastActionBy" = p_action_by,
				"LastActionByName" = p_action_by_name,
				"LastActionDate" = NOW()
		WHERE "LotteryChannelId" = (SELECT 
																	SCFL."LotteryChannelId"
																FROM "ScratchcardFullLog" SCFL
																LEFT JOIN "ScratchcardFull" SCF
																	ON SCF."LotteryChannelId" = SCFL."LotteryChannelId"
																WHERE SCFL."ScratchcardFullLogId"= p_scratchcard_full_log_id);
	ELSE
		UPDATE "ScratchcardFullLog"
		SET
				"ActionBy" = p_action_by,
				"ActionByName" = p_action_by_name,
				"IsActive" = FALSE,
				"ActionDate" = NOW()
		WHERE "ScratchcardFullLogId"= p_scratchcard_full_log_id;
		UPDATE "ScratchcardFull" 
			SET 
				"TotalRemaining" = ("TotalRemaining" - v_number),
				"LastActionBy" = p_action_by,
				"LastActionByName" = p_action_by_name,
				"LastActionDate" = NOW()
		WHERE "LotteryChannelId" = (SELECT 
																	SCFL."LotteryChannelId"
																FROM "ScratchcardFullLog" SCFL
																LEFT JOIN "ScratchcardFull" SCF
																	ON SCF."LotteryChannelId" = SCFL."LotteryChannelId"
																WHERE SCFL."ScratchcardFullLogId"= p_scratchcard_full_log_id);
		END IF;
    
    v_id := 1;
    v_mess := 'Update thanh cong';
   RETURN QUERY   
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_scratchcard_log(p_action_by integer, p_action_by_name character varying, p_scratchcard_log_id integer, p_revision_number integer)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE   
v_id INT;  
v_mess TEXT;   
v_number INT := (SELECT SL."TotalReceived" FROM "ScratchcardLog" SL WHERE SL."ScratchcardLogId" = p_scratchcard_log_id) - p_revision_number;
BEGIN   
	
	UPDATE "ScratchcardLog"
	SET
		"TotalReceived" = p_revision_number
	WHERE "ScratchcardLogId" = p_scratchcard_log_id;
	UPDATE "ScratchcardFull" 
	SET "TotalRemaining" = ("TotalRemaining" + v_number)
	WHERE "LotteryChannelId" = (SELECT 
																SCL."LotteryChannelId"
															FROM "ScratchcardLog" SCL
															LEFT JOIN "ScratchcardFull" SCF
																ON SCF."LotteryChannelId" = SCL."LotteryChannelId"
															WHERE SCL."ScratchcardLogId"= p_scratchcard_log_id);
	UPDATE "Scratchcard"
	SET "TotalReceived" = ("TotalReceived" - v_number),
	"TotalRemaining" = ("TotalRemaining" - v_number)
	WHERE "SalePointId" = (SELECT 
													 SL."SalePointId"
												 FROM "ScratchcardLog" SL
												 WHERE SL."ScratchcardLogId" = p_scratchcard_log_id
												 LIMIT 1);
    
    v_id := 1;
    v_mess := 'Update thanh cong';
		raise notice '%',v_number;
   RETURN QUERY   
	 
SELECT
    v_id,
    v_mess;

   EXCEPTION WHEN OTHERS THEN    
			BEGIN        
				v_id := -1;        
				v_mess := sqlerrm;        
	 RETURN QUERY        
			SELECT 
				v_id, 
				v_mess;    
	 END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_shift(p_action_by integer, p_action_by_name character varying, p_distribute_date timestamp without time zone, p_sale_point_id integer, p_shift_id integer, p_user_id integer, p_shift_type_id integer, p_note text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE 
	v_id INT;
	v_mess TEXT;
BEGIN
	
	UPDATE "ShiftDistribute"
	SET
		"IsActive" = FALSE,
		"ActionBy" = p_action_by,
		"ActionByName" = p_action_by_name,
		"ActionDate" = NOW()
	WHERE "DistributeDate" = p_distribute_date::DATE
		AND "SalePointId" = p_sale_point_id
		AND "ShiftId" = p_shift_id;
		
	INSERT INTO "ShiftDistribute"(
		"DistributeDate",
		"SalePointId",
		"ShiftId",
		"UserId",
		"ActionBy",
		"ActionByName",
		"ShiftTypeId",
		"Note"
	) VALUES (
		p_distribute_date::DATE,
		p_sale_point_id,
		p_shift_id,
		p_user_id,
		p_action_by,
		p_action_by_name,
		p_shift_type_id,
		p_note
	) RETURNING "ShiftDistributeId" INTO v_id;
	
	v_mess := 'Cập nhật ca làm việc thành công';
	
	RETURN QUERY 
	SELECT 	v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN				
		v_id := -1;
		v_mess := sqlerrm;
		
		RETURN QUERY 
		SELECT 	v_id, v_mess;
	END;

END;
$function$


CREATE OR REPLACE FUNCTION public.crm_user_update_target(p_action_by integer, p_action_by_name character varying, p_target_type_id integer, p_data text)
 RETURNS TABLE("Id" integer, "Message" text)
 LANGUAGE plpgsql
AS $function$
DECLARE
	v_id INT;
	v_mess TEXT;
	ele JSON;
	A INT[] := '{}' ::INT[];
	v_time TIMESTAMP := NOW();
	v_value_index INT;
BEGIN
	FOR ele IN SELECT * FROM json_array_elements(p_data::JSON) LOOP
		
		IF NOT EXISTS (
			SELECT 1 FROM "TargetData" T 
			WHERE T."TargetDataTypeId" = p_target_type_id 
				AND T."FromValue" = (ele->>'FromValue')::INT 
				AND T."ToValue" = (ele->>'ToValue')::INT
				AND T."Value" = (ele->>'Value')::NUMERIC
				AND T."IsDeleted" IS FALSE
		) THEN 
			-- Insert nếu chưa có
			INSERT INTO "TargetData"(
				"TargetDataTypeId",
				"FromValue",
				"ToValue",
				"Value",
				"IsDeleted",
			  "CreatedBy",
				"CreatedByName",
				"CreatedDate"
			)
			VALUES(
				p_target_type_id,
				(ele->>'FromValue')::INT,
				(ele->>'ToValue')::INT,
				(ele->>'Value')::NUMERIC,
				FALSE,
				p_action_by,
				p_action_by_name,
				v_time
			) RETURNING "TargetDataId" INTO v_value_index;
			
			SELECT array_append(A, v_value_index) INTO A;
			
		ELSE
			--Thêm id của số đã có vào mảng tạm
			SELECT T."TargetDataId" INTO v_value_index 
			FROM "TargetData" T
			WHERE T."TargetDataTypeId" = p_target_type_id 
				AND T."FromValue" = (ele->>'FromValue')::INT 
				AND T."ToValue" = (ele->>'ToValue')::INT
				AND T."Value" = (ele->>'Value')::NUMERIC
				AND T."IsDeleted" IS FALSE;
				
			SELECT array_append(A, v_value_index) INTO A;
						
		END IF;
		
	END LOOP;
	
	UPDATE "TargetData"
	SET "IsDeleted" = TRUE
	WHERE "TargetDataTypeId" = p_target_type_id
		AND "TargetDataId" <> ALL(A)
		AND "IsDeleted" IS FALSE;
	
	v_id := 1;
	v_mess := 'Cập nhật thành công';
	
	RETURN QUERY
	SELECT v_id, v_mess;

	EXCEPTION WHEN OTHERS THEN
	BEGIN
		v_id := -1;
		v_mess := sqlerrm;
		RETURN QUERY
		SELECT v_id, v_mess;
	END;

END;
$function$


